-- ----------------------------
-- Table structure for `p_task`
-- ----------------------------
DROP TABLE IF EXISTS `p_task`;
CREATE TABLE `p_task` (
  `taskId` varchar(36) NOT NULL,
  `taskName` varchar(20) NOT NULL COMMENT '任务',
  `description` varchar(100) DEFAULT NULL COMMENT '描述',
  `publisher` varchar(20) NOT NULL COMMENT '发布者',
  `publishId` varchar(36) NOT NULL COMMENT '发布者ID',
  `startTime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  `finishTime` varchar(20) NOT NULL,
  `isReceive` int(11) NOT NULL DEFAULT '0' COMMENT '是否被接收',
  `taskMoney` int(11) NOT NULL COMMENT '悬赏',
  `receiveNum` int(11) NOT NULL DEFAULT '0',
  `state` int(11) NOT NULL DEFAULT '0' COMMENT '查看自己发布的任务是否完成',
  `totalNum` int(11) NOT NULL DEFAULT '1' COMMENT '需要人数',
  `isChild` int(11) NOT NULL DEFAULT '0' COMMENT '是否为子任务',
  `taskText` mediumtext CHARACTER SET utf8mb4,
  `parentId` varchar(36) DEFAULT NULL,
  PRIMARY KEY (`taskId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of p_task
-- ----------------------------
INSERT INTO `p_task` VALUES ('0b1c691f-93f7-488c-865d-d3c2adb15405', '测试8[卷12]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '12', '---------------\r\n1951年4月4日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月4日\r\n　　海莲亲爱的：\r\n　　你寄来的复活节包裹已经收到，这些礼物实在太棒了！不过，因为弗兰克第二天一早就出差去了，所以他没空回信向你道谢，大家都急得跳脚，而其他人全都不敢斗胆写信给“弗兰克的汉芙小姐”。\r\n　　包裹里的肉食实在太棒！我认为你真的不该再这样子为我们破费了，一定花了你不少钱吧？愿上帝保佑你的好心肠。\r\n　　本·马克斯先生走过来要找事给我做了，就此搁笔。\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　伦敦西八区肯辛顿高地街伯爵社区\r\n　　1951年4月5日\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您寄到马克斯与科恩书店的复活节礼物，已于几天前寄达。但弗兰克·德尔先生因公外出，错过与大家分享喜悦了。\r\n　　一见到包裹里头的肉，所有人的眼睛都看直了，而鸡蛋也大获欢迎。我觉得有必要给您写个信，向您报告：所有同仁对您的好意和慷慨都万分感激。\r\n　　我们所有同仁都期盼您能尽快来英国，届时我们一定会竭尽心力，让您有一趟愉快的英伦之旅。\r\n　　梅甘·韦尔斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　埃塞克斯滨海南庄桶桥路\r\n　　1951年4月5日\r\n　　敬爱的汉芙小姐：\r\n　　我是马克斯与科恩书店的编目员，已在书店任职即将届满两年。谨在此向您表达感激之意。谢谢您多次寄赠礼物包裹给我们。\r\n　　我现在与七十五岁的姨婆住在一起。当我带着您送的肉、牛舌罐头回到家里，我想如果您能在场目睹姨婆脸上惊喜万状的表情，您大概就不难想见我们满溢的感激之情了。知道远方有人竟能为从未谋面的一群人付出这么多关怀和慷慨，我的内心实在备感温暖。而我相信，所有同仁必定与我深有同感。\r\n　　如果您想到有什么事我可以代劳，或是从伦敦寄点儿什么给您的，请务必交代我去办，我将会引以为荣。\r\n　　比尔·汉弗莱斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月9日\r\n---------------\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('0ec8bf79-48ad-46a3-9c7b-484eda6931fa', '测试8[卷26]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '26', '亲爱的海莲:\r\n    关于您的朋友想要的《定本萧伯纳作品全集》，原出版社目前仍有新书发行。红褐色布面精装——正如您所描述的。我想全套共有三十册。旧书反而不常见到，如果您的朋友不介意购买新书，我们可以安排对他方便的方式，每个月寄给\r\n他三或四册。\r\n    我们这几年来都没有塞西莉·法尔的消息，至于梅甘·韦尔斯，她在南非没多久就待不下去了，回国后曾到书店来看大家，给了大伙儿一个发“早跟你说了偏不听”牢骚的机会。不过她后来又搬去澳洲碰运气了。几年前曾收到她寄到书店的圣诞卡，最近则又断了音讯。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1965年10月4日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    很高兴再度收到您的来信。是的，我们都还健在如昔——老态益发龙钟，工作更加忙碌，口袋却没能加倍饱满。\r\n    我们购人了一本E.M.德拉菲尔德的《村姑日记》，一九四二年的麦克米兰版，书品很好，定价两美元。今天我会将书及账单为您寄去。\r\n    我们度过了一个“青春洋溢”的夏天——今年的游客比往年更多，大批年轻人全涌向卡纳比街朝圣。我们只能老远隔着安全距离打量着他们。老实说，我还蛮喜欢披头士的，只希望他们的歌迷们不要放声尖叫。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    海莲·汉芙  纽约州，纽约市21，东七十二大街305号\r\n    1968年9月30日\r\n我们都仍健在，可不是吗……\r\n    我为儿童编写美国历史读物已经长达四五年，得将这玩意儿告一段落了——为了写这些书，我自己还买了一大堆关于美国历史的书，全都是长相丑、装订差的美国书。我想，大概没有哪个循规蹈矩的英国人会在家里收藏詹姆斯·麦迪\r\n逊的制宪会议记录。或是T.杰弗逊写给J.亚当斯的书信吧。\r\n    你当上祖父没?告诉希拉和玛莉，她们的小孩将可以免费获赠有作者签名的《少年历史读本》，这样子应该能让她们比较愿意安定下来增产报国了吧。\r\n    我挑了一个细雨霏霏的星期天介绍一位年轻朋友读《傲慢与偏见》，她现在果然已经疯狂迷恋简·奥斯汀了。她的生日就在万圣节前后，你能帮我找几本奥斯汀的书当礼物吗?如果是一整套的话，先让我知道价钱，万一太贵，我会叫她的\r\n先生分摊，我和他各送半套。\r\n    祝诺拉和你周围所有人好。\r\n海莲\r\n\r\n    伦敦中西二区 查令十事街84号\r\n    1968年10月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市X\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    是的，我们依然健在，手脚也还勉强灵光。这个夏天真是把大家忙坏了。从美国、法国、北欧和其他各国来的大批观光客，几乎把我们比较好的皮面精装书全都搜刮一空。由于书源短缺。加上书价节节攀升，恐怕很难赶在您的朋友生日前找到任何奥斯汀的书，我们会设法在圣诞节之前为您办妥这件事。\r\n    诺拉和女孩儿们都很好。希拉已经开始执起教鞭;玛莉则和一位人品不错的小伙子订了亲—不过一年半载的还结不成婚，因为双方的经济条件都不太宽裕。所以。诺拉一心想当个福福泰泰的祖母，这希望恐怕愈来愈渺茫呢。\r\n    想念您\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1969年l月8日\r\n    H.汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街Los号\r\n敬爱的小姐:\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('1003e0ac-2425-4942-8892-5c1855bb4f44', '测试8[卷10]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '10', '　　塞西莉谨上\r\n\r\n\r\n\r\n---------------\r\n1950年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年10月15日\r\n　　真是的！！！\r\n　　不是我爱唠叨，弗兰克·德尔！看到书店竟忍心把这么美的古书五马分尸，拿内页充当包装纸、填箱料，我真是觉得世道中落、万劫不复了。我向被包在里头的约翰·亨利告状：“主教阁下，斯文如此扫地，君岂信乎哉？”\r\n　　他说他也实在百思不得其解。更可恶的是你把书拆散了，随便抓来几页顺手就包，害我根本搞不清楚上头到底是在打哪一仗哪一役。\r\n　　这本书大约一个星期前寄达，现在气也慢慢消了。我把它端端正正地摆在案前，整天陪着我。我不时停下打字，伸手过去，无限爱怜地抚摸它。倒不全然因为这是首版书，主要是打出生起我从没见过这么标致的书。拥有这样的书，竟让我油然而生莫名的罪恶感。它那光可鉴人的皮装封面，古雅的烫金书名，秀丽的印刷铅字，它实在应该置身于英国乡间的一幢木造宅邸；由一位优雅的老绅士坐在炉火前的皮制摇椅里，慢条斯理地轻轻展读……而不该委身在一间寒酸破公寓里，让我坐在蹩脚旧沙发上翻阅。\r\n　　我要买那本Q的文集，可是忘了多少钱，我把你的上一封信搞丢了。好像是两块钱吧？附上两张一元钞票，若是不够就来信告诉我。\r\n　　下回要寄书来时，拿第五一二页和五一三页来包书怎么样？这样我才晓得最后哪一边打赢了，还有那到底是哪一场战役。\r\n　　HH\r\n　　P.S.你们那儿可有《佩皮斯日记》？我需要它来伴我度过漫漫冬夜。\r\n\r\n\r\n\r\n---------------\r\n1950年11月1日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年11月1日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您致歉，迟至今日才回信给您。我因公到外地出差了一个多星期，一回到办公室就被许多待办的事务耽搁，现在才有空提笔给您回信。\r\n　　首先，请您完全无须为我们拿旧书内页当包装纸用而感到忧心，那只是全套《英国叛乱和内战史》之中装订破散且多出来的一册复本，我想应该是无法当成商品卖给任何人的。\r\n　　奎勒—库奇的文集《朝圣之路》，已付邮寄给您。您的待付款项为一元八十五分，所以您新近寄来的两元用来支付该书仍绰绰有余。另，《佩皮斯日记》目前店内暂无存书，将为您留意。\r\n　　祝万事如意\r\n　　马克斯与科恩书店\r\n　　F.德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月2日\r\n---------------\r\n\r\n\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('1c29c237-f792-4cdc-890f-d37145d8bdb4', '测试8[卷13]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '13', '\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　我猜您大概已经开始担心，我们竟然这么久都没写信谢谢您寄来的包裹，心里头一定正在嘀咕：真是一群不知好歹的家伙。实际上，我刚离开伦敦到乡间跑了一大圈，到处拜访私人宅第，搜寻待售的藏书，努力补充店里捉襟见肘的库存。我太太已经开始把我当成房客来招呼了——我总是只回家睡觉，一吃完早餐又不见人影。不过，当我带着您送的肉(鸡蛋、火腿就更不用说了)回到家里，她就会觉得我毕竟也非一无是处。当然，所有的不开心也就随之烟消云散。说实在的，我们已经太久没能见到一块完整的肉了。\r\n　　我们总得想点儿法子，表达我们对您的感激。于是，我们将另行寄上一本小书，希望您会喜欢它。我还记得您曾经想买一本情诗集，这是我所能找到尽可能符合您的要求的了。全体同仁为您献上此书，盼您笑纳。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　(附在《伊丽莎白时期情诗选》里的卡片：)\r\n　　谨以此书赠予\r\n　　海莲·汉芙\r\n　　并为其诸多美善情谊\r\n　　致上最诚挚的祝福\r\n　　与无尽的感激\r\n　　伦敦查令十字街84号\r\n　　全体员工一同\r\n　　一九五一年四月\r\n\r\n\r\n\r\n---------------\r\n1951年4月16日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年4月16日\r\n　　此致伦敦查令十字街84号全体同仁：\r\n　　谢谢你们送我这本书。我从没拥有过这么一本三边的页缘都上金的书。你们知道吗？我竟在生日当天收到这本书！\r\n　　你们另外写了一张卡片，而不直接题签在扉页上，我真希望你们不要这样过分拘谨。如果我猜得没错，这一定是你们的“书商本性”作祟使然吧，你们担心一旦写了字在书上，将会折损了它的价值。差矣，你们如果真能这么做，不仅对我而言，对未来的书主，都增添了无可估算的价值。我喜欢扉页上有题签、页边写满注记的旧书；我爱极了那种与心有灵犀的前人冥冥共读，时而戚戚于胸、时而被耳提面命的感觉。\r\n　　还有，为什么大家都不签上名字呢？我猜一定是弗兰克不准你们签的，他大概怕我会撇下他，一一给你们大家写情书吧！\r\n　　隔着汪洋，我在美国此端遥寄我对你们的祝福——“美国”，好一个“坚定的盟邦”！当她一掷千金帮日本、德国从败仗中“复苏”，却眼睁睁看着英国同胞饱受饥馑之苦！皇天为证，总有一天我要亲自去英国，当面为她向你们道歉。(等我回国后，我会叫她加倍向我赔罪！)\r\n　　再次感谢你们送我这本美丽的书，我一定会格外小心，免得让它溅到酒滴、沾了烟灰。这份礼物对我这种人来说实在太隆重了。\r\n　　海莲·汉芙上\r\n\r\n\r\n\r\n---------------\r\n1951年9月10日\r\n---------------\r\n\r\n\r\n　　(玛克辛写于后台)\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('26c107e3-56fe-4e17-b771-01d8f4678d5d', '测试8[卷19]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '19', '亲爱的海莲:\r\n    我又要再度代表我们这儿所有人，写信向您道谢了，三件甚获欢迎的包裹几天前寄达。您实在太好心了，将辛苦挣来的钱花在我们身上。我们都非常感激您对我们的关怀。\r\n    这几天，店里进了三十几册“洛布经典文库”，可是，唉。里头不巧就缺了贺拉斯、萨福、卡图卢斯。\r\n    从九月一日起，我会休几个礼拜的假。可是因为我最近买了车，家中经济失血甚多，所以不能再大事铺张。诺拉有个姐姐住在海边，我们都冀望她会怜悯我们无处可去而邀我们去玩。这是我头一回买车，所以全家都非常兴奋——尽管这\r\n是一九三九型的老款车。但只要它不会老是半路抛锚，我们就该偷笑了。\r\n    全心祝福您\r\n    弗兰克·德尔\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年9月18日\r\n弗兰基，猜猜看，当你去度假时，谁上门来啦?萨姆·佩皮斯是也!不管是谁代你寄的书，别忘了要谢谢人家。这些书上个星期就到了，三册扎扎实实的海军蓝布面精装本，用四大页四开报纸裹着。我边吃午饭边读旧报;晚餐过后，开始和萨姆\r\n神交。\r\n    他要我转告你:他非常高兴能来到敝宝地，他的前任主人是个大草包，连书页都懒得裁开。我将它们一一裁开，内页用的是薄得几可透光的印度纸——我们这儿管这种纸叫“洋葱皮”，真是恰如其分。要是换成厚一点的纸张，难保不变成\r\n六册、甚至七册，印度纸果然功德无量。我只有三座书架，可是实在已经找不到可以让我清掉的书了。\r\n    每年一到春天，我就会“大清仓”，把一些我再也不会重读的书全丢掉，就像我也会把再也不穿的衣服扔了同样道理。倒是旁人都很惊讶、依我看，他们爱惜书本的方式才奇怪呢。他们买一堆新出版的畅销书，囫囵吞枣似的看完，我常\r\n想:他们也未免读得太潦草了吧。然后呢，因为他们从不重读那些书，不消一年，书里头的内容早就被他们抛到九霄云外!不过，当他们看见我一箱一箱地把书往外扔时，却又露出一副“这怎么得了!”的表情。要是照着他们的做法:买了一本\r\n书，好——读过了，好——上架，好——没事了，一辈子也不会再去碰它第二回，可是呢，“丢掉?万万使不得呀!”为什么使不得?我个人坚信:一本不好的书——哪怕它只是不够好，弃之毫不足惜!\r\n    你和诺拉过了一个不赖的假期吧?我自己则全消磨在中央公园里。我的宝贝牙医师放了我一个月的假，他却欢欢喜喜带着娇妻度蜜月去了，旅费是我出的——我有没有提过?好几个月前，我发现牙齿一颗接一颗全坏光了，我要么乖乖装上牙套。要不然就得全部拔光!因为还不想当个无齿之徒，我最后还是决定装牙套。可是诊疗费简直是天文数字!看来伊丽莎白只好在我没来的情况下登基了，我呢，今后几年里也只能留在这里看着我的牙齿一一加冕了。\r\n    不过我可没打算停止买书!连牙齿都弃我而去了。总该给自己留点儿什么呀!你能为我找到萧伯纳的剧评和乐评吗?我想他应该写了好几本，把你所能找到的都寄给我。还有!弗兰基，漫漫冬天眼看着又要来了，我兼差帮人带小孩时可不能闲着，所以，亟需读物!——快，别老坐着，起身帮我找找书!\r\n\r\nhh\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n  1952年12月12日\r\n此致“伦敦查令十字街84号的众好友们”:\r\n    一拆开包装，《爱书人文选》款款现身—镶金边的皮面、上金漆的上书口……轻而易举勇夺“我的藏书选美”的后冠，连首版的《大学论》也甘败下风。它看起来仍如此清新、纯朴，宛若从未遭染指——不过我知道，它的确曾被频繁地悉心翻阅过:因为一打开书页，总会落在某几个特定段落，冥冥之中似有前任书主的幽灵导引我，领我来到我未曾徜徉的优美辞藻。例如特里斯特拉姆·项狄描述他父亲富丽堂皇的书房:“架上罗列多善本，筐中广纳皆美卷。”(弗兰克!快去给我找一本《项狄传》！)\r\n    我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年;而你们送给我的礼物，却能和我朝夕相处，至死方休;我甚至还能将它遗爱人间而含笑以终。\r\n    谢谢你们，祝你们新年快乐。\r\n海莲\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n   1952年12月17日\r\n亲爱的海莲:\r\n    很抱歉这么久都没给您只字片语。希望阿德莱败选不致于让您太难过，也许下一回他可以东山再起。\r\n    博尔顿老太太跟我说，如果明年夏天您来英国时，她仍健在的话，很欢迎您住在她家。她是我所认识最长寿的人，我还不晓得有谁能像她活那么大岁数儿，我相信她一定可以活到一百岁的。无论如何，我们都会把您的住宿问题打点好。\r\n    谢谢您寄给我们这么好的圣诞礼物，您实在是个大好人。海莲!如果您明年来英国的时候，马克斯与科恩书店的人没好好地为您设宴接风的话，他们就太该死了。\r\n    我希望您将有个快乐的圣诞节。下回再聊了，献上我们\r\n的祝福和感激。\r\n    愿上帝保佑您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1953年5月3日\r\n弗兰基，告诉你个包准让你乐翻的消息-—\r\n    首先，随信寄上三元钞票。邮包已收到，这本书长得就像简·奥斯汀该有的模样儿——皮细骨瘦、清癯、完美无瑕。\r\n    好，进人正题——埃勒里电视剧集停播了。正当我青黄不接，又为了支付看牙的庞大开销而焦头烂额的当头，有人找我为一个新的节目写个草案——将名人轶事编成电视单元剧。所以我快马加鞭，完成一个故事大纲。送出之后，电视\r\n公司接受了;于是我又写了一个完整剧本，他们也颇为满意——所以再过个把月，新差事就有着落了。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('39f00425-a216-45d8-a540-6ce8f13528a4', '测试8[卷15]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '15', '\r\n\r\n\r\n---------------\r\n1951年10月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年10月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　首先，在此为我们的疏忽向您致以十二万分的歉意。我一直错认为那是收录完整的布雷布鲁克版。我相当能够了解，当您发现该书阙漏了喜爱的章节时，会有何等的失落感受。\r\n　　我一定会尽力另找一本书价合理、完整收录您在信中提及的段落的《佩皮斯日记》，并尽快为您寄上。\r\n　　同时，很高兴地在此向您报告：本店最近将收购一批私人藏书，我已从该批书籍中捡选出一些您会喜欢的书，包括一本利·亨特的选集，收录大半您曾提及的文章；还有一册《通俗拉丁文新约全书》——希望这回不会再出错了；同时有一本也许对您相当实用的《通俗拉丁文圣经辞典》；另一本《二十世纪英国散文选》，内容虽收入希莱尔·贝洛克的文章，但并不是谈论厕所的那篇。随信附上发票，上列书款十七先令六便士，约合美金二元五角，此书账已扣除您在敝店账户中的二元余额。\r\n　　至于鸡蛋的问题——店内同仁商量的结果，大家似乎意见一致，均认为新鲜鸡蛋较好。诚如您所言：鲜蛋虽较难久放，但风味确实不同。\r\n　　我们都期盼大选后日子会好转。如果丘吉尔先生和他的政党能赢得选举——这也是我的衷心期望，将会是一件振奋民心的好事。\r\n　　衷心祝福您\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1951年1Z月2日\r\n    亲爱的急惊风:\r\n    你简直是“迅雷不及掩耳”，利·亨特的书和《通俗拉丁文新约全书》“倏忽”寄达。你大约还没弄明白吧——这不就正是我两年前向你们订购的书吗?如果你继续照着这种提心吊胆的步调干活儿，要不得心脏病也难。\r\n    我真恶毒。你为了帮我找书，忙东忙西的，我竟然不曾向你道过一声谢，我真是坏透了。其实，你在那头儿受苦受难，我都是铭感在心底的。附上三元钞票，抱歉，最上头那一张被我溅到了几滴咖啡，应该还不致于作废的地步，上头的面额还可以看得出来。\r\n    你们有精装版的合唱乐谱吗?比如说巴赫的《马太受难曲》或韩德尔的《弥赛亚》?虽然我顶着寒风，走到五十条街外就能买到，但我想我还是“就近”先问问你们吧。\r\n    恭贺丘吉尔先生和他所属的政党，希望他能让你们的日子好过些。\r\n    “德尔”?你的祖先是威尔士人吗?\r\n                                          HH\r\n\r\n    1951年12月7日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    希望您会高兴得知，两箱鸡蛋和牛舌罐头已平安寄达，而我们也要再度向您道谢。\r\n    本店的一位老同事——马丁先生，躺卧病榻已有一段时日了，因此，我们一致决定让他多分一些鸡蛋——其中一整箱。一如以往，对于这些难能可贵的物资，我们十分欣喜。牛舌罐头亦相当诱人，它们大大地补充了我们的贮粮。至于我\r\n自已，我会将它们好好地收藏起来，以备特殊场合再派上用场。\r\n    我询问了此间所有的乐谱店，不过全都没有干净的二手精装版《弥赛亚》或巴赫的《马太受难曲》，然而我发现新书目前仍持续发行。价格虽略嫌高了些，但我想了想，仍为您买了下来，并在几天前寄发出去了，最近几天您应该就会收到这两本乐谱。一英镑十先令(约合美金四元二角)的账单亦会随\r\n书寄上。\r\n    我们将寄给您一件小小的圣诞礼物。这是一件刺绣品，我们会在邮包上注明“内装礼品”，希望这样可以避免您被课上关税。所有人都希望您笑纳这份薄礼，大伙儿同声祝您圣诞快乐，并有一个美好的来年。\r\n    我的姓氏绝非源自威尔士，鉴于其发音近似法文“挪尔”，我想我的祖籍或许是法国也未可知。\r\n      马克斯与科恩书店\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('3bc2ffb8-a68d-404c-85ab-a4a0d7d36ce6', '测试8[卷27]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '27', '    我于近日整理公文档案时，偶然发现一封您于去年九月三十日寄给德尔先生的信。我在此非常遗憾地向您报告:德尔先生甫于上个礼拜天(十二月二十二日)去世了。丧礼则已在上周三(元月一日)举行。\r\n    德尔先生于十二月十五日因罹患急性盲肠炎被紧急送医，虽然立即施行手术，但他仍不幸因病情扩散，导致腹膜炎并发而于七日后不治。\r\n    德尔先生在本书店服务已超过四十年，加上马克斯先生也刚辞世未久，科恩先生对于这个不幸的事件自然万分悲坳。\r\n    您是否仍须本店为您寻找简·奥斯汀的书?\r\n         \r\n马克斯与科恩书店\r\n              秘书 琼·托德敬上\r\n\r\n(未署明日期，邮戳日期为1969年1月9日。无寄信地址〕\r\n\r\n亲爱的海莲:\r\n    感谢您寄来的慰问信，我完全不认为那冒犯了我。我真希望您在弗兰克在世时能够与他见面，并亲自结识他本人。我原先只知道他是一个处事严谨同时也很幽默的人;现在还了解了他在待人处事上更是一位谦冲的君子。我收到许许多多来\r\n自各地的信，都异口同声地赞杨他对古书业的贡献;许多人还说他是如何饱富学识而又不吝予与其他人分享……如果您想要看这些信，我可以将它们寄给您。\r\n    不瞒您说，我过去一直对您心存妒忌，因为弗兰克生前如此爱读您的来信，而你们俩似乎有许多共通点;我也羡慕您能写出那么好的信。弗兰克和我却是两个极端不同的人，他总温和和有耐性;而因为我的爱尔兰出身，我的脾气总是又倔又拗。生命就是选么爱捉弄人，他从前总是试图教导我书中的知识……我现在好想念他。\r\n    孩子们都很懂事、我为此深感欣慰。像我这样要一辈子孤寂以终的人想必大有人在吧。\r\n    希望您能原谅我的字迹潦草。\r\n                 祝福您\r\n                 诺拉\r\n    我盼望有一天您还是能来造访我们，两个孩子都很想见见您。\r\n    1969年4月11日\r\n\r\n亲爱的凯瑟琳——\r\n    我正在整理我的书架，现在抽空蹲在书堆中写信给你，祝你们一路顺风。我希望你和布莱恩在伦敦能玩得尽兴。布莱恩在电话中对我说:“如果你手头宽裕些就好了，这样子你就可以和我们一道去了。”我一听他这么说，眼泪差点儿要夺\r\n眶而出。\r\n    大概因为我长久以来就渴望能踏上那片土地……我曾经只为了瞧伦敦的街景而看了许多英国电影。记得好多年前有个朋友曾经说:人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我:“就在那儿!”\r\n    或许是吧。就算那儿没有，环顾我的四周……我很笃定:它们已在此驻足。\r\n    卖这些好书给我的那个好心人已在数月前去世了，书店老板马克斯先生也已不在人间。但是，书店还在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……\r\n海莲\r\n\r\n尾声1969年10月\r\n\r\n    伦敦北十一区 温顿道\r\n    1969年10月\r\n亲爱的海莲:\r\n    这是德尔家族第二号通讯员首次发言!\r\n    首先，我要对我们长时间的静默向您致歉。请相信我，其实我们心中一直惦记着您。只是不知如何将这样的意念用文辞表达。直到今天我们又收到您的来信，我们都感到万分惭愧，并决定应该立刻动笔回信给您。\r\n    我们很高兴得知您的出版计划，也同意并很愿意提供这些信件供您作为出书之用。\r\n    我们现在搬到可爱的新家，常常会想着:如果父亲依然健在，一定也会喜欢这儿。\r\n    再多的悲坳亦无济于事。虽然父亲生前从未拥有财富、权势，但他始终是一个快乐自得又具有丰富内涵的人，我们应该以拥有一位这样的亲人而深感欣慰。\r\n    也许只是为了冲淡愁思，我们都尽量让自己忙碌着。玛莉白天在大学图书馆辛勤工作，晚上则和朋友开车出游散心，深夜方归;我除了正常的教职外，还兼修一个学位;妈妈则整天忙上忙下，一刻也不教自己闲下来!所以，恐怕大家都无法好好地回信——不过，当然我们会很高兴能继续收到您的来信。无论如何，只要有空，我们还是会努力回信的，并期盼能再有您的消息。\r\n    希拉诚挚敬上\r\n\r\n\r\n**************\r\n*附录\r\n***************\r\n\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。						\r\n\r\n---------------\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('3cb867b2-236c-4e14-aeb4-d8fcd0129b33', '测试1', '历史', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-27 16:32:32', '2017-09-01', '1', '100', '7', '1', '1', '0', '洛克菲勒家书：拒绝责难，拒绝推委\r\n2016-01-05 10:17 阅读(408)评论(2)\r\n洛克菲勒家书：拒绝责难，拒绝推委\r\n\r\n亲爱的约翰：\r\n\r\n如果我说一直不甘示弱、总以为自己是世界第一富豪的安德鲁·卡内基先生来拜访我，并向我讨教了一个非常严肃的问题，你会不会感到惊讶？事实上，那位伟大的铁匠就是这么做的。\r\n\r\n两天前，卡内基先生来到我们的基奎特。或许是我笑容可掬的态度，和我们轻松的谈话气氛，熔化了卡内基先生钢铁般的自尊，让他放下架子问我：“约翰，我知道，你领导着一群很能干的人。不过，我不认为他们的才干不可匹敌，但令我疑惑的是，他们似乎无坚不摧，总能轻松击败你们的竞争对手。我想知道，你施了什么魔法让他们有那种精神的，难道是金钱的力量？”\r\n\r\n我告诉他，金钱的力量当然不可低估，但责任的力量更是巨大。有时，行动并非源于想法，而是源自揽起责任。标准石油公司的人都有负责精神，都知道“我的责任是什么？我做什么可以把事情做得更好？”但我从不高谈阔论责任或义务，我只是通过我的领导方式来创造具有责任感的企业。\r\n\r\n我以为这个话题到此就应该结束了，但我的回答显然挑动了卡内基先生的好奇心，他很认真地追问我：“约翰，那你能告诉我你是怎么干的吗？”\r\n\r\n看着卡内基先生谦逊的神态，我无法拒绝，我必须如实相告。我告诉他，如果我们想要永续生存，那么我们的领导方式就意味着断然拒绝为了任何理由，去责难任何一个人或任何一件事。责难就如同一片沼泽，一旦失足跌落进去，你便失去了立足点和前进的方向，你会变得动弹不得，陷入憎恨和挫折的困境之中。结果只有一个：失去手下的尊重与支持。一旦落到这步田地，那你就好比是一个将王冠拱手让给他人的国王，无法再主宰一切。\r\n\r\n我知道责难是摧毁领导力的头号敌人，我还知道在这个世界上没有常胜将军，不管是谁都将遭遇挫折和失败。所以，当问题出现时，我不会感到愤恨不满，我只是在想：怎么能让情势好转起来？采取什么行动可以补救或是修复我们的失误？积极地选择朝向更高的生产力和满意度前进。\r\n\r\n当然，我不会放过我自己。当坏事降临在我们身上时，我会先停下来问自己一个问题：“我的职责是什么？”回归原点，借着对自身角色进行完全坦诚的评估，可以避免空窥探他人做了什么，或是要求其他人改变什么，等等无意义的行为。事实上，只有将焦点专注在自己身上，我才能将无意中拱手让出的王冠重新收回。\r\n\r\n但是，分析“我的职责是什么”并不意味着自责。自责是一种最阴险狡猾的责难陷阱，诸如“那真是一个愚蠢的错误！”等自我责难，只会使我陷入与其他任何责难相同的忿恨与不满的圈套之中。事实上，“我的职责是什么”是一种具有强大分析力和自我肯定的步骤，当我知道，真正的问题不是他们应该要做什么，而是我应该要做什么时，我不会自怨自艾，而只会让自己更强大。自己越强大，别人的影响力就会越小，看来这不是件坏事。\r\n\r\n如果我能将每一个阻碍视为了解自己的一个机会，而非斤斤计较他人对我做了什么，那么我就能在领导危机的高墙外找到出路。\r\n\r\n当然，我从不把自己视为救世主，也没有救世主的心态。我自问：我在哪些方面应为自己负责？也自问：在哪些方面，部属们要为我负责？领导者的工作不是全知全能、全权负责。如果我视自己为英勇的正义使者，准备去拯救这个世界，那就只会让自己陷入领导危机之中。我的责任中，很大一部分是让其他人也为自己该负的责任负责。如果一个雇员对于事关自己切身利益的事情都不在乎的话，我不相信这样的雇员能对出色完成工作有强烈的渴望，那他就应该离开，为别人去服务了。\r\n\r\n感觉责任在肩的那种压力能让人不自觉的兴奋起来。没有一件事像个人的责任感一样，可以激发并强化做事的能力，而将重责大任托付部属，并让他了解我对他充分信任，无疑是对他最大的帮助。所以，我不会将部属必须并且能够负担的责任揽在自己身上。\r\n\r\n我不只光靠示范作用来营造公司负责的氛围与风气，我的部属都知道我的基本原则：在标准石油公司没有责难、没有藉口！这是我坚持的理念，每一个人都知道。我不会因为他们犯错而惩罚他们，但是我决不能容忍不负责任的行为存在。我们的信念就是要彻底奉行。我们的箴言是支持、鼓励和尊重将被全心接受与加倍颂扬。只会找藉口而不提供解决方式，在标准石油公司是无法容忍的。\r\n\r\n我们很少犯任何错误，因为我的大门随时为部属敞开着，他们可以提出高见，或是纯粹的发牢骚，但是要用一个负责任的方式。这样的结果会让我们彼此信任，因为我们了解所有的事都需要摊在阳光下来讨论。\r\n\r\n卡内基先生是位优秀的老学生，他没有让我浪费时间，他在我结束这个话题时说：“在抱怨声中，优秀的雇员也会变成乌合之众！”他真聪明。\r\n\r\n约翰，几乎所有的人都有推委真正责任的防御心理，以致推委责任的现象处处可见。但它贻害无穷。避免防御的方法就是开始倾听。\r\n\r\n领导者最大的挑战在于，要如何创造出一个能让人们觉得开诚布公会比隐藏实情来得舒适的环境。主动邀请其他人陈述他们的想法，用一些诸如“再多说一点”，或是“我真的想听听你的意见”的话语来鼓励他们说出自己的想法。和一般人所相信的刚好相反，在对话中，聆听者才是拥有权力的人，而非陈述者。\r\n\r\n难以置信吧？想想看，陈述者的语调、焦点还有内容，事实上都取决于你倾听的方式。试想，和一个面露敌意且肢体呈现侵略性姿态的人，以及一个对你表示全神贯注的人说话时，两者之间的差异。当你单纯地聆听其他人说话时，你卸下了你的防卫。你会得到这些好处：你对有攻击性或愤怒的语言的背后隐含的议题，会有着更透彻的了解。你可以得到更多的信息，而这些资讯可以改变你对整个事件来龙去脉的假设。你会有更多的时间来整理思绪。\r\n\r\n陈述者会感觉你重视他们的观点。最令人兴奋的是，当你专注地倾听之后，原来的陈述者也会更愿意聆听你的意见。\r\n\r\n真实的倾听是不具任何防御性的。即使你不喜欢这个信息，你也应该倾听了解，而非立即做出回应。专注地倾听不太像是一种技巧，它比较像是一种态度。滑雪的人在遭遇障碍时的每一秒钟，都投注百分之百的注意力，绝对不会分神去思考过一会儿他要对伙伴说什么。同样地，作为一名积极的倾听者，你贡献百分之百的注意力给另外一个人，不会出现想到什么就脱口而出的情况。如此一来，你去除了先入为主的观念，并敞开胸襟开创一段更有意义和更有效果的对话。\r\n\r\n长久以来，我们塑造了生活也塑造了自己。这个过程将会持续下去，我们最终都将为自己的选择负责。就如“目的”决定你的方向，拒绝责难将筑出一条实现目标的大道。\r\n\r\n爱你的父亲', null);
INSERT INTO `p_task` VALUES ('3fd2f8ac-d232-4188-a98d-228cb9ca534e', '测试8[卷7]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '7', '\r\n---------------\r\n1949年12月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年12月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您的礼品包裹于今日平安抵达，并已均分给大家。而马克斯先生和科恩先生则坚持只由员工均分即可，不用关照“老板”。再者，我想让您知道，您所寄来的物品，我们不是久未看到，就是只能偶尔在黑市匆匆一瞥。您能这样子顾虑我们，实在是太亲切也太慷慨了，我们都深怀感激。\r\n　　我们要在此表达对您的感谢，并祈祝您未来一年一切顺心。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年3月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年3月25日\r\n　　弗兰克·德尔！你在干吗？我啥也没收到！你该不是在打混吧？\r\n　　利·亨特呢？《牛津英语诗选》呢？《通俗拉丁文圣经》和书呆子约翰·亨利的书呢？我好整以暇，等着这些书来陪我过大斋节，结果你连个影儿也没寄来！\r\n　　你害我只好枯坐在家里，把密密麻麻的注记写在图书馆的书上。哪天要是让他们发现了，包准吊销我的借书证。\r\n　　我已经叫复活节兔子给你捎个“蛋”，希望它到达时不会看到你已经慵懒而死了！\r\n　　春意渐浓，我想读点儿情诗。别给我寄济慈或雪莱！我要那种款款深情而不是口沫横飞的。怀亚特还是琼森或谁的，该寄什么给我，你自己动点儿脑筋！最好是小小一本，可以让我轻松塞进口袋里，带到中央公园去读。\r\n　　行啦！别老坐着，快去把它找出来！真搞不懂你们是怎么做生意的！\r\n\r\n\r\n\r\n---------------\r\n1950年4月7日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('3fd4774d-54be-4d66-8f10-52df5da46b1e', '测试8[卷24]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '24', '    纽约州，纽约市2l\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我又迟回您的信了，因为我希望能等到有好消息再向您报告。我找到一本萧伯纳与埃伦·特丽的书信集，虽然装帧不特别吸引人，但书品完好。我想这一次我还是尽快将书寄给您为妙、因为这是一本颇抢手的书，要等到下一本出现，恐\r\n怕得花一段时日。扣掉书价的二元六十五分，您的账户余额尚余五角。\r\n    关于收录完整的多恩布道文，恐怕只能在《约翰·多恩全集》中才能找到，而这套书共有四十余册，若书况稍好些的也\r\n必然所费不赀。\r\n    我们希望您后来没耗费太多心神在那本“巨匠现代文库”上，好好地过了一个愉快的圣诞节和新年。\r\n    诺拉也要祝您一切顾遂。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1960年5月8日\r\n    德·托克维尔阁下在此致意，宣告他已安然渡抵美利坚。他顾盼满志地坐谈时事，而他的臧否都卓然成理。尤其当他论及“律师治国”时，我只有额手、点头的分儿。我参加了一个民主党的小型聚会，前儿天晚上，出席的十四个人里头有十一个是律师;回到家在报纸上读到一堆名人励志故事——有为青年怀抱鸿鸽之志，力争上游，终于晋身一国之尊——史蒂文森、汉弗莱丹、肯尼迪、史塔生.、尼克松……一整票人除了汉弗莱，全是干律师的。\r\n    我附上三块钱钞票，这是一本漂亮的书，实在不能算是“二手书”，连书页都还未裁开哩。我有没向你说过，我终于找到方便好用的裁页刀了?这是一把珍珠柄的水果刀，我母亲留给我一整打这种刀，我挑了一把搁在案头的笔筒里。大概\r\n是交游不力吧，我好像没有机会请来十二位宾客。让他们围坐在餐桌前集体切水果哩。\r\n    笑口常开\r\n\r\n    海莲·汉芙 纽约州，纽约市21，东七十二大街305号\r\n    1961年2月2日\r\n弗兰克?\r\n你还在吗?\r\n    我答应自己在找到工作前不再写信给你的。\r\n    终于卖了一篇稿子给《哈泼杂志》。被这篇稿子折腾了三个星期，他们付给我两百美元稿费。现在他们再度向我约稿，要我将生平事迹写成一本书，他们将“预付”给我一千五百美元!并预估我不用半年就能写得出来，我是无所谓啦，不过房东可又要头疼了。\r\n    所以这阵子暂且不能买书了。去年十月，有人介绍我读圣西蒙公爵路易的书，不过这是一本乏善可陈的节译本。于是我火速赶往学会图书馆，因为这个图书馆不仅全馆开架，还让我爱借多少就借多少，我在那里找到货真价实的路易。现在我已经对他不可自拔。正在读的这一套是六卷版，昨晚读到第六卷一半时，一想到等我把书归还后，家里就连一本路易也没有了，这实在令我难以接受。\r\n    我现在读的是弗朗西斯·阿克赖特的译本，他的译笔甚雅。不过我放手让你挑你信得过的版本。但先别忙着寄来!找到后暂时搁着，先报价，然后再一本一本卖给我。\r\n    希望诺拉和女孩儿们都一切安好—还有你和其他所有认识我的人。\r\n海莲\r\n\r\n    伦敦中西二区 查令十宇街84号.\r\n    1961年2月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    您听到一定会开心的:我们正好有一部《圣西蒙公爵回忆录》在店里，译者正是阿克赖特。六卷全帙，装帧精良且品相完好。我们将于今天寄给您，大约一两个礼拜可望寄达您的手中。这套书的价格约合十八元七十五分，但请勿挂怀书\r\n款，您在敝店的付款记录一向良好。\r\n    同时，很高兴又有了您的消息。我们大家都很好，依然盼望您能到英国一游。\r\n    所有人诚心祝福您。\r\n    弗兰克\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1961年3月10日\r\n亲爱的弗兰基:\r\n    附上“千万不能掉了”的十元钞票，它务必平安到达你们手里。倒不是最近发了什么横财，而是路易要我不能留他一个未赎之身。他在法庭上受够了俗不可耐的赖债痞子，可不希望保持了两百七十年的清誉毁于一旦。\r\n    《哈泼杂志》指派给我的编辑昨晚来家里吃饭，和我讨论“我的生平故事”，于是想起你。我们聊到我曾为电视节目“不朽名人堂”改写兰多的《伊索与萝多彼》(我好像告诉过你了?)——兰多笔下那个天真浪漫的萝多彼由萨拉·丘吉尔\r\n饰演。这一集节目在某个周日下午播出。就在播出前两个钟头，我翻着《纽约时报》周日书评版，在第三版中有一篇针对波莉·阿德勒关于娼妓业的新书《野花莫如家花香》的书评，附了一帧照片—一尊希腊的女人头像，图说这么写着:“萝\r\n多彼——希腊最艳名远播的娼妇。”兰多本人对此倒并未详加着墨，许多学者咸称：兰多笔下的萝多彼其实就是让萨福的兄弟千金散尽的众女子的综合体。我既非学者，而且多年前死记强背下的希腊文语尾变化，我也早忘得一干二净啦。\r\n    我对金(我的编辑)谈起这段轶闻，她问我:“兰多到底是何方神圣啊?”我不厌其详地为她细说从头——正当我苦心孤诣一头热滔滔不绝时，她竟不耐烦地插嘴说:“你还真的中毒不轻唉。”\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('3ffc9945-327b-4b9f-86de-c508732adb81', '测试8[卷17]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '17', '    h.汉芙(必也正名乎的海莲)\r\nP.S.你妈妈今天一早就打扮得花枝招展地出门，要为你在第八大道和五十大街一带找间公寓，因为你曾交代她去剧院区找。我说玛克辛啊，你该很清楚才对，她穿那一身行头在那种地方晃，谁还敢租房子给她啊?\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n大懒虫!\r\n    依我看若要等到你寄书来.我都不晓得要超渡几回了。我不如干脆直接冲进布伦塔诺书店，有什么就买什么，不管印得多糟!\r\n    你们不妨再加记一笔沃尔顿的《五人传》到我那份“该寄而未寄”的书单里头。老实说，订这本书实在是违背了我的购书原则。我从来不买没读过的书——否则，不就像买了一件没试穿过的衣服同样下场吗?可是，这儿竟然连图书馆也\r\n借不到这本书。\r\n    要读的话倒是有。四十二大街上的分馆有一本，但，恕不外借!坐镇在柜台的女馆员用力摇了摇头，盛气凌人地说:“仅供内用!”然后只准我窝在密不通风的315号阅览室里啃完整本书。既不能边读边喝咖啡，抽烟就更纯属妄想了。\r\n    没关系，反正Q多次引用这本书里的段子，所以我肯定也会喜欢它。只要是Q喜欢的，我都照单全收——小说除外，我就是没法儿喜欢那些根本不存在的虚构人物操演着不曾发生过的事儿。\r\n    你们成天都没事干吗?是不是都窝在店里头看书?何不起身做点儿生意呢?\r\n                                              汉芙小姐上\r\n                      (只有我的“朋友”才可以叫我“海莲”!)\r\np.s.转告女孩们和诺拉，如果一切顺利的话，她们在大斋节都有丝袜可穿。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年2月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我也十分同意，该是我们都摒弃无谓的“小姐”、“先生”敬称的时候了。不瞒您说，我本人实在并不像您长久以为的那样既木讷又严峻。只是我写给您的信都必须存放一份副本作为业务存档，所以我认为行礼如仪似乎比较妥当。不过，此封信既然与书店业务无关，自然毋须顾虑副本、存档的问题。\r\n    我们百思不得其解，不明白您如何隔海变戏法，让四双丝袜无中生有。我所知道的只是:今天中午我用过午餐回到书店，就赫然发现它们已经好端端地摆在我的办公桌上，上头还附着一张写着“海莲·汉芙赠”的卡片。没有人晓得它们是什么时候或是怎么来的。女孩们都吓呆了，我晓得她们正在打主意待会儿自行写信给您。\r\n    有一件令人遗憾的事要向您报告:久卧病榻的乔治·马丁先生上周在医院中病逝了。他在本书店工作已有多个年头。伴随着这个噩耗。国王的猝然驾崩亦使我们此刻都笼罩在一片哀戚之中。\r\n    我实在不知该如何回报您对我们的不断付出。我所能做到的，只是当您确定访问英国时，橡原巷37号将会有一个房间，可供您无限期地住宿。\r\n    全员共祝您一切美好\r\n    弗兰克。德尔\r\n\r\n 纽约市\r\n东九十五大街14号\r\n    1952年3月3日\r\n    噢!真感谢你寄来的《五人传》。实在难以置信，这本一八\r\n四〇年出版的书，经过了一百多年。竟然还能保持这么完好的书况!质地柔细、依旧带着毛边的书页尤其可人。我真为前\r\n任书主(扉页上有“威廉.T.戈登”的签名)感到悲哀，真是子孙不肖哟!竟把这么宝贵的东西一股脑儿全卖给你们。哼!我真想趁它们被称斤论两前，拎着鞋溜进他们的书房，先下手搜刮一番!\r\n    这真是一本引人入胜的书。你可知道，当约翰·多恩带着他主子的掌上明珠私奔(人家好不容易老来得女的)，他因此下狱被囚禁在伦敦塔，他在里头握饿受冻。饿着饿着……居然就“得道”了！——不是沃尔顿的原句啦，这是我自己改编的说法。\r\n    注意!我附寄五元钞票一张。由于这本《五人传》赫然登场，害我原来那本《垂钓者言》相形见绌(认识你们之前买的，这是一本硬邦邦的“美国大众经典文库”版)。《五人传》跟它不登对，总嫌它在跟前晃来晃去，看了就讨厌。所以，多出来的两块半就帮我找一本好点儿的《垂钓者言》——劳您大驾啦。\r\n    你得当心了，如果电视剧续签，明年我就会杀到你们那儿去。到时候我会蹬着古董木梯，掸去你们的书架顶层的陈年积垢，顺便也把你们的优雅端庄一并一扫而光。我跟你提过我帮埃勒里·奎因的电视节目编写巧艺谋杀吗?我所写的剧本总会安插艺术场景——芭蕾舞团啦，音乐厅啦，歌剧院什么的;连嫌疑犯或尸体也得写得文绉绉的。为了向你致敬，也许我该编一出发生在古书店里的谋杀案，怎么样?你要演行凶歹徒呢?还是要扮刀下亡魂?\r\n hh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年3月24日\r\n亲爱的汉芙小姐:\r\n    我实在不知如何表达我的感激，今天收到您寄给我的食物包裹，我有生以来未曾收到过包裹。您实在不须为我这个老太婆如此费心。我只能谢谢您的好意，并好好地享用您寄来的礼物。\r\n    感谢您为我设想如此周到。我拿您寄来的东西给德尔太太瞧，她也直夸您实在是一位大好人。\r\n    再次谢谢您，并析祷您一切安好。\r\n玛丽·博尔顿谨上\r\n\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('441190dd-67b3-4e16-8e4c-3cff9f9574be', '测试8[卷18]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '18', '    伦敦中西二区 令十字街84号.\r\n    1952年4月17日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲(您看，我已经不再“行礼如仪”了):\r\n    我相信您一定会高兴听到这个消息:我们刚购入一批私人藏书，里头有一册非常好的《垂钓者言》，待我将收购手续完成，本书可望于下周为您寄上。我们大概会标价二元二角五分，您账户中的余额仍够支付。\r\n    您为埃勒里·奎因写的剧本似乎挺有意思。我希望这部剧集也能在我们这儿播映，它也该改善其沉沉死气了(我是指我们的电视节目，不是您写的剧本)。\r\n    诺拉和所有人与我同祝您一切美好。\r\n弗兰克·德尔敬上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年5月4日星期日\r\n亲爱的海莲:\r\n    谢谢您寄赠的干燥蛋罐头，包裹在两天前收到了。尽管难掩喜悦之情，但我实在觉得很难为情，因为我竟向您提起蛋源短缺的事情。不过今年倒是还没发生减量配给的情形，所以我们就当成是上天的恩赐，用它们多烤了个蛋糕什么的。弗兰克还拿了一些去办公室，打算要寄给塞西莉，他老是忘了把她的地址带回家。我想您应该也听说了吧，塞西莉离职了，她正打算随丈夫调防到中亚去。\r\n    我附了几张相片，弗兰克说这些照片全没把他拍好，还说他本人比照片好看多了。不打紧，我们就让他说梦话好了。\r\n    希拉的学校放了一个月的假，以前我们全家偶尔会一起到海边走走或随处晃晃。但是现在恐怕也不能那么常出游了，因为交通费贵得吓人。我们一直有买车的念头，但车价也不便宜，物色一部性能尚可的二手车或许比较可行。大部分\r\n新车都出口到国外，国内只能分到少量的配额。我们有一些朋友，光是为了订一部新车，就等了五六年。\r\n    希拉现在要为您朗读一篇《丰收祈祷文》，祈祷您可以如愿到英国一游;而且在复活节时，我们也已经借花献佛，用您送给我们的咸肉罐头犒赏过神明了。所以，如果再加上《丰收祈祷文》奏效，老天将会保佑您获得一笔意外之财，这样您和我们就很快可以见面了!\r\n    就此停笔并再次感谢您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年5月11日\r\n亲爱的弗兰克:\r\n    本打算一收到书就写信给你的。就是想跟你道句谢谢，《垂钓者言》里的木刻版画太棒了，光这些插图的价值就十倍于书价。我们活在一个诡异的世界——这么漂亮，又能终生厮守的书，只须花相当于看场电影的代价就能拥有;上医院\r\n做一副牙套却要五十倍于此。\r\n    唉!如果你们依照每本书的实际价值去标价的话，我肯定一本也买不起。\r\n    如果你知道我这个一向厌恶小说的人终究回头读起简·奥斯汀来了，一定会大大地惊讶。《傲慢与偏见》深深掳获了我的心!我千不甘万不愿将我手头上这本送还给图书馆，所以快找一本卖我。\r\n    代我问候诺拉和办公室里可怜的上班族们。\r\nhh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年8月24日\r\n亲爱的海莲:\r\n    我要再一次对您寄给马克斯与科恩书店，并让我们家分享的物资向您道谢，我真希望能回寄点儿什么报答您。\r\n    对了，海莲，这个礼拜我们终于荣登有车阶级了。虽然不是一部新车，没什么好到处吹嘘的。不过，车子只要能跑就行了，您说是不?怎么样，您现在比较愿意来找我们玩了吧?\r\n    前一阵子，我有两位亲戚从苏格兰南下来看我们，博尔顿老太太心肠很好，肯让他们在她家叨扰了几个礼拜，他们说住在那儿很舒服。他们那一阵子就吃我家，睡博尔顿老太太家。如果您手头方便，可以赶明年儿，趁着女王登基大典时\r\n来一块儿凑凑热闹，博尔顿老太太说她会为您将房间准备好。\r\n    好了，不多说了，寄上我的祝福和感谢——谢谢您送的肉与鸡蛋。\r\n诺拉谨上\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年8月26日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('48ccb166-3032-4e21-858b-6eb28817b652', '测试8[卷3]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '3', '　　但是做妻子的总是会有所察觉的，每个人都很敏感，无论这个人爱不爱书，读不读文学作品，生活中的许多东西远比书要重要。\r\n　　有的时候，我并不忌讳告诉你我曾经很嫉妒过你，因为弗兰克对你的信如此喜欢，你的信与他的幽默感又如此相同！而且，我也嫉妒过你的写作能力。我与弗兰克在各方面都恰恰相反，他友善、温和，而我的爱尔兰的血性使我总是与人争斗。我很思念他，以前的生活太有意思了。他总在向我解释，也不住地教我些有关书的知识……\r\n　　(一九六九年一月二十九日寄出)\r\n　　与查令十字街84号有关的书另外还有两本。汉芙一九七一年初访英伦时每一天都记有日记，出版成《布鲁姆斯伯里的女公爵》(TheDuchessofBloomsburyStreet)；一九八五年又出版自传《Q的遗产》(Q’sLegacy)，介绍她爱书的起始，与德尔通信以及书、电视剧、舞台剧出品的前后经过。《八十四号》中的那些信件，大都是信手写来，原本并不是为给别人看的，如果不是因德尔不幸早亡，它们可能永远不会面世，它们是率情之作，有些有趣的书话，信函来往间更能看出美国老小姐与英国拘谨绅士间的不同性情风格，读来流畅而有趣。《女公爵》虽是日记，但原本便是为读者而记的，虽说笔法仍流利，但总归有些矫作牵强。汉芙这么多年对英国魂牵梦萦，仿佛一位怀春女子二十年后才得见梦中情人，一时手足无措，不知该如何讨好对方才行，便乱了章法，信函中所有的那种风趣幽默全被吓跑了。而且汉芙对英国传统过于热爱，一叶障目，她所见的只是她自己头脑中的百余年前的英国，实在是遗憾。到了《Q》一书，《八十四号》早已经历了大江大海，若干年后回忆往事，汉芙反而能心如止水，返璞归真，不动声容了。\r\n　　海莲·汉芙，一九一六年四月十五日出生，一九九七年四月九日去世。终身未嫁。\r\n　　一九九八年十二月二十五日于北京方星园\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(1)\r\n---------------\r\n\r\n\r\n　　陈建铭\r\n　　一九四九年，E.B.怀特窝居在纽约市中城的一家小旅馆里。他坐在“窄得让人透不过气、又热又闷”的房间内挥汗写下脍炙人口的《纽约漫谈》(HereIsNewYork，此书曾被《纽约时报》评选为“有史以来关于纽约市最佳的十本书之一”)，里头这么说：“任何人都不该搬到纽约来住，除非他下定决心让自己好运临头。”当然，这位曾写出《夏洛的网》、《精灵鼠小弟》、《天鹅的喇叭》等经典童话故事和无数优美、隽永散文的作家，依旧继续抱持他一贯的知命乐天，徜徉在纽约自由、愉悦的文化天空下。\r\n　　但是与此同时，另一位在这个城市住了将近半辈子的穷作家却没有这份好运。她略乏才气却嗜读好书——货真价实的好书；她嫌这个城市没有气质，害她老是买不到想读的书(在电影版((电影84CharingCrossRoad于一九八七年出品，由英国导演戴维·休·琼斯执导，休·怀特摩编剧；安妮·班克劳夫特饰演海莲·汉芙，安东尼·霍普金斯饰演弗兰克·德尔，茱迪·丹奇饰演诺拉·德尔。片中每位演员的演出均十分传神；编、导的成绩亦相当不恶(得了一九八八年英国电影学院的最佳女主角奖并提名最佳改编剧本奖；一九八九年，美国编剧协会更因此颁奖给海莲·汉芙与怀特摩)。坊间某些录像带租售店或许仍可寻获年代公司的授权版，要特别留意的是：台译片名居然成了《迷阵血影》，而影片对白字幕亦惨不忍睹，简直到了令人坐立难安的地步。我翻译这本书，多少也想为它赎点儿罪罢。\r\n　　除了电影，一九七五年由马克·库龄汉导演、休·怀特摩编剧，此书曾在英国以电视单元剧的形式上演。上述电影即是根据这一个剧本拍成的。时至今日，欧美地区仍有剧团不时公演由此书改编的舞台剧。))的《查令十字街84号》里，饰演海莲·汉芙的女演员一上场就开骂了：“全纽约市没人读英国文学啦？”)，她只好转而向伦敦的一家小旧书店邮购那些“这年头没人要买的英国佬写的英文书”(引电影一开始，被汉芙索书不成的美国某书店经理的话)。于是，一桩原本单纯的买卖关系竟成就了长达二十年、多人参与的越洋友谊。\r\n　　我的眼界不若汉芙那般高且深，但同为住在另一个没有气质的城市里的爱书人，也偶叹好书难寻的挑剔读者，一开始被这本书吸引的，自然是关于“旧书”的部分，但是旋即引我动容的，则是关于书写——隔着距离的书写(当然还有阅读)如何承建伟大的心灵构筑工程。\r\n　　我一直以为：把手写的信件装入信封，填了地址、贴上邮票，旷日费时投递的书信具有无可磨灭的魔力——对寄件人、收信者双方皆然。其中的奥义便在于“距离”——或者该说是“等待”——等待对方的信件寄达；也等待自己的信件送达对方手中。这来往之间因延迟所造成的时间差，大抵只有天然酵母的发菌时间之微妙差可比拟。\r\n　　我始终不愿也不甘臣服于转瞬出现在对方屏幕上的电子邮件；自然更视ICQ(线上实时对谈)为畏途。拜传统邮政犹运作不辍之赐，我至今仍与老友、至亲维持着以手写、投递信函的老把戏，全然是因为我由衷相信：致力消弭空间、时间的距离纯属不智亦无益。就在那些自以为省下来的时、空缝隙里，美好的事物大量流失。我指的不仅仅是亲笔书写时遗下的手泽无法取代；更重要的是：一旦交流变得太有效率，不再需要翘首引颈、两两相望，某些情意也将因而迅速贬值而不被察觉。我喜欢因不能立即传达而必须沉静耐心，句句寻思、字字落笔的过程；亦珍惜读着对方的前一封信、想着几日后对方读信时的景状和情绪。老电影《街角商店》(TheShopAroundtheCorner1940)晚近被改拍成《电子情书》(YouveGotMail1998)((TheShopAroundtheCorner，由殿堂级的大师厄尼斯·刘别谦执导；詹姆斯·斯图尔特与玛格丽特·萨拉文扮演故事中那两位每日错身却不对盘、频生龃龉而又各自暗自仰慕未谋面笔友的百货行员工。多年前我曾在专于夜间播映老电影的TNT频道上观赏过，至今难忘。\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(2)\r\n---------------\r\n\r\n\r\n　　改编后的《电子情书》仍依稀能看出向前作致意的影子，如：女主角匿名通信时的代号为“shopgirl”；而她经营的童书店店名赫然就是“ShopAroundTheCorner”！另外，我也怀疑，她迷恋《傲慢与偏见》一节的神来之笔，则俨然得自《查令十字街84号》。))，两者之间出现不少有趣的辩证关系，读者们不妨自行参酌。\r\n　　我自私地以为这才是《查令十字街84号》全书的题旨所系。\r\n　　从没“好运临头”的汉芙小姐，年届晚年终于有了一个仅有的机会，抱着酝酿二十载的怀想，坐在机舱里(这里也是引自电影)奔赴另一座魂牵梦萦的城市。邻座一名男士问她：“这是你头一回去伦敦？”接着他说：“听我奉劝：别相信出租车司机，明明三条街外的目的地，他会载你兜上五里路；还有，别白费力气读地图了，在伦敦没有人可以找得到路，即使是伦敦人也不例外。”不过，“你一定会爱上她的，伦敦实在太棒了。”\r\n　　当我伫立在伦敦街头，我实在也无法打心底恭维这座城市——街道窄仄、杂芜；气候湿冷、灰暗；市容脏乱、交通拥挤——比起台北不遑多让。但我也终究难免渐渐地——从查令十字街开始——爱上了这座城市(拜汉芙之赐，“查令十字街84号”这个门牌号码几乎快与伦敦市的另一个地址“贝克街221号B座”齐名了)。甚至笃定相信她也能让我事后——隔着距离——对她怀想、惦念个二十年不成问题。\r\n　　将这本书中译，想必可以聊偿许多爱书人多年以来的期盼。我知道：所有读过84CharingCrossRoad的爱书同好——就像我自己一样——总将这本小书珍藏在身边，屡屡重读，让汉芙的珠玑妙语和古道热肠不时温暖自己被冷硬现实尘覆的凡心；而我相信：中文世界之所以长年不见此书问世，一定是所有珍爱此书的人——也像我自己一样——不忍丝毫更动书中的每一句话、每一个字。\r\n　　多年前的某一个下午，在曾经任职的古书店里，我和钟芳玲聊起这本书真该有个中文版。对于这个工作，她自然是当仁不让，而且以她作为此书的头号死忠书迷，加上她与汉芙本人的私交，我也十分赞成她是担任中译者的不二人选，如今我却因苦等不及而掠占了她原先的任务。我相信芳玲也是因为顾及前述的原因，宁可维护汉芙的原貌而迟迟不谋此图。于是，我在翻译的过程中虽然尽可能地保留原书的滋味，但我仍须在此报告：我刻意做了极小的更动。除了让它更能适应中文环境外；我私下盼望这个须臾的“失真”也能转而成为让中文版的读者们动心发愿去读“货真价实的”汉芙原文的伏笔。\r\n　　这是我第一部翻译作品。倘使这个中译版本侥幸能稍稍不使原文蒙羞，则首先必须在此感谢小威和建兴，铭谢他们夫妻俩前仆后继，在追索此书版权归属的过程中上穷碧落下黄泉、锲而不舍；还有推广实体书店文化不遗余力的钟芳玲，他们才是承继汉芙精神的真正后人，也是让这本书能有机会进驻更多人心的幕后功臣。\r\n　　此外，代汉芙对唐诺首肯为中文版赐序((台湾版原唐诺序见附录。——编注))表示感激。我自己常为了读唐诺的序文而看了一堆原先没打算看的书，不过这并不全然是我对这个译本打的如意算盘，而是我晓得查令十字街是他每回在伦敦磨蹭得最久的一条街；何况，若汉芙仍在人间，一定也会找他写的。\r\n\r\n\r\n\r\n***************\r\n*查令十字街84号\r\n***************\r\n\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。						\r\n\r\n---------------\r\n1949年10月5日\r\n---------------\r\n\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('55561f5f-86b9-49d8-89bf-bfb3e960afa0', '测试8[卷8]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '8', '　　亲爱的汉芙小姐：\r\n　　感谢您寄来的复活节礼物，包裹已于昨日平安寄达。看到这些罐头和那一盒生鸡蛋，大家都十分开心，全体同仁与我在此感激您对我们的亲切与慷慨。\r\n　　非常抱歉我们一直没能寄上您想要的书。关于您所提到的情诗集，敝店偶尔会收购到一些，可惜目前店内没有存书，但会竭力为您搜寻。\r\n　　再次感谢您寄来的礼物包裹。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　亲爱的汉芙小姐：\r\n　　请不要让弗兰克知道我写信给您。每回寄账单给您时，我都好想偷偷塞一张短笺到信封里。不过弗兰克一定会认为，以我的职务这么做并不适当。您听到我这么说，大概会以为他是个老古板吧？其实他是一个好得不能再好的人。只是每次您寄到书店的信或包裹都以他为收信人，而且他也将回信给您视为他的分内职责。不过我倒是一直很想自己给您写信。\r\n　　我们都好喜欢读您的来信，大伙儿也常凑在一块儿揣摩您的模样儿。我坚信您一定是一位年轻、有教养且长相聪慧的人；而老马丁先生竟无视您流露出来的绝顶幽默，硬是把您想成一个学究型的人。您愿意寄一张您的照片给我们吗？我们都很想瞧瞧呢！\r\n　　如果您也对弗兰克感到好奇的话，我偷偷告诉您：他年近四十，长得很帅，娶了一位漂亮的爱尔兰姑娘——好像是他的第二任太太。\r\n　　大家对您寄来的包裹都万分感激。我家那两个小家伙(女孩五岁、男孩四岁)简直乐翻了，因为有了您寄来的葡萄干和鸡蛋，我就可以为他们烤个蛋糕了！\r\n　　希望您不介意我私下写信给您，也请不要告诉弗兰克哟\r\n　　诚心祝福您\r\n　　塞西莉·法尔\r\n　　P.S.我会将家里的地址写在信封背后，万一您想到有什么我可以从这儿寄给您的，可以写信告诉我。\r\n\r\n\r\n\r\n---------------\r\n1950年4月10日\r\n---------------\r\n\r\n\r\n　　C.F.\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年4月10日\r\n　　亲爱的塞西莉：\r\n　　真是让老马丁先生大失所望了，请转告他：我非但一丁点儿学问都没有，连大学也没上过哩！我只不过碰巧喜欢看书罢了。说起来还得感谢一位剑桥的学者奎勒—库奇(一般都称他为Q)，是他让我在十七岁那一年一头栽进书堆里，从此不可自拔。至于我的长相，大概就跟百老汇街上的叫化子一样“聪慧”吧！\r\n　　我住在一幢白蚁丛生、摇摇欲坠、白天不供应暖气的老公寓里。整幢五层楼的其他住户早上九点出门，不到晚上六点不会回来，房东认为他犯不着为了一个窝在家里摇笔杆的小作家，而整天开着暖气。\r\n　　可怜的弗兰克，真是难为他了，我老是对他颐指气使的。我只是在打趣，不过就知道他会当真。我一直想要戳穿他那英国式的矜持。要是哪天他得了胃溃疡，都是我害的。\r\n　　请多来信告诉我关于伦敦的一切。我幻想着那一天快点到来——我步下轮船、火车，踩上布着尘灰的人行道……我要走遍柏克莱广场，逛尽温柏街；我要置身在约翰·多恩布道的圣保罗大教堂；我要趺坐在伊丽莎白拒不阶下为囚的伦敦塔前台阶上……我有一位战时派驻在伦敦的记者朋友，他曾经对我说：游客往往带着先入之见，所以他们总能在英国瞧见他们原先想看的。我告诉他，我到英国是为了探寻英国文学。而他这么告诉我：\r\n　　“去那儿准没错。”\r\n　　祝一切安好\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1950年9月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年9月20日\r\n　　海莲·汉芙小姐\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('60547ea2-f301-4976-aa25-cc1750d3dfa8', '测试8', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:43', '2017-09-08', '0', '80', '0', '0', '3', '0', '查令十字街84号\r\n\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n《查令十字街84号》\r\n\r\n\r\n\r\n\r\n\r\n\r\n***************\r\n*序言\r\n***************\r\n\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。						\r\n\r\n---------------\r\n序：书缘·情缘(1)\r\n---------------\r\n\r\n\r\n　　恺蒂\r\n　　如同每一个晴日的上午，阳光将这排歪歪斜斜的二手书店的影子投到街中心上，街上还少行人，穿着对襟毛衣，半秃着顶，行动悠缓的店主们正在将一切生意准备停当，掸一下桌面、橱窗中的灰尘，把书架上那排排参差的布面、皮面书摆正，再将一匣匣便宜的小本平装书移到门外，沿着窗前的墙根摆齐。不用吆喝生意，不用招揽顾客，这群书商们如同他们店中中层书架上的那些小羊皮装帧而成的上个世纪的书籍，虽并不昂贵，但却见过世面，口中叼着一枚烟斗，看着大红色的双层汽车在街上阳光屋影间叮咚过往。\r\n　　她跨下了一辆黑色的计程车，纤巧单薄的女人，游移的目光掠过那一家家摆着书的橱窗，68号，72号，76号，78号，82号，寻寻觅觅，像是丢失了件宝物。最终停了下来，但面前的84号却是空空如也。灰蒙蒙的玻璃窗里面蛛网遍织的书架东倒西歪，地上散落着些废纸，满是尘埃；推门进去，没有想像中的惊喜问候，空空的楼梯通向另一些同样废弃了的房间。孤身女人想张口告诉主人她已到来，她信守了诺言，但空屋中并无人回应，只有一阵冷风袭过，泪水顺着面颊静静地流淌下来。是一段书缘，还是一段情缘，竟让这纽约的独居女人千里迢迢为了伦敦小街这破落关门的书店而如此神伤？手中握着那本薄薄的小书，是为了还查令十字街(CharingCrossRoad)84号的哪一种心愿？\r\n　　一\r\n　　他约她出来聊天，选定的地方是孔乙己酒家，面前摆的是一樽绍兴花雕，自然少不了一碟五香豆，还有几样小菜。谈着各自喜欢的东西作家，纳博科夫、钱钟书、尤瑟纳尔、沈从文。谈着那本他最钟爱的书，《说吧，记忆》，在伦敦买到的初版本，自然便谈到那些古旧的书屋，里面的善本孤本初版本那些只有爱书人才能欣赏的古老气味。记得那条破街吗？我最爱做的是星期六早上睡个懒觉，约几个朋友去唐人街饮早茶，然后就去对面那条破街的老店中翻旧书。为什么我从未在那里遇见过你呢？回忆起从未共同经历过的伦敦往事，却怎么也想不起来查令十字街84号现在是做着什么样的买卖。知道那位纽约的老姑娘和那位一丝不苟的旧书商，他们通了二十多年的信，最终却仍未能谋面，是没有缘分？\r\n　　一九四九年十月，一切开始于一封很简单的从纽约到伦敦的商业性的信函：\r\n　　先生：\r\n　　你们在《星期六文学评论》的广告上说你们长于经营绝版的书籍，你们所用的“珍本书商”一词让我有些害怕，因为我总是把“珍本”与昂贵相联的。我是位穷作家，但对书却有一些“珍本”般的嗜好，我所要的书在这里都很难买到……寄上我最急需的书的名单，如果你们有干干净净不超过五美元一本的二手货，请将此函视作订购单，给我悉数寄来。(恺蒂女士的译文与本书正文译文行文不同，为尊重作者，特不作统一。下同。)\r\n　　(一九四九年十月五日)\r\n　　署名海莲·汉芙(HeleneHanff)，还特地注明了“小姐”。\r\n　　其实，这位小姐此年已三十有三，是一位以写电视、舞台剧本为生的自由撰稿人。汉芙出生于制衣人家庭，父亲原本是位民谣说唱艺人，虽为生活所迫做起了手艺活，但夫妻俩仍喜欢带着女儿去逛戏院。汉芙十九岁时进费城大学读英文，但家境贫困，一年后辍学，求职谋生，后来得一戏剧写作奖项，便以写作糊口。对书的热爱来自于在纽约市立图书馆中的刻苦自学，特别得益于英国剑桥大学一位阿Q教授(SirArthurQuillerCouch)的著作。然而美国书价昂贵，汉芙热爱英国文学，便将买书的对象转向英伦三岛，偶然选中一家小书店写了信去，第一次订货便得到价廉物美的圆满服务，海峡这边，查令十字街84号Marks&Co.书店的主管，弗兰克·德尔先生，则是汉芙二十年通信的对象。\r\n　　虽然三十有余，汉芙却仍是轻松活泼，特别是简牍之上，更善于以轻松调侃的笔墨，信手写来，天马行空，不拘格式。德尔先生给她回的第一封信中称之为“女士”，汉芙第二封信尾便加了注脚，“我希望‘女士’在你们那边的含义与这边不一样”。德尔先生下封信中便乖乖称之为“小姐”了。第五封信后，汉芙已将信首的尊称“先生”或“阁下”改为直呼其名，信的内容也像是写给一位相识已久的老友，且不乏亲昵、撒娇之态：\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(2)\r\n---------------\r\n\r\n\r\n　　弗兰克·德尔，你在那儿究竟干什么？你什么都没干，你只是闲坐着！\r\n　　我的利·亨特在哪里？我的《牛津诗集》在哪里？……\r\n　　你把我冷落在这里，坐在图书馆中，在那些不属于我的书上写着长长的眉批，总有一天他们会发现，会把我的图书卡收走！\r\n　　我已经安排了复活节的小兔子给你们带去礼物，等它到达时，你可能已慵懒而死了。\r\n　　春天到来之际，我要一本情诗集，不要济慈或雪莱，请寄给我一本不太煽情的情诗集，你自己挑选吧，要一本小开本的，可以放入裤兜中带到中央公园去。\r\n　　行了，不要只坐在那里，快去帮我找书吧，真不明白你们书店是怎么维持的。\r\n　　(一九五○年三月二十五日)\r\n　　汉芙性情率真，人更是善良，通信之初，她便得知战后的英国经济困难，肉类、鸡蛋等食品都是限量供应，女人的长统丝袜更是奢侈品。一九四九年圣诞节她将一块重六磅的火腿寄往伦敦，让德尔先生分给书店中的同事们，以后美式食品源源不断几年。汉芙本身手头并不宽裕，她的慷慨大度让书店的工作人员把她视作亲人，纷纷与她通信，聊天。只是德尔先生从未在信中对汉芙的轻松笑语做任何回报，他是正人君子，地道的好丈夫，典型的英国绅士，惟一的报答是兢兢业业地为汉芙寻觅好书。直到一九五二年，德尔太太登场写信给汉芙道谢这几年的礼物，并向汉芙介绍说德尔先生已有二女，德尔先生才在汉芙的强烈要求之下，在答谢汉芙所寄给他妻女的长统袜时，将例来一贯的信头的“汉芙小姐”的称呼，改为“亲爱的海莲”，写信的日期恰与情人节巧合，不过想必当时德尔先生压根没有注意到。\r\n　　亲爱的海莲：\r\n　　我同意，现在写信给你，是该把“小姐”放弃的时候了。我并不如你想像的那样古板，只是因为我所写给你的信，都得在办公室的卷宗中存档，所以我觉得正式的称呼更合适，但这封信与书没有关系，是不会被存档的。\r\n　　……\r\n　　真不知该如何报答你这么多好礼物，我能说的只是，如果有一天你来伦敦，橡原巷37号会有一张床给你，你爱待多久便待多久。\r\n　　(一九五二年二月十四日)\r\n　　去家里作客的邀请一直没有兑现，汉芙几次犹豫要去英格兰圆梦，但终因手头拮据而放弃。倒是德尔先生紧接着寄去的那本沃尔顿的《五人传》着实让汉芙惊喜不已：\r\n　　噢，天哪，老天感谢你沃尔顿的《五人传》，这本书出版于一八四○年，百年之后还能这样完美，真是奇迹！如此漂亮，久经摩挲的粗裁本！我真同情他，这位曾于一八四一年在书的扉页上签名的戈登先生。他那一群不肖子孙呀！几乎不值分文地便把它卖给了你！真希望在他们出卖图书馆之前，我曾去那边赤脚跑过！\r\n　　(一九五二年三月三日)\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。六十年代末，汉芙颇为潦倒，写出去的剧本屡遭拒绝，书的选题也无人感兴趣。一九六九年一月，纽约冬天很冷，汉芙从图书馆回到家中，已近六点。她手上捧着一摞书，把从门房中取来的信件放在书上，走向电梯。在电梯中，她发现在那一大堆账单之间，有一封薄薄的蓝色的从Marks&Co.寄来的航空信封。这信有些异样，因为德尔先生所寄的信，信封上的地址都是单行距打成，而且向来是把她连名带姓拼全的，而这封信上，地址是双行距，她的名字是由一个字母H代替的。她只道是德尔离开了书店，没太在意，夜深人静捧杯独坐时，她才打开此信。这一夜，她再没有睡着。因为信中的消息，是德尔的死讯。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(3)\r\n---------------\r\n\r\n\r\n　　二\r\n　　桌上的黄酒已过三巡。言谈嬉笑，话语投机，共同喜爱的书与作家们一时让他们觉得很亲近。然而，手中抚弄着那樽精致的铜酒壶，眼光却不敢对视，他早已与另一位美丽的女子谈婚论嫁，书缘与情缘，在现实生活中原本是风马牛不相干的事。\r\n　　然而，浪漫向来是作家们难以割舍的情怀。书中自有颜如玉，红袖添香夜读书，虽然这都是男人们近乎梦想的宣言，但是，自古以来的好书，大多都是激情之作，没有情的文章和书，是太过干涩枯燥，没有人愿读的。于是，温润的花雕虽只逗出跳跃键盘上往返数次的几行短语，将消除键轻轻按下便了无痕迹，查令十字街84号却被好心的好事者演绎成一曲情感故事。\r\n　　泪尽之后，汉芙觉得体内像被掏空了一样，一片冰凉。应该做些什么，但是又能做什么呢？想起这二十年来的通信，几次搬家，这丝带束成的一小扎竟还静静地躺在抽屉的底部。仿佛是为了了却一桩心愿，汉芙将它们结成一集，送到出版商的手中，也许是她时来运转，也许是德尔在天亡灵的保佑，此书一经出版，便受欢迎，英国出版商亦决定在英国推出此书，并邀请汉芙前往英国，下榻于大英博物馆旁布鲁姆斯伯里区的一家古旧的老饭店。\r\n　　英国是汉芙魂牵梦系的地方，从一九五○年开始她便屡次想去，但都因无川资而未成。《查令十字街84号》的最后一封信，是她于一九六九年四月写给一位前往伦敦度假的朋友的，读来让许多英国人觉得鼻子酸酸的：\r\n　　亲爱的凯瑟琳：\r\n　　我在家中打扫卫生，整理书架。偷闲坐在地上，四面地毯上散放的都是书。希望你与布莱恩在伦敦玩得愉快。他在电话上对我说：“如果你有路费的话，是否想和我们同去？”我几乎哭了。\r\n　　我不知道，可能对我来说去或不去那儿已是无所谓了。我梦到那儿的次数太多了。我常常是为了看那些宽街窄巷才去看那些英国电影。记得许久以前，有个人对我说，那些去过英国的人，都能在那儿找到他最想要的东西。我告诉他我想去英国，是为了找英国文学。他说：“它们就在那儿。”\r\n　　或许在那儿，或许不在。看着四周地毯上散乱的书籍，我知道，它们肯定在这儿。\r\n　　那位卖给我这所有书的好人几个月前去世了，书店的主人也死了，但是书店还在那里。如果你正巧经过查令十字街84号，能否为我吻它？我欠它的实在太多了。\r\n　　(一九六九年四月十一日)\r\n　　其实此时，查令十字街84号已准备关门大吉，书店主人的后代无心经营旧书，一年后汉芙的书的畅销也未让书店起死回生。此时的伦敦，经过六十年代文化革命和摇滚乐的洗礼，已与五十年代完全不同。七十年代初，英国是激进先锋、朋克即将形成势力的年代，关心旧书旧文化的人实在太少了。汉芙一九七一年前往英国，一心一意醉心于寻找的是维多利亚时代的情怀，触目所见，根本不是心中的英国，这位战战兢兢小心翼翼的老姑娘此时肯定已极难让一般人亲近，无奈大英帝国也确实有一批为数不多的怀旧之人，他们虽已过时，但却仍有生息，大洋彼岸这位老姑娘对英国潦倒二手书店的无限热爱，对英国旧文化旧文学旧传统的一片痴情，对这些怀旧的人来说，是一帖温润滋补的药，把他们熏得晕晕乎乎，舒舒服服，而且这是一副中国式的汤药，头剂、二剂、三剂，喝了十几年还不舍得把药渣子扔掉，这是这本书话式的信集能在英国成为畅销书的原因了。\r\n　　一九七五年，汉芙家中所有的鞋盒子都被腾出来装了英国各地的书迷们寄来的信件，BBC决定把《查令十字街84号》搬上荧屏；六年之后，素有盛名的英国戏剧界决定把它改编为舞台剧，在伦敦最好的剧院上演三月不衰；再过六年，此书又被改编成电影，由著名演员安妮·班克劳夫特及安东尼·霍普金斯领衔主演，电影介绍中称，“这部片子旨在反映两种爱情，一是汉芙对书的激情之爱，二是她对德尔的精神之爱”，终于在书缘与情缘之间系了根红线。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(4)\r\n---------------\r\n\r\n\r\n　　三\r\n　　现实生活中没有的缘分只能靠文学作品去演绎，然而他们最终未曾见面，电影中也没能让他们见面，没有缘分就是没有缘分。\r\n　　霍普金斯演德尔是最贴切不过的了，《霍华德庄园》、《长日将尽》，他最适合演的就是那种正经而又有些压抑的英国绅士。\r\n　　那部电影太干了，幸亏我不是英国男人。\r\n　　没有缘分的也会有感情，所以，并不能说是电影做作。\r\n　　这样的感情最好还是藏在心底。\r\n　　但是做妻子的总是会有所察觉的，每个人都很敏感，无论这个人爱不爱书，读不读文学作品，生活中的许多东西远比书要重要。\r\n　　有的时候，我并不忌讳告诉你我曾经很嫉妒过你，因为弗兰克对你的信如此喜欢，你的信与他的幽默感又如此相同！而且，我也嫉妒过你的写作能力。我与弗兰克在各方面都恰恰相反，他友善、温和，而我的爱尔兰的血性使我总是与人争斗。我很思念他，以前的生活太有意思了。他总在向我解释，也不住地教我些有关书的知识……\r\n　　(一九六九年一月二十九日寄出)\r\n　　与查令十字街84号有关的书另外还有两本。汉芙一九七一年初访英伦时每一天都记有日记，出版成《布鲁姆斯伯里的女公爵》(TheDuchessofBloomsburyStreet)；一九八五年又出版自传《Q的遗产》(Q’sLegacy)，介绍她爱书的起始，与德尔通信以及书、电视剧、舞台剧出品的前后经过。《八十四号》中的那些信件，大都是信手写来，原本并不是为给别人看的，如果不是因德尔不幸早亡，它们可能永远不会面世，它们是率情之作，有些有趣的书话，信函来往间更能看出美国老小姐与英国拘谨绅士间的不同性情风格，读来流畅而有趣。《女公爵》虽是日记，但原本便是为读者而记的，虽说笔法仍流利，但总归有些矫作牵强。汉芙这么多年对英国魂牵梦萦，仿佛一位怀春女子二十年后才得见梦中情人，一时手足无措，不知该如何讨好对方才行，便乱了章法，信函中所有的那种风趣幽默全被吓跑了。而且汉芙对英国传统过于热爱，一叶障目，她所见的只是她自己头脑中的百余年前的英国，实在是遗憾。到了《Q》一书，《八十四号》早已经历了大江大海，若干年后回忆往事，汉芙反而能心如止水，返璞归真，不动声容了。\r\n　　海莲·汉芙，一九一六年四月十五日出生，一九九七年四月九日去世。终身未嫁。\r\n　　一九九八年十二月二十五日于北京方星园\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(1)\r\n---------------\r\n\r\n\r\n　　陈建铭\r\n　　一九四九年，E.B.怀特窝居在纽约市中城的一家小旅馆里。他坐在“窄得让人透不过气、又热又闷”的房间内挥汗写下脍炙人口的《纽约漫谈》(HereIsNewYork，此书曾被《纽约时报》评选为“有史以来关于纽约市最佳的十本书之一”)，里头这么说：“任何人都不该搬到纽约来住，除非他下定决心让自己好运临头。”当然，这位曾写出《夏洛的网》、《精灵鼠小弟》、《天鹅的喇叭》等经典童话故事和无数优美、隽永散文的作家，依旧继续抱持他一贯的知命乐天，徜徉在纽约自由、愉悦的文化天空下。\r\n　　但是与此同时，另一位在这个城市住了将近半辈子的穷作家却没有这份好运。她略乏才气却嗜读好书——货真价实的好书；她嫌这个城市没有气质，害她老是买不到想读的书(在电影版((电影84CharingCrossRoad于一九八七年出品，由英国导演戴维·休·琼斯执导，休·怀特摩编剧；安妮·班克劳夫特饰演海莲·汉芙，安东尼·霍普金斯饰演弗兰克·德尔，茱迪·丹奇饰演诺拉·德尔。片中每位演员的演出均十分传神；编、导的成绩亦相当不恶(得了一九八八年英国电影学院的最佳女主角奖并提名最佳改编剧本奖；一九八九年，美国编剧协会更因此颁奖给海莲·汉芙与怀特摩)。坊间某些录像带租售店或许仍可寻获年代公司的授权版，要特别留意的是：台译片名居然成了《迷阵血影》，而影片对白字幕亦惨不忍睹，简直到了令人坐立难安的地步。我翻译这本书，多少也想为它赎点儿罪罢。\r\n　　除了电影，一九七五年由马克·库龄汉导演、休·怀特摩编剧，此书曾在英国以电视单元剧的形式上演。上述电影即是根据这一个剧本拍成的。时至今日，欧美地区仍有剧团不时公演由此书改编的舞台剧。))的《查令十字街84号》里，饰演海莲·汉芙的女演员一上场就开骂了：“全纽约市没人读英国文学啦？”)，她只好转而向伦敦的一家小旧书店邮购那些“这年头没人要买的英国佬写的英文书”(引电影一开始，被汉芙索书不成的美国某书店经理的话)。于是，一桩原本单纯的买卖关系竟成就了长达二十年、多人参与的越洋友谊。\r\n　　我的眼界不若汉芙那般高且深，但同为住在另一个没有气质的城市里的爱书人，也偶叹好书难寻的挑剔读者，一开始被这本书吸引的，自然是关于“旧书”的部分，但是旋即引我动容的，则是关于书写——隔着距离的书写(当然还有阅读)如何承建伟大的心灵构筑工程。\r\n　　我一直以为：把手写的信件装入信封，填了地址、贴上邮票，旷日费时投递的书信具有无可磨灭的魔力——对寄件人、收信者双方皆然。其中的奥义便在于“距离”——或者该说是“等待”——等待对方的信件寄达；也等待自己的信件送达对方手中。这来往之间因延迟所造成的时间差，大抵只有天然酵母的发菌时间之微妙差可比拟。\r\n　　我始终不愿也不甘臣服于转瞬出现在对方屏幕上的电子邮件；自然更视ICQ(线上实时对谈)为畏途。拜传统邮政犹运作不辍之赐，我至今仍与老友、至亲维持着以手写、投递信函的老把戏，全然是因为我由衷相信：致力消弭空间、时间的距离纯属不智亦无益。就在那些自以为省下来的时、空缝隙里，美好的事物大量流失。我指的不仅仅是亲笔书写时遗下的手泽无法取代；更重要的是：一旦交流变得太有效率，不再需要翘首引颈、两两相望，某些情意也将因而迅速贬值而不被察觉。我喜欢因不能立即传达而必须沉静耐心，句句寻思、字字落笔的过程；亦珍惜读着对方的前一封信、想着几日后对方读信时的景状和情绪。老电影《街角商店》(TheShopAroundtheCorner1940)晚近被改拍成《电子情书》(YouveGotMail1998)((TheShopAroundtheCorner，由殿堂级的大师厄尼斯·刘别谦执导；詹姆斯·斯图尔特与玛格丽特·萨拉文扮演故事中那两位每日错身却不对盘、频生龃龉而又各自暗自仰慕未谋面笔友的百货行员工。多年前我曾在专于夜间播映老电影的TNT频道上观赏过，至今难忘。\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(2)\r\n---------------\r\n\r\n\r\n　　改编后的《电子情书》仍依稀能看出向前作致意的影子，如：女主角匿名通信时的代号为“shopgirl”；而她经营的童书店店名赫然就是“ShopAroundTheCorner”！另外，我也怀疑，她迷恋《傲慢与偏见》一节的神来之笔，则俨然得自《查令十字街84号》。))，两者之间出现不少有趣的辩证关系，读者们不妨自行参酌。\r\n　　我自私地以为这才是《查令十字街84号》全书的题旨所系。\r\n　　从没“好运临头”的汉芙小姐，年届晚年终于有了一个仅有的机会，抱着酝酿二十载的怀想，坐在机舱里(这里也是引自电影)奔赴另一座魂牵梦萦的城市。邻座一名男士问她：“这是你头一回去伦敦？”接着他说：“听我奉劝：别相信出租车司机，明明三条街外的目的地，他会载你兜上五里路；还有，别白费力气读地图了，在伦敦没有人可以找得到路，即使是伦敦人也不例外。”不过，“你一定会爱上她的，伦敦实在太棒了。”\r\n　　当我伫立在伦敦街头，我实在也无法打心底恭维这座城市——街道窄仄、杂芜；气候湿冷、灰暗；市容脏乱、交通拥挤——比起台北不遑多让。但我也终究难免渐渐地——从查令十字街开始——爱上了这座城市(拜汉芙之赐，“查令十字街84号”这个门牌号码几乎快与伦敦市的另一个地址“贝克街221号B座”齐名了)。甚至笃定相信她也能让我事后——隔着距离——对她怀想、惦念个二十年不成问题。\r\n　　将这本书中译，想必可以聊偿许多爱书人多年以来的期盼。我知道：所有读过84CharingCrossRoad的爱书同好——就像我自己一样——总将这本小书珍藏在身边，屡屡重读，让汉芙的珠玑妙语和古道热肠不时温暖自己被冷硬现实尘覆的凡心；而我相信：中文世界之所以长年不见此书问世，一定是所有珍爱此书的人——也像我自己一样——不忍丝毫更动书中的每一句话、每一个字。\r\n　　多年前的某一个下午，在曾经任职的古书店里，我和钟芳玲聊起这本书真该有个中文版。对于这个工作，她自然是当仁不让，而且以她作为此书的头号死忠书迷，加上她与汉芙本人的私交，我也十分赞成她是担任中译者的不二人选，如今我却因苦等不及而掠占了她原先的任务。我相信芳玲也是因为顾及前述的原因，宁可维护汉芙的原貌而迟迟不谋此图。于是，我在翻译的过程中虽然尽可能地保留原书的滋味，但我仍须在此报告：我刻意做了极小的更动。除了让它更能适应中文环境外；我私下盼望这个须臾的“失真”也能转而成为让中文版的读者们动心发愿去读“货真价实的”汉芙原文的伏笔。\r\n　　这是我第一部翻译作品。倘使这个中译版本侥幸能稍稍不使原文蒙羞，则首先必须在此感谢小威和建兴，铭谢他们夫妻俩前仆后继，在追索此书版权归属的过程中上穷碧落下黄泉、锲而不舍；还有推广实体书店文化不遗余力的钟芳玲，他们才是承继汉芙精神的真正后人，也是让这本书能有机会进驻更多人心的幕后功臣。\r\n　　此外，代汉芙对唐诺首肯为中文版赐序((台湾版原唐诺序见附录。——编注))表示感激。我自己常为了读唐诺的序文而看了一堆原先没打算看的书，不过这并不全然是我对这个译本打的如意算盘，而是我晓得查令十字街是他每回在伦敦磨蹭得最久的一条街；何况，若汉芙仍在人间，一定也会找他写的。\r\n\r\n\r\n\r\n***************\r\n*查令十字街84号\r\n***************\r\n\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。						\r\n\r\n---------------\r\n1949年10月5日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年10月5日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。\r\n　　随信附上一份清单，上面列出我目前最想读而又遍寻不着的书。如果贵店有符合该书单所列，而每本又不高于五美元的话，可否径将此函视为订购单，并将书寄给我？\r\n　　你忠实的\r\n　　海莲·汉芙(小姐)\r\n\r\n\r\n\r\n---------------\r\n1949年10月25日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年10月25日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的夫人：\r\n　　谨在此回复您于本月五日的来函。敝店很荣幸能为您解除三分之二的困扰。您所列出的三种哈兹里特散文，均收录于这本典范出版社的《哈兹里特散文选》内；斯蒂文森的作品则在《致少女少男》中可以找到。我们挑出两本品相较好的书为您寄上，相信不久后即可送达您的手中，祈盼您会满意。随书附上发票，请查收。\r\n　　至于您提及的利·亨特的散文，目前颇不易得见，不过我们会留意是否能找到收罗齐全且装帧精良的版本，届时将再为您寄上。而您所描述的拉丁文圣经，目前敝店并无存书，仅有晚近出版、布面精装普通版的拉丁文和希腊文《新约全书》，不知您是否有兴趣？\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月3日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年11月3日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　今天收到你们寄来的书，斯蒂文森的书真是漂亮！把它放进我用水果箱权充的书架里，实在太委屈它。我捧着它，深怕污损它那细致的皮装封面和米黄色的厚实内页。看惯了那些用惨白纸张和硬纸板大量印制的美国书，我简直不晓得一本书竟也能这么迷人，光抚摸着就教人打心里头舒服。\r\n　　住在楼上的女孩儿凯特，她的英国男朋友布莱恩帮我将账单上列的书价一英镑十七先令六便士换算成美金五元三角，希望他没算错。我寄了五元和一元的钞票各一张，多出来的七角请用来支付《新约全书》，那两本我都要买。\r\n　　你们可否行行好？下回先将书价换算成美金。我连加减美金都一塌糊涂了，要我把英镑换算成美金真是阿弥陀佛。\r\n　　海莲·汉芙\r\n　　我希望在你们那边，“夫人”的意思和我们这边指的是两码事。\r\n\r\n\r\n\r\n---------------\r\n1949年11月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您寄来的六元书款已悉数收到，不过我们建议您不妨改以邮政划拨的方式付款，如此不但对你我双方都较为便利；亦比直接将钞票放入信封内要保险得多。\r\n　　我们非常高兴得知您如此喜欢那本斯蒂文森的书。两本《新约全书》已于今日付邮，账单亦一并附上，同时依照您的嘱咐，将书款分别以英镑与美金计价。我们期盼您也会喜欢此次寄去的两本书。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月18日\r\n---------------\r\n\r\n\r\n　　东九十五大街14号\r\n　　1949年11月18日\r\n　　这算哪门子的新约圣经啊！\r\n　　好心替我转告英国圣公会诸公，他们平白糟蹋了有史以来最优美的文字。是哪个家伙出馊主意把通俗拉丁文圣经整成这副德性？他们准会把他来活活烧死，不信记住我说的话。\r\n　　其实我犯不着火冒三丈，我本身是犹太人。不过我的嫂子是天主教徒；弟媳是卫理公会的；还有一票皈依长老教派的表亲(全是被我的亚伯拉罕叔公拉去改宗的)；还有一个到处宣扬基督信仰疗法的姑妈。他们要是知道有这么一本英国人搞出来的不三不四的拉丁文圣经，个个不暴跳如雷才怪！(话说回来，他们搞不好根本不晓得现在还有拉丁文哩。)\r\n　　哼，去他的！我手边还有一本从我的拉丁文老师那儿借来的圣经，暂且先不还他就是了，等你们找一本卖给我再说。\r\n　　寄去四元支付我欠你们的三元八角八分，你就拿多出来的一角二去买杯咖啡喝吧！我住的地方附近没有邮局，我才不要为了汇三元八角八分，大老远跑到洛克菲勒广场去大摆长龙呢。何况，如果要等到我哪天有空顺道去办事，口袋里的三元八角早就被我花得一毛不剩了。我对美国邮政和皇家邮政有十足的信心。\r\n　　你们有兰多的《假想对话录》吗？我想全套应该不止一本，我想读的是“希腊对话录”，如果里面有伊索和萝多彼的对话，就是那一本没错。\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年11月26日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月26日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您的书款已安全寄达，我们会将多出的一角二分计入您在敝店的专属账户中。\r\n　　很凑巧，敝店正好有收录“希腊对话录”的《沃尔特·萨维奇·兰多作品暨传记全集》中的第二卷，“罗马对话录”亦收录其中。本书由于是一八七六年出版的旧版本，并不是非常漂亮，但装订完好，书亦称干净。我们今日会将书与账单一并为您寄上。\r\n　　我在此为那本让您深感不满的拉丁文圣经向您致歉，我们将重新为您找一本正宗《通俗拉丁文圣经》；利·亨特的书仍持续密切留意中。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年12月8日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月8日\r\n　　敬启者：\r\n　　(老用“诸位”着实不智，我察觉到显然从头到尾都是同一个人为我服务。)\r\n　　萨维奇·兰多的书今天寄达，我迫不及待立即翻开“罗马对话录”——两座城市刚毁于兵燹战火，涂炭生灵被钉在十字架上，苦苦哀求列队行过的罗马士兵，干脆一戟刺死他们，好尽早结束这无止尽的折磨……再翻到“希腊对话录”，情境有了一百八十度的转换：读着伊索和萝多彼的娓娓对谈，这里惟一的忧虑只是怕饿着了肚子……我着实喜爱被前人翻读过无数回的旧书。上次《哈兹里特散文选》寄达时，一翻开就看到扉页上写着“我厌恶读新书”，我不禁对这位未曾谋面的前任书主肃然高呼：“同志！”\r\n　　随函附上一元，布莱恩(楼上女孩凯特的英国男朋友)说这够付我欠你的八先令，你忘了换算了。\r\n　　言归正传，布莱恩告诉我：你们每一户每个星期才配给到两盎司肉；而每个人每个月只分得一只鸡蛋！我一听简直吓坏了。他拿出一本目录给我看，这是一家设籍在美国的英国公司，专门代人从丹麦寄送补给物资到英国。所以我会寄给马克斯与科恩书店一份小小的圣诞礼物，希望数量足够让你们大家都能分得一些，因为布莱恩跟我说：查令十字街上的书店全都“小得很”。\r\n　　我会在包裹上注明由你——FPD——代转，天晓得你叫啥。\r\n　　祝佳节愉快\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月9日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月9日\r\n　　FPD！糟了！\r\n　　我刚把包裹寄走，里面主寄的是一条六磅重的火腿，我想你们应该可以自行拿去给肉贩，请他切片后再分给大家。\r\n　　不过我刚刚才发现你们寄来的账单上头印着“B.马克斯、M.科恩”。\r\n　　他们是犹太人吗？我该火速补寄点儿牛舌吗？\r\n　　快通知我该怎么办！\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年12月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您的礼品包裹于今日平安抵达，并已均分给大家。而马克斯先生和科恩先生则坚持只由员工均分即可，不用关照“老板”。再者，我想让您知道，您所寄来的物品，我们不是久未看到，就是只能偶尔在黑市匆匆一瞥。您能这样子顾虑我们，实在是太亲切也太慷慨了，我们都深怀感激。\r\n　　我们要在此表达对您的感谢，并祈祝您未来一年一切顺心。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年3月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年3月25日\r\n　　弗兰克·德尔！你在干吗？我啥也没收到！你该不是在打混吧？\r\n　　利·亨特呢？《牛津英语诗选》呢？《通俗拉丁文圣经》和书呆子约翰·亨利的书呢？我好整以暇，等着这些书来陪我过大斋节，结果你连个影儿也没寄来！\r\n　　你害我只好枯坐在家里，把密密麻麻的注记写在图书馆的书上。哪天要是让他们发现了，包准吊销我的借书证。\r\n　　我已经叫复活节兔子给你捎个“蛋”，希望它到达时不会看到你已经慵懒而死了！\r\n　　春意渐浓，我想读点儿情诗。别给我寄济慈或雪莱！我要那种款款深情而不是口沫横飞的。怀亚特还是琼森或谁的，该寄什么给我，你自己动点儿脑筋！最好是小小一本，可以让我轻松塞进口袋里，带到中央公园去读。\r\n　　行啦！别老坐着，快去把它找出来！真搞不懂你们是怎么做生意的！\r\n\r\n\r\n\r\n---------------\r\n1950年4月7日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　感谢您寄来的复活节礼物，包裹已于昨日平安寄达。看到这些罐头和那一盒生鸡蛋，大家都十分开心，全体同仁与我在此感激您对我们的亲切与慷慨。\r\n　　非常抱歉我们一直没能寄上您想要的书。关于您所提到的情诗集，敝店偶尔会收购到一些，可惜目前店内没有存书，但会竭力为您搜寻。\r\n　　再次感谢您寄来的礼物包裹。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　亲爱的汉芙小姐：\r\n　　请不要让弗兰克知道我写信给您。每回寄账单给您时，我都好想偷偷塞一张短笺到信封里。不过弗兰克一定会认为，以我的职务这么做并不适当。您听到我这么说，大概会以为他是个老古板吧？其实他是一个好得不能再好的人。只是每次您寄到书店的信或包裹都以他为收信人，而且他也将回信给您视为他的分内职责。不过我倒是一直很想自己给您写信。\r\n　　我们都好喜欢读您的来信，大伙儿也常凑在一块儿揣摩您的模样儿。我坚信您一定是一位年轻、有教养且长相聪慧的人；而老马丁先生竟无视您流露出来的绝顶幽默，硬是把您想成一个学究型的人。您愿意寄一张您的照片给我们吗？我们都很想瞧瞧呢！\r\n　　如果您也对弗兰克感到好奇的话，我偷偷告诉您：他年近四十，长得很帅，娶了一位漂亮的爱尔兰姑娘——好像是他的第二任太太。\r\n　　大家对您寄来的包裹都万分感激。我家那两个小家伙(女孩五岁、男孩四岁)简直乐翻了，因为有了您寄来的葡萄干和鸡蛋，我就可以为他们烤个蛋糕了！\r\n　　希望您不介意我私下写信给您，也请不要告诉弗兰克哟\r\n　　诚心祝福您\r\n　　塞西莉·法尔\r\n　　P.S.我会将家里的地址写在信封背后，万一您想到有什么我可以从这儿寄给您的，可以写信告诉我。\r\n\r\n\r\n\r\n---------------\r\n1950年4月10日\r\n---------------\r\n\r\n\r\n　　C.F.\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年4月10日\r\n　　亲爱的塞西莉：\r\n　　真是让老马丁先生大失所望了，请转告他：我非但一丁点儿学问都没有，连大学也没上过哩！我只不过碰巧喜欢看书罢了。说起来还得感谢一位剑桥的学者奎勒—库奇(一般都称他为Q)，是他让我在十七岁那一年一头栽进书堆里，从此不可自拔。至于我的长相，大概就跟百老汇街上的叫化子一样“聪慧”吧！\r\n　　我住在一幢白蚁丛生、摇摇欲坠、白天不供应暖气的老公寓里。整幢五层楼的其他住户早上九点出门，不到晚上六点不会回来，房东认为他犯不着为了一个窝在家里摇笔杆的小作家，而整天开着暖气。\r\n　　可怜的弗兰克，真是难为他了，我老是对他颐指气使的。我只是在打趣，不过就知道他会当真。我一直想要戳穿他那英国式的矜持。要是哪天他得了胃溃疡，都是我害的。\r\n　　请多来信告诉我关于伦敦的一切。我幻想着那一天快点到来——我步下轮船、火车，踩上布着尘灰的人行道……我要走遍柏克莱广场，逛尽温柏街；我要置身在约翰·多恩布道的圣保罗大教堂；我要趺坐在伊丽莎白拒不阶下为囚的伦敦塔前台阶上……我有一位战时派驻在伦敦的记者朋友，他曾经对我说：游客往往带着先入之见，所以他们总能在英国瞧见他们原先想看的。我告诉他，我到英国是为了探寻英国文学。而他这么告诉我：\r\n　　“去那儿准没错。”\r\n　　祝一切安好\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1950年9月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年9月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　自前封信以来，许久未向您报告，盼您不致认为我们因弛废店务而忘却了您交代我们该找的书。\r\n　　言归正传，《牛津英语诗选》新近到库，此书内页以印度纸印制，原版的蓝布精装，出版于一九○五年，扉页有前人签记，算是一本书况不错的二手书。标价两美元，我们认为在径自寄给您之前，应先向您略述此书的状况，以免您在这段时间内已另行购得一册。\r\n　　许久以前，您曾垂询纽曼的《大学论》一书的下落。您是否钟意首版书？最近我们收购到一册，谨描述如下：\r\n　　纽曼(约翰·亨利，神学博士)：《大学教育之目的及其本质——应都柏林天主教会之邀所作的演说之讲义稿》。首版，八开，小牛皮装帧。一八五二年于都柏林出版，若干页面稍有渍斑但装帧完好。\r\n　　价格：美金六元\r\n　　为了避免让别人捷足先登，这两本书我们会先为您保留。静候您的回复。\r\n　　祝身体健康\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年9月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年9月25日\r\n　　(他手上有只卖六美元的首版《大学论》，竟还问我要不要买！真不晓得该说他老实呢，还是憨？)\r\n　　亲爱的弗兰克：\r\n　　是的！我要！我真是快受不了我自己了，本来我并不特别讲究首版不首版的，可是，“那本书”的首版……！\r\n　　哗——我真迫不及待想看到它。\r\n　　也请把《牛津诗选》一并寄来。下回可别再纳闷我有没有跟别人买书了。既然我可以寸步不离书桌，就能向你们买到既干净又漂亮的书，我干吗跑到十七大街去买那些又脏又丑的？从我坐着的地方，伦敦可近得太多啦。\r\n　　附上“千万不能掉了”的八元钞票。我对你提过布莱恩正在打官司没？他向伦敦的一家理工专门书店订了一大套特贵的物理学书。他可不像我，既邋遢又老是漫不经心，他特地到洛克菲勒广场，乖乖地排队等着划拨那一大笔书款，该办的事一件也没漏。他精得很，聪明人自有聪明人按部就班的做法。\r\n　　结果你猜怎么着？那笔钱不知道给汇到哪儿去啦！\r\n　　皇家邮政，加把劲儿！\r\n　　HH\r\n　　为了庆祝我的第一本首版书，加上海外邮购公司终于给了我一本目录，我决定要寄一个小小的包裹送你们。\r\n\r\n\r\n\r\n---------------\r\n1950年10月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年10月2日\r\n　　亲爱的海莲：\r\n　　这些照片我带到店里好几个礼拜了，不过我们这阵子真是忙得昏天暗地，所以一直找不到空当寄给你看。这些都是我和道格(我的先生)在诺福克拍的，那儿是他所属的皇家空军驻地。里头我没有一张拍得漂亮的，不过这是我所能找到最好的了，孩子们和道格那几张倒都还不错。\r\n　　亲爱的海莲，我好盼望你真的能如愿到英国来，你何不省点儿买书钱，好让你能在明年夏天成行呢？我的爸爸妈妈在米德尔塞克斯有个房子，我们会很高兴接你来住的。\r\n　　梅甘·韦尔斯(老板的秘书)和我打算明年七月一起去泽西岛(在海峡群岛之中)度假一个礼拜，你可以来和我们一块儿玩，反正回到米德尔塞克斯不须花用你太多开销。\r\n　　本·马克斯先生在瞄我写些什么了，就此停笔。\r\n　　塞西莉谨上\r\n\r\n\r\n\r\n---------------\r\n1950年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年10月15日\r\n　　真是的！！！\r\n　　不是我爱唠叨，弗兰克·德尔！看到书店竟忍心把这么美的古书五马分尸，拿内页充当包装纸、填箱料，我真是觉得世道中落、万劫不复了。我向被包在里头的约翰·亨利告状：“主教阁下，斯文如此扫地，君岂信乎哉？”\r\n　　他说他也实在百思不得其解。更可恶的是你把书拆散了，随便抓来几页顺手就包，害我根本搞不清楚上头到底是在打哪一仗哪一役。\r\n　　这本书大约一个星期前寄达，现在气也慢慢消了。我把它端端正正地摆在案前，整天陪着我。我不时停下打字，伸手过去，无限爱怜地抚摸它。倒不全然因为这是首版书，主要是打出生起我从没见过这么标致的书。拥有这样的书，竟让我油然而生莫名的罪恶感。它那光可鉴人的皮装封面，古雅的烫金书名，秀丽的印刷铅字，它实在应该置身于英国乡间的一幢木造宅邸；由一位优雅的老绅士坐在炉火前的皮制摇椅里，慢条斯理地轻轻展读……而不该委身在一间寒酸破公寓里，让我坐在蹩脚旧沙发上翻阅。\r\n　　我要买那本Q的文集，可是忘了多少钱，我把你的上一封信搞丢了。好像是两块钱吧？附上两张一元钞票，若是不够就来信告诉我。\r\n　　下回要寄书来时，拿第五一二页和五一三页来包书怎么样？这样我才晓得最后哪一边打赢了，还有那到底是哪一场战役。\r\n　　HH\r\n　　P.S.你们那儿可有《佩皮斯日记》？我需要它来伴我度过漫漫冬夜。\r\n\r\n\r\n\r\n---------------\r\n1950年11月1日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年11月1日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您致歉，迟至今日才回信给您。我因公到外地出差了一个多星期，一回到办公室就被许多待办的事务耽搁，现在才有空提笔给您回信。\r\n　　首先，请您完全无须为我们拿旧书内页当包装纸用而感到忧心，那只是全套《英国叛乱和内战史》之中装订破散且多出来的一册复本，我想应该是无法当成商品卖给任何人的。\r\n　　奎勒—库奇的文集《朝圣之路》，已付邮寄给您。您的待付款项为一元八十五分，所以您新近寄来的两元用来支付该书仍绰绰有余。另，《佩皮斯日记》目前店内暂无存书，将为您留意。\r\n　　祝万事如意\r\n　　马克斯与科恩书店\r\n　　F.德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年2月2日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　很高兴听到您喜欢那本奎勒—库奇的书。《牛津英语散文选》此刻并无库存，我们将会为您留意。\r\n　　至于《罗杰·德·科弗利爵士正传》，我们手头上恰好有一本十八世纪的文集，除了包含该书中不少篇章之外，亦收入切斯特菲尔德与哥尔德斯密斯的文章。此书由奥斯汀·多布森精心编选，敝店仅标价一元十五分。我们已将书寄去给您。如果您想要更为完整的艾迪生与斯梯尔文集的话，请通知我，我将尽力为您寻找。\r\n　　敝店若不包括马克斯先生与科恩先生，共有职员六人。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月20日\r\n---------------\r\n\r\n\r\n　　米德尔塞克斯品内东厢\r\n　　1951年2月20日\r\n　　亲爱的海莲：\r\n　　做法不只一种，妈妈和我一致认为下面这个方法对你而言应该是最简单的：准备面粉一杯、鸡蛋一只、鲜奶半杯，撒入少许盐，在一个海碗里充分搅拌，直到变成浓稠的奶油状。搁进冰箱里摆几个钟头(所以一大早开始做，时间最刚好不过)。当你要把肉放进炉子时，挪个位子摆一个铁盘让它预热。在肉烤好前一个半钟头，浇一点儿肉汁在铁盘上，不用太多，浅浅的够铺满铁盘即可。记住！铁盘得烘烤得“非常热”才行。接着，把你先前准备好的布丁料全拨到铁盘上头，然后再放着继续烤。这样一来，肉和布丁就可以同时出炉上桌啦！\r\n　　对于从没见过的人，我实在不知道该怎么恰当地形容它。总之，一个大功告成的约克郡布丁应该会蓬松得高高的，表皮焦脆，当你切开它时，会发现里头其实是空心的。\r\n　　道格仍然随着皇家空军驻扎在诺福克。你寄来的圣诞节罐头，我们严格地实施管制囤积，以等他休假回家时再全家一起享用。亲爱的海莲，你一定不难想见，届时我们将会有个多么棒的庆祝餐会啊！不过，你实在不该这样子为我们破费的！\r\n　　该赶快把信寄出去了，这样你才来得及在布莱恩的生日晚宴上推出这道菜，成果如何一定要写信告诉我哟！\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年2月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年2月25日\r\n　　亲爱的塞西莉：\r\n　　约克郡布丁简直太棒了！因为我们这儿从没人见识过这玩意儿，我后来只好向别人形容成“一笼高高鼓起、松软细致、入口即化的特大号烤饼”！\r\n　　请别为我寄去的那些食品操心。我自己也觉得不可思议，那家海外邮购公司也不晓得是不是非营利机构，抑或是商品可以免税什么的吧？总之，他们的东西都便宜得很，我自己买的那只火鸡都还比寄给你们的那一大箱圣诞包裹贵哩。他们的确有一些价格比较高的商品，比如大块的烤肋排，或是一整只羊腿之类的。不过，即使是那些东西，也比跟这儿的肉贩买要便宜许多，如果真得那样，把我剁了也没法子寄东西给你们。现在正浏览着目录，我把它摊在地毯上，琢磨着两个旗鼓相当的商品组合：编号105的包裹(内含鸡蛋一打外加甜面饼一箱)和编号217B的包裹(内含鸡蛋两打、没有甜面饼)，我实在不甘心寄一打装的鸡蛋，让你们每人各分得两只能干吗？不过布莱恩跟我说，粉末干燥蛋吃起来味同嚼蜡，还真伤脑筋。\r\n　　有一位制作人刚打电话给我，说他蛮喜欢我写的剧本(还没喜欢到要把它搬上舞台的程度)。他正打算制作一出电视剧集，问我是否有兴趣编电视剧本，他漫不经心地说：“一集给两张！”搞了半天才弄明白他的意思是：每一集的稿费两百元。我原先为剧团修改剧本，一周的酬劳也才不过四十元！明天要去和他详谈，快祝我好运吧！\r\n　　祝福你\r\n　　海莲\r\n\r\n\r\n\r\n---------------\r\n1951年4月4日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月4日\r\n　　海莲亲爱的：\r\n　　你寄来的复活节包裹已经收到，这些礼物实在太棒了！不过，因为弗兰克第二天一早就出差去了，所以他没空回信向你道谢，大家都急得跳脚，而其他人全都不敢斗胆写信给“弗兰克的汉芙小姐”。\r\n　　包裹里的肉食实在太棒！我认为你真的不该再这样子为我们破费了，一定花了你不少钱吧？愿上帝保佑你的好心肠。\r\n　　本·马克斯先生走过来要找事给我做了，就此搁笔。\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　伦敦西八区肯辛顿高地街伯爵社区\r\n　　1951年4月5日\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您寄到马克斯与科恩书店的复活节礼物，已于几天前寄达。但弗兰克·德尔先生因公外出，错过与大家分享喜悦了。\r\n　　一见到包裹里头的肉，所有人的眼睛都看直了，而鸡蛋也大获欢迎。我觉得有必要给您写个信，向您报告：所有同仁对您的好意和慷慨都万分感激。\r\n　　我们所有同仁都期盼您能尽快来英国，届时我们一定会竭尽心力，让您有一趟愉快的英伦之旅。\r\n　　梅甘·韦尔斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　埃塞克斯滨海南庄桶桥路\r\n　　1951年4月5日\r\n　　敬爱的汉芙小姐：\r\n　　我是马克斯与科恩书店的编目员，已在书店任职即将届满两年。谨在此向您表达感激之意。谢谢您多次寄赠礼物包裹给我们。\r\n　　我现在与七十五岁的姨婆住在一起。当我带着您送的肉、牛舌罐头回到家里，我想如果您能在场目睹姨婆脸上惊喜万状的表情，您大概就不难想见我们满溢的感激之情了。知道远方有人竟能为从未谋面的一群人付出这么多关怀和慷慨，我的内心实在备感温暖。而我相信，所有同仁必定与我深有同感。\r\n　　如果您想到有什么事我可以代劳，或是从伦敦寄点儿什么给您的，请务必交代我去办，我将会引以为荣。\r\n　　比尔·汉弗莱斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　我猜您大概已经开始担心，我们竟然这么久都没写信谢谢您寄来的包裹，心里头一定正在嘀咕：真是一群不知好歹的家伙。实际上，我刚离开伦敦到乡间跑了一大圈，到处拜访私人宅第，搜寻待售的藏书，努力补充店里捉襟见肘的库存。我太太已经开始把我当成房客来招呼了——我总是只回家睡觉，一吃完早餐又不见人影。不过，当我带着您送的肉(鸡蛋、火腿就更不用说了)回到家里，她就会觉得我毕竟也非一无是处。当然，所有的不开心也就随之烟消云散。说实在的，我们已经太久没能见到一块完整的肉了。\r\n　　我们总得想点儿法子，表达我们对您的感激。于是，我们将另行寄上一本小书，希望您会喜欢它。我还记得您曾经想买一本情诗集，这是我所能找到尽可能符合您的要求的了。全体同仁为您献上此书，盼您笑纳。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　(附在《伊丽莎白时期情诗选》里的卡片：)\r\n　　谨以此书赠予\r\n　　海莲·汉芙\r\n　　并为其诸多美善情谊\r\n　　致上最诚挚的祝福\r\n　　与无尽的感激\r\n　　伦敦查令十字街84号\r\n　　全体员工一同\r\n　　一九五一年四月\r\n\r\n\r\n\r\n---------------\r\n1951年4月16日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年4月16日\r\n　　此致伦敦查令十字街84号全体同仁：\r\n　　谢谢你们送我这本书。我从没拥有过这么一本三边的页缘都上金的书。你们知道吗？我竟在生日当天收到这本书！\r\n　　你们另外写了一张卡片，而不直接题签在扉页上，我真希望你们不要这样过分拘谨。如果我猜得没错，这一定是你们的“书商本性”作祟使然吧，你们担心一旦写了字在书上，将会折损了它的价值。差矣，你们如果真能这么做，不仅对我而言，对未来的书主，都增添了无可估算的价值。我喜欢扉页上有题签、页边写满注记的旧书；我爱极了那种与心有灵犀的前人冥冥共读，时而戚戚于胸、时而被耳提面命的感觉。\r\n　　还有，为什么大家都不签上名字呢？我猜一定是弗兰克不准你们签的，他大概怕我会撇下他，一一给你们大家写情书吧！\r\n　　隔着汪洋，我在美国此端遥寄我对你们的祝福——“美国”，好一个“坚定的盟邦”！当她一掷千金帮日本、德国从败仗中“复苏”，却眼睁睁看着英国同胞饱受饥馑之苦！皇天为证，总有一天我要亲自去英国，当面为她向你们道歉。(等我回国后，我会叫她加倍向我赔罪！)\r\n　　再次感谢你们送我这本美丽的书，我一定会格外小心，免得让它溅到酒滴、沾了烟灰。这份礼物对我这种人来说实在太隆重了。\r\n　　海莲·汉芙上\r\n\r\n\r\n\r\n---------------\r\n1951年9月10日\r\n---------------\r\n\r\n\r\n　　(玛克辛写于后台)\r\n　　伦敦\r\n　　1951年9月10日\r\n　　甜心儿：\r\n　　这是一间活脱从狄更斯书里头蹦出来的可爱铺子，如果让你见到了，不爱死了才怪。\r\n　　店门口陈列了几架书，开门进去前，我先站在外头假装随意翻阅几本书，好让自己看起来像是若无其事地逛书店。一走进店内，喧嚣全被关在门外。一阵古书的陈旧气味扑鼻而来。我实在不知道该怎么形容：那是一种混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……店内左手边有张书桌，坐着一位年约五十、长着一只贺加斯式鼻子的男士。他站起身来，操着北方口音对我说：“日安。”我回答说我只是随意逛逛，而他则有礼地说：“请。”\r\n　　极目所见全是书架——高耸直抵到天花板的深色的古老书架，橡木架面经过漫长岁月的洗礼，虽已褪色仍径放光芒。接着是摆放画片的专区——应该说：一张叠放着许多画片的大桌台。上头有克鲁克香克、拉克姆矠、斯派和许许多多我叫不出名字的英国插画家的美丽画作；另一边还放着几叠迷人的古旧画刊。\r\n　　我在店内待了约莫半个钟头光景，期待着你的弗兰克或是哪个女孩儿翩翩现身。不过，因为我到达时已过了一点钟，我猜他们全都外出用餐去了，而我又不能待太久。\r\n　　就是这样咯！新戏的预告并没有造成万人空巷，不过据说对方人蛮好的，排给我们几个月的档期，所以我昨天出门找出租公寓，在骑士桥附近有一间小小的、蛮不错的小套房。现在还没确定，一旦定下来，我会写信告诉你，你也可以再打电话问我妈。\r\n　　三餐不成问题，我们都在餐厅或旅馆里用餐，像最高级的克拉里兹大饭店就能充分供应烤牛肉、排骨。价钱虽然贵得离谱，不过换算成美金倒是挺划算的，所以我们还吃得起。假使换成我是英国人，瞧见这光景一定会恨得牙痒痒的。但是他们却反而对我们好得不得了，到处都有人邀请我们去家里作客或上馆子。\r\n　　惟一短缺的东西就是糖。凡是甜的东西，一应俱缺。也许我反该谢天谢地，正好让我在这里瘦它个几磅。\r\n　　写信给我。\r\n　　爱你的\r\n　　玛克辛\r\n\r\n\r\n\r\n---------------\r\n1951年9月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年9月15日\r\n　　玛克辛：真多亏了你的慧心巧手，书店简直被你给写活了——你的文笔实在比我好得太多啦！\r\n　　我刚打了电话给你妈妈要你的住址，她要我转告你：方糖和巧克力棒依照你的交代，已经给你寄去了。你不是还跟我说你要趁机减肥的吗？\r\n　　我不想让你以为我是酸葡萄，不过我实在不明白，你究竟是何德何能？老天竟任由你饱览遍逛“我的书店”；而我为什么就只得乖乖蹲在九十五大街的破公寓里，埋头写着这劳什子《埃勒里·奎因的冒险》电视剧集脚本！“我不是告诉过你，不能安排一截沾着口红的烟蒂当做破案线索吗？”“我们这个节目是由百优雪茄公司赞助的，千万别给我编出“香烟”这个台词儿。”……就连在场景里安排一只道具烟灰缸也不许出现烟屁股；也不能摆雪茄屁股——厂商嫌不好看——所以，只要剧情出现烟灰缸，里头全好端端地搁着一管全新的、未拆封的百优雪茄！\r\n　　简直岂有此理！你却还能跟约翰·吉尔古德坐在克拉里兹的酒吧里打情骂俏！\r\n　　来信时多写些伦敦的事物——地铁、巷弄胡同、古宅大院……随便什么都好，写仔细点儿。告诉我骑士桥长什么模样，此刻我的耳畔似乎响起了科茨的《伦敦组曲》……听起来是那么绿意盎然、雄壮典雅。\r\n　　xxxx\r\n　　hh\r\n\r\n\r\n\r\n---------------\r\n1951年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年10月15日\r\n　　这哪是佩皮斯日记呢？你倒是给我交代清楚！\r\n　　这本书根本不够资格称之为《佩皮斯日记》，这只是哪个没事找事做的半吊子编辑，从佩皮斯日记里东挖西补、断章取义，存心让他死不瞑目！\r\n　　真想啐它一口！\r\n　　一六六八年一月十二日的日记跑到哪儿去了？记着他的老婆把他踹下床，抄了根烫红的拨火棍，追着他满屋子乱跑的那天的日记呢？\r\n　　记着W.佩恩爵士福至心灵的儿子成天揣着教谕，把大伙儿搞得七荤八素的日记呢？这些偷工减料的手脚可别想逃过我的法眼。\r\n　　附上两张皱巴巴的钞票。我想，用来付这本玩意儿，外加你将要为我找来的那本“货真价实的佩皮斯”，应该绰绰有余了！到时候，我会将这本烂书碎尸万段，然后，一页一页撕下来——拿来包东西！\r\n　　HH\r\n　　P.S.圣诞节快到了，到底是寄新鲜的鸡蛋，还是干燥蛋好呢？我当然明白干燥蛋可以放得比较久，但是，“丹麦空运直送的新鲜鸡蛋”光听都觉得香，你们快帮我拿主意吧！\r\n\r\n\r\n\r\n---------------\r\n1951年10月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年10月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　首先，在此为我们的疏忽向您致以十二万分的歉意。我一直错认为那是收录完整的布雷布鲁克版。我相当能够了解，当您发现该书阙漏了喜爱的章节时，会有何等的失落感受。\r\n　　我一定会尽力另找一本书价合理、完整收录您在信中提及的段落的《佩皮斯日记》，并尽快为您寄上。\r\n　　同时，很高兴地在此向您报告：本店最近将收购一批私人藏书，我已从该批书籍中捡选出一些您会喜欢的书，包括一本利·亨特的选集，收录大半您曾提及的文章；还有一册《通俗拉丁文新约全书》——希望这回不会再出错了；同时有一本也许对您相当实用的《通俗拉丁文圣经辞典》；另一本《二十世纪英国散文选》，内容虽收入希莱尔·贝洛克的文章，但并不是谈论厕所的那篇。随信附上发票，上列书款十七先令六便士，约合美金二元五角，此书账已扣除您在敝店账户中的二元余额。\r\n　　至于鸡蛋的问题——店内同仁商量的结果，大家似乎意见一致，均认为新鲜鸡蛋较好。诚如您所言：鲜蛋虽较难久放，但风味确实不同。\r\n　　我们都期盼大选后日子会好转。如果丘吉尔先生和他的政党能赢得选举——这也是我的衷心期望，将会是一件振奋民心的好事。\r\n　　衷心祝福您\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1951年1Z月2日\r\n    亲爱的急惊风:\r\n    你简直是“迅雷不及掩耳”，利·亨特的书和《通俗拉丁文新约全书》“倏忽”寄达。你大约还没弄明白吧——这不就正是我两年前向你们订购的书吗?如果你继续照着这种提心吊胆的步调干活儿，要不得心脏病也难。\r\n    我真恶毒。你为了帮我找书，忙东忙西的，我竟然不曾向你道过一声谢，我真是坏透了。其实，你在那头儿受苦受难，我都是铭感在心底的。附上三元钞票，抱歉，最上头那一张被我溅到了几滴咖啡，应该还不致于作废的地步，上头的面额还可以看得出来。\r\n    你们有精装版的合唱乐谱吗?比如说巴赫的《马太受难曲》或韩德尔的《弥赛亚》?虽然我顶着寒风，走到五十条街外就能买到，但我想我还是“就近”先问问你们吧。\r\n    恭贺丘吉尔先生和他所属的政党，希望他能让你们的日子好过些。\r\n    “德尔”?你的祖先是威尔士人吗?\r\n                                          HH\r\n\r\n    1951年12月7日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    希望您会高兴得知，两箱鸡蛋和牛舌罐头已平安寄达，而我们也要再度向您道谢。\r\n    本店的一位老同事——马丁先生，躺卧病榻已有一段时日了，因此，我们一致决定让他多分一些鸡蛋——其中一整箱。一如以往，对于这些难能可贵的物资，我们十分欣喜。牛舌罐头亦相当诱人，它们大大地补充了我们的贮粮。至于我\r\n自已，我会将它们好好地收藏起来，以备特殊场合再派上用场。\r\n    我询问了此间所有的乐谱店，不过全都没有干净的二手精装版《弥赛亚》或巴赫的《马太受难曲》，然而我发现新书目前仍持续发行。价格虽略嫌高了些，但我想了想，仍为您买了下来，并在几天前寄发出去了，最近几天您应该就会收到这两本乐谱。一英镑十先令(约合美金四元二角)的账单亦会随\r\n书寄上。\r\n    我们将寄给您一件小小的圣诞礼物。这是一件刺绣品，我们会在邮包上注明“内装礼品”，希望这样可以避免您被课上关税。所有人都希望您笑纳这份薄礼，大伙儿同声祝您圣诞快乐，并有一个美好的来年。\r\n    我的姓氏绝非源自威尔士，鉴于其发音近似法文“挪尔”，我想我的祖籍或许是法国也未可知。\r\n      马克斯与科恩书店\r\n      弗兰克·德尔谨上\r\n\r\n    195年l月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    首先，我们都十分高兴您喜欢那块桌布。总算能够回赠您一件礼物，亦带给我们一丝欣喜——虽然比起您过去这些年来为我们的奉献，那实在是太微不足道了。您或许想知道那件桌布的来历，其实这是最近刚完成的作品，出自我的邻\r\n居—一位八十多岁老太太的巧手。她一个人独居，平日以女红自娱。虽然她做了许多手工刺绣，却儿乎全都自己留着。她之所以愿意割爱，破例卖出这么一块桌布，乃是禁不住我太太的不断恳求。当然，您之前寄给我们的干燥蛋也使上不\r\n少助力。\r\n    如果您非到万不得已，一定要“清洗”您的《格罗里埃圣\r\n经》。的话，我们会建议您用一般的肥皂和清水即可。做法是:加一茶匙的苏打粉到一品脱的温水中，用蘸了肥皂的海绵轻轻擦拭。我相信您会发现，这样可以有效地除去污渍。经过上述的处理后，您可以再用少许绵羊油为它上光。\r\n    J.彭伯顿是一位女士，J是珍妮特的缩写。\r\n    祝您新的一年一切如意。\r\n弗兰克·德尔敬上\r\n\r\n\r\n    伦教北八区克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年1月20日\r\n    亲爱的汉芙小姐:\r\n    谢谢您长期不断地寄赠马克斯与科恩书店礼物包裹，并让我家也能有幸分得一部分。我长久以来就一直想写信给您，现在终于有正当理由可以动笔了。弗兰克跟我说，您很想知道绣了那块桌布的老太太的姓名、地址。那件刺绣的手工\r\n真的很漂亮吧？\r\n    这位博尔顿老太太就住在我们的公寓隔壁，地址是:橡原巷36号。当我告诉她，这件桌巾飘洋过海送给了一位好心的女士时，她好吃惊。如果能再亲耳听到您对她的手艺赞不绝口，我相信她一定会十分开心。\r\n    很感谢您说还要寄更多干燥蛋给我们。不过上回您送来的还剩一些，应该可以让我们享用到春天。四月到九月这段时间，我们并无须操心蛋的问题。如果偶尔会因物资短缺而临时减少配给量，我们就会拿别的东西去跟别人换罐头。我曾经用一双丝袜在黑市换得一罐干燥蛋，当然这么做不尽合法，却是非常时期不得已的变通办法。\r\n    过些日子我会寄一些全家福照片给您看，我们的小女儿玛莉上周刚度过她的四岁生日;大女儿去年八月满十二岁了，名叫希拉，是弗兰克的前妻生的——她不幸在战时丧生了。战后我嫁给弗，顺道有了个现成的女儿。去年五月，希拉在学校当着修女们的面(她念的是教会学校)，说她要送一张卡片祝福爸爸、妈妈的结婚四周年纪念日。您不难想像，这可怜的小姑娘花了多少唇舌才解释清楚。\r\n    最后，祝您新的一年一切顺遂，并期盼不久后我们可以在英国相会。\r\n诺拉·德尔谨上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年l月29日\r\n    亲爱的汉芙小姐:\r\n    谢谢您的来信。我很感激您如此好心地告诉我:您十分喜欢我绣的桌布。我当初真该多花点儿工夫在那上头。我想，德尔太太一定也对您提过了吧，我是一个上了年纪的人，双手也不太听使唤，能做的活儿也不像以前那么多了。我用这\r\n双老手做的东西有幸能交到喜欢它的人手上，这真是一件令人欣慰的事儿。\r\n    我常见到德尔太太，而她也不时向我提起您的事儿。您若到英国来，或许我也能有幸见您一面罢。\r\n    再次谢谢您。\r\n    玛丽·博尔顿谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n听好了!玛克辛:\r\n    我刚和你妈妈聊过。她说你们的戏也许下个月就会结束公演;她还告诉我。你带走了两打丝袜。帮我一个忙，趁你的戏下档前，拿四双去书店交给弗兰克·德尔，就说是送给店里的三个女孩和诺拉（他太太）。\r\n    你妈妈还特别交代，要我用不着付钱给你。她说那些丝袜全是她自已在去年夏天萨克斯百货店清仓大减价时便宜买到的，她决定要乐捐出来，好让她自个儿也能沾沾“共赴国难”的光。\r\n    等你回国就可以瞧见他们送我的圣诞节礼物了。这是一条漂亮的爱尔兰绣花桌巾，米黄色的底布上以手工绣着古典的花草图案——全是各自不同颜色、浓淡有致的花儿。保证你从没看过这么美的桌巾，我那张从旧货店买回来的破茶几\r\n就更肯定没见识过啦!我真是迫不及待想披上维多利亚时代的水袖，优雅地举起手，幻想自己执着一只乔治王朝的古董茶壶，轻轻地斟上一盏茗茶……你快点儿回来吧!我们可以在家里扮一出斯坦尼斯拉夫斯基！\r\n    埃勒里调高了我的剧本稿酬，现在是一集两百五十元。如果照着这样的调薪幅度。到了六月，也许我就可以启程赴英，自己去逛“我的书店”——如果我斗胆敢去的话。隔着三千英里的安全距离，我写了一堆没大没小的信，我大概只会\r\n悄悄溜进去又静静踱出来，而不敢告诉他们我是谁。\r\n    我实在不明白你怎么会在杂货铺里被搞得一楞一楞的呢?老板跟你说的不是“兔兜脚”，他说的是“土豆胶”!——我也觉得这才是惟一合理的称呼。你动点儿脑筋想想:豆子长在土里，叫它“土豆”合情人理;从土里头挖出来，一直“搅”、一直“搅”，搅成“胶”状，不就成了“土一豆一胶”!这个词儿不是要比“花生酱”更贴近事实吗?你真是不懂国语!\r\n    \r\n    h.汉芙(必也正名乎的海莲)\r\nP.S.你妈妈今天一早就打扮得花枝招展地出门，要为你在第八大道和五十大街一带找间公寓，因为你曾交代她去剧院区找。我说玛克辛啊，你该很清楚才对，她穿那一身行头在那种地方晃，谁还敢租房子给她啊?\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n大懒虫!\r\n    依我看若要等到你寄书来.我都不晓得要超渡几回了。我不如干脆直接冲进布伦塔诺书店，有什么就买什么，不管印得多糟!\r\n    你们不妨再加记一笔沃尔顿的《五人传》到我那份“该寄而未寄”的书单里头。老实说，订这本书实在是违背了我的购书原则。我从来不买没读过的书——否则，不就像买了一件没试穿过的衣服同样下场吗?可是，这儿竟然连图书馆也\r\n借不到这本书。\r\n    要读的话倒是有。四十二大街上的分馆有一本，但，恕不外借!坐镇在柜台的女馆员用力摇了摇头，盛气凌人地说:“仅供内用!”然后只准我窝在密不通风的315号阅览室里啃完整本书。既不能边读边喝咖啡，抽烟就更纯属妄想了。\r\n    没关系，反正Q多次引用这本书里的段子，所以我肯定也会喜欢它。只要是Q喜欢的，我都照单全收——小说除外，我就是没法儿喜欢那些根本不存在的虚构人物操演着不曾发生过的事儿。\r\n    你们成天都没事干吗?是不是都窝在店里头看书?何不起身做点儿生意呢?\r\n                                              汉芙小姐上\r\n                      (只有我的“朋友”才可以叫我“海莲”!)\r\np.s.转告女孩们和诺拉，如果一切顺利的话，她们在大斋节都有丝袜可穿。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年2月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我也十分同意，该是我们都摒弃无谓的“小姐”、“先生”敬称的时候了。不瞒您说，我本人实在并不像您长久以为的那样既木讷又严峻。只是我写给您的信都必须存放一份副本作为业务存档，所以我认为行礼如仪似乎比较妥当。不过，此封信既然与书店业务无关，自然毋须顾虑副本、存档的问题。\r\n    我们百思不得其解，不明白您如何隔海变戏法，让四双丝袜无中生有。我所知道的只是:今天中午我用过午餐回到书店，就赫然发现它们已经好端端地摆在我的办公桌上，上头还附着一张写着“海莲·汉芙赠”的卡片。没有人晓得它们是什么时候或是怎么来的。女孩们都吓呆了，我晓得她们正在打主意待会儿自行写信给您。\r\n    有一件令人遗憾的事要向您报告:久卧病榻的乔治·马丁先生上周在医院中病逝了。他在本书店工作已有多个年头。伴随着这个噩耗。国王的猝然驾崩亦使我们此刻都笼罩在一片哀戚之中。\r\n    我实在不知该如何回报您对我们的不断付出。我所能做到的，只是当您确定访问英国时，橡原巷37号将会有一个房间，可供您无限期地住宿。\r\n    全员共祝您一切美好\r\n    弗兰克。德尔\r\n\r\n 纽约市\r\n东九十五大街14号\r\n    1952年3月3日\r\n    噢!真感谢你寄来的《五人传》。实在难以置信，这本一八\r\n四〇年出版的书，经过了一百多年。竟然还能保持这么完好的书况!质地柔细、依旧带着毛边的书页尤其可人。我真为前\r\n任书主(扉页上有“威廉.T.戈登”的签名)感到悲哀，真是子孙不肖哟!竟把这么宝贵的东西一股脑儿全卖给你们。哼!我真想趁它们被称斤论两前，拎着鞋溜进他们的书房，先下手搜刮一番!\r\n    这真是一本引人入胜的书。你可知道，当约翰·多恩带着他主子的掌上明珠私奔(人家好不容易老来得女的)，他因此下狱被囚禁在伦敦塔，他在里头握饿受冻。饿着饿着……居然就“得道”了！——不是沃尔顿的原句啦，这是我自己改编的说法。\r\n    注意!我附寄五元钞票一张。由于这本《五人传》赫然登场，害我原来那本《垂钓者言》相形见绌(认识你们之前买的，这是一本硬邦邦的“美国大众经典文库”版)。《五人传》跟它不登对，总嫌它在跟前晃来晃去，看了就讨厌。所以，多出来的两块半就帮我找一本好点儿的《垂钓者言》——劳您大驾啦。\r\n    你得当心了，如果电视剧续签，明年我就会杀到你们那儿去。到时候我会蹬着古董木梯，掸去你们的书架顶层的陈年积垢，顺便也把你们的优雅端庄一并一扫而光。我跟你提过我帮埃勒里·奎因的电视节目编写巧艺谋杀吗?我所写的剧本总会安插艺术场景——芭蕾舞团啦，音乐厅啦，歌剧院什么的;连嫌疑犯或尸体也得写得文绉绉的。为了向你致敬，也许我该编一出发生在古书店里的谋杀案，怎么样?你要演行凶歹徒呢?还是要扮刀下亡魂?\r\n hh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年3月24日\r\n亲爱的汉芙小姐:\r\n    我实在不知如何表达我的感激，今天收到您寄给我的食物包裹，我有生以来未曾收到过包裹。您实在不须为我这个老太婆如此费心。我只能谢谢您的好意，并好好地享用您寄来的礼物。\r\n    感谢您为我设想如此周到。我拿您寄来的东西给德尔太太瞧，她也直夸您实在是一位大好人。\r\n    再次谢谢您，并析祷您一切安好。\r\n玛丽·博尔顿谨上\r\n\r\n    伦敦中西二区 令十字街84号.\r\n    1952年4月17日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲(您看，我已经不再“行礼如仪”了):\r\n    我相信您一定会高兴听到这个消息:我们刚购入一批私人藏书，里头有一册非常好的《垂钓者言》，待我将收购手续完成，本书可望于下周为您寄上。我们大概会标价二元二角五分，您账户中的余额仍够支付。\r\n    您为埃勒里·奎因写的剧本似乎挺有意思。我希望这部剧集也能在我们这儿播映，它也该改善其沉沉死气了(我是指我们的电视节目，不是您写的剧本)。\r\n    诺拉和所有人与我同祝您一切美好。\r\n弗兰克·德尔敬上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年5月4日星期日\r\n亲爱的海莲:\r\n    谢谢您寄赠的干燥蛋罐头，包裹在两天前收到了。尽管难掩喜悦之情，但我实在觉得很难为情，因为我竟向您提起蛋源短缺的事情。不过今年倒是还没发生减量配给的情形，所以我们就当成是上天的恩赐，用它们多烤了个蛋糕什么的。弗兰克还拿了一些去办公室，打算要寄给塞西莉，他老是忘了把她的地址带回家。我想您应该也听说了吧，塞西莉离职了，她正打算随丈夫调防到中亚去。\r\n    我附了几张相片，弗兰克说这些照片全没把他拍好，还说他本人比照片好看多了。不打紧，我们就让他说梦话好了。\r\n    希拉的学校放了一个月的假，以前我们全家偶尔会一起到海边走走或随处晃晃。但是现在恐怕也不能那么常出游了，因为交通费贵得吓人。我们一直有买车的念头，但车价也不便宜，物色一部性能尚可的二手车或许比较可行。大部分\r\n新车都出口到国外，国内只能分到少量的配额。我们有一些朋友，光是为了订一部新车，就等了五六年。\r\n    希拉现在要为您朗读一篇《丰收祈祷文》，祈祷您可以如愿到英国一游;而且在复活节时，我们也已经借花献佛，用您送给我们的咸肉罐头犒赏过神明了。所以，如果再加上《丰收祈祷文》奏效，老天将会保佑您获得一笔意外之财，这样您和我们就很快可以见面了!\r\n    就此停笔并再次感谢您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年5月11日\r\n亲爱的弗兰克:\r\n    本打算一收到书就写信给你的。就是想跟你道句谢谢，《垂钓者言》里的木刻版画太棒了，光这些插图的价值就十倍于书价。我们活在一个诡异的世界——这么漂亮，又能终生厮守的书，只须花相当于看场电影的代价就能拥有;上医院\r\n做一副牙套却要五十倍于此。\r\n    唉!如果你们依照每本书的实际价值去标价的话，我肯定一本也买不起。\r\n    如果你知道我这个一向厌恶小说的人终究回头读起简·奥斯汀来了，一定会大大地惊讶。《傲慢与偏见》深深掳获了我的心!我千不甘万不愿将我手头上这本送还给图书馆，所以快找一本卖我。\r\n    代我问候诺拉和办公室里可怜的上班族们。\r\nhh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年8月24日\r\n亲爱的海莲:\r\n    我要再一次对您寄给马克斯与科恩书店，并让我们家分享的物资向您道谢，我真希望能回寄点儿什么报答您。\r\n    对了，海莲，这个礼拜我们终于荣登有车阶级了。虽然不是一部新车，没什么好到处吹嘘的。不过，车子只要能跑就行了，您说是不?怎么样，您现在比较愿意来找我们玩了吧?\r\n    前一阵子，我有两位亲戚从苏格兰南下来看我们，博尔顿老太太心肠很好，肯让他们在她家叨扰了几个礼拜，他们说住在那儿很舒服。他们那一阵子就吃我家，睡博尔顿老太太家。如果您手头方便，可以赶明年儿，趁着女王登基大典时\r\n来一块儿凑凑热闹，博尔顿老太太说她会为您将房间准备好。\r\n    好了，不多说了，寄上我的祝福和感谢——谢谢您送的肉与鸡蛋。\r\n诺拉谨上\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年8月26日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我又要再度代表我们这儿所有人，写信向您道谢了，三件甚获欢迎的包裹几天前寄达。您实在太好心了，将辛苦挣来的钱花在我们身上。我们都非常感激您对我们的关怀。\r\n    这几天，店里进了三十几册“洛布经典文库”，可是，唉。里头不巧就缺了贺拉斯、萨福、卡图卢斯。\r\n    从九月一日起，我会休几个礼拜的假。可是因为我最近买了车，家中经济失血甚多，所以不能再大事铺张。诺拉有个姐姐住在海边，我们都冀望她会怜悯我们无处可去而邀我们去玩。这是我头一回买车，所以全家都非常兴奋——尽管这\r\n是一九三九型的老款车。但只要它不会老是半路抛锚，我们就该偷笑了。\r\n    全心祝福您\r\n    弗兰克·德尔\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年9月18日\r\n弗兰基，猜猜看，当你去度假时，谁上门来啦?萨姆·佩皮斯是也!不管是谁代你寄的书，别忘了要谢谢人家。这些书上个星期就到了，三册扎扎实实的海军蓝布面精装本，用四大页四开报纸裹着。我边吃午饭边读旧报;晚餐过后，开始和萨姆\r\n神交。\r\n    他要我转告你:他非常高兴能来到敝宝地，他的前任主人是个大草包，连书页都懒得裁开。我将它们一一裁开，内页用的是薄得几可透光的印度纸——我们这儿管这种纸叫“洋葱皮”，真是恰如其分。要是换成厚一点的纸张，难保不变成\r\n六册、甚至七册，印度纸果然功德无量。我只有三座书架，可是实在已经找不到可以让我清掉的书了。\r\n    每年一到春天，我就会“大清仓”，把一些我再也不会重读的书全丢掉，就像我也会把再也不穿的衣服扔了同样道理。倒是旁人都很惊讶、依我看，他们爱惜书本的方式才奇怪呢。他们买一堆新出版的畅销书，囫囵吞枣似的看完，我常\r\n想:他们也未免读得太潦草了吧。然后呢，因为他们从不重读那些书，不消一年，书里头的内容早就被他们抛到九霄云外!不过，当他们看见我一箱一箱地把书往外扔时，却又露出一副“这怎么得了!”的表情。要是照着他们的做法:买了一本\r\n书，好——读过了，好——上架，好——没事了，一辈子也不会再去碰它第二回，可是呢，“丢掉?万万使不得呀!”为什么使不得?我个人坚信:一本不好的书——哪怕它只是不够好，弃之毫不足惜!\r\n    你和诺拉过了一个不赖的假期吧?我自己则全消磨在中央公园里。我的宝贝牙医师放了我一个月的假，他却欢欢喜喜带着娇妻度蜜月去了，旅费是我出的——我有没有提过?好几个月前，我发现牙齿一颗接一颗全坏光了，我要么乖乖装上牙套。要不然就得全部拔光!因为还不想当个无齿之徒，我最后还是决定装牙套。可是诊疗费简直是天文数字!看来伊丽莎白只好在我没来的情况下登基了，我呢，今后几年里也只能留在这里看着我的牙齿一一加冕了。\r\n    不过我可没打算停止买书!连牙齿都弃我而去了。总该给自己留点儿什么呀!你能为我找到萧伯纳的剧评和乐评吗?我想他应该写了好几本，把你所能找到的都寄给我。还有!弗兰基，漫漫冬天眼看着又要来了，我兼差帮人带小孩时可不能闲着，所以，亟需读物!——快，别老坐着，起身帮我找找书!\r\n\r\nhh\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n  1952年12月12日\r\n此致“伦敦查令十字街84号的众好友们”:\r\n    一拆开包装，《爱书人文选》款款现身—镶金边的皮面、上金漆的上书口……轻而易举勇夺“我的藏书选美”的后冠，连首版的《大学论》也甘败下风。它看起来仍如此清新、纯朴，宛若从未遭染指——不过我知道，它的确曾被频繁地悉心翻阅过:因为一打开书页，总会落在某几个特定段落，冥冥之中似有前任书主的幽灵导引我，领我来到我未曾徜徉的优美辞藻。例如特里斯特拉姆·项狄描述他父亲富丽堂皇的书房:“架上罗列多善本，筐中广纳皆美卷。”(弗兰克!快去给我找一本《项狄传》！)\r\n    我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年;而你们送给我的礼物，却能和我朝夕相处，至死方休;我甚至还能将它遗爱人间而含笑以终。\r\n    谢谢你们，祝你们新年快乐。\r\n海莲\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n   1952年12月17日\r\n亲爱的海莲:\r\n    很抱歉这么久都没给您只字片语。希望阿德莱败选不致于让您太难过，也许下一回他可以东山再起。\r\n    博尔顿老太太跟我说，如果明年夏天您来英国时，她仍健在的话，很欢迎您住在她家。她是我所认识最长寿的人，我还不晓得有谁能像她活那么大岁数儿，我相信她一定可以活到一百岁的。无论如何，我们都会把您的住宿问题打点好。\r\n    谢谢您寄给我们这么好的圣诞礼物，您实在是个大好人。海莲!如果您明年来英国的时候，马克斯与科恩书店的人没好好地为您设宴接风的话，他们就太该死了。\r\n    我希望您将有个快乐的圣诞节。下回再聊了，献上我们\r\n的祝福和感激。\r\n    愿上帝保佑您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1953年5月3日\r\n弗兰基，告诉你个包准让你乐翻的消息-—\r\n    首先，随信寄上三元钞票。邮包已收到，这本书长得就像简·奥斯汀该有的模样儿——皮细骨瘦、清癯、完美无瑕。\r\n    好，进人正题——埃勒里电视剧集停播了。正当我青黄不接，又为了支付看牙的庞大开销而焦头烂额的当头，有人找我为一个新的节目写个草案——将名人轶事编成电视单元剧。所以我快马加鞭，完成一个故事大纲。送出之后，电视\r\n公司接受了;于是我又写了一个完整剧本，他们也颇为满意——所以再过个把月，新差事就有着落了。\r\n    而你猜我改编哪一个故事?“多恩与领主千金私奔记”—灵感来自沃尔顿的《五人传》!电视观众大概没几个人晓得约翰·多恩是谁，不过，拜海明威之赐，大家都听过“没有人是一座孤岛”。我只消将这句名言编进剧本里，便顺利\r\n卖出啦!\r\n    于是，约翰·多恩成功登上“不朽名人堂”。我也拿到一笔酬劳—价码大约是我前前后后花在你们店里的书款外加五颗牙!\r\n    我打算半夜爬起来听收音机的现场实况转播，和你们一块儿参加加冕典礼，同时惦念着你们所有人。\r\n    祝开心\r\n\r\n    伦敦中西二区，查令十字街84.\r\n    1953年6月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    您的包裹于六月一日平安抵达了，正好赶上加冕大典。当天家里来了不少朋友。和我们一起收看电视转播。我们用您寄来的火腿做了些三明治供大家享用，每个人都直说美味极了。于是我们全体举杯同祝您及女王都凤体康泰。\r\n    您实在太仁慈了，竟将您的辛苦所得拿来关照我们。所有同仁与我在此同声向您道句:万分感激。\r\n    祝福您\r\n弗兰克·德尔谨，上\r\n\r\n    米德尔塞克斯 品内东厢 包德米尔路\r\n    1953年9月23日\r\n亲爱的海莲:\r\n    我必须赶紧通知你:今年圣诞节可千万不许再寄礼物来。所有的东西都已经不用配给了，稍好一点的店里头也能买到丝袜。请攒下你的钱，现在最要紧的就是等你治好牙齿之后，能到英国来。只是别挑明年来。因为那时我不在国内。后年我才会回国，这样子你来了才能住在我们家。\r\n    道格写信告诉我，我们候补的家庭宿舍就快有着落了，孩子们和我希望能在圣诞节前就搬去和他团聚。他现在暂时被调到巴林岛(在波斯湾，你可以在地图上找找看)的基地，日子过得愉快惬意。等到家庭宿舍一分配下来，他便会回到哈巴尼亚(在伊拉克)的皇家空军基地和我们会合。一切都很顺利。\r\n    要写信给我哟，即使我暂时“出局”了，妈妈也会把你的信转寄给我的。\r\n思念并祝福您\r\n塞西莉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1955年9月2日\r\n你们店里一直发行这么棒的目录。却直到现在才寄给我!难道你还好意思跟我说你老是忘了吗?汝等无赖!\r\n    忘了哪个复辟时代的剧作家老爱用“汝等无赖”这个词儿数落别人，我好不容易终于逮到机会可以用它来造个句儿。\r\n    活说回来，这整本目录里头只有这本卡图卢斯我有点儿兴趣——虽然不是“洛布经典文库”版，不过看起来还算差强人意，如果这本书还在的话就寄给我。至于书价六先令两便士，只要你换算成美金，我马上付——凯特和布莱恩搬到郊\r\n区。这下子没有人可以帮我换算了。\r\n    如果你从下个月起每个星期都能携家带眷乖乖上教堂，我会十分感激你。请一起为吉廉、李斯、史奈德、坎帕内拉、罗宾逊、哈吉斯、费里罗、帕德瑞斯、纽坎姆与拉宾—布鲁克林道奇队全体球员祷告，祈祷他们身强体健并获天助神力。要是他们打输了世界大赛，我也不想活了，到时你再后悔就来不及了。\r\n    你们有德·托克维尔的《美洲见闻录》吗?有人把我原有的一本借走了赖着不还。我实在百思不解，再循规蹈矩的人怎么一霸占起书来都是一副理直气壮的气派。\r\n    代我问梅甘好，要是她还在店里头帮忙的话。还有。塞西莉现在怎么样了?从伊拉克回来了吗?\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1955年12月13日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    十分抱歉没能早一点写信给您。不过请您先别忙着动气，因为我生了一场小病，请了几个礼拜病假在家。一回到店里又被一堆待补办的公事绊住。\r\n    关于目录里登载的那本卡图卢斯，收到您的信之前就已经被买走。但我仍为您找到另一个版本寄去——这也是一本附拉丁原文的版本，韵诗部分由理查德·伯顿爵士翻译;内文则为伦纳德·史密瑟翻译，大字印刷，标价三美元七十八\r\n分。装帧并非十分漂亮，但品相尚称良好。我们目前没有德，托克维尔的书，将会为您留意。\r\n    梅甘仍在书店，但是她正打算搬到南非去，大家都还在劝她打俏念头;自从塞西莉随丈夫去了中亚之后，我们就再也没有她的消息，转眼间她竟然已经离职一年了。\r\n    我很乐意为布鲁克林道奇队加油——如果您也愿意为“热刺队”(托特纳姆足球队)打气的话。他们目前在联盟的排名敬陪末座，不过，球季尚未结束，同志仍须努力。希望他们在明年四月前能扭转颓势。\r\n    诺拉、所有人和我在此祝您圣诞快乐、恭贺新禧!\r\n弗兰克·德尔谨上\r\n\r\n     纽约市\r\n    东九十五大街14号\r\n    1956年l月4日\r\n    我现在趴在床脚下写信给你——这本卡图卢斯害我气得滚下来。\r\n译得洁屈赘牙的，真教人伤脑筋!\r\n到目前为止，我只听过一个名字也叫理查德·伯顿的帅哥演员——我曾在几部英国电影里头看过他，我想维持这么点交情也就够了。至于这个翻译者理查德·伯顿，他译得也未免太花俏了吧。\r\n    而可怜的史密瑟先生，他一定害怕他妈妈会读这本书，所以忍痛把那些原本应该活色生香的文章译得道貌岸然。咱们这么着吧，你索性找一本好的通俗拉丁文版的给我。我自己有一本卡塞尔氏拉丁文字典，那些难懂的段落，我自个\r\n儿去整明白得了!\r\n    梅甘头壳坏掉了吗?如果她真的那么厌烦文明世界，怎么不干脆搬去西伯利亚!\r\n    行行行!没问题!我会当热刺队的拉拉队!\r\n    正努力攒钱当中。如果电视台继续赏我饭吃，明年夏天我就可以成行了。我要去亲眼瞧瞧贵书店、圣保罗大教堂、国会殿堂、伦敦塔、柯芬园、老维克剧院. ....还要见见博尔顿老太太。\r\n    附寄十元大钞一张。这本白色软精装的卡图卢斯，居然还配着白色的丝质书签带……弗兰基，你打哪儿找来这些玩意儿啊?\r\n\r\n    伦敦中西二区 查令十字街84号.\r\n    1956年3月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    很抱歉又隔了好一段时间才回您的信。因为直到今天，我们才有好消息可以向您报告。原先我还在犹豫，是不是该等到卡图卢斯的书也一并我到时再写这封信比较好。\r\n    我们终于寻获一本版本相当不错的《项狄传》，附有罗布的插图，价格约合美金二元七十五分。同时我们也收到一册柏拉图的《苏格拉底四论》，译者是本杰明·乔伊特，一九〇三年在牛津出版。此书标价一美元，您是否要买?您在敝店\r\n户头内尚有美金一元二十二分的余额，如您两册都购买，仅\r\n需再付给我们美金二元五十三分。\r\n    我们仍翘首期盼您今夏能来，我家的两个女孩儿都离家住校，所以届时橡原巷3号将有两间卧房可任您挑选。很遗憾地向您报告:博尔顿老太太已被送到老人之家，我们都很难过，但毕竟她在那儿能得到比较好的照料。\r\n弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1956年6月1日\r\n亲爱的弗兰克:\r\n    布莱恩介绍我读肯尼思·格雷厄姆的《杨柳风》，因此我迷上了谢泼德的插图，决定自己也要买一本。但是!先别忙着寄来，帮我保留到九月，届时我会迁入新址。\r\n    原本住得舒舒服服的旧公寓老命不保喽。上个月所有住户都收到了搬迁通知。现址要盖新大楼。我想也是该为自己觅一间好公寓、买几件好家具的时候了。于是我打铁趁热地到第二大道的一处工地，订了一间连影儿都还没有的客卧两\r\n用预售屋。我现在忙着四处张罗新家具和书架、地毯，几乎把钱都花光了。可是，我一辈子都跟摇摇晃晃的桌椅、到处爬满蟑螂的厨房为伍，现在我想过点儿像样的日子。至于英国之旅，只好等着有人招待我去了。\r\n    与此同时，我的房东怕大家赖着不走，索性把门房给解雇了，害得垃圾和热水都没人打理;还打算要把信箱间、走道灯和我的厨房和卧室间的隔间墙全拆了(本周即将发生);这些烦心的事加上眼睁睁看着道奇队兵败如山倒，真是心酸谁\r\n人知噢……\r\n对了，新地址如下:\r\n纽约州，纽约市21，东七十二大街305号(九月一日启用)\r\n\r\n    伦敦中西二区，查令十字街84号\r\n    1957年5月3日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    预备好大震一惊:您上回信中提到的三本书，一口气全都找到了!而且已经在上周寄去给您，现在应该正在途中。别惊讶我们究竟是怎么办到的，这理应是本店的服务项目之一。账单附在此信中，扣除账户余额之后的书款为五美元。\r\n    几天前，有两位您的朋友到店里来探望我们——很抱歉我现在不记得他们的大名了，是一对很可爱的新婚夫妇。不过，可惜他们行程匆忙，只能在我们这儿稍坐片刻，抽根烟的光景，就必须告辞了。\r\n    今年的美国游客似乎较以往更多了。我曾见到由上百位律师组成的旅游团，每个人西装上都别着一块大大的名牌—上头写着他们的姓名和居住的城镇名。他们好像都玩得颇开心，所以明年您一定也要来一趟。\r\n致上全体的祝福\r\n弗兰克\r\n\r\n    海莲·汉芙 纽约州，纽约市21。东七十二大街305号\r\n    1958年1月10日\r\n嗨，弗兰基——\r\n    快叫诺拉更新通讯薄里的地址，你们家的圣诞卡寄到旧址去啦!她写成了东九十五大街14号。\r\n    忘了是不是说过了，我好喜欢那本《项狄传》，罗布的插图简直美得令人销魂，托比叔叔。地下有知也可含笑瞑目啦。我在信纸背面列了几本“麦克唐纳绣像经典”的书。其中有一本《伊利亚随笔》，有“麦克唐纳绣像经典”版最好，若是有其他不错的版本也行，但价格得“合理”我才会考虑——现在已经找不到“便宜”的东西了，所有的东西都标榜“价格公道”，要不然就是“收费合理”。对街又在盖大楼了，他们立了一块大招牌，上头写着斗大的字:\r\n    “一房一厅或两房一厅任君选择/租金合理”\r\n    房租什么时候“合理”过?其他东西也没好到哪儿去。尽管招牌上盖得天花乱坠，终究只是个广告文案。\r\n    我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天。就像米尼弗·奇维一样，余生也晚。而我也只能学他“干咳两声，自叹一句:奈何老天作弄。然后继续借酒浇愁。\r\nP.S. 喂，柏拉图的《苏格拉底四论》的下落呢？\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1958年3月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    在此向您致歉，无法及早回信。我们前一阵子都累垮了，诺拉生了一场病，在医院里待了几个月，而我只好暂时请假在家代理家务。她现在已经复元得差不多了，再过个把礼拜就能出院回家。这段时间真够折腾我们两人的，不过还真得\r\n感谢国民保健制度，我们几乎没花自己一毛钱。\r\n    关于“麦克唐纳绣像经典”，我们偶尔能收购到一些，不\r\n巧现在手头上都没有。而兰姆的《伊利亚随笔》本来也有好几本，但上回旅游旺季时全被买走了。下周我还会出一趟差，届时再尽力为您搜寻。另，柏拉图的书也会为您留意。\r\n    所有人齐祝您有一个愉快的春假。女孩儿们要我转告:她们对您感到很抱歉，粗心将圣诞卡寄到旧地址去了。\r\n弗兰克敬上\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1958年5月7日\r\n亲爱的海莲:\r\n    谢谢您接连寄来两封问候信。也很感激您的好意，不过说真的，海莲，我们现在什么都不缺。我真希望我们能自已开一家书店，这样我们就能送几本书给您，多少报答您的一片好心。\r\n    随信附上几张全家福照片。本想寄些拍得更好的，但是比较好一点的都被亲戚们要走了。你一定也会发现了吧，希拉和玛莉竟然长得那么像，这实在是一件很奇妙的事。弗兰克说。玛莉现在长得就像是希拉在她这个年纪时的模样。希拉的母亲是威尔士人，而我的老家则是在爱尔兰，所以她们一定得自弗兰克的遗传多些，不过她们都比弗兰克要漂亮多了，当然弗兰克死都不愿承认的!\r\n    要是您晓得我写这样一封信得绞尽多少脑汁，一定也会可怜我的。弗兰克老爱笑我:成天叽哩呱啦地说个不停，怎么一拿起纸笔就不灵光了呢?\r\n    再次感激您的问候和您的来信。\r\n上帝保佑您!\r\n诺拉\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1959年3月18日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我实在不知如何向您启齿。就在上一封信向您报告，为您的朋友找到了《简明牛津辞典》之后隔两天，我才一转身，这本书就被别人买走了。我之所以这么晚才回信，也是因为希望能尽快再另寻一本，可惜至今仍一无所获。辜负了您的\r\n朋友，我感到万分愧疚，当时我实在应该先将书收起来的。\r\n    我们今天会将约翰逊的《莎士比亚评传》寄出，正好敝店内有这本附沃尔特·雷利导言的牛津出版社版。定价为一元五分，您的账户内尚有余额足够支付此书。\r\n    您参与的电视节目将移师到好莱坞，大家都觉得很可惜;夏天又快到了，预料将会有更多美国游客到英国来，然而我们期盼的“那位美国游客”却仍独独教我们望穿秋水。我很能理解您不愿离开纽约搬到南加州的心情。我们也在此祝您好运，并希望您很快可以再找到类似的工作。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙 纽约州，纽约市21.东七十二大街305号\r\n    1959年8月15日\r\n仁兄：\r\n    告诉你吧，我又有活儿可干啦!\r\n    是我凭实力挣来的。我得到一笔CBS提供的编剧奖助金——为数五千美元，供我未来一年将美国历史编写成电视剧本。我头一个要写的题材是英国占领期间的纽约。我一边写一边踟蹰——贵国同胞于一七七六年到一七八三年在这儿的行径那般龌龊不堪，而我偏挑这一段来写，实在有点儿对不起你们现在待我如此温良友善，体谅慈悲。\r\n    你们可有白话版的《坎特伯雷故事集》?实在罪过，我从没读过乔叟。我想学古盎格鲁——撒克逊或〔和)中世纪英语的念头叫一位要拿它做博士论文的朋友给打消了。教授们对她说:关于古盎格鲁——撤克逊，想写什么题目都行。“乍听之下似乎很棒，”我的朋友哭丧着脸告诉我，“可是惟一能找到够多古盎格鲁——萨克逊语的文章，尽是些‘烧教堂、砍人头’的玩意儿。”\r\n    她还跟我讲了一堆贝奥武甫和他的私生子希卫斯(还是卫希斯?)的故事，她说那些也没啥看头。经她冷水一浇，我的兴致也全没了。所以给我白话版的《坎特伯雷故事集)就行了。\r\n代我问候诺拉。\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1959年9月2日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    人家都很高兴听到您得到一笔奖金，而且也有工作了。对于您所挑选的任何题材，我们完全不会介意。但我必须向您报告，店里的年轻同事们坦承:若不是您在信中提及，他们浑然不知英国竟然曾经“占领’过美国。\r\n    关于您的疑问，似乎所有最顶尖的研究学者对于将乔叟的文章译成白话文都敬而远之。但是，在一九三四年由希尔改写、朗文出版社出版的《坎特伯雷故事集》，应该是目前惟一能找到的白话版了。我认为还算是不错的书，此书现已绝\r\n版(这自然不在话下)，我将为您找一本书品较好的。\r\n弗兰克谨上\r\n\r\n        一九六〇年开春头一个糟糕透顶的周日夜\r\n我快没辙啦!弗兰基—\r\n    有人送我这么一本书充圣诞礼物。这是一本“巨匠现代文库”。你可曾见过这种版本?装帧比起纽约州议会公报好不到哪儿去，重量倒是略胜一筹。一个爱附庸风雅的家伙，听说我喜欢约翰·多恩。不知打哪儿找来送我。书名是:\r\n     约翰·多恩\r\n    诗全集、文选\r\n        与\r\n    威廉·布来克\r\n       诗全集?\r\n    问号是我自己加上去的。你学问大，能不能告诉我:这两个老小子浑身上下有哪点相同，得这样子凑合在一块儿?——只因他们俩都是英国佬，也都舞文弄墨?我努力读着导论，想找出些蛛丝马迹来。导论洋洋洒洒共分四大章:头两章巨细靡遗地描述了多恩的学术生涯;第三章开头是这么写的(我真不想引述):\r\n    “话说少年布来克，在茵茵夏日草原，于树下邂逅先知以西结，而遭母亲深责痛打。”我百分之百支持他娘。我的意思是:就算没瞻仰到上帝的后背，好歹领教一下圣母的尊容也不赖呀——谁叫他好死不死见到什么鬼先知以西结?\r\n    反正我不喜欢布来克，他还真是让这本书减色不少。看来我势必又得清掉一些书啦，弗兰基，你得伸出援手。\r\n    我现在蜷瘫在安乐椅里，聆听着收音机传出恬淡闲适的古典音乐——大概是科莱利吧... ...慵懒地享受这天下太平的短暂时刻。而这玩意儿——“巨匠现代文库”就站在桌上直盯着我瞧。我心想:好吧，不妨朗读读第十五篇布道词中的那\r\n三个标准段落好了。”多恩的文章合该大声朗读，它简直就像是巴赫的赋格!\r\n    可是你晓不晓得，这个无端之举给我自己惹了什么天大\r\n的麻烦?\r\n    打开“巨匠现代文库”，翻到“布道文第十五篇”——竟被肢解成三个选段。该到第一段末尾，才一发现“耶洗别篇”被删掉了;我赶紧找来另一本多恩的《布道文选编》(罗根·皮尔史·史密斯编)，花了二十来分钟拼命找“布道文第十五篇”……搞了半天才发现:原来，照史密斯的编法，它不叫“证道文第十五篇”，而是“第126节:人必有终”。好不容易找到了，却——同样没有“耶洗别篇”。再搬出《约翰·多恩诗全集暨文选》（典范版）.依然少了那段“耶洗别篇”;转而向《牛津英语诗选》求助，再度穷花二十分钟，因为在这本书里，既不叫“布道文第十五篇”，也不叫“第126节:人必有终”……好歹“耶洗\r\n别篇”总算是找着了，于是开始正襟危坐大声朗读……读到末尾，啥，又不行了——这本书没有第二、第三段!所以咯，我若要从头到尾朗读一篇完整的布道文，得将三巨册全摊开，翻到正确的页面，然后绕着它们来同奔波!\r\n    请好心地告诉我:一本完整收录约翰·多恩布道文的书有那么难找吗?我得花多少钱才买得到?\r\n    我得去睡了。我会做一个可怕的噩梦——披着道袍的妖魔鬼怪，拎着一把把血淋淋的屠刀—上面分别标示着\r\n“段”、‘’节”、“选”、“删”等字眼、霍霍朝我追来……\r\n        祝好\r\nhh\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    l960年3月5日\r\n    海莲· 汉芙小姐\r\n    美国\r\n    纽约州，纽约市2l\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我又迟回您的信了，因为我希望能等到有好消息再向您报告。我找到一本萧伯纳与埃伦·特丽的书信集，虽然装帧不特别吸引人，但书品完好。我想这一次我还是尽快将书寄给您为妙、因为这是一本颇抢手的书，要等到下一本出现，恐\r\n怕得花一段时日。扣掉书价的二元六十五分，您的账户余额尚余五角。\r\n    关于收录完整的多恩布道文，恐怕只能在《约翰·多恩全集》中才能找到，而这套书共有四十余册，若书况稍好些的也\r\n必然所费不赀。\r\n    我们希望您后来没耗费太多心神在那本“巨匠现代文库”上，好好地过了一个愉快的圣诞节和新年。\r\n    诺拉也要祝您一切顾遂。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1960年5月8日\r\n    德·托克维尔阁下在此致意，宣告他已安然渡抵美利坚。他顾盼满志地坐谈时事，而他的臧否都卓然成理。尤其当他论及“律师治国”时，我只有额手、点头的分儿。我参加了一个民主党的小型聚会，前儿天晚上，出席的十四个人里头有十一个是律师;回到家在报纸上读到一堆名人励志故事——有为青年怀抱鸿鸽之志，力争上游，终于晋身一国之尊——史蒂文森、汉弗莱丹、肯尼迪、史塔生.、尼克松……一整票人除了汉弗莱，全是干律师的。\r\n    我附上三块钱钞票，这是一本漂亮的书，实在不能算是“二手书”，连书页都还未裁开哩。我有没向你说过，我终于找到方便好用的裁页刀了?这是一把珍珠柄的水果刀，我母亲留给我一整打这种刀，我挑了一把搁在案头的笔筒里。大概\r\n是交游不力吧，我好像没有机会请来十二位宾客。让他们围坐在餐桌前集体切水果哩。\r\n    笑口常开\r\n\r\n    海莲·汉芙 纽约州，纽约市21，东七十二大街305号\r\n    1961年2月2日\r\n弗兰克?\r\n你还在吗?\r\n    我答应自己在找到工作前不再写信给你的。\r\n    终于卖了一篇稿子给《哈泼杂志》。被这篇稿子折腾了三个星期，他们付给我两百美元稿费。现在他们再度向我约稿，要我将生平事迹写成一本书，他们将“预付”给我一千五百美元!并预估我不用半年就能写得出来，我是无所谓啦，不过房东可又要头疼了。\r\n    所以这阵子暂且不能买书了。去年十月，有人介绍我读圣西蒙公爵路易的书，不过这是一本乏善可陈的节译本。于是我火速赶往学会图书馆，因为这个图书馆不仅全馆开架，还让我爱借多少就借多少，我在那里找到货真价实的路易。现在我已经对他不可自拔。正在读的这一套是六卷版，昨晚读到第六卷一半时，一想到等我把书归还后，家里就连一本路易也没有了，这实在令我难以接受。\r\n    我现在读的是弗朗西斯·阿克赖特的译本，他的译笔甚雅。不过我放手让你挑你信得过的版本。但先别忙着寄来!找到后暂时搁着，先报价，然后再一本一本卖给我。\r\n    希望诺拉和女孩儿们都一切安好—还有你和其他所有认识我的人。\r\n海莲\r\n\r\n    伦敦中西二区 查令十宇街84号.\r\n    1961年2月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    您听到一定会开心的:我们正好有一部《圣西蒙公爵回忆录》在店里，译者正是阿克赖特。六卷全帙，装帧精良且品相完好。我们将于今天寄给您，大约一两个礼拜可望寄达您的手中。这套书的价格约合十八元七十五分，但请勿挂怀书\r\n款，您在敝店的付款记录一向良好。\r\n    同时，很高兴又有了您的消息。我们大家都很好，依然盼望您能到英国一游。\r\n    所有人诚心祝福您。\r\n    弗兰克\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1961年3月10日\r\n亲爱的弗兰基:\r\n    附上“千万不能掉了”的十元钞票，它务必平安到达你们手里。倒不是最近发了什么横财，而是路易要我不能留他一个未赎之身。他在法庭上受够了俗不可耐的赖债痞子，可不希望保持了两百七十年的清誉毁于一旦。\r\n    《哈泼杂志》指派给我的编辑昨晚来家里吃饭，和我讨论“我的生平故事”，于是想起你。我们聊到我曾为电视节目“不朽名人堂”改写兰多的《伊索与萝多彼》(我好像告诉过你了?)——兰多笔下那个天真浪漫的萝多彼由萨拉·丘吉尔\r\n饰演。这一集节目在某个周日下午播出。就在播出前两个钟头，我翻着《纽约时报》周日书评版，在第三版中有一篇针对波莉·阿德勒关于娼妓业的新书《野花莫如家花香》的书评，附了一帧照片—一尊希腊的女人头像，图说这么写着:“萝\r\n多彼——希腊最艳名远播的娼妇。”兰多本人对此倒并未详加着墨，许多学者咸称：兰多笔下的萝多彼其实就是让萨福的兄弟千金散尽的众女子的综合体。我既非学者，而且多年前死记强背下的希腊文语尾变化，我也早忘得一干二净啦。\r\n    我对金(我的编辑)谈起这段轶闻，她问我:“兰多到底是何方神圣啊?”我不厌其详地为她细说从头——正当我苦心孤诣一头热滔滔不绝时，她竟不耐烦地插嘴说:“你还真的中毒不轻唉。”\r\n    唉，这下子你该明白了吧，弗兰基，这个世界上了解我的人只剩你一个了。\r\nP.S.金小姐乃中国人是也。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年10月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    当您收到弗吉尼亚·吴尔夫的《普通读者》。上、下两册时，一定会十分惊喜——它们已在寄往您的路上。如果您还有其他想要的书。我也会倾全力尽速为您服务。\r\n    我们都很好，仍是活蹦乱跳的。我的大女儿希拉(现在已经二十四岁了)两年前突然决定改行当老师，便辞掉原来的秘书工作。跑去念大学了，她还得待在学校一年。看来，要靠儿女供养我们这些老人家颐养天年的日子还有得等哩。\r\n    所有人诚心祝福您。\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年11月9日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    许久以前您曾询问过白话版《坎特伯雷故事集》，前几天我们收购了一部，心想您或许仍想买。虽然这并非是收录所有故事的完整版本，但价格十分便宜而且似乎编写还算严谨。我今天会将它寄给您，书价是一元三十五分。如果您读了\r\n之后，还想要更完整的乔叟作品，我会尽力去找。\r\n    弗兰克谨上\r\n\r\n星欺六\r\n    行了!白话版的乔叟真是够了!简直就像兰姆的《莎士比亚故事集》嘛——适合学龄儿童阅读!\r\n    充其量就是故事嘛，我讨厌虚构故事这事儿你是知道的。倒是里头描述一个吃相优雅、食不沾手的修女的那段还算有趣，换了我就不行，还是得动刀舞叉才成。其余内容全引不起我的兴趣，我就是不喜欢故事。如果乔叟能留下日记，里\r\n头规规矩矩记述他在理查三世的皇宫里当差的经过，那才是我真正该读的东西，否则我辛辛苦苦学文言文所为何来。\r\n    最近才刚扔了一本人家送的书——作者描述奥立弗·克伦威尔时代的社会状况。天晓得这个自作聪明的家伙是不是瞎瓣。他又不是那个时代的人，哪晓得那个时代的社会状况?如果我真想了解那个时代的社会状况，大可左阅弥尔顿，右\r\n读沃尔顿。这些货真价实的作品不仅能清楚明白告诉我那个时代的社会状况，还能引领我身历其境。\r\n    君不见沃尔顿尝曰:“若非身临现场、亲眼目睹，何以让读者尽信余言?”\r\n    这段话铿锵有力，深得吾心!我坚决拥护“亲身经历”的作者、作品。\r\n    附上两元支付这本乔臾，这样我在你们的户头里应该还会有六十五分钱的余额——比起我的其他任何一个户头都多。\r\n\r\n    海莲·汉芙 纽约州 纽约市21 东七十二大街305号\r\n    1964年3月30日\r\n亲爱的弗兰克——\r\n    我手头上正在编写给孩子们读的历史教材(已经写到第四本了，惊讶吧?)，忽然想起要帮一位朋友问你:你们有没有一套萧伯纳全集——他坚称书名前冠着“定本xxx”、暗红褐色的布面装帧——希望你有印象。我附上一张清单，上面列的是他已经有的几本。如果你可以为他补齐其他几本，先别全部寄来!他会分批购买，他和我一样——甲级贫户一个。你们可以直接寄给他，地址就写在清单上头。如果你嫌我的字太潦草看不懂的话，那是“第二十二大道”。\r\n    你可有塞西莉或梅甘的消息?\r\n祝一切顺利\r\n海莲\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1964年4月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    关于您的朋友想要的《定本萧伯纳作品全集》，原出版社目前仍有新书发行。红褐色布面精装——正如您所描述的。我想全套共有三十册。旧书反而不常见到，如果您的朋友不介意购买新书，我们可以安排对他方便的方式，每个月寄给\r\n他三或四册。\r\n    我们这几年来都没有塞西莉·法尔的消息，至于梅甘·韦尔斯，她在南非没多久就待不下去了，回国后曾到书店来看大家，给了大伙儿一个发“早跟你说了偏不听”牢骚的机会。不过她后来又搬去澳洲碰运气了。几年前曾收到她寄到书店的圣诞卡，最近则又断了音讯。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1965年10月4日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    很高兴再度收到您的来信。是的，我们都还健在如昔——老态益发龙钟，工作更加忙碌，口袋却没能加倍饱满。\r\n    我们购人了一本E.M.德拉菲尔德的《村姑日记》，一九四二年的麦克米兰版，书品很好，定价两美元。今天我会将书及账单为您寄去。\r\n    我们度过了一个“青春洋溢”的夏天——今年的游客比往年更多，大批年轻人全涌向卡纳比街朝圣。我们只能老远隔着安全距离打量着他们。老实说，我还蛮喜欢披头士的，只希望他们的歌迷们不要放声尖叫。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    海莲·汉芙  纽约州，纽约市21，东七十二大街305号\r\n    1968年9月30日\r\n我们都仍健在，可不是吗……\r\n    我为儿童编写美国历史读物已经长达四五年，得将这玩意儿告一段落了——为了写这些书，我自己还买了一大堆关于美国历史的书，全都是长相丑、装订差的美国书。我想，大概没有哪个循规蹈矩的英国人会在家里收藏詹姆斯·麦迪\r\n逊的制宪会议记录。或是T.杰弗逊写给J.亚当斯的书信吧。\r\n    你当上祖父没?告诉希拉和玛莉，她们的小孩将可以免费获赠有作者签名的《少年历史读本》，这样子应该能让她们比较愿意安定下来增产报国了吧。\r\n    我挑了一个细雨霏霏的星期天介绍一位年轻朋友读《傲慢与偏见》，她现在果然已经疯狂迷恋简·奥斯汀了。她的生日就在万圣节前后，你能帮我找几本奥斯汀的书当礼物吗?如果是一整套的话，先让我知道价钱，万一太贵，我会叫她的\r\n先生分摊，我和他各送半套。\r\n    祝诺拉和你周围所有人好。\r\n海莲\r\n\r\n    伦敦中西二区 查令十事街84号\r\n    1968年10月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市X\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    是的，我们依然健在，手脚也还勉强灵光。这个夏天真是把大家忙坏了。从美国、法国、北欧和其他各国来的大批观光客，几乎把我们比较好的皮面精装书全都搜刮一空。由于书源短缺。加上书价节节攀升，恐怕很难赶在您的朋友生日前找到任何奥斯汀的书，我们会设法在圣诞节之前为您办妥这件事。\r\n    诺拉和女孩儿们都很好。希拉已经开始执起教鞭;玛莉则和一位人品不错的小伙子订了亲—不过一年半载的还结不成婚，因为双方的经济条件都不太宽裕。所以。诺拉一心想当个福福泰泰的祖母，这希望恐怕愈来愈渺茫呢。\r\n    想念您\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1969年l月8日\r\n    H.汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街Los号\r\n敬爱的小姐:\r\n    我于近日整理公文档案时，偶然发现一封您于去年九月三十日寄给德尔先生的信。我在此非常遗憾地向您报告:德尔先生甫于上个礼拜天(十二月二十二日)去世了。丧礼则已在上周三(元月一日)举行。\r\n    德尔先生于十二月十五日因罹患急性盲肠炎被紧急送医，虽然立即施行手术，但他仍不幸因病情扩散，导致腹膜炎并发而于七日后不治。\r\n    德尔先生在本书店服务已超过四十年，加上马克斯先生也刚辞世未久，科恩先生对于这个不幸的事件自然万分悲坳。\r\n    您是否仍须本店为您寻找简·奥斯汀的书?\r\n         \r\n马克斯与科恩书店\r\n              秘书 琼·托德敬上\r\n\r\n(未署明日期，邮戳日期为1969年1月9日。无寄信地址〕\r\n\r\n亲爱的海莲:\r\n    感谢您寄来的慰问信，我完全不认为那冒犯了我。我真希望您在弗兰克在世时能够与他见面，并亲自结识他本人。我原先只知道他是一个处事严谨同时也很幽默的人;现在还了解了他在待人处事上更是一位谦冲的君子。我收到许许多多来\r\n自各地的信，都异口同声地赞杨他对古书业的贡献;许多人还说他是如何饱富学识而又不吝予与其他人分享……如果您想要看这些信，我可以将它们寄给您。\r\n    不瞒您说，我过去一直对您心存妒忌，因为弗兰克生前如此爱读您的来信，而你们俩似乎有许多共通点;我也羡慕您能写出那么好的信。弗兰克和我却是两个极端不同的人，他总温和和有耐性;而因为我的爱尔兰出身，我的脾气总是又倔又拗。生命就是选么爱捉弄人，他从前总是试图教导我书中的知识……我现在好想念他。\r\n    孩子们都很懂事、我为此深感欣慰。像我这样要一辈子孤寂以终的人想必大有人在吧。\r\n    希望您能原谅我的字迹潦草。\r\n                 祝福您\r\n                 诺拉\r\n    我盼望有一天您还是能来造访我们，两个孩子都很想见见您。\r\n    1969年4月11日\r\n\r\n亲爱的凯瑟琳——\r\n    我正在整理我的书架，现在抽空蹲在书堆中写信给你，祝你们一路顺风。我希望你和布莱恩在伦敦能玩得尽兴。布莱恩在电话中对我说:“如果你手头宽裕些就好了，这样子你就可以和我们一道去了。”我一听他这么说，眼泪差点儿要夺\r\n眶而出。\r\n    大概因为我长久以来就渴望能踏上那片土地……我曾经只为了瞧伦敦的街景而看了许多英国电影。记得好多年前有个朋友曾经说:人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我:“就在那儿!”\r\n    或许是吧。就算那儿没有，环顾我的四周……我很笃定:它们已在此驻足。\r\n    卖这些好书给我的那个好心人已在数月前去世了，书店老板马克斯先生也已不在人间。但是，书店还在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……\r\n海莲\r\n\r\n尾声1969年10月\r\n\r\n    伦敦北十一区 温顿道\r\n    1969年10月\r\n亲爱的海莲:\r\n    这是德尔家族第二号通讯员首次发言!\r\n    首先，我要对我们长时间的静默向您致歉。请相信我，其实我们心中一直惦记着您。只是不知如何将这样的意念用文辞表达。直到今天我们又收到您的来信，我们都感到万分惭愧，并决定应该立刻动笔回信给您。\r\n    我们很高兴得知您的出版计划，也同意并很愿意提供这些信件供您作为出书之用。\r\n    我们现在搬到可爱的新家，常常会想着:如果父亲依然健在，一定也会喜欢这儿。\r\n    再多的悲坳亦无济于事。虽然父亲生前从未拥有财富、权势，但他始终是一个快乐自得又具有丰富内涵的人，我们应该以拥有一位这样的亲人而深感欣慰。\r\n    也许只是为了冲淡愁思，我们都尽量让自己忙碌着。玛莉白天在大学图书馆辛勤工作，晚上则和朋友开车出游散心，深夜方归;我除了正常的教职外，还兼修一个学位;妈妈则整天忙上忙下，一刻也不教自己闲下来!所以，恐怕大家都无法好好地回信——不过，当然我们会很高兴能继续收到您的来信。无论如何，只要有空，我们还是会努力回信的，并期盼能再有您的消息。\r\n    希拉诚挚敬上\r\n\r\n\r\n**************\r\n*附录\r\n***************\r\n\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。						\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(1)\r\n---------------\r\n\r\n\r\n　　唐诺\r\n　　乍读这本书稿时，我一直努力在回想，查令十字街84号这家小书店究竟是长什么个模样(我坚信写书的海莲·汉芙不是胡诌的，在现实世界中必然有这么一家“坚实”存在的书店)，我一定不止一次从这家书店门口走过，甚至进去过，还取下架上的书翻阅过——《查令十字街84号》书中，通过一封1951年9月10日海莲·汉芙友人玛克辛的书店寻访后的信，我们看到它是“一间活脱从狄更斯书里头蹦出来的可爱铺子”，店门口陈列了几架书(一定是较廉价的)，店内则放眼全是直抵天花板的老橡木书架，扑鼻而来全是古书的气味，那是“混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……”，当然，还有一位五十开外年纪、以老英国腔老英国礼仪淡淡招呼你的男士(称店员好像不礼貌也不适切)。\r\n　　但这不也就是半世纪之后今天、查令十字街上一堆老书店的依然长相吗？——如此悬念，让我再次鼓起余勇、生出远志，很想再去查令十字街仔细查看一次，对一个有抽烟习性又加上轻微幽闭恐惧毛病如我者，这长达二十小时的飞行之旅，我自以为是个很大的冲动而且很英勇的企图不是吗？\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。\r\n　　至少，本书的译者一定会支持我的武断——陈建铭，就我个人的认识，正是书籍阅读世界的此道中人。一般，社会对他的粗浅身分辨识，是个优美、老英国典雅风味却内向不擅长议价的绝佳书版美术设计者，但这本《查令十字街84号》充分暴露了他的原形，他跳出来翻译了此书，而且还在没跟任何出版社联系且尚未跟国外购买版权的情况下就先译出了全书(因此，陈建铭其实正是本书的选书人)，以他对出版作业程序的理解，不可能不晓得其后只要一个环节没配合上，所有的心血当场成为白工，但安静有条理的陈建铭就可以因为查令十字街忽然疯狂起来。\r\n　　这是我熟悉、喜欢、也经常心生感激的疯子，在书籍和阅读的世界中，他们人数不多但代代有人，是这些人的持续存在，且持续进行他们一己“哈萨克人式的小小游击战”(借用赫尔岑的自况之言)，才让强大到几近无坚不摧的市场法则，始终无法放心地遂行其zhuanzhi统治，从而让书籍和阅读的世界，如汉娜·鄂兰谈本雅明时说的，总是在最边缘最异质的人身上，才得到自身最清晰的印记。\r\n　　在与不在的书街\r\n　　《查令十字街84号》这部美好的书，系以1949年至1969年长达廿年流光，往复于美国纽约和这家小书店的来往信函交织而成——住纽约的女剧作家买书，任职“马克斯与科恩书店”的经理弗兰克·德尔负责寻书寄书，原本是再乏味不过的商业往来，但很快的，书籍击败了商业，如约翰·房龙说“一个马槽击败了一个帝国”(当然，在书籍堆栈的基础之上，一开始是汉芙以她莽撞如火的白羊座人热情凿开缺口，尤其她不断寄送鸡蛋、火腿等食物包裹给彼时因战争物资短缺、仰赖配给和黑市的可怜英国人)，人的情感、心思乃至于咫尺天涯的友谊开始自由流窜漫溢开来。查令十字街那头，他们全体职员陆续加入(共六名)，然后是德尔自己的家人(妻子诺拉和两个女儿)，再来还有邻居的刺绣老太太玛丽·伯尔顿；至于纽约这边，则先后有舞台剧女演员玛克辛、友人金妮和埃德替代汉芙实地造访“她的书店”，惟遗憾且稍稍戏剧性的是，反倒汉芙本人终究没能在一切落幕之前踩上英国，实践她念念不忘的查令十字街之旅。全书结束于1969年10月德尔大女儿替代父亲的一封回信，德尔本人已于1968年底腹膜炎病逝。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(2)\r\n---------------\r\n\r\n\r\n　　一样产自英国的了不起小说家格林，在他的《哈瓦那特派员》中这么说：“人口研究报告可以印出各种统计数值、计算城市人口，借以描绘一个城市，但对城里的每个人而言，一个城市不过是几条巷道、几间房子和几个人的组合。没有了这些，一个城市如同陨落，只剩下悲凉的记忆。”——是的，1969年之后，对海莲·汉芙来说，这家书店、这道书街已不可能再一样了，如同陨落，只因为“卖这些好书给我的好心人已在数月前去世了，书店老板马克斯先生也已不在人间”，这本《查令十字街84号》于是是一本哀悼伤逝的书，纪念人心在二十年书籍时光中的一场奇遇。\r\n　　但海莲·汉芙把这一场写成书，这一切便不容易再失去一次了，甚至自此比她自身的生命有了更坚强抵御时间冲刷的力量——人类发明了文字，懂得写成并印制成书籍，我们便不再徒然无策地只受时间的摆弄宰制，我们甚至可以局部地、甚富意义地击败时间。\r\n　　书籍，确实是人类所成功拥有最好的记忆存留形式，记忆从此可置放于我们的身体之外，不随我们肉身朽坏。\r\n　　也因此，那家书店，当然更重要是用一本一本书铺起来的查令十字街便不会因这场人的奇遇戛然中止而跟着消失，事实上，它还会因多纳入海莲·汉芙的美好记忆而更添一分光晕色泽，就像它从不间断纳入所有思维者、纪念者、张望者、梦想者的书写一般，所以哀伤的汉芙仍能鼓起余勇地说：“但是，书店还是在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……”\r\n　　这是不会错的，今天，包括我个人在内，很多人都可以证实，查令十字街的确还在那儿，我是又过了十多年之后的八十年代、九十年代去的，即便84号的“马克斯与科恩书店”很遗憾如书末注释说的，没再撑下去，而成为“柯芬园唱片行”，但查令十字街的确还好好在那里。\r\n　　一道时间大河\r\n　　查令十字街，这个十字不是指十字路口，而是十字架的意思，事实上它是一道长约一公里许的蜿蜒市街，南端直抵泰晤士河，这里有最漂亮的查令十字街车站，如一个美丽的句点，往北路经国家艺廊，穿过苏活区和唐人街，旁及柯芬园，至牛津街为止，再往下走就成了托登罕路，很快就可看到著名的大英博物馆(大英博物馆一带又是另一个书店聚集处，但这里以精印的彩色大版本艺术书为主体)。\r\n　　老英国老伦敦遍地是好东西，这是老帝国长而辉煌的昔日一样样堆栈下来的，如书中汉芙说的(类似的话她说了不止一回)：“记得好多年前有个朋友曾经说：人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我：‘就在那儿！’”\r\n　　然而，和老英国其他如夕晖晚照荣光事物大大不同之处在于，查令十字街不是遗迹不是封存保护以待观光客拍照存念的古物，它源远流长，但它却是active，现役的，当下的，就在我们谈话这会儿仍孜孜勤勤劳动之中，我们可同时缅怀它并同时使用它，既是历史从来的又是此时此刻的，这样一种奇特的时间完整感受，仔细想起来，不正正好就是书籍这一人类最了不起发明成就的原来本质吗？我们之所以丧失了如此感受，可能是因为我们持续除魅的现实世界已成功一并驱除了时间，截去了过去未来，成为一种稍纵即逝却又驻留不去的所谓“永恒当下”——有生物学者告诉我们，人类而外的其他动物和时间的关系极可能只有这样，永恒的当下，记忆湮渺只留模糊的鬼影子，从而也就产生不来向前的有意义瞻望，只剩如此窄迫不容发的时间隙缝，于是很难容受得了人独有的持续思维和精致感受，只有不占时间的本能反射还能有效运作，这其实就是返祖。\r\n　　更正确地说，查令十字街的时间景观，指的不单单是它的经历、出身以及悠悠存在的岁月，而是更重要的，就算你不晓得它的历史沿革和昔日荣光，你仍可以在乍乍相见那一刻就清晰捕捉到的实时景观，由它林立的各个书店和店中各自藏书所自然构成——查令十字街的书店几乎每一家一个样，大小、陈列布置、书类书种、价格以及书店整体氛围所透出的难以言喻的鉴赏力、美学和心事。当然，书店又大体参差为一般新书书店和二手古书店的分别，拉开了时间的幅员，但其实就算卖新书的一般书店，彼此差异也是大的，各自收容着出版时日极不一致的各色书籍，呈现出极丰硕极细致的各自时间层次。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(3)\r\n---------------\r\n\r\n\r\n　　不太夸张地说，这于是成了最像时间大河的一条街，更像人类智识思维的完整化石层，你可以而且势必得一家一家地进出，行为上像进陈列室而不是卖场。\r\n　　相对来说，我们在台湾所谓的“逛书店”，便很难不是只让自我感觉良好的溢美之辞。一方面，进单一一家书店比较接近纯商业行为的“购买”，而不是带着本雅明式游手好闲意味的“逛”，一本书你在这家买不到，大概另一家也就休想；另一方面，“逛”，应该是不完全预设标的物的，你期待且预留着惊喜、发现、不期而遇的空间，但台湾既没二手书店，一般书店的书籍进退作业又积极，两三个月前出版的书，很可能和两三千年前的出土文物一样不好找。\r\n　　连书店及其图书景观都是永恒当下的，在我们台湾。\r\n　　永恒当下的灾难\r\n　　海莲·汉芙在书中说到过她看书买书的守则之一，对我们毋宁是极陌生到足以吓人一跳的，她正色告诉德尔，她绝不买一本没读过的书，那不是跟买衣服没试穿过一样冒失吗？当然我们没必要激烈如这位可敬的白羊座女士，但这其实是很有意思的话，说明旧书(广义的，不单指的珍版珍藏之书)的购买、收存和再阅读，不仅仅只是屯积居奇的讨人厌行为或附庸风雅的恶心行为而已。这根源于书籍的不易理解，不易完整掌握的恒定本质，尤其是愈好、内容愈丰硕、创见之路走得愈远的书，往往远远超过我们当下的知识准备、道德准备和情感准备，我们于是需要一段或长或短的回身空间与它相处。好书像真爱，可能一见钟情，但死生契阔与子成说，执子之手与子偕老的杳远理解和同情却总需要悠悠岁月。\r\n　　因此，从阅读的需求面来说，一本书的再阅读不仅仅只是可能，而是必要，你不能希冀自己一眼就洞穿它，而是你十五岁看，二十岁看，四十岁五十岁看，它都会因着你不同的询问、关注和困惑，开放给你不一样的东西，说真的，我努力回想，还想不出哪本我真心喜欢的书没有而且不需要再再重读的(你甚至深深记得其中片段，意思是你在记忆中持续重读)；也因此，从书籍取得的供给面来看，我们就应该聪明点给书籍多一点时间、给我们自己多一点机会，历史经验一再告诉我们，极多开创力十足且意义重大的书，我们当下的社会并没那个能力一眼就认得出来，不信的人可去翻阅大名鼎鼎的纽约时报历来书评(坊间有其结集成书的译本)，百年来，日后证明的经典著作，他们漏失掉的比他们慧眼捕捉到的何止十倍百倍，而少数捕捉到的书中又有诸如塞林格的《麦田里的守望者》或钱德勒的《大眠》被修理得一无是处(理由是脏话太多云云)。一个社会，若意图在两星期到一个月内就决定一本书的好坏去留，要求书籍打它不擅长的单败淘汰赛，这个社会不仅自大愚蠢，而且可悲的一步步向着灾难走去。\r\n　　一种只剩永恒当下的可悲灾难。\r\n　　部分远大于全体\r\n　　便是这个永恒当下的灾难启示，让我们得以在书籍暨阅读的世界中，推翻一项亘古的数学原理——这是柏拉图最爱引用的，全体永远大于部分，但我们晓得事实并不尽然，短短的一道查令十字街，的确只是我们居住世界的一个小小部分，但很多时候，我们却觉得查令十字街远比我们一整个世界还大，大太多了。\r\n　　最是在什么时候，我们会生出如此诡异的感觉呢？特别当我们满心迫切的困惑不能解之时。我们很容易在一本一本书中再再惊异到，原来我们所在的现实世界，相较于既有的书籍世界，懂得的事这么少，瞻望的视野这么窄，思维的续航能力这么差，人心又是这么封闭懒怠，诸多持续折磨我们的难题，包括公领域的和私领域的，不仅有人经历过受苦过认真思索过，甚至还把经验和睿智细腻的解答好好封存在书中。\r\n　　从形态上来看，我们眼前的世界往往只有当下这薄薄的一层，而查令十字街通过书籍所揭示的世界图像，却是无尽的时间层次叠合而成的，包括我们因失忆而遗失乃至于根本不知有过的无尽过去，以及我们无力也无意瞻望的无尽未来。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(4)\r\n---------------\r\n\r\n\r\n　　看看穆勒的《论自由》和《论代议政治》，这是足足一百五十年前就有的书，今天我们对自由社会和民主政治的建构、挫折、一再摔落的陷阱以及自以为聪明的恶意操弄，不好端端都写在书里头吗？\r\n　　看看李嘉图的《政治经济学原理》，这是两百年前的书，书中再清晰不过所揭示的经济学最基本道理和必要提醒，我们今天，尤其手握财经权力的决策者，不还在日日持续犯错吗？\r\n　　或者看看本雅明的《发达资本主义时代的抒情诗人》，这又是超过半个世纪以前的书，而今天，我们的大台北市才刚刚换好新的人行步道、才刚刚开始学习在城市走路并试图开始理解这个城市不是吗？\r\n　　还是我们要问宪法的问题(内阁制、总统制、双首长制、还有神秘的塞内加尔制)？要问民族主义和民粹主义的问题？问生态环保或仅仅只是整治一条基隆河的问题？问男女平权？问劳工和失业？问选举制度和选区规划？问媒体角色和自律他律？或更大哉问的问整体教育和社会价值暨道德危机等等问题？\r\n　　是的，如海莲·汉芙说的，书店还是在那儿。\r\n　　全世界最便宜的东西\r\n　　而查令十字街不仅比我们眼前的世界大，事实上，它做得更好——查令十字街不仅有着丰硕的时间层次，还呈现具体的空间分割；它是一道川流不息的时间之街，更是一个个书店、隔间、单一书籍所围拥成的自在小世界，让闲步其中的人柳暗花明。\r\n　　我猜，这一部分原因有历史的偶然渗入作用而成，比方说，老式的、动辄百年以上的老伦敦建筑物，厚实坚强的石墙风雨不动的制限了商业流窜的、拆毁一切夷平一切的侵略性格，因此，小书店各自盛开如繁花，即便是大型的综合性书店，内部格局也曲折回旋，每一区块往往是封闭的、隔绝的，自成洞天，毋宁更像书籍层层架起的读书阅览小房间而非卖场；而且，美国的霸权接收，让英文不随老帝国的坠落而衰败，仍是今天的“准世界语”，仍是普世书籍出版活动的总源头和荟萃之地，因此，你一旋身，才两步路便由持续挣扎的东欧世界出来，却马上误入古怪拼字，但极可能正是人类最远古家乡非洲黝暗世界，如同安博托·艾柯在《玫瑰之名》书中最(禁止)的惊心动魄一幕——第七天，威廉修士和见习僧艾森终于进入了大迷宫图书馆中一切秘密埋藏所在的非洲之末。\r\n　　一个无垠无边的智识世界，却是由一个个小洞窟构成的。\r\n　　我尤其喜欢查令十字街的一个个如此洞窟，一方面，这有可能正是人类亘古的记忆存留，是某种乡愁，像每一代小孩都有寻找洞窟打造洞窟置身洞窟的冲动，有某种安适安全之感，而读书，从阅读、思索到着迷，最根柢处，本来就是宛如置身一己洞窟的孤独活动；另一方面，我总时时想到列维—施特劳斯的话，这些自成天地般洞窟的存在，提供我们逃避的机会，逃避什么样的压迫呢？逃避一种列维—施特劳斯指称的大众化现象，意即一种愈发一致的、无趣的、再没性格可言的普世性可怖压逼(正是社会永恒当下的呈现)，而这些动人的洞窟，正像《爱丽丝漫游仙境》的树洞，你穿过它，便掉落到一个完全异质、完全始料未及的世界里去。\r\n　　于是，我遂也时时忧虑我们最终仍会失去属于我们这一代的查令十字街，如同汉芙早已失去她的查令十字街一般，我们的杞忧，一方面是现实中断续传来的不利信息(如商业的腐蚀性只是被减缓，并没真正被阻止)，更是人面对足够美好事物的很自然的神经质反应，你深知万事万物持续流变，珍爱的东西尤其不可能一直存留，如朝霞，如春花，如爱情。\r\n　　但你可以买它——当然不是整条查令十字街，而是它真正赖以存在、赖以得着意义的书籍，市街从不是有效抵御时间风蚀的形式，书籍才是，就像汉芙所说：“或许是吧，就算那儿没有(意指英国和查令十字街)，环顾我的四周(意指她从查令十字街买到的书)……我很笃定，它们已在此驻足。”\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(5)\r\n---------------\r\n\r\n\r\n　　从事出版已超过半辈子之久，我个人仍始终有个问题得不到满意的答案：我始终不真正明白人们为什么不买书？这不是全世界最便宜的一样东西吗？一个人类所曾拥有过最聪明最认真最富想像力最伟大的心灵，你不是极可能只用买一件看不上眼衣服的三千台币就可买下他奇迹一生所有吗(以一名作家，一生十本书，一本书三百元计，更何况这么买通常有折扣)？你不是用吃一顿平价午餐的支付，就可得到一个美好的洞窟、以及一个由此联通的完整世界吗？\r\n　　汉芙显然是同我一国的，她付钱买书，但自掏腰包寄食物还托朋友送丝袜，却仍觉得自己占便宜，在1952年12月12日，她说的是：“我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年；而你们送给我的礼物，却能和我朝夕相处、至死方休；我甚至还能将它遗爱人间而含笑以终。”而在1969年4月11日的最终决算，她仍得到“我亏欠它良多”的结论。\r\n　　美国当前最好的侦探小说家，同样也住纽约的劳伦斯·卜洛克也如此想，他在《麦田贼手》一书，通过一名仗义小偷之口对一名小说家(即塞林格)说：“这个人，写了这么一本书，改变了我们整整一代人，我总觉得我欠他点什么。”所以——买下它，我指的是书，好好读它，在读书时日里若省下花费，存起来找机会去一趟查令十字街，趁它还在，如果你真的成行并顺利到那儿，请代我们献上一吻，我们都亏欠它良多……\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(1)\r\n---------------\r\n\r\n\r\n　　张立宪\r\n　　“你们若恰好路经查令十字街84号，请代我献上一吻，我亏欠她良多……”\r\n　　生活中发生了一桩小小的笑话。一位朋友在英国，按照我的介绍，某一天逛伦敦的书店一条街——查令十字街，并到84号朝圣。奈何那里已经改为一家酒吧，只是在门边挂个铜牌，上书“查令十字街84号，马克斯与科恩书店的旧址，因为海莲·汉芙的书而闻名天下”。收银小姐看着她笑问：“为那本书来的吧？”她只好狠狠地点一瓶红酒，并为我买了期待已久的书《查令十字街84号》，然后寄往北京，还兴冲冲地先用数码相机将书拍了照mail过来，让我预热一下。结果，不幸的很，这本书在大英帝国的邮政系统兜了一个圈子，又回到了朋友的手上——她将收件人与寄件人的位置弄颠倒了。\r\n　　其实以我的英文修养，肯定啃不动原版书，但对于这本书，还是希望能保留一本，因为它被誉为“爱书人的《圣经》”。\r\n　　这本书讲的就是一个纽约爱书人通过书信往来在伦敦一家古旧书店(书名即是这家书店的地址)淘书并建立深厚友谊的故事。来往的书信被她汇集成此书，成为读书人的掌上明珠。\r\n　　得到这本书并非易事。好在，根据原著拍摄的同名电影已经有DVD出售，万幸的是，我买到了，并且看了。该片由美国哥伦比亚公司1986年拍就，片长100分钟，担纲主演的是演技派演员安妮·班克劳夫特和安东尼·霍普金斯，拍得真是无可挑剔。\r\n　　之所以说是“万幸”，是因为影碟被碟商译成了一个耸人听闻的动作片名字——《迷阵血影》。我敢担保，即使你看到这张影碟，最大的可能也是与其失之交臂。这个异想天开的译名惹恼了一个台湾人，他叫陈建铭，是诚品书店古书区的员工，在没与任何出版社联系出版且未跟国外购买版权的情况下，就先译出了全书。按照他的说法，这般牛劲发作，就是因为《迷阵血影》这个名字，而且影片的对白字幕也是惨不忍睹，“我翻译这本书，多少也想为它赎点儿罪罢”。\r\n　　恕我饶舌，复述一遍这个再简单不过的故事。\r\n　　穷困的女作家海莲受不了纽约昂贵庸俗的古旧书店，便按照《星期六文学评论》上的地址，给位于伦敦查令十字街84号的马克斯与科恩书店(后来被海莲派去伦敦侦察的好友形容成一家“活脱从狄更斯书里头蹦出来的可爱铺子”)写了一封信，求购一些绝版图书。这一天是1949年10月5日。\r\n　　很快，回信和她要的书就来了，那些书令海莲的书架相形见绌。双方的信任和欣喜很快达成，除了海莲有一点点麻烦，她是个连付账和找零都搞不清楚的女人，更不用说将英镑换算成美元了。马克斯与科恩书店的经理弗兰克除了满足她购书的要求外，还得给她准备英镑和美元两种发票。\r\n　　温暖的相知借助娓娓道来的书信，很快就俘获了远隔重洋的海莲和弗兰克。\r\n　　五十年代初期的英国百废待兴，物资实行配给制。海莲就从美国给书店的店员们寄来火腿、鸡蛋和香肠，让他们吃到很久没有见过的“完整而大块”的肉。而弗兰克并不是不知感恩的人，他开始在英国各地奔波，出入豪宅，为存货不多的书店添置新品，踏破铁鞋，为她寻觅难得一见的珍本。\r\n　　日子一天天地过去，书信成为他们平静流淌的生活中无时不在的旁白。\r\n　　海莲不是没想过去伦敦看看书店看看弗兰克。她终于有了自己的积蓄，而英女王的登基又使得赴英的费用打了折。眼看可以成行，但她的牙逼着她留在了纽约。她只好给弗兰克写信：“我陪着我的牙，而牙医却在度蜜月，他的结婚费用是我出的……”\r\n　　弗兰克只好为她和刚刚登基的伊丽莎白女王祝福。\r\n　　书照买，信照写。\r\n　　到了这一天，海莲的信三个月后才接到回音，她被告知：弗兰克于1968年12月22日病逝。\r\n　　海莲马上赶到查令十字街84号。走进即将被拆迁的马克斯与科恩书店时，距离她第一次给这里写信，已经过去了二十年。\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(2)\r\n---------------\r\n\r\n\r\n　　她笑着对空荡荡的书店说：“我来了，弗兰克，我终于来了。”\r\n　　通过胶片来诉说图书的故事，总显得不太解气。不过，看平静的生活围绕着他们的讨书买书谈书一幕幕展开，仿佛将惟一彩色的道具放在黑白环境里，使原本素朴的书本也显得绚丽，一如荒漠甘泉。\r\n　　事关读书的故事总是令人解颐。\r\n　　海莲对一本拉丁文版《圣经》极为不满，在给弗兰克的信中说翻译简直是想毁掉这本世界上“最美的散文”，建议拿正宗的拉丁文版对照来读，才不致暴殄天物，并出卖了她七大姑八大姨的说法加以佐证。可爱的女人，总是将自己试图保守的机密在另一种心情下泄露无遗。\r\n　　弗兰克看到纽曼的《大学论》，写信问海莲：“有兴趣买初版的吗？”同时叮嘱店员为她留下来。镜头马上从伦敦切到纽约，海莲对着空气质问：“你有初版的《大学论》，只要六美元，居然还傻傻地问我‘你要吗’？”“亲爱的弗兰克：是的，我要。我本不在乎是不是初版，可这本书的初版！……”\r\n　　等她收到这本有百年历史的初版书后，写信对他说：“我占有它有一种罪恶感，那么漂亮的封面和烫金，它理应属于某幢英国乡间的木造宅邸才对。”\r\n　　这个莽撞如火的白羊座女人颇有豪侠之风，不但体现在她为书店员工邮寄生活用品的慷慨上，甚至她的性格也凸显在信纸上：“我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天，就像米尼弗·奇维一样，余生也晚。而我也只能学他‘干咳两声，自叹一句：奈何老天作弄’，然后继续借酒浇愁。”\r\n　　“这是个堕落颓废的年代，他们居然把漂亮的旧书页撕下来当包装纸。上面描述的是一场战役的中段，但我已经看不出是哪场战役了……”海莲在信中抱怨，又该可怜的弗兰克忙活了。\r\n　　在一家豪宅，弗兰克见到了帮海莲遍寻不着的伊丽莎白一世时期的情诗集，以书店全体员工的名义寄给她。“你们相信它是在我生日那天寄达的吗？这是我拥有的第一本镶金边的书。可惜你们太客气了，将字句写在卡片上，而非扉页上。你们全都是爱书人，惟恐会减损书的价值，其实你们已经为书的主人甚至书未来的主人提升了它的价值。”海莲在回信中兴奋地絮叨。\r\n　　……\r\n　　1969年1月8日，海莲收到马克斯与科恩书店通报弗兰克的死讯，那封信的最后一句是：\r\n　　“你还要我们寻找你所订的书吗？”\r\n　　该说说海莲和弗兰克之间的事儿了。\r\n　　海莲的爱人死于二战，她终身未嫁。弗兰克则有妻子和两个女儿。一大两小三个女人会收到海莲寄自美国的尼龙袜，弗兰克的太太也会和海莲雅谑几句：“弗兰克给你的照片够难看的，但他狡辩说本人比照片帅多了，我们就让他臭美去吧。”\r\n　　一切看来都那么正常，正常到两人相识二十年却缘悭一面，正常到两人通信数百封而未涉一个“爱”字。\r\n　　但是，弗兰克死后，他的太太写信给海莲说：“不怕你见笑，有时候我还会嫉妒你。”\r\n　　马克斯与科恩书店的店员们把海莲想像成一个“年轻，成熟，时髦”的女人，海莲老实告诉他们，自己“和百老汇的乞丐一样时髦”。就是这样一个执拗邋遢的女人，将骄蛮趣致的女性一面全都呈现给弗兰克。她会为一本欺世盗名的书而冲弗兰克发飙，将满腔怨气倾泄到打字机上，然后突然收起霸道，对着空气娇媚地笑了：“弗兰克，你是惟一了解我的人。”\r\n　　独身的海莲是自由的，而弗兰克眼前连这团自由的空气也没有。他只能努力让自己正常地度过二十年的光阴，只是在某一刻，他会注意到书店中驻足的一个女子，大概就是他想像中那个女人的模样？她说她来自美国，他的眼光一下子变得热切，却又不是，他好像习惯了这种失望和等待。电视机里在转播纽约元旦嘉年华的情景，广场上人多如织，他的眼睛在搜寻什么？\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(3)\r\n---------------\r\n\r\n\r\n　　只是到了打烊的时候，书店里再没有别人，最柔软的情思才在这一刻展开，他会让自己的眼睛盯住某一处，款款道来。此刻，那个女人正躺在自己的床上，身罩破旧的睡衣，翻看着他抚摩过的书，点燃一支烟，不时发出一声声咳嗽。\r\n　　弗兰克死后，海莲来到查令十字街84号，站立的地方，正是他深情凝视的所在。\r\n　　经过了二十年岁月的打磨，他们的眼神都那么一致。\r\n　　海莲所推崇的英国玄学诗人、散文家多恩有一句话：“全体人类就是一本书。当一个人死亡，这并非有一章被从书中撕去，而是被翻译成一种更好的语言。”\r\n　　我想，当爱情以另外一种方式展现铺陈时，也并非被撕去，而是翻译成了一种更好的语言。上帝派来的那几个译者，名叫机缘，名叫责任，名叫蕴藉，名叫沉默。\r\n　　还有一位，名叫怀恋。\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n', null);
INSERT INTO `p_task` VALUES ('77d6d322-dcd5-4085-86bd-aecfcb751fc5', '测试6[卷3]', '历史', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-21 22:54:30', '2017-09-06', '1', '600', '2', '0', '1', '3', '或〔和)中世纪英语的念头叫一位要拿它做博士论文的朋友给打消了。教授们对她说:关于古盎格鲁——撤克逊，想写什么题目都行。“乍听之下似乎很棒，”我的朋友哭丧着脸告诉我，“可是惟一能找到够多古盎格鲁——萨克逊语的文章，尽是些‘烧教堂、砍人头’的玩意儿。”\r\n    她还跟我讲了一堆贝奥武甫和他的私生子希卫斯(还是卫希斯?)的故事，她说那些也没啥看头。经她冷水一浇，我的兴致也全没了。所以给我白话版的《坎特伯雷故事集)就行了。\r\n代我问候诺拉。\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1959年9月2日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    人家都很高兴听到您得到一笔奖金，而且也有工作了。对于您所挑选的任何题材，我们完全不会介意。但我必须向您报告，店里的年轻同事们坦承:若不是您在信中提及，他们浑然不知英国竟然曾经“占领’过美国。\r\n    关于您的疑问，似乎所有最顶尖的研究学者对于将乔叟的文章译成白话文都敬而远之。但是，在一九三四年由希尔改写、朗文出版社出版的《坎特伯雷故事集》，应该是目前惟一能找到的白话版了。我认为还算是不错的书，此书现已绝\r\n版(这自然不在话下)，我将为您找一本书品较好的。\r\n弗兰克谨上\r\n\r\n        一九六〇年开春头一个糟糕透顶的周日夜\r\n我快没辙啦!弗兰基—\r\n    有人送我这么一本书充圣诞礼物。这是一本“巨匠现代文库”。你可曾见过这种版本?装帧比起纽约州议会公报好不到哪儿去，重量倒是略胜一筹。一个爱附庸风雅的家伙，听说我喜欢约翰·多恩。不知打哪儿找来送我。书名是:\r\n     约翰·多恩\r\n    诗全集、文选\r\n        与\r\n    威廉·布来克\r\n       诗全集?\r\n    问号是我自己加上去的。你学问大，能不能告诉我:这两个老小子浑身上下有哪点相同，得这样子凑合在一块儿?——只因他们俩都是英国佬，也都舞文弄墨?我努力读着导论，想找出些蛛丝马迹来。导论洋洋洒洒共分四大章:头两章巨细靡遗地描述了多恩的学术生涯;第三章开头是这么写的(我真不想引述):\r\n    “话说少年布来克，在茵茵夏日草原，于树下邂逅先知以西结，而遭母亲深责痛打。”我百分之百支持他娘。我的意思是:就算没瞻仰到上帝的后背，好歹领教一下圣母的尊容也不赖呀——谁叫他好死不死见到什么鬼先知以西结?\r\n    反正我不喜欢布来克，他还真是让这本书减色不少。看来我势必又得清掉一些书啦，弗兰基，你得伸出援手。\r\n    我现在蜷瘫在安乐椅里，聆听着收音机传出恬淡闲适的古典音乐——大概是科莱利吧... ...慵懒地享受这天下太平的短暂时刻。而这玩意儿——“巨匠现代文库”就站在桌上直盯着我瞧。我心想:好吧，不妨朗读读第十五篇布道词中的那\r\n三个标准段落好了。”多恩的文章合该大声朗读，它简直就像是巴赫的赋格!\r\n    可是你晓不晓得，这个无端之举给我自己惹了什么天大\r\n的麻烦?\r\n    打开“巨匠现代文库”，翻到“布道文第十五篇”——竟被肢解成三个选段。该到第一段末尾，才一发现“耶洗别篇”被删掉了;我赶紧找来另一本多恩的《布道文选编》(罗根·皮尔史·史密斯编)，花了二十来分钟拼命找“布道文第十五篇”……搞了半天才发现:原来，照史密斯的编法，它不叫“证道文第十五篇”，而是“第126节:人必有终”。好不容易找到了，却——同样没有“耶洗别篇”。再搬出《约翰·多恩诗全集暨文选》（典范版）.依然少了那段“耶洗别篇”;转而向《牛津英语诗选》求助，再度穷花二十分钟，因为在这本书里，既不叫“布道文第十五篇”，也不叫“第126节:人必有终”……好歹“耶洗\r\n别篇”总算是找着了，于是开始正襟危坐大声朗读……读到末尾，啥，又不行了——这本书没有第二、第三段!所以咯，我若要从头到尾朗读一篇完整的布道文，得将三巨册全摊开，翻到正确的页面，然后绕着它们来同奔波!\r\n    请好心地告诉我:一本完整收录约翰·多恩布道文的书有那么难找吗?我得花多少钱才买得到?\r\n    我得去睡了。我会做一个可怕的噩梦——披着道袍的妖魔鬼怪，拎着一把把血淋淋的屠刀—上面分别标示着\r\n“段”、‘’节”、“选”、“删”等字眼、霍霍朝我追来……\r\n        祝好\r\nhh\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    l960年3月5日\r\n    海莲· 汉芙小姐\r\n    美国\r\n    纽约州，纽约市2l\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我又迟回您的信了，因为我希望能等到有好消息再向您报告。我找到一本萧伯纳与埃伦·特丽的书信集，虽然装帧不特别吸引人，但书品完好。我想这一次我还是尽快将书寄给您为妙、因为这是一本颇抢手的书，要等到下一本出现，恐\r\n怕得花一段时日。扣掉书价的二元六十五分，您的账户余额尚余五角。\r\n    关于收录完整的多恩布道文，恐怕只能在《约翰·多恩全集》中才能找到，而这套书共有四十余册，若书况稍好些的也\r\n必然所费不赀。\r\n    我们希望您后来没耗费太多心神在那本“巨匠现代文库”上，好好地过了一个愉快的圣诞节和新年。\r\n    诺拉也要祝您一切顾遂。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1960年5月8日\r\n    德·托克维尔阁下在此致意，宣告他已安然渡抵美利坚。他顾盼满志地坐谈时事，而他的臧否都卓然成理。尤其当他论及“律师治国”时，我只有额手、点头的分儿。我参加了一个民主党的小型聚会，前儿天晚上，出席的十四个人里头有十一个是律师;回到家在报纸上读到一堆名人励志故事——有为青年怀抱鸿鸽之志，力争上游，终于晋身一国之尊——史蒂文森、汉弗莱丹、肯尼迪、史塔生.、尼克松……一整票人除了汉弗莱，全是干律师的。\r\n    我附上三块钱钞票，这是一本漂亮的书，实在不能算是“二手书”，连书页都还未裁开哩。我有没向你说过，我终于找到方便好用的裁页刀了?这是一把珍珠柄的水果刀，我母亲留给我一整打这种刀，我挑了一把搁在案头的笔筒里。大概\r\n是交游不力吧，我好像没有机会请来十二位宾客。让他们围坐在餐桌前集体切水果哩。\r\n    笑口常开\r\n\r\n    海莲·汉芙 纽约州，纽约市21，东七十二大街305号\r\n    1961年2月2日\r\n弗兰克?\r\n你还在吗?\r\n    我答应自己在找到工作前不再写信给你的。\r\n    终于卖了一篇稿子给《哈泼杂志》。被这篇稿子折腾了三个星期，他们付给我两百美元稿费。现在他们再度向我约稿，要我将生平事迹写成一本书，他们将“预付”给我一千五百美元!并预估我不用半年就能写得出来，我是无所谓啦，不过房东可又要头疼了。\r\n    所以这阵子暂且不能买书了。去年十月，有人介绍我读圣西蒙公爵路易的书，不过这是一本乏善可陈的节译本。于是我火速赶往学会图书馆，因为这个图书馆不仅全馆开架，还让我爱借多少就借多少，我在那里找到货真价实的路易。现在我已经对他不可自拔。正在读的这一套是六卷版，昨晚读到第六卷一半时，一想到等我把书归还后，家里就连一本路易也没有了，这实在令我难以接受。\r\n    我现在读的是弗朗西斯·阿克赖特的译本，他的译笔甚雅。不过我放手让你挑你信得过的版本。但先别忙着寄来!找到后暂时搁着，先报价，然后再一本一本卖给我。\r\n    希望诺拉和女孩儿们都一切安好—还有你和其他所有认识我的人。\r\n海莲\r\n\r\n    伦敦中西二区 查令十宇街84号.\r\n    1961年2月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    您听到一定会开心的:我们正好有一部《圣西蒙公爵回忆录》在店里，译者正是阿克赖特。六卷全帙，装帧精良且品相完好。我们将于今天寄给您，大约一两个礼拜可望寄达您的手中。这套书的价格约合十八元七十五分，但请勿挂怀书\r\n款，您在敝店的付款记录一向良好。\r\n    同时，很高兴又有了您的消息。我们大家都很好，依然盼望您能到英国一游。\r\n    所有人诚心祝福您。\r\n    弗兰克\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1961年3月10日\r\n亲爱的弗兰基:\r\n    附上“千万不能掉了”的十元钞票，它务必平安到达你们手里。倒不是最近发了什么横财，而是路易要我不能留他一个未赎之身。他在法庭上受够了俗不可耐的赖债痞子，可不希望保持了两百七十年的清誉毁于一旦。\r\n    《哈泼杂志》指派给我的编辑昨晚来家里吃饭，和我讨论“我的生平故事”，于是想起你。我们聊到我曾为电视节目“不朽名人堂”改写兰多的《伊索与萝多彼》(我好像告诉过你了?)——兰多笔下那个天真浪漫的萝多彼由萨拉·丘吉尔\r\n饰演。这一集节目在某个周日下午播出。就在播出前两个钟头，我翻着《纽约时报》周日书评版，在第三版中有一篇针对波莉·阿德勒关于娼妓业的新书《野花莫如家花香》的书评，附了一帧照片—一尊希腊的女人头像，图说这么写着:“萝\r\n多彼——希腊最艳名远播的娼妇。”兰多本人对此倒并未详加着墨，许多学者咸称：兰多笔下的萝多彼其实就是让萨福的兄弟千金散尽的众女子的综合体。我既非学者，而且多年前死记强背下的希腊文语尾变化，我也早忘得一干二净啦。\r\n    我对金(我的编辑)谈起这段轶闻，她问我:“兰多到底是何方神圣啊?”我不厌其详地为她细说从头——正当我苦心孤诣一头热滔滔不绝时，她竟不耐烦地插嘴说:“你还真的中毒不轻唉。”\r\n    唉，这下子你该明白了吧，弗兰基，这个世界上了解我的人只剩你一个了。\r\nP.S.金小姐乃中国人是也。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年10月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    当您收到弗吉尼亚·吴尔夫的《普通读者》。上、下两册时，一定会十分惊喜——它们已在寄往您的路上。如果您还有其他想要的书。我也会倾全力尽速为您服务。\r\n    我们都很好，仍是活蹦乱跳的。我的大女儿希拉(现在已经二十四岁了)两年前突然决定改行当老师，便辞掉原来的秘书工作。跑去念大学了，她还得待在学校一年。看来，要靠儿女供养我们这些老人家颐养天年的日子还有得等哩。\r\n    所有人诚心祝福您。\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年11月9日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    许久以前您曾询问过白话版《坎特伯雷故事集》，前几天我们收购了一部，心想您或许仍想买。虽然这并非是收录所有故事的完整版本，但价格十分便宜而且似乎编写还算严谨。我今天会将它寄给您，书价是一元三十五分。如果您读了\r\n之后，还想要更完整的乔叟作品，我会尽力去找。\r\n    弗兰克谨上\r\n\r\n星欺六\r\n    行了!白话版的乔叟真是够了!简直就像兰姆的《莎士比亚故事集》嘛——适合学龄儿童阅读!\r\n    充其量就是故事嘛，我讨厌虚构故事这事儿你是知道的。倒是里头描述一个吃相优雅、食不沾手的修女的那段还算有趣，换了我就不行，还是得动刀舞叉才成。其余内容全引不起我的兴趣，我就是不喜欢故事。如果乔叟能留下日记，里\r\n头规规矩矩记述他在理查三世的皇宫里当差的经过，那才是我真正该读的东西，否则我辛辛苦苦学文言文所为何来。\r\n    最近才刚扔了一本人家送的书——作者描述奥立弗·克伦威尔时代的社会状况。天晓得这个自作聪明的家伙是不是瞎瓣。他又不是那个时代的人，哪晓得那个时代的社会状况?如果我真想了解那个时代的社会状况，大可左阅弥尔顿，右\r\n读沃尔顿。这些货真价实的作品不仅能清楚明白告诉我那个时代的社会状况，还能引领我身历其境。\r\n    君不见沃尔顿尝曰:“若非身临现场、亲眼目睹，何以让读者尽信余言?”\r\n    这段话铿锵有力，深得吾心!我坚决拥护“亲身经历”的作者、作品。\r\n    附上两元支付这本乔臾，这样我在你们的户头里应该还会有六十五分钱的余额——比起我的其他任何一个户头都多。\r\n\r\n    海莲·汉芙 纽约州 纽约市21 东七十二大街305号\r\n    1964年3月30日\r\n亲爱的弗兰克——\r\n    我手头上正在编写给孩子们读的历史教材(已经写到第四本了，惊讶吧?)，忽然想起要帮一位朋友问你:你们有没有一套萧伯纳全集——他坚称书名前冠着“定本xxx”、暗红褐色的布面装帧——希望你有印象。我附上一张清单，上面列的是他已经有的几本。如果你可以为他补齐其他几本，先别全部寄来!他会分批购买，他和我一样——甲级贫户一个。你们可以直接寄给他，地址就写在清单上头。如果你嫌我的字太潦草看不懂的话，那是“第二十二大道”。\r\n    你可有塞西莉或梅甘的消息?\r\n祝一切顺利\r\n海莲\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1964年4月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    关于您的朋友想要的《定本萧伯纳作品全集》，原出版社目前仍有新书发行。红褐色布面精装——正如您所描述的。我想全套共有三十册。旧书反而不常见到，如果您的朋友不介意购买新书，我们可以安排对他方便的方式，每个月寄给\r\n他三或四册。\r\n    我们这几年来都没有塞西莉·法尔的消息，至于梅甘·韦尔斯，她在南非没多久就待不下去了，回国后曾到书店来看大家，给了大伙儿一个发“早跟你说了偏不听”牢骚的机会。不过她后来又搬去澳洲碰运气了。几年前曾收到她寄到书店的圣诞卡，最近则又断了音讯。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1965年10月4日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    很高兴再度收到您的来信。是的，我们都还健在如昔——老态益发龙钟，工作更加忙碌，口袋却没能加倍饱满。\r\n    我们购人了一本E.M.德拉菲尔德的《村姑日记》，一九四二年的麦克米兰版，书品很好，定价两美元。今天我会将书及账单为您寄去。\r\n    我们度过了一个“青春洋溢”的夏天——今年的游客比往年更多，大批年轻人全涌向卡纳比街朝圣。我们只能老远隔着安全距离打量着他们。老实说，我还蛮喜欢披头士的，只希望他们的歌迷们不要放声尖叫。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    海莲·汉芙  纽约州，纽约市21，东七十二大街305号\r\n    1968年9月30日\r\n我们都仍健在，可不是吗……\r\n    我为儿童编写美国历史读物已经长达四五年，得将这玩意儿告一段落了——为了写这些书，我自己还买了一大堆关于美国历史的书，全都是长相丑、装订差的美国书。我想，大概没有哪个循规蹈矩的英国人会在家里收藏詹姆斯·麦迪\r\n逊的制宪会议记录。或是T.杰弗逊写给J.亚当斯的书信吧。\r\n    你当上祖父没?告诉希拉和玛莉，她们的小孩将可以免费获赠有作者签名的《少年历史读本》，这样子应该能让她们比较愿意安定下来增产报国了吧。\r\n    我挑了一个细雨霏霏的星期天介绍一位年轻朋友读《傲慢与偏见》，她现在果然已经疯狂迷恋简·奥斯汀了。她的生日就在万圣节前后，你能帮我找几本奥斯汀的书当礼物吗?如果是一整套的话，先让我知道价钱，万一太贵，我会叫她的\r\n先生分摊，我和他各送半套。\r\n    祝诺拉和你周围所有人好。\r\n海莲\r\n\r\n    伦敦中西二区 查令十事街84号\r\n    1968年10月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市X\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    是的，我们依然健在，手脚也还勉强灵光。这个夏天真是把大家忙坏了。从美国、法国、北欧和其他各国来的大批观光客，几乎把我们比较好的皮面精装书全都搜刮一空。由于书源短缺。加上书价节节攀升，恐怕很难赶在您的朋友生日前找到任何奥斯汀的书，我们会设法在圣诞节之前为您办妥这件事。\r\n    诺拉和女孩儿们都很好。希拉已经开始执起教鞭;玛莉则和一位人品不错的小伙子订了亲—不过一年半载的还结不成婚，因为双方的经济条件都不太宽裕。所以。诺拉一心想当个福福泰泰的祖母，这希望恐怕愈来愈渺茫呢。\r\n    想念您\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1969年l月8日\r\n    H.汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街Los号\r\n敬爱的小姐:\r\n    我于近日整理公文档案时，偶然发现一封您于去年九月三十日寄给德尔先生的信。我在此非常遗憾地向您报告:德尔先生甫于上个礼拜天(十二月二十二日)去世了。丧礼则已在上周三(元月一日)举行。\r\n    德尔先生于十二月十五日因罹患急性盲肠炎被紧急送医，虽然立即施行手术，但他仍不幸因病情扩散，导致腹膜炎并发而于七日后不治。\r\n    德尔先生在本书店服务已超过四十年，加上马克斯先生也刚辞世未久，科恩先生对于这个不幸的事件自然万分悲坳。\r\n    您是否仍须本店为您寻找简·奥斯汀的书?\r\n         \r\n马克斯与科恩书店\r\n              秘书 琼·托德敬上\r\n\r\n(未署明日期，邮戳日期为1969年1月9日。无寄信地址〕\r\n\r\n亲爱的海莲:\r\n    感谢您寄来的慰问信，我完全不认为那冒犯了我。我真希望您在弗兰克在世时能够与他见面，并亲自结识他本人。我原先只知道他是一个处事严谨同时也很幽默的人;现在还了解了他在待人处事上更是一位谦冲的君子。我收到许许多多来\r\n自各地的信，都异口同声地赞杨他对古书业的贡献;许多人还说他是如何饱富学识而又不吝予与其他人分享……如果您想要看这些信，我可以将它们寄给您。\r\n    不瞒您说，我过去一直对您心存妒忌，因为弗兰克生前如此爱读您的来信，而你们俩似乎有许多共通点;我也羡慕您能写出那么好的信。弗兰克和我却是两个极端不同的人，他总温和和有耐性;而因为我的爱尔兰出身，我的脾气总是又倔又拗。生命就是选么爱捉弄人，他从前总是试图教导我书中的知识……我现在好想念他。\r\n    孩子们都很懂事、我为此深感欣慰。像我这样要一辈子孤寂以终的人想必大有人在吧。\r\n    希望您能原谅我的字迹潦草。\r\n                 祝福您\r\n                 诺拉\r\n    我盼望有一天您还是能来造访我们，两个孩子都很想见见您。\r\n    1969年4月11日\r\n\r\n亲爱的凯瑟琳——\r\n    我正在整理我的书架，现在抽空蹲在书堆中写信给你，祝你们一路顺风。我希望你和布莱恩在伦敦能玩得尽兴。布莱恩在电话中对我说:“如果你手头宽裕些就好了，这样子你就可以和我们一道去了。”我一听他这么说，眼泪差点儿要夺\r\n眶而出。\r\n    大概因为我长久以来就渴望能踏上那片土地……我曾经只为了瞧伦敦的街景而看了许多英国电影。记得好多年前有个朋友曾经说:人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我:“就在那儿!”\r\n    或许是吧。就算那儿没有，环顾我的四周……我很笃定:它们已在此驻足。\r\n    卖这些好书给我的那个好心人已在数月前去世了，书店老板马克斯先生也已不在人间。但是，书店还在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……\r\n海莲\r\n\r\n尾声1969年10月\r\n\r\n    伦敦北十一区 温顿道\r\n    1969年10月\r\n亲爱的海莲:\r\n    这是德尔家族第二号通讯员首次发言!\r\n    首先，我要对我们长时间的静默向您致歉。请相信我，其实我们心中一直惦记着您。只是不知如何将这样的意念用文辞表达。直到今天我们又收到您的来信，我们都感到万分惭愧，并决定应该立刻动笔回信给您。\r\n    我们很高兴得知您的出版计划，也同意并很愿意提供这些信件供您作为出书之用。\r\n    我们现在搬到可爱的新家，常常会想着:如果父亲依然健在，一定也会喜欢这儿。\r\n    再多的悲坳亦无济于事。虽然父亲生前从未拥有财富、权势，但他始终是一个快乐自得又具有丰富内涵的人，我们应该以拥有一位这样的亲人而深感欣慰。\r\n    也许只是为了冲淡愁思，我们都尽量让自己忙碌着。玛莉白天在大学图书馆辛勤工作，晚上则和朋友开车出游散心，深夜方归;我除了正常的教职外，还兼修一个学位;妈妈则整天忙上忙下，一刻也不教自己闲下来!所以，恐怕大家都无法好好地回信——不过，当然我们会很高兴能继续收到您的来信。无论如何，只要有空，我们还是会努力回信的，并期盼能再有您的消息。\r\n    希拉诚挚敬上\r\n\r\n\r\n**************\r\n*附录\r\n***************\r\n\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。						\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(1)\r\n---------------\r\n\r\n\r\n　　唐诺\r\n　　乍读这本书稿时，我一直努力在回想，查令十字街84号这家小书店究竟是长什么个模样(我坚信写书的海莲·汉芙不是胡诌的，在现实世界中必然有这么一家“坚实”存在的书店)，我一定不止一次从这家书店门口走过，甚至进去过，还取下架上的书翻阅过——《查令十字街84号》书中，通过一封1951年9月10日海莲·汉芙友人玛克辛的书店寻访后的信，我们看到它是“一间活脱从狄更斯书里头蹦出来的可爱铺子”，店门口陈列了几架书(一定是较廉价的)，店内则放眼全是直抵天花板的老橡木书架，扑鼻而来全是古书的气味，那是“混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……”，当然，还有一位五十开外年纪、以老英国腔老英国礼仪淡淡招呼你的男士(称店员好像不礼貌也不适切)。\r\n　　但这不也就是半世纪之后今天、查令十字街上一堆老书店的依然长相吗？——如此悬念，让我再次鼓起余勇、生出远志，很想再去查令十字街仔细查看一次，对一个有抽烟习性又加上轻微幽闭恐惧毛病如我者，这长达二十小时的飞行之旅，我自以为是个很大的冲动而且很英勇的企图不是吗？\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。\r\n　　至少，本书的译者一定会支持我的武断——陈建铭，就我个人的认识，正是书籍阅读世界的此道中人。一般，社会对他的粗浅身分辨识，是个优美、老英国典雅风味却内向不擅长议价的绝佳书版美术设计者，但这本《查令十字街84号》充分暴露了他的原形，他跳出来翻译了此书，而且还在没跟任何出版社联系且尚未跟国外购买版权的情况下就先译出了全书(因此，陈建铭其实正是本书的选书人)，以他对出版作业程序的理解，不可能不晓得其后只要一个环节没配合上，所有的心血当场成为白工，但安静有条理的陈建铭就可以因为查令十字街忽然疯狂起来。\r\n　　这是我熟悉、喜欢、也经常心生感激的疯子，在书籍和阅读的世界中，他们人数不多但代代有人，是这些人的持续存在，且持续进行他们一己“哈萨克人式的小小游击战”(借用赫尔岑的自况之言)，才让强大到几近无坚不摧的市场法则，始终无法放心地遂行其zhuanzhi统治，从而让书籍和阅读的世界，如汉娜·鄂兰谈本雅明时说的，总是在最边缘最异质的人身上，才得到自身最清晰的印记。\r\n　　在与不在的书街\r\n　　《查令十字街84号》这部美好的书，系以1949年至1969年长达廿年流光，往复于美国纽约和这家小书店的来往信函交织而成——住纽约的女剧作家买书，任职“马克斯与科恩书店”的经理弗兰克·德尔负责寻书寄书，原本是再乏味不过的商业往来，但很快的，书籍击败了商业，如约翰·房龙说“一个马槽击败了一个帝国”(当然，在书籍堆栈的基础之上，一开始是汉芙以她莽撞如火的白羊座人热情凿开缺口，尤其她不断寄送鸡蛋、火腿等食物包裹给彼时因战争物资短缺、仰赖配给和黑市的可怜英国人)，人的情感、心思乃至于咫尺天涯的友谊开始自由流窜漫溢开来。查令十字街那头，他们全体职员陆续加入(共六名)，然后是德尔自己的家人(妻子诺拉和两个女儿)，再来还有邻居的刺绣老太太玛丽·伯尔顿；至于纽约这边，则先后有舞台剧女演员玛克辛、友人金妮和埃德替代汉芙实地造访“她的书店”，惟遗憾且稍稍戏剧性的是，反倒汉芙本人终究没能在一切落幕之前踩上英国，实践她念念不忘的查令十字街之旅。全书结束于1969年10月德尔大女儿替代父亲的一封回信，德尔本人已于1968年底腹膜炎病逝。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(2)\r\n---------------\r\n\r\n\r\n　　一样产自英国的了不起小说家格林，在他的《哈瓦那特派员》中这么说：“人口研究报告可以印出各种统计数值、计算城市人口，借以描绘一个城市，但对城里的每个人而言，一个城市不过是几条巷道、几间房子和几个人的组合。没有了这些，一个城市如同陨落，只剩下悲凉的记忆。”——是的，1969年之后，对海莲·汉芙来说，这家书店、这道书街已不可能再一样了，如同陨落，只因为“卖这些好书给我的好心人已在数月前去世了，书店老板马克斯先生也已不在人间”，这本《查令十字街84号》于是是一本哀悼伤逝的书，纪念人心在二十年书籍时光中的一场奇遇。\r\n　　但海莲·汉芙把这一场写成书，这一切便不容易再失去一次了，甚至自此比她自身的生命有了更坚强抵御时间冲刷的力量——人类发明了文字，懂得写成并印制成书籍，我们便不再徒然无策地只受时间的摆弄宰制，我们甚至可以局部地、甚富意义地击败时间。\r\n　　书籍，确实是人类所成功拥有最好的记忆存留形式，记忆从此可置放于我们的身体之外，不随我们肉身朽坏。\r\n　　也因此，那家书店，当然更重要是用一本一本书铺起来的查令十字街便不会因这场人的奇遇戛然中止而跟着消失，事实上，它还会因多纳入海莲·汉芙的美好记忆而更添一分光晕色泽，就像它从不间断纳入所有思维者、纪念者、张望者、梦想者的书写一般，所以哀伤的汉芙仍能鼓起余勇地说：“但是，书店还是在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……”\r\n　　这是不会错的，今天，包括我个人在内，很多人都可以证实，查令十字街的确还在那儿，我是又过了十多年之后的八十年代、九十年代去的，即便84号的“马克斯与科恩书店”很遗憾如书末注释说的，没再撑下去，而成为“柯芬园唱片行”，但查令十字街的确还好好在那里。\r\n　　一道时间大河\r\n　　查令十字街，这个十字不是指十字路口，而是十字架的意思，事实上它是一道长约一公里许的蜿蜒市街，南端直抵泰晤士河，这里有最漂亮的查令十字街车站，如一个美丽的句点，往北路经国家艺廊，穿过苏活区和唐人街，旁及柯芬园，至牛津街为止，再往下走就成了托登罕路，很快就可看到著名的大英博物馆(大英博物馆一带又是另一个书店聚集处，但这里以精印的彩色大版本艺术书为主体)。\r\n　　老英国老伦敦遍地是好东西，这是老帝国长而辉煌的昔日一样样堆栈下来的，如书中汉芙说的(类似的话她说了不止一回)：“记得好多年前有个朋友曾经说：人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我：‘就在那儿！’”\r\n　　然而，和老英国其他如夕晖晚照荣光事物大大不同之处在于，查令十字街不是遗迹不是封存保护以待观光客拍照存念的古物，它源远流长，但它却是active，现役的，当下的，就在我们谈话这会儿仍孜孜勤勤劳动之中，我们可同时缅怀它并同时使用它，既是历史从来的又是此时此刻的，这样一种奇特的时间完整感受，仔细想起来，不正正好就是书籍这一人类最了不起发明成就的原来本质吗？我们之所以丧失了如此感受，可能是因为我们持续除魅的现实世界已成功一并驱除了时间，截去了过去未来，成为一种稍纵即逝却又驻留不去的所谓“永恒当下”——有生物学者告诉我们，人类而外的其他动物和时间的关系极可能只有这样，永恒的当下，记忆湮渺只留模糊的鬼影子，从而也就产生不来向前的有意义瞻望，只剩如此窄迫不容发的时间隙缝，于是很难容受得了人独有的持续思维和精致感受，只有不占时间的本能反射还能有效运作，这其实就是返祖。\r\n　　更正确地说，查令十字街的时间景观，指的不单单是它的经历、出身以及悠悠存在的岁月，而是更重要的，就算你不晓得它的历史沿革和昔日荣光，你仍可以在乍乍相见那一刻就清晰捕捉到的实时景观，由它林立的各个书店和店中各自藏书所自然构成——查令十字街的书店几乎每一家一个样，大小、陈列布置、书类书种、价格以及书店整体氛围所透出的难以言喻的鉴赏力、美学和心事。当然，书店又大体参差为一般新书书店和二手古书店的分别，拉开了时间的幅员，但其实就算卖新书的一般书店，彼此差异也是大的，各自收容着出版时日极不一致的各色书籍，呈现出极丰硕极细致的各自时间层次。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(3)\r\n---------------\r\n\r\n\r\n　　不太夸张地说，这于是成了最像时间大河的一条街，更像人类智识思维的完整化石层，你可以而且势必得一家一家地进出，行为上像进陈列室而不是卖场。\r\n　　相对来说，我们在台湾所谓的“逛书店”，便很难不是只让自我感觉良好的溢美之辞。一方面，进单一一家书店比较接近纯商业行为的“购买”，而不是带着本雅明式游手好闲意味的“逛”，一本书你在这家买不到，大概另一家也就休想；另一方面，“逛”，应该是不完全预设标的物的，你期待且预留着惊喜、发现、不期而遇的空间，但台湾既没二手书店，一般书店的书籍进退作业又积极，两三个月前出版的书，很可能和两三千年前的出土文物一样不好找。\r\n　　连书店及其图书景观都是永恒当下的，在我们台湾。\r\n　　永恒当下的灾难\r\n　　海莲·汉芙在书中说到过她看书买书的守则之一，对我们毋宁是极陌生到足以吓人一跳的，她正色告诉德尔，她绝不买一本没读过的书，那不是跟买衣服没试穿过一样冒失吗？当然我们没必要激烈如这位可敬的白羊座女士，但这其实是很有意思的话，说明旧书(广义的，不单指的珍版珍藏之书)的购买、收存和再阅读，不仅仅只是屯积居奇的讨人厌行为或附庸风雅的恶心行为而已。这根源于书籍的不易理解，不易完整掌握的恒定本质，尤其是愈好、内容愈丰硕、创见之路走得愈远的书，往往远远超过我们当下的知识准备、道德准备和情感准备，我们于是需要一段或长或短的回身空间与它相处。好书像真爱，可能一见钟情，但死生契阔与子成说，执子之手与子偕老的杳远理解和同情却总需要悠悠岁月。\r\n　　因此，从阅读的需求面来说，一本书的再阅读不仅仅只是可能，而是必要，你不能希冀自己一眼就洞穿它，而是你十五岁看，二十岁看，四十岁五十岁看，它都会因着你不同的询问、关注和困惑，开放给你不一样的东西，说真的，我努力回想，还想不出哪本我真心喜欢的书没有而且不需要再再重读的(你甚至深深记得其中片段，意思是你在记忆中持续重读)；也因此，从书籍取得的供给面来看，我们就应该聪明点给书籍多一点时间、给我们自己多一点机会，历史经验一再告诉我们，极多开创力十足且意义重大的书，我们当下的社会并没那个能力一眼就认得出来，不信的人可去翻阅大名鼎鼎的纽约时报历来书评(坊间有其结集成书的译本)，百年来，日后证明的经典著作，他们漏失掉的比他们慧眼捕捉到的何止十倍百倍，而少数捕捉到的书中又有诸如塞林格的《麦田里的守望者》或钱德勒的《大眠》被修理得一无是处(理由是脏话太多云云)。一个社会，若意图在两星期到一个月内就决定一本书的好坏去留，要求书籍打它不擅长的单败淘汰赛，这个社会不仅自大愚蠢，而且可悲的一步步向着灾难走去。\r\n　　一种只剩永恒当下的可悲灾难。\r\n　　部分远大于全体\r\n　　便是这个永恒当下的灾难启示，让我们得以在书籍暨阅读的世界中，推翻一项亘古的数学原理——这是柏拉图最爱引用的，全体永远大于部分，但我们晓得事实并不尽然，短短的一道查令十字街，的确只是我们居住世界的一个小小部分，但很多时候，我们却觉得查令十字街远比我们一整个世界还大，大太多了。\r\n　　最是在什么时候，我们会生出如此诡异的感觉呢？特别当我们满心迫切的困惑不能解之时。我们很容易在一本一本书中再再惊异到，原来我们所在的现实世界，相较于既有的书籍世界，懂得的事这么少，瞻望的视野这么窄，思维的续航能力这么差，人心又是这么封闭懒怠，诸多持续折磨我们的难题，包括公领域的和私领域的，不仅有人经历过受苦过认真思索过，甚至还把经验和睿智细腻的解答好好封存在书中。\r\n　　从形态上来看，我们眼前的世界往往只有当下这薄薄的一层，而查令十字街通过书籍所揭示的世界图像，却是无尽的时间层次叠合而成的，包括我们因失忆而遗失乃至于根本不知有过的无尽过去，以及我们无力也无意瞻望的无尽未来。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(4)\r\n---------------\r\n\r\n\r\n　　看看穆勒的《论自由》和《论代议政治》，这是足足一百五十年前就有的书，今天我们对自由社会和民主政治的建构、挫折、一再摔落的陷阱以及自以为聪明的恶意操弄，不好端端都写在书里头吗？\r\n　　看看李嘉图的《政治经济学原理》，这是两百年前的书，书中再清晰不过所揭示的经济学最基本道理和必要提醒，我们今天，尤其手握财经权力的决策者，不还在日日持续犯错吗？\r\n　　或者看看本雅明的《发达资本主义时代的抒情诗人》，这又是超过半个世纪以前的书，而今天，我们的大台北市才刚刚换好新的人行步道、才刚刚开始学习在城市走路并试图开始理解这个城市不是吗？\r\n　　还是我们要问宪法的问题(内阁制、总统制、双首长制、还有神秘的塞内加尔制)？要问民族主义和民粹主义的问题？问生态环保或仅仅只是整治一条基隆河的问题？问男女平权？问劳工和失业？问选举制度和选区规划？问媒体角色和自律他律？或更大哉问的问整体教育和社会价值暨道德危机等等问题？\r\n　　是的，如海莲·汉芙说的，书店还是在那儿。\r\n　　全世界最便宜的东西\r\n　　而查令十字街不仅比我们眼前的世界大，事实上，它做得更好——查令十字街不仅有着丰硕的时间层次，还呈现具体的空间分割；它是一道川流不息的时间之街，更是一个个书店、隔间、单一书籍所围拥成的自在小世界，让闲步其中的人柳暗花明。\r\n　　我猜，这一部分原因有历史的偶然渗入作用而成，比方说，老式的、动辄百年以上的老伦敦建筑物，厚实坚强的石墙风雨不动的制限了商业流窜的、拆毁一切夷平一切的侵略性格，因此，小书店各自盛开如繁花，即便是大型的综合性书店，内部格局也曲折回旋，每一区块往往是封闭的、隔绝的，自成洞天，毋宁更像书籍层层架起的读书阅览小房间而非卖场；而且，美国的霸权接收，让英文不随老帝国的坠落而衰败，仍是今天的“准世界语”，仍是普世书籍出版活动的总源头和荟萃之地，因此，你一旋身，才两步路便由持续挣扎的东欧世界出来，却马上误入古怪拼字，但极可能正是人类最远古家乡非洲黝暗世界，如同安博托·艾柯在《玫瑰之名》书中最(禁止)的惊心动魄一幕——第七天，威廉修士和见习僧艾森终于进入了大迷宫图书馆中一切秘密埋藏所在的非洲之末。\r\n　　一个无垠无边的智识世界，却是由一个个小洞窟构成的。\r\n　　我尤其喜欢查令十字街的一个个如此洞窟，一方面，这有可能正是人类亘古的记忆存留，是某种乡愁，像每一代小孩都有寻找洞窟打造洞窟置身洞窟的冲动，有某种安适安全之感，而读书，从阅读、思索到着迷，最根柢处，本来就是宛如置身一己洞窟的孤独活动；另一方面，我总时时想到列维—施特劳斯的话，这些自成天地般洞窟的存在，提供我们逃避的机会，逃避什么样的压迫呢？逃避一种列维—施特劳斯指称的大众化现象，意即一种愈发一致的、无趣的、再没性格可言的普世性可怖压逼(正是社会永恒当下的呈现)，而这些动人的洞窟，正像《爱丽丝漫游仙境》的树洞，你穿过它，便掉落到一个完全异质、完全始料未及的世界里去。\r\n　　于是，我遂也时时忧虑我们最终仍会失去属于我们这一代的查令十字街，如同汉芙早已失去她的查令十字街一般，我们的杞忧，一方面是现实中断续传来的不利信息(如商业的腐蚀性只是被减缓，并没真正被阻止)，更是人面对足够美好事物的很自然的神经质反应，你深知万事万物持续流变，珍爱的东西尤其不可能一直存留，如朝霞，如春花，如爱情。\r\n　　但你可以买它——当然不是整条查令十字街，而是它真正赖以存在、赖以得着意义的书籍，市街从不是有效抵御时间风蚀的形式，书籍才是，就像汉芙所说：“或许是吧，就算那儿没有(意指英国和查令十字街)，环顾我的四周(意指她从查令十字街买到的书)……我很笃定，它们已在此驻足。”\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(5)\r\n---------------\r\n\r\n\r\n　　从事出版已超过半辈子之久，我个人仍始终有个问题得不到满意的答案：我始终不真正明白人们为什么不买书？这不是全世界最便宜的一样东西吗？一个人类所曾拥有过最聪明最认真最富想像力最伟大的心灵，你不是极可能只用买一件看不上眼衣服的三千台币就可买下他奇迹一生所有吗(以一名作家，一生十本书，一本书三百元计，更何况这么买通常有折扣)？你不是用吃一顿平价午餐的支付，就可得到一个美好的洞窟、以及一个由此联通的完整世界吗？\r\n　　汉芙显然是同我一国的，她付钱买书，但自掏腰包寄食物还托朋友送丝袜，却仍觉得自己占便宜，在1952年12月12日，她说的是：“我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年；而你们送给我的礼物，却能和我朝夕相处、至死方休；我甚至还能将它遗爱人间而含笑以终。”而在1969年4月11日的最终决算，她仍得到“我亏欠它良多”的结论。\r\n　　美国当前最好的侦探小说家，同样也住纽约的劳伦斯·卜洛克也如此想，他在《麦田贼手》一书，通过一名仗义小偷之口对一名小说家(即塞林格)说：“这个人，写了这么一本书，改变了我们整整一代人，我总觉得我欠他点什么。”所以——买下它，我指的是书，好好读它，在读书时日里若省下花费，存起来找机会去一趟查令十字街，趁它还在，如果你真的成行并顺利到那儿，请代我们献上一吻，我们都亏欠它良多……\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(1)\r\n---------------\r\n\r\n\r\n　　张立宪\r\n　　“你们若恰好路经查令十字街84号，请代我献上一吻，我亏欠她良多……”\r\n　　生活中发生了一桩小小的笑话。一位朋友在英国，按照我的介绍，某一天逛伦敦的书店一条街——查令十字街，并到84号朝圣。奈何那里已经改为一家酒吧，只是在门边挂个铜牌，上书“查令十字街84号，马克斯与科恩书店的旧址，因为海莲·汉芙的书而闻名天下”。收银小姐看着她笑问：“为那本书来的吧？”她只好狠狠地点一瓶红酒，并为我买了期待已久的书《查令十字街84号》，然后寄往北京，还兴冲冲地先用数码相机将书拍了照mail过来，让我预热一下。结果，不幸的很，这本书在大英帝国的邮政系统兜了一个圈子，又回到了朋友的手上——她将收件人与寄件人的位置弄颠倒了。\r\n　　其实以我的英文修养，肯定啃不动原版书，但对于这本书，还是希望能保留一本，因为它被誉为“爱书人的《圣经》”。\r\n　　这本书讲的就是一个纽约爱书人通过书信往来在伦敦一家古旧书店(书名即是这家书店的地址)淘书并建立深厚友谊的故事。来往的书信被她汇集成此书，成为读书人的掌上明珠。\r\n　　得到这本书并非易事。好在，根据原著拍摄的同名电影已经有DVD出售，万幸的是，我买到了，并且看了。该片由美国哥伦比亚公司1986年拍就，片长100分钟，担纲主演的是演技派演员安妮·班克劳夫特和安东尼·霍普金斯，拍得真是无可挑剔。\r\n　　之所以说是“万幸”，是因为影碟被碟商译成了一个耸人听闻的动作片名字——《迷阵血影》。我敢担保，即使你看到这张影碟，最大的可能也是与其失之交臂。这个异想天开的译名惹恼了一个台湾人，他叫陈建铭，是诚品书店古书区的员工，在没与任何出版社联系出版且未跟国外购买版权的情况下，就先译出了全书。按照他的说法，这般牛劲发作，就是因为《迷阵血影》这个名字，而且影片的对白字幕也是惨不忍睹，“我翻译这本书，多少也想为它赎点儿罪罢”。\r\n　　恕我饶舌，复述一遍这个再简单不过的故事。\r\n　　穷困的女作家海莲受不了纽约昂贵庸俗的古旧书店，便按照《星期六文学评论》上的地址，给位于伦敦查令十字街84号的马克斯与科恩书店(后来被海莲派去伦敦侦察的好友形容成一家“活脱从狄更斯书里头蹦出来的可爱铺子”)写了一封信，求购一些绝版图书。这一天是1949年10月5日。\r\n　　很快，回信和她要的书就来了，那些书令海莲的书架相形见绌。双方的信任和欣喜很快达成，除了海莲有一点点麻烦，她是个连付账和找零都搞不清楚的女人，更不用说将英镑换算成美元了。马克斯与科恩书店的经理弗兰克除了满足她购书的要求外，还得给她准备英镑和美元两种发票。\r\n　　温暖的相知借助娓娓道来的书信，很快就俘获了远隔重洋的海莲和弗兰克。\r\n　　五十年代初期的英国百废待兴，物资实行配给制。海莲就从美国给书店的店员们寄来火腿、鸡蛋和香肠，让他们吃到很久没有见过的“完整而大块”的肉。而弗兰克并不是不知感恩的人，他开始在英国各地奔波，出入豪宅，为存货不多的书店添置新品，踏破铁鞋，为她寻觅难得一见的珍本。\r\n　　日子一天天地过去，书信成为他们平静流淌的生活中无时不在的旁白。\r\n　　海莲不是没想过去伦敦看看书店看看弗兰克。她终于有了自己的积蓄，而英女王的登基又使得赴英的费用打了折。眼看可以成行，但她的牙逼着她留在了纽约。她只好给弗兰克写信：“我陪着我的牙，而牙医却在度蜜月，他的结婚费用是我出的……”\r\n　　弗兰克只好为她和刚刚登基的伊丽莎白女王祝福。\r\n　　书照买，信照写。\r\n　　到了这一天，海莲的信三个月后才接到回音，她被告知：弗兰克于1968年12月22日病逝。\r\n　　海莲马上赶到查令十字街84号。走进即将被拆迁的马克斯与科恩书店时，距离她第一次给这里写信，已经过去了二十年。\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(2)\r\n---------------\r\n\r\n\r\n　　她笑着对空荡荡的书店说：“我来了，弗兰克，我终于来了。”\r\n　　通过胶片来诉说图书的故事，总显得不太解气。不过，看平静的生活围绕着他们的讨书买书谈书一幕幕展开，仿佛将惟一彩色的道具放在黑白环境里，使原本素朴的书本也显得绚丽，一如荒漠甘泉。\r\n　　事关读书的故事总是令人解颐。\r\n　　海莲对一本拉丁文版《圣经》极为不满，在给弗兰克的信中说翻译简直是想毁掉这本世界上“最美的散文”，建议拿正宗的拉丁文版对照来读，才不致暴殄天物，并出卖了她七大姑八大姨的说法加以佐证。可爱的女人，总是将自己试图保守的机密在另一种心情下泄露无遗。\r\n　　弗兰克看到纽曼的《大学论》，写信问海莲：“有兴趣买初版的吗？”同时叮嘱店员为她留下来。镜头马上从伦敦切到纽约，海莲对着空气质问：“你有初版的《大学论》，只要六美元，居然还傻傻地问我‘你要吗’？”“亲爱的弗兰克：是的，我要。我本不在乎是不是初版，可这本书的初版！……”\r\n　　等她收到这本有百年历史的初版书后，写信对他说：“我占有它有一种罪恶感，那么漂亮的封面和烫金，它理应属于某幢英国乡间的木造宅邸才对。”\r\n　　这个莽撞如火的白羊座女人颇有豪侠之风，不但体现在她为书店员工邮寄生活用品的慷慨上，甚至她的性格也凸显在信纸上：“我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天，就像米尼弗·奇维一样，余生也晚。而我也只能学他‘干咳两声，自叹一句：奈何老天作弄’，然后继续借酒浇愁。”\r\n　　“这是个堕落颓废的年代，他们居然把漂亮的旧书页撕下来当包装纸。上面描述的是一场战役的中段，但我已经看不出是哪场战役了……”海莲在信中抱怨，又该可怜的弗兰克忙活了。\r\n　　在一家豪宅，弗兰克见到了帮海莲遍寻不着的伊丽莎白一世时期的情诗集，以书店全体员工的名义寄给她。“你们相信它是在我生日那天寄达的吗？这是我拥有的第一本镶金边的书。可惜你们太客气了，将字句写在卡片上，而非扉页上。你们全都是爱书人，惟恐会减损书的价值，其实你们已经为书的主人甚至书未来的主人提升了它的价值。”海莲在回信中兴奋地絮叨。\r\n　　……\r\n　　1969年1月8日，海莲收到马克斯与科恩书店通报弗兰克的死讯，那封信的最后一句是：\r\n　　“你还要我们寻找你所订的书吗？”\r\n　　该说说海莲和弗兰克之间的事儿了。\r\n　　海莲的爱人死于二战，她终身未嫁。弗兰克则有妻子和两个女儿。一大两小三个女人会收到海莲寄自美国的尼龙袜，弗兰克的太太也会和海莲雅谑几句：“弗兰克给你的照片够难看的，但他狡辩说本人比照片帅多了，我们就让他臭美去吧。”\r\n　　一切看来都那么正常，正常到两人相识二十年却缘悭一面，正常到两人通信数百封而未涉一个“爱”字。\r\n　　但是，弗兰克死后，他的太太写信给海莲说：“不怕你见笑，有时候我还会嫉妒你。”\r\n　　马克斯与科恩书店的店员们把海莲想像成一个“年轻，成熟，时髦”的女人，海莲老实告诉他们，自己“和百老汇的乞丐一样时髦”。就是这样一个执拗邋遢的女人，将骄蛮趣致的女性一面全都呈现给弗兰克。她会为一本欺世盗名的书而冲弗兰克发飙，将满腔怨气倾泄到打字机上，然后突然收起霸道，对着空气娇媚地笑了：“弗兰克，你是惟一了解我的人。”\r\n　　独身的海莲是自由的，而弗兰克眼前连这团自由的空气也没有。他只能努力让自己正常地度过二十年的光阴，只是在某一刻，他会注意到书店中驻足的一个女子，大概就是他想像中那个女人的模样？她说她来自美国，他的眼光一下子变得热切，却又不是，他好像习惯了这种失望和等待。电视机里在转播纽约元旦嘉年华的情景，广场上人多如织，他的眼睛在搜寻什么？\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(3)\r\n---------------\r\n\r\n\r\n　　只是到了打烊的时候，书店里再没有别人，最柔软的情思才在这一刻展开，他会让自己的眼睛盯住某一处，款款道来。此刻，那个女人正躺在自己的床上，身罩破旧的睡衣，翻看着他抚摩过的书，点燃一支烟，不时发出一声声咳嗽。\r\n　　弗兰克死后，海莲来到查令十字街84号，站立的地方，正是他深情凝视的所在。\r\n　　经过了二十年岁月的打磨，他们的眼神都那么一致。\r\n　　海莲所推崇的英国玄学诗人、散文家多恩有一句话：“全体人类就是一本书。当一个人死亡，这并非有一章被从书中撕去，而是被翻译成一种更好的语言。”\r\n　　我想，当爱情以另外一种方式展现铺陈时，也并非被撕去，而是翻译成了一种更好的语言。上帝派来的那几个译者，名叫机缘，名叫责任，名叫蕴藉，名叫沉默。\r\n　　还有一位，名叫怀恋。\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n----------------------------------------------------------------', 'a38f41ef-288e-4081-802f-c7b6689e30e8');
INSERT INTO `p_task` VALUES ('78dc91f4-0ed4-451a-941e-f795dd08ae00', '测试8[卷14]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '14', '　　伦敦\r\n　　1951年9月10日\r\n　　甜心儿：\r\n　　这是一间活脱从狄更斯书里头蹦出来的可爱铺子，如果让你见到了，不爱死了才怪。\r\n　　店门口陈列了几架书，开门进去前，我先站在外头假装随意翻阅几本书，好让自己看起来像是若无其事地逛书店。一走进店内，喧嚣全被关在门外。一阵古书的陈旧气味扑鼻而来。我实在不知道该怎么形容：那是一种混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……店内左手边有张书桌，坐着一位年约五十、长着一只贺加斯式鼻子的男士。他站起身来，操着北方口音对我说：“日安。”我回答说我只是随意逛逛，而他则有礼地说：“请。”\r\n　　极目所见全是书架——高耸直抵到天花板的深色的古老书架，橡木架面经过漫长岁月的洗礼，虽已褪色仍径放光芒。接着是摆放画片的专区——应该说：一张叠放着许多画片的大桌台。上头有克鲁克香克、拉克姆矠、斯派和许许多多我叫不出名字的英国插画家的美丽画作；另一边还放着几叠迷人的古旧画刊。\r\n　　我在店内待了约莫半个钟头光景，期待着你的弗兰克或是哪个女孩儿翩翩现身。不过，因为我到达时已过了一点钟，我猜他们全都外出用餐去了，而我又不能待太久。\r\n　　就是这样咯！新戏的预告并没有造成万人空巷，不过据说对方人蛮好的，排给我们几个月的档期，所以我昨天出门找出租公寓，在骑士桥附近有一间小小的、蛮不错的小套房。现在还没确定，一旦定下来，我会写信告诉你，你也可以再打电话问我妈。\r\n　　三餐不成问题，我们都在餐厅或旅馆里用餐，像最高级的克拉里兹大饭店就能充分供应烤牛肉、排骨。价钱虽然贵得离谱，不过换算成美金倒是挺划算的，所以我们还吃得起。假使换成我是英国人，瞧见这光景一定会恨得牙痒痒的。但是他们却反而对我们好得不得了，到处都有人邀请我们去家里作客或上馆子。\r\n　　惟一短缺的东西就是糖。凡是甜的东西，一应俱缺。也许我反该谢天谢地，正好让我在这里瘦它个几磅。\r\n　　写信给我。\r\n　　爱你的\r\n　　玛克辛\r\n\r\n\r\n\r\n---------------\r\n1951年9月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年9月15日\r\n　　玛克辛：真多亏了你的慧心巧手，书店简直被你给写活了——你的文笔实在比我好得太多啦！\r\n　　我刚打了电话给你妈妈要你的住址，她要我转告你：方糖和巧克力棒依照你的交代，已经给你寄去了。你不是还跟我说你要趁机减肥的吗？\r\n　　我不想让你以为我是酸葡萄，不过我实在不明白，你究竟是何德何能？老天竟任由你饱览遍逛“我的书店”；而我为什么就只得乖乖蹲在九十五大街的破公寓里，埋头写着这劳什子《埃勒里·奎因的冒险》电视剧集脚本！“我不是告诉过你，不能安排一截沾着口红的烟蒂当做破案线索吗？”“我们这个节目是由百优雪茄公司赞助的，千万别给我编出“香烟”这个台词儿。”……就连在场景里安排一只道具烟灰缸也不许出现烟屁股；也不能摆雪茄屁股——厂商嫌不好看——所以，只要剧情出现烟灰缸，里头全好端端地搁着一管全新的、未拆封的百优雪茄！\r\n　　简直岂有此理！你却还能跟约翰·吉尔古德坐在克拉里兹的酒吧里打情骂俏！\r\n　　来信时多写些伦敦的事物——地铁、巷弄胡同、古宅大院……随便什么都好，写仔细点儿。告诉我骑士桥长什么模样，此刻我的耳畔似乎响起了科茨的《伦敦组曲》……听起来是那么绿意盎然、雄壮典雅。\r\n　　xxxx\r\n　　hh\r\n\r\n\r\n\r\n---------------\r\n1951年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年10月15日\r\n　　这哪是佩皮斯日记呢？你倒是给我交代清楚！\r\n　　这本书根本不够资格称之为《佩皮斯日记》，这只是哪个没事找事做的半吊子编辑，从佩皮斯日记里东挖西补、断章取义，存心让他死不瞑目！\r\n　　真想啐它一口！\r\n　　一六六八年一月十二日的日记跑到哪儿去了？记着他的老婆把他踹下床，抄了根烫红的拨火棍，追着他满屋子乱跑的那天的日记呢？\r\n　　记着W.佩恩爵士福至心灵的儿子成天揣着教谕，把大伙儿搞得七荤八素的日记呢？这些偷工减料的手脚可别想逃过我的法眼。\r\n　　附上两张皱巴巴的钞票。我想，用来付这本玩意儿，外加你将要为我找来的那本“货真价实的佩皮斯”，应该绰绰有余了！到时候，我会将这本烂书碎尸万段，然后，一页一页撕下来——拿来包东西！\r\n　　HH\r\n　　P.S.圣诞节快到了，到底是寄新鲜的鸡蛋，还是干燥蛋好呢？我当然明白干燥蛋可以放得比较久，但是，“丹麦空运直送的新鲜鸡蛋”光听都觉得香，你们快帮我拿主意吧！\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('7eb26649-4bc2-4e51-ae8c-aedc59a77c3b', '测试8[卷1]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '1', '查令十字街84号\r\n\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n《查令十字街84号》\r\n\r\n\r\n\r\n\r\n\r\n\r\n***************\r\n*序言\r\n***************\r\n\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。						\r\n\r\n---------------\r\n序：书缘·情缘(1)\r\n---------------\r\n\r\n\r\n　　恺蒂\r\n　　如同每一个晴日的上午，阳光将这排歪歪斜斜的二手书店的影子投到街中心上，街上还少行人，穿着对襟毛衣，半秃着顶，行动悠缓的店主们正在将一切生意准备停当，掸一下桌面、橱窗中的灰尘，把书架上那排排参差的布面、皮面书摆正，再将一匣匣便宜的小本平装书移到门外，沿着窗前的墙根摆齐。不用吆喝生意，不用招揽顾客，这群书商们如同他们店中中层书架上的那些小羊皮装帧而成的上个世纪的书籍，虽并不昂贵，但却见过世面，口中叼着一枚烟斗，看着大红色的双层汽车在街上阳光屋影间叮咚过往。\r\n　　她跨下了一辆黑色的计程车，纤巧单薄的女人，游移的目光掠过那一家家摆着书的橱窗，68号，72号，76号，78号，82号，寻寻觅觅，像是丢失了件宝物。最终停了下来，但面前的84号却是空空如也。灰蒙蒙的玻璃窗里面蛛网遍织的书架东倒西歪，地上散落着些废纸，满是尘埃；推门进去，没有想像中的惊喜问候，空空的楼梯通向另一些同样废弃了的房间。孤身女人想张口告诉主人她已到来，她信守了诺言，但空屋中并无人回应，只有一阵冷风袭过，泪水顺着面颊静静地流淌下来。是一段书缘，还是一段情缘，竟让这纽约的独居女人千里迢迢为了伦敦小街这破落关门的书店而如此神伤？手中握着那本薄薄的小书，是为了还查令十字街(CharingCrossRoad)84号的哪一种心愿？\r\n　　一\r\n　　他约她出来聊天，选定的地方是孔乙己酒家，面前摆的是一樽绍兴花雕，自然少不了一碟五香豆，还有几样小菜。谈着各自喜欢的东西作家，纳博科夫、钱钟书、尤瑟纳尔、沈从文。谈着那本他最钟爱的书，《说吧，记忆》，在伦敦买到的初版本，自然便谈到那些古旧的书屋，里面的善本孤本初版本那些只有爱书人才能欣赏的古老气味。记得那条破街吗？我最爱做的是星期六早上睡个懒觉，约几个朋友去唐人街饮早茶，然后就去对面那条破街的老店中翻旧书。为什么我从未在那里遇见过你呢？回忆起从未共同经历过的伦敦往事，却怎么也想不起来查令十字街84号现在是做着什么样的买卖。知道那位纽约的老姑娘和那位一丝不苟的旧书商，他们通了二十多年的信，最终却仍未能谋面，是没有缘分？\r\n　　一九四九年十月，一切开始于一封很简单的从纽约到伦敦的商业性的信函：\r\n　　先生：\r\n　　你们在《星期六文学评论》的广告上说你们长于经营绝版的书籍，你们所用的“珍本书商”一词让我有些害怕，因为我总是把“珍本”与昂贵相联的。我是位穷作家，但对书却有一些“珍本”般的嗜好，我所要的书在这里都很难买到……寄上我最急需的书的名单，如果你们有干干净净不超过五美元一本的二手货，请将此函视作订购单，给我悉数寄来。(恺蒂女士的译文与本书正文译文行文不同，为尊重作者，特不作统一。下同。)\r\n　　(一九四九年十月五日)\r\n　　署名海莲·汉芙(HeleneHanff)，还特地注明了“小姐”。\r\n　　其实，这位小姐此年已三十有三，是一位以写电视、舞台剧本为生的自由撰稿人。汉芙出生于制衣人家庭，父亲原本是位民谣说唱艺人，虽为生活所迫做起了手艺活，但夫妻俩仍喜欢带着女儿去逛戏院。汉芙十九岁时进费城大学读英文，但家境贫困，一年后辍学，求职谋生，后来得一戏剧写作奖项，便以写作糊口。对书的热爱来自于在纽约市立图书馆中的刻苦自学，特别得益于英国剑桥大学一位阿Q教授(SirArthurQuillerCouch)的著作。然而美国书价昂贵，汉芙热爱英国文学，便将买书的对象转向英伦三岛，偶然选中一家小书店写了信去，第一次订货便得到价廉物美的圆满服务，海峡这边，查令十字街84号Marks&Co.书店的主管，弗兰克·德尔先生，则是汉芙二十年通信的对象。\r\n　　虽然三十有余，汉芙却仍是轻松活泼，特别是简牍之上，更善于以轻松调侃的笔墨，信手写来，天马行空，不拘格式。德尔先生给她回的第一封信中称之为“女士”，汉芙第二封信尾便加了注脚，“我希望‘女士’在你们那边的含义与这边不一样”。德尔先生下封信中便乖乖称之为“小姐”了。第五封信后，汉芙已将信首的尊称“先生”或“阁下”改为直呼其名，信的内容也像是写给一位相识已久的老友，且不乏亲昵、撒娇之态：\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(2)\r\n---------------\r\n\r\n\r\n　　弗兰克·德尔，你在那儿究竟干什么？你什么都没干，你只是闲坐着！\r\n　　我的利·亨特在哪里？我的《牛津诗集》在哪里？……\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('92568f9f-1595-4cd9-8ecc-8d2185610e1d', '测试9', '地理', 'sanking', '2dc347c8-d396-477f-8500-e368a05ae436', '2017-09-24 22:12:51', '2017-09-30', '0', '233', '0', '0', '1', '0', '					测试同情心\r\n     最近，新西兰一家警察局要招聘几名警察，采用什么方式才能招聘到最优秀的警察呢？笔试面试的方式显然都不行。警察局认为，一名优秀的警察，最为核心的一点就是要具有同情心以及设身处地为他人着想和服务的品格。\r\n为了测试人们的同情心，警察局让一名儿童演员扮演的流浪男孩在人流如织的人行道旁的垃圾桶里不停地翻找着食物，男孩蓬头垢面，狼狈不堪。\r\n很长一段时间里，路人行色匆匆，对正在垃圾桶里找寻食物的男孩无动于衷、漠然离去，根本没有要停下脚步来问一问或是帮助一下的意思，一些人还不屑一顾地往垃圾桶里扔垃圾，对一旁翻找食物的男孩视而不见，还有人甚至想要掏出手机拍照。直到到了放学时分，一群穿着校服的女学生走到小男孩身旁，热心地询问他是否需要帮忙，这才算给这次测试划上了一个圆满的句号。\r\n视而不见，听而不闻，在我们身边，此类事件层出不穷，警察局就是想要通过这种测试，来考问一下我们每一个人的同情心，对身边的人、事是否会热心，对那些需要帮助的人是否会伸出我们的援助之手。\r\n其实，不光是警察，要胜任任何一种职业，都需要一个人具有基本道德素质和价值观：我为人人，人人才会为我。', null);
INSERT INTO `p_task` VALUES ('934b0e74-2709-4795-86b6-3f084a2a7f0f', '测试8[卷5]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '5', '　　住在楼上的女孩儿凯特，她的英国男朋友布莱恩帮我将账单上列的书价一英镑十七先令六便士换算成美金五元三角，希望他没算错。我寄了五元和一元的钞票各一张，多出来的七角请用来支付《新约全书》，那两本我都要买。\r\n　　你们可否行行好？下回先将书价换算成美金。我连加减美金都一塌糊涂了，要我把英镑换算成美金真是阿弥陀佛。\r\n　　海莲·汉芙\r\n　　我希望在你们那边，“夫人”的意思和我们这边指的是两码事。\r\n\r\n\r\n\r\n---------------\r\n1949年11月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您寄来的六元书款已悉数收到，不过我们建议您不妨改以邮政划拨的方式付款，如此不但对你我双方都较为便利；亦比直接将钞票放入信封内要保险得多。\r\n　　我们非常高兴得知您如此喜欢那本斯蒂文森的书。两本《新约全书》已于今日付邮，账单亦一并附上，同时依照您的嘱咐，将书款分别以英镑与美金计价。我们期盼您也会喜欢此次寄去的两本书。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月18日\r\n---------------\r\n\r\n\r\n　　东九十五大街14号\r\n　　1949年11月18日\r\n　　这算哪门子的新约圣经啊！\r\n　　好心替我转告英国圣公会诸公，他们平白糟蹋了有史以来最优美的文字。是哪个家伙出馊主意把通俗拉丁文圣经整成这副德性？他们准会把他来活活烧死，不信记住我说的话。\r\n　　其实我犯不着火冒三丈，我本身是犹太人。不过我的嫂子是天主教徒；弟媳是卫理公会的；还有一票皈依长老教派的表亲(全是被我的亚伯拉罕叔公拉去改宗的)；还有一个到处宣扬基督信仰疗法的姑妈。他们要是知道有这么一本英国人搞出来的不三不四的拉丁文圣经，个个不暴跳如雷才怪！(话说回来，他们搞不好根本不晓得现在还有拉丁文哩。)\r\n　　哼，去他的！我手边还有一本从我的拉丁文老师那儿借来的圣经，暂且先不还他就是了，等你们找一本卖给我再说。\r\n　　寄去四元支付我欠你们的三元八角八分，你就拿多出来的一角二去买杯咖啡喝吧！我住的地方附近没有邮局，我才不要为了汇三元八角八分，大老远跑到洛克菲勒广场去大摆长龙呢。何况，如果要等到我哪天有空顺道去办事，口袋里的三元八角早就被我花得一毛不剩了。我对美国邮政和皇家邮政有十足的信心。\r\n　　你们有兰多的《假想对话录》吗？我想全套应该不止一本，我想读的是“希腊对话录”，如果里面有伊索和萝多彼的对话，就是那一本没错。\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年11月26日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('993dd19f-31c8-4f1d-997e-63d706ca64e3', '测试8[卷28]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '28', '有这一道街，它比整个世界还要大(1)\r\n---------------\r\n\r\n\r\n　　唐诺\r\n　　乍读这本书稿时，我一直努力在回想，查令十字街84号这家小书店究竟是长什么个模样(我坚信写书的海莲·汉芙不是胡诌的，在现实世界中必然有这么一家“坚实”存在的书店)，我一定不止一次从这家书店门口走过，甚至进去过，还取下架上的书翻阅过——《查令十字街84号》书中，通过一封1951年9月10日海莲·汉芙友人玛克辛的书店寻访后的信，我们看到它是“一间活脱从狄更斯书里头蹦出来的可爱铺子”，店门口陈列了几架书(一定是较廉价的)，店内则放眼全是直抵天花板的老橡木书架，扑鼻而来全是古书的气味，那是“混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……”，当然，还有一位五十开外年纪、以老英国腔老英国礼仪淡淡招呼你的男士(称店员好像不礼貌也不适切)。\r\n　　但这不也就是半世纪之后今天、查令十字街上一堆老书店的依然长相吗？——如此悬念，让我再次鼓起余勇、生出远志，很想再去查令十字街仔细查看一次，对一个有抽烟习性又加上轻微幽闭恐惧毛病如我者，这长达二十小时的飞行之旅，我自以为是个很大的冲动而且很英勇的企图不是吗？\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。\r\n　　至少，本书的译者一定会支持我的武断——陈建铭，就我个人的认识，正是书籍阅读世界的此道中人。一般，社会对他的粗浅身分辨识，是个优美、老英国典雅风味却内向不擅长议价的绝佳书版美术设计者，但这本《查令十字街84号》充分暴露了他的原形，他跳出来翻译了此书，而且还在没跟任何出版社联系且尚未跟国外购买版权的情况下就先译出了全书(因此，陈建铭其实正是本书的选书人)，以他对出版作业程序的理解，不可能不晓得其后只要一个环节没配合上，所有的心血当场成为白工，但安静有条理的陈建铭就可以因为查令十字街忽然疯狂起来。\r\n　　这是我熟悉、喜欢、也经常心生感激的疯子，在书籍和阅读的世界中，他们人数不多但代代有人，是这些人的持续存在，且持续进行他们一己“哈萨克人式的小小游击战”(借用赫尔岑的自况之言)，才让强大到几近无坚不摧的市场法则，始终无法放心地遂行其zhuanzhi统治，从而让书籍和阅读的世界，如汉娜·鄂兰谈本雅明时说的，总是在最边缘最异质的人身上，才得到自身最清晰的印记。\r\n　　在与不在的书街\r\n　　《查令十字街84号》这部美好的书，系以1949年至1969年长达廿年流光，往复于美国纽约和这家小书店的来往信函交织而成——住纽约的女剧作家买书，任职“马克斯与科恩书店”的经理弗兰克·德尔负责寻书寄书，原本是再乏味不过的商业往来，但很快的，书籍击败了商业，如约翰·房龙说“一个马槽击败了一个帝国”(当然，在书籍堆栈的基础之上，一开始是汉芙以她莽撞如火的白羊座人热情凿开缺口，尤其她不断寄送鸡蛋、火腿等食物包裹给彼时因战争物资短缺、仰赖配给和黑市的可怜英国人)，人的情感、心思乃至于咫尺天涯的友谊开始自由流窜漫溢开来。查令十字街那头，他们全体职员陆续加入(共六名)，然后是德尔自己的家人(妻子诺拉和两个女儿)，再来还有邻居的刺绣老太太玛丽·伯尔顿；至于纽约这边，则先后有舞台剧女演员玛克辛、友人金妮和埃德替代汉芙实地造访“她的书店”，惟遗憾且稍稍戏剧性的是，反倒汉芙本人终究没能在一切落幕之前踩上英国，实践她念念不忘的查令十字街之旅。全书结束于1969年10月德尔大女儿替代父亲的一封回信，德尔本人已于1968年底腹膜炎病逝。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(2)\r\n---------------\r\n\r\n\r\n　　一样产自英国的了不起小说家格林，在他的《哈瓦那特派员》中这么说：“人口研究报告可以印出各种统计数值、计算城市人口，借以描绘一个城市，但对城里的每个人而言，一个城市不过是几条巷道、几间房子和几个人的组合。没有了这些，一个城市如同陨落，只剩下悲凉的记忆。”——是的，1969年之后，对海莲·汉芙来说，这家书店、这道书街已不可能再一样了，如同陨落，只因为“卖这些好书给我的好心人已在数月前去世了，书店老板马克斯先生也已不在人间”，这本《查令十字街84号》于是是一本哀悼伤逝的书，纪念人心在二十年书籍时光中的一场奇遇。\r\n　　但海莲·汉芙把这一场写成书，这一切便不容易再失去一次了，甚至自此比她自身的生命有了更坚强抵御时间冲刷的力量——人类发明了文字，懂得写成并印制成书籍，我们便不再徒然无策地只受时间的摆弄宰制，我们甚至可以局部地、甚富意义地击败时间。\r\n　　书籍，确实是人类所成功拥有最好的记忆存留形式，记忆从此可置放于我们的身体之外，不随我们肉身朽坏。\r\n　　也因此，那家书店，当然更重要是用一本一本书铺起来的查令十字街便不会因这场人的奇遇戛然中止而跟着消失，事实上，它还会因多纳入海莲·汉芙的美好记忆而更添一分光晕色泽，就像它从不间断纳入所有思维者、纪念者、张望者、梦想者的书写一般，所以哀伤的汉芙仍能鼓起余勇地说：“但是，书店还是在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……”\r\n　　这是不会错的，今天，包括我个人在内，很多人都可以证实，查令十字街的确还在那儿，我是又过了十多年之后的八十年代、九十年代去的，即便84号的“马克斯与科恩书店”很遗憾如书末注释说的，没再撑下去，而成为“柯芬园唱片行”，但查令十字街的确还好好在那里。\r\n　　一道时间大河\r\n　　查令十字街，这个十字不是指十字路口，而是十字架的意思，事实上它是一道长约一公里许的蜿蜒市街，南端直抵泰晤士河，这里有最漂亮的查令十字街车站，如一个美丽的句点，往北路经国家艺廊，穿过苏活区和唐人街，旁及柯芬园，至牛津街为止，再往下走就成了托登罕路，很快就可看到著名的大英博物馆(大英博物馆一带又是另一个书店聚集处，但这里以精印的彩色大版本艺术书为主体)。\r\n　　老英国老伦敦遍地是好东西，这是老帝国长而辉煌的昔日一样样堆栈下来的，如书中汉芙说的(类似的话她说了不止一回)：“记得好多年前有个朋友曾经说：人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我：‘就在那儿！’”\r\n　　然而，和老英国其他如夕晖晚照荣光事物大大不同之处在于，查令十字街不是遗迹不是封存保护以待观光客拍照存念的古物，它源远流长，但它却是active，现役的，当下的，就在我们谈话这会儿仍孜孜勤勤劳动之中，我们可同时缅怀它并同时使用它，既是历史从来的又是此时此刻的，这样一种奇特的时间完整感受，仔细想起来，不正正好就是书籍这一人类最了不起发明成就的原来本质吗？我们之所以丧失了如此感受，可能是因为我们持续除魅的现实世界已成功一并驱除了时间，截去了过去未来，成为一种稍纵即逝却又驻留不去的所谓“永恒当下”——有生物学者告诉我们，人类而外的其他动物和时间的关系极可能只有这样，永恒的当下，记忆湮渺只留模糊的鬼影子，从而也就产生不来向前的有意义瞻望，只剩如此窄迫不容发的时间隙缝，于是很难容受得了人独有的持续思维和精致感受，只有不占时间的本能反射还能有效运作，这其实就是返祖。\r\n　　更正确地说，查令十字街的时间景观，指的不单单是它的经历、出身以及悠悠存在的岁月，而是更重要的，就算你不晓得它的历史沿革和昔日荣光，你仍可以在乍乍相见那一刻就清晰捕捉到的实时景观，由它林立的各个书店和店中各自藏书所自然构成——查令十字街的书店几乎每一家一个样，大小、陈列布置、书类书种、价格以及书店整体氛围所透出的难以言喻的鉴赏力、美学和心事。当然，书店又大体参差为一般新书书店和二手古书店的分别，拉开了时间的幅员，但其实就算卖新书的一般书店，彼此差异也是大的，各自收容着出版时日极不一致的各色书籍，呈现出极丰硕极细致的各自时间层次。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(3)\r\n---------------\r\n\r\n\r\n　　不太夸张地说，这于是成了最像时间大河的一条街，更像人类智识思维的完整化石层，你可以而且势必得一家一家地进出，行为上像进陈列室而不是卖场。\r\n　　相对来说，我们在台湾所谓的“逛书店”，便很难不是只让自我感觉良好的溢美之辞。一方面，进单一一家书店比较接近纯商业行为的“购买”，而不是带着本雅明式游手好闲意味的“逛”，一本书你在这家买不到，大概另一家也就休想；另一方面，“逛”，应该是不完全预设标的物的，你期待且预留着惊喜、发现、不期而遇的空间，但台湾既没二手书店，一般书店的书籍进退作业又积极，两三个月前出版的书，很可能和两三千年前的出土文物一样不好找。\r\n　　连书店及其图书景观都是永恒当下的，在我们台湾。\r\n　　永恒当下的灾难\r\n　　海莲·汉芙在书中说到过她看书买书的守则之一，对我们毋宁是极陌生到足以吓人一跳的，她正色告诉德尔，她绝不买一本没读过的书，那不是跟买衣服没试穿过一样冒失吗？当然我们没必要激烈如这位可敬的白羊座女士，但这其实是很有意思的话，说明旧书(广义的，不单指的珍版珍藏之书)的购买、收存和再阅读，不仅仅只是屯积居奇的讨人厌行为或附庸风雅的恶心行为而已。这根源于书籍的不易理解，不易完整掌握的恒定本质，尤其是愈好、内容愈丰硕、创见之路走得愈远的书，往往远远超过我们当下的知识准备、道德准备和情感准备，我们于是需要一段或长或短的回身空间与它相处。好书像真爱，可能一见钟情，但死生契阔与子成说，执子之手与子偕老的杳远理解和同情却总需要悠悠岁月。\r\n　　因此，从阅读的需求面来说，一本书的再阅读不仅仅只是可能，而是必要，你不能希冀自己一眼就洞穿它，而是你十五岁看，二十岁看，四十岁五十岁看，它都会因着你不同的询问、关注和困惑，开放给你不一样的东西，说真的，我努力回想，还想不出哪本我真心喜欢的书没有而且不需要再再重读的(你甚至深深记得其中片段，意思是你在记忆中持续重读)；也因此，从书籍取得的供给面来看，我们就应该聪明点给书籍多一点时间、给我们自己多一点机会，历史经验一再告诉我们，极多开创力十足且意义重大的书，我们当下的社会并没那个能力一眼就认得出来，不信的人可去翻阅大名鼎鼎的纽约时报历来书评(坊间有其结集成书的译本)，百年来，日后证明的经典著作，他们漏失掉的比他们慧眼捕捉到的何止十倍百倍，而少数捕捉到的书中又有诸如塞林格的《麦田里的守望者》或钱德勒的《大眠》被修理得一无是处(理由是脏话太多云云)。一个社会，若意图在两星期到一个月内就决定一本书的好坏去留，要求书籍打它不擅长的单败淘汰赛，这个社会不仅自大愚蠢，而且可悲的一步步向着灾难走去。\r\n　　一种只剩永恒当下的可悲灾难。\r\n　　部分远大于全体\r\n　　便是这个永恒当下的灾难启示，让我们得以在书籍暨阅读的世界中，推翻一项亘古的数学原理——这是柏拉图最爱引用的，全体永远大于部分，但我们晓得事实并不尽然，短短的一道查令十字街，的确只是我们居住世界的一个小小部分，但很多时候，我们却觉得查令十字街远比我们一整个世界还大，大太多了。\r\n　　最是在什么时候，我们会生出如此诡异的感觉呢？特别当我们满心迫切的困惑不能解之时。我们很容易在一本一本书中再再惊异到，原来我们所在的现实世界，相较于既有的书籍世界，懂得的事这么少，瞻望的视野这么窄，思维的续航能力这么差，人心又是这么封闭懒怠，诸多持续折磨我们的难题，包括公领域的和私领域的，不仅有人经历过受苦过认真思索过，甚至还把经验和睿智细腻的解答好好封存在书中。\r\n　　从形态上来看，我们眼前的世界往往只有当下这薄薄的一层，而查令十字街通过书籍所揭示的世界图像，却是无尽的时间层次叠合而成的，包括我们因失忆而遗失乃至于根本不知有过的无尽过去，以及我们无力也无意瞻望的无尽未来。\r\n\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('9942507a-7f02-4da3-9d1e-ba9bf0db07e6', '测试7', '地理', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-25 10:49:46', '2017-09-07', '1', '700', '2', '0', '1', '0', '洛克菲勒家书：拒绝责难，拒绝推委\r\n2016-01-05 10:17 阅读(408)评论(2)\r\n洛克菲勒家书：拒绝责难，拒绝推委\r\n\r\n亲爱的约翰：\r\n\r\n如果我说一直不甘示弱、总以为自己是世界第一富豪的安德鲁·卡内基先生来拜访我，并向我讨教了一个非常严肃的问题，你会不会感到惊讶？事实上，那位伟大的铁匠就是这么做的。\r\n\r\n两天前，卡内基先生来到我们的基奎特。或许是我笑容可掬的态度，和我们轻松的谈话气氛，熔化了卡内基先生钢铁般的自尊，让他放下架子问我：“约翰，我知道，你领导着一群很能干的人。不过，我不认为他们的才干不可匹敌，但令我疑惑的是，他们似乎无坚不摧，总能轻松击败你们的竞争对手。我想知道，你施了什么魔法让他们有那种精神的，难道是金钱的力量？”\r\n\r\n我告诉他，金钱的力量当然不可低估，但责任的力量更是巨大。有时，行动并非源于想法，而是源自揽起责任。标准石油公司的人都有负责精神，都知道“我的责任是什么？我做什么可以把事情做得更好？”但我从不高谈阔论责任或义务，我只是通过我的领导方式来创造具有责任感的企业。\r\n\r\n我以为这个话题到此就应该结束了，但我的回答显然挑动了卡内基先生的好奇心，他很认真地追问我：“约翰，那你能告诉我你是怎么干的吗？”\r\n\r\n看着卡内基先生谦逊的神态，我无法拒绝，我必须如实相告。我告诉他，如果我们想要永续生存，那么我们的领导方式就意味着断然拒绝为了任何理由，去责难任何一个人或任何一件事。责难就如同一片沼泽，一旦失足跌落进去，你便失去了立足点和前进的方向，你会变得动弹不得，陷入憎恨和挫折的困境之中。结果只有一个：失去手下的尊重与支持。一旦落到这步田地，那你就好比是一个将王冠拱手让给他人的国王，无法再主宰一切。\r\n\r\n我知道责难是摧毁领导力的头号敌人，我还知道在这个世界上没有常胜将军，不管是谁都将遭遇挫折和失败。所以，当问题出现时，我不会感到愤恨不满，我只是在想：怎么能让情势好转起来？采取什么行动可以补救或是修复我们的失误？积极地选择朝向更高的生产力和满意度前进。\r\n\r\n当然，我不会放过我自己。当坏事降临在我们身上时，我会先停下来问自己一个问题：“我的职责是什么？”回归原点，借着对自身角色进行完全坦诚的评估，可以避免空窥探他人做了什么，或是要求其他人改变什么，等等无意义的行为。事实上，只有将焦点专注在自己身上，我才能将无意中拱手让出的王冠重新收回。\r\n\r\n但是，分析“我的职责是什么”并不意味着自责。自责是一种最阴险狡猾的责难陷阱，诸如“那真是一个愚蠢的错误！”等自我责难，只会使我陷入与其他任何责难相同的忿恨与不满的圈套之中。事实上，“我的职责是什么”是一种具有强大分析力和自我肯定的步骤，当我知道，真正的问题不是他们应该要做什么，而是我应该要做什么时，我不会自怨自艾，而只会让自己更强大。自己越强大，别人的影响力就会越小，看来这不是件坏事。\r\n\r\n如果我能将每一个阻碍视为了解自己的一个机会，而非斤斤计较他人对我做了什么，那么我就能在领导危机的高墙外找到出路。\r\n\r\n当然，我从不把自己视为救世主，也没有救世主的心态。我自问：我在哪些方面应为自己负责？也自问：在哪些方面，部属们要为我负责？领导者的工作不是全知全能、全权负责。如果我视自己为英勇的正义使者，准备去拯救这个世界，那就只会让自己陷入领导危机之中。我的责任中，很大一部分是让其他人也为自己该负的责任负责。如果一个雇员对于事关自己切身利益的事情都不在乎的话，我不相信这样的雇员能对出色完成工作有强烈的渴望，那他就应该离开，为别人去服务了。\r\n\r\n感觉责任在肩的那种压力能让人不自觉的兴奋起来。没有一件事像个人的责任感一样，可以激发并强化做事的能力，而将重责大任托付部属，并让他了解我对他充分信任，无疑是对他最大的帮助。所以，我不会将部属必须并且能够负担的责任揽在自己身上。\r\n\r\n我不只光靠示范作用来营造公司负责的氛围与风气，我的部属都知道我的基本原则：在标准石油公司没有责难、没有藉口！这是我坚持的理念，每一个人都知道。我不会因为他们犯错而惩罚他们，但是我决不能容忍不负责任的行为存在。我们的信念就是要彻底奉行。我们的箴言是支持、鼓励和尊重将被全心接受与加倍颂扬。只会找藉口而不提供解决方式，在标准石油公司是无法容忍的。\r\n\r\n我们很少犯任何错误，因为我的大门随时为部属敞开着，他们可以提出高见，或是纯粹的发牢骚，但是要用一个负责任的方式。这样的结果会让我们彼此信任，因为我们了解所有的事都需要摊在阳光下来讨论。\r\n\r\n卡内基先生是位优秀的老学生，他没有让我浪费时间，他在我结束这个话题时说：“在抱怨声中，优秀的雇员也会变成乌合之众！”他真聪明。\r\n\r\n约翰，几乎所有的人都有推委真正责任的防御心理，以致推委责任的现象处处可见。但它贻害无穷。避免防御的方法就是开始倾听。\r\n\r\n领导者最大的挑战在于，要如何创造出一个能让人们觉得开诚布公会比隐藏实情来得舒适的环境。主动邀请其他人陈述他们的想法，用一些诸如“再多说一点”，或是“我真的想听听你的意见”的话语来鼓励他们说出自己的想法。和一般人所相信的刚好相反，在对话中，聆听者才是拥有权力的人，而非陈述者。\r\n\r\n难以置信吧？想想看，陈述者的语调、焦点还有内容，事实上都取决于你倾听的方式。试想，和一个面露敌意且肢体呈现侵略性姿态的人，以及一个对你表示全神贯注的人说话时，两者之间的差异。当你单纯地聆听其他人说话时，你卸下了你的防卫。你会得到这些好处：你对有攻击性或愤怒的语言的背后隐含的议题，会有着更透彻的了解。你可以得到更多的信息，而这些资讯可以改变你对整个事件来龙去脉的假设。你会有更多的时间来整理思绪。\r\n\r\n陈述者会感觉你重视他们的观点。最令人兴奋的是，当你专注地倾听之后，原来的陈述者也会更愿意聆听你的意见。\r\n\r\n真实的倾听是不具任何防御性的。即使你不喜欢这个信息，你也应该倾听了解，而非立即做出回应。专注地倾听不太像是一种技巧，它比较像是一种态度。滑雪的人在遭遇障碍时的每一秒钟，都投注百分之百的注意力，绝对不会分神去思考过一会儿他要对伙伴说什么。同样地，作为一名积极的倾听者，你贡献百分之百的注意力给另外一个人，不会出现想到什么就脱口而出的情况。如此一来，你去除了先入为主的观念，并敞开胸襟开创一段更有意义和更有效果的对话。\r\n\r\n长久以来，我们塑造了生活也塑造了自己。这个过程将会持续下去，我们最终都将为自己的选择负责。就如“目的”决定你的方向，拒绝责难将筑出一条实现目标的大道。\r\n\r\n爱你的父亲', null);
INSERT INTO `p_task` VALUES ('9bc9c252-ea65-40e2-b2fc-ec9c2a125ade', '测试4', '人文', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-27 16:20:13', '2017-09-04', '1', '400', '6', '0', '1', '0', '					测试同情心\r\n     最近，新西兰一家警察局要招聘几名警察，采用什么方式才能招聘到最优秀的警察呢？笔试面试的方式显然都不行。警察局认为，一名优秀的警察，最为核心的一点就是要具有同情心以及设身处地为他人着想和服务的品格。\r\n为了测试人们的同情心，警察局让一名儿童演员扮演的流浪男孩在人流如织的人行道旁的垃圾桶里不停地翻找着食物，男孩蓬头垢面，狼狈不堪。\r\n很长一段时间里，路人行色匆匆，对正在垃圾桶里找寻食物的男孩无动于衷、漠然离去，根本没有要停下脚步来问一问或是帮助一下的意思，一些人还不屑一顾地往垃圾桶里扔垃圾，对一旁翻找食物的男孩视而不见，还有人甚至想要掏出手机拍照。直到到了放学时分，一群穿着校服的女学生走到小男孩身旁，热心地询问他是否需要帮忙，这才算给这次测试划上了一个圆满的句号。\r\n视而不见，听而不闻，在我们身边，此类事件层出不穷，警察局就是想要通过这种测试，来考问一下我们每一个人的同情心，对身边的人、事是否会热心，对那些需要帮助的人是否会伸出我们的援助之手。\r\n其实，不光是警察，要胜任任何一种职业，都需要一个人具有基本道德素质和价值观：我为人人，人人才会为我。', null);
INSERT INTO `p_task` VALUES ('9ee603d0-8fe2-44aa-9d59-4fbfef4b79c0', '测试8[卷29]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '29', '\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(4)\r\n---------------\r\n\r\n\r\n　　看看穆勒的《论自由》和《论代议政治》，这是足足一百五十年前就有的书，今天我们对自由社会和民主政治的建构、挫折、一再摔落的陷阱以及自以为聪明的恶意操弄，不好端端都写在书里头吗？\r\n　　看看李嘉图的《政治经济学原理》，这是两百年前的书，书中再清晰不过所揭示的经济学最基本道理和必要提醒，我们今天，尤其手握财经权力的决策者，不还在日日持续犯错吗？\r\n　　或者看看本雅明的《发达资本主义时代的抒情诗人》，这又是超过半个世纪以前的书，而今天，我们的大台北市才刚刚换好新的人行步道、才刚刚开始学习在城市走路并试图开始理解这个城市不是吗？\r\n　　还是我们要问宪法的问题(内阁制、总统制、双首长制、还有神秘的塞内加尔制)？要问民族主义和民粹主义的问题？问生态环保或仅仅只是整治一条基隆河的问题？问男女平权？问劳工和失业？问选举制度和选区规划？问媒体角色和自律他律？或更大哉问的问整体教育和社会价值暨道德危机等等问题？\r\n　　是的，如海莲·汉芙说的，书店还是在那儿。\r\n　　全世界最便宜的东西\r\n　　而查令十字街不仅比我们眼前的世界大，事实上，它做得更好——查令十字街不仅有着丰硕的时间层次，还呈现具体的空间分割；它是一道川流不息的时间之街，更是一个个书店、隔间、单一书籍所围拥成的自在小世界，让闲步其中的人柳暗花明。\r\n　　我猜，这一部分原因有历史的偶然渗入作用而成，比方说，老式的、动辄百年以上的老伦敦建筑物，厚实坚强的石墙风雨不动的制限了商业流窜的、拆毁一切夷平一切的侵略性格，因此，小书店各自盛开如繁花，即便是大型的综合性书店，内部格局也曲折回旋，每一区块往往是封闭的、隔绝的，自成洞天，毋宁更像书籍层层架起的读书阅览小房间而非卖场；而且，美国的霸权接收，让英文不随老帝国的坠落而衰败，仍是今天的“准世界语”，仍是普世书籍出版活动的总源头和荟萃之地，因此，你一旋身，才两步路便由持续挣扎的东欧世界出来，却马上误入古怪拼字，但极可能正是人类最远古家乡非洲黝暗世界，如同安博托·艾柯在《玫瑰之名》书中最(禁止)的惊心动魄一幕——第七天，威廉修士和见习僧艾森终于进入了大迷宫图书馆中一切秘密埋藏所在的非洲之末。\r\n　　一个无垠无边的智识世界，却是由一个个小洞窟构成的。\r\n　　我尤其喜欢查令十字街的一个个如此洞窟，一方面，这有可能正是人类亘古的记忆存留，是某种乡愁，像每一代小孩都有寻找洞窟打造洞窟置身洞窟的冲动，有某种安适安全之感，而读书，从阅读、思索到着迷，最根柢处，本来就是宛如置身一己洞窟的孤独活动；另一方面，我总时时想到列维—施特劳斯的话，这些自成天地般洞窟的存在，提供我们逃避的机会，逃避什么样的压迫呢？逃避一种列维—施特劳斯指称的大众化现象，意即一种愈发一致的、无趣的、再没性格可言的普世性可怖压逼(正是社会永恒当下的呈现)，而这些动人的洞窟，正像《爱丽丝漫游仙境》的树洞，你穿过它，便掉落到一个完全异质、完全始料未及的世界里去。\r\n　　于是，我遂也时时忧虑我们最终仍会失去属于我们这一代的查令十字街，如同汉芙早已失去她的查令十字街一般，我们的杞忧，一方面是现实中断续传来的不利信息(如商业的腐蚀性只是被减缓，并没真正被阻止)，更是人面对足够美好事物的很自然的神经质反应，你深知万事万物持续流变，珍爱的东西尤其不可能一直存留，如朝霞，如春花，如爱情。\r\n　　但你可以买它——当然不是整条查令十字街，而是它真正赖以存在、赖以得着意义的书籍，市街从不是有效抵御时间风蚀的形式，书籍才是，就像汉芙所说：“或许是吧，就算那儿没有(意指英国和查令十字街)，环顾我的四周(意指她从查令十字街买到的书)……我很笃定，它们已在此驻足。”\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(5)\r\n---------------\r\n\r\n\r\n　　从事出版已超过半辈子之久，我个人仍始终有个问题得不到满意的答案：我始终不真正明白人们为什么不买书？这不是全世界最便宜的一样东西吗？一个人类所曾拥有过最聪明最认真最富想像力最伟大的心灵，你不是极可能只用买一件看不上眼衣服的三千台币就可买下他奇迹一生所有吗(以一名作家，一生十本书，一本书三百元计，更何况这么买通常有折扣)？你不是用吃一顿平价午餐的支付，就可得到一个美好的洞窟、以及一个由此联通的完整世界吗？\r\n　　汉芙显然是同我一国的，她付钱买书，但自掏腰包寄食物还托朋友送丝袜，却仍觉得自己占便宜，在1952年12月12日，她说的是：“我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年；而你们送给我的礼物，却能和我朝夕相处、至死方休；我甚至还能将它遗爱人间而含笑以终。”而在1969年4月11日的最终决算，她仍得到“我亏欠它良多”的结论。\r\n　　美国当前最好的侦探小说家，同样也住纽约的劳伦斯·卜洛克也如此想，他在《麦田贼手》一书，通过一名仗义小偷之口对一名小说家(即塞林格)说：“这个人，写了这么一本书，改变了我们整整一代人，我总觉得我欠他点什么。”所以——买下它，我指的是书，好好读它，在读书时日里若省下花费，存起来找机会去一趟查令十字街，趁它还在，如果你真的成行并顺利到那儿，请代我们献上一吻，我们都亏欠它良多……\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(1)\r\n---------------\r\n\r\n\r\n　　张立宪\r\n　　“你们若恰好路经查令十字街84号，请代我献上一吻，我亏欠她良多……”\r\n　　生活中发生了一桩小小的笑话。一位朋友在英国，按照我的介绍，某一天逛伦敦的书店一条街——查令十字街，并到84号朝圣。奈何那里已经改为一家酒吧，只是在门边挂个铜牌，上书“查令十字街84号，马克斯与科恩书店的旧址，因为海莲·汉芙的书而闻名天下”。收银小姐看着她笑问：“为那本书来的吧？”她只好狠狠地点一瓶红酒，并为我买了期待已久的书《查令十字街84号》，然后寄往北京，还兴冲冲地先用数码相机将书拍了照mail过来，让我预热一下。结果，不幸的很，这本书在大英帝国的邮政系统兜了一个圈子，又回到了朋友的手上——她将收件人与寄件人的位置弄颠倒了。\r\n　　其实以我的英文修养，肯定啃不动原版书，但对于这本书，还是希望能保留一本，因为它被誉为“爱书人的《圣经》”。\r\n　　这本书讲的就是一个纽约爱书人通过书信往来在伦敦一家古旧书店(书名即是这家书店的地址)淘书并建立深厚友谊的故事。来往的书信被她汇集成此书，成为读书人的掌上明珠。\r\n　　得到这本书并非易事。好在，根据原著拍摄的同名电影已经有DVD出售，万幸的是，我买到了，并且看了。该片由美国哥伦比亚公司1986年拍就，片长100分钟，担纲主演的是演技派演员安妮·班克劳夫特和安东尼·霍普金斯，拍得真是无可挑剔。\r\n　　之所以说是“万幸”，是因为影碟被碟商译成了一个耸人听闻的动作片名字——《迷阵血影》。我敢担保，即使你看到这张影碟，最大的可能也是与其失之交臂。这个异想天开的译名惹恼了一个台湾人，他叫陈建铭，是诚品书店古书区的员工，在没与任何出版社联系出版且未跟国外购买版权的情况下，就先译出了全书。按照他的说法，这般牛劲发作，就是因为《迷阵血影》这个名字，而且影片的对白字幕也是惨不忍睹，“我翻译这本书，多少也想为它赎点儿罪罢”。\r\n　　恕我饶舌，复述一遍这个再简单不过的故事。\r\n　　穷困的女作家海莲受不了纽约昂贵庸俗的古旧书店，便按照《星期六文学评论》上的地址，给位于伦敦查令十字街84号的马克斯与科恩书店(后来被海莲派去伦敦侦察的好友形容成一家“活脱从狄更斯书里头蹦出来的可爱铺子”)写了一封信，求购一些绝版图书。这一天是1949年10月5日。\r\n　　很快，回信和她要的书就来了，那些书令海莲的书架相形见绌。双方的信任和欣喜很快达成，除了海莲有一点点麻烦，她是个连付账和找零都搞不清楚的女人，更不用说将英镑换算成美元了。马克斯与科恩书店的经理弗兰克除了满足她购书的要求外，还得给她准备英镑和美元两种发票。\r\n　　温暖的相知借助娓娓道来的书信，很快就俘获了远隔重洋的海莲和弗兰克。\r\n　　五十年代初期的英国百废待兴，物资实行配给制。海莲就从美国给书店的店员们寄来火腿、鸡蛋和香肠，让他们吃到很久没有见过的“完整而大块”的肉。而弗兰克并不是不知感恩的人，他开始在英国各地奔波，出入豪宅，为存货不多的书店添置新品，踏破铁鞋，为她寻觅难得一见的珍本。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('a27e6443-37ee-46e5-aade-72a95a997c29', '测试8[卷21]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '21', '    诺拉、所有人和我在此祝您圣诞快乐、恭贺新禧!\r\n弗兰克·德尔谨上\r\n\r\n     纽约市\r\n    东九十五大街14号\r\n    1956年l月4日\r\n    我现在趴在床脚下写信给你——这本卡图卢斯害我气得滚下来。\r\n译得洁屈赘牙的，真教人伤脑筋!\r\n到目前为止，我只听过一个名字也叫理查德·伯顿的帅哥演员——我曾在几部英国电影里头看过他，我想维持这么点交情也就够了。至于这个翻译者理查德·伯顿，他译得也未免太花俏了吧。\r\n    而可怜的史密瑟先生，他一定害怕他妈妈会读这本书，所以忍痛把那些原本应该活色生香的文章译得道貌岸然。咱们这么着吧，你索性找一本好的通俗拉丁文版的给我。我自己有一本卡塞尔氏拉丁文字典，那些难懂的段落，我自个\r\n儿去整明白得了!\r\n    梅甘头壳坏掉了吗?如果她真的那么厌烦文明世界，怎么不干脆搬去西伯利亚!\r\n    行行行!没问题!我会当热刺队的拉拉队!\r\n    正努力攒钱当中。如果电视台继续赏我饭吃，明年夏天我就可以成行了。我要去亲眼瞧瞧贵书店、圣保罗大教堂、国会殿堂、伦敦塔、柯芬园、老维克剧院. ....还要见见博尔顿老太太。\r\n    附寄十元大钞一张。这本白色软精装的卡图卢斯，居然还配着白色的丝质书签带……弗兰基，你打哪儿找来这些玩意儿啊?\r\n\r\n    伦敦中西二区 查令十字街84号.\r\n    1956年3月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    很抱歉又隔了好一段时间才回您的信。因为直到今天，我们才有好消息可以向您报告。原先我还在犹豫，是不是该等到卡图卢斯的书也一并我到时再写这封信比较好。\r\n    我们终于寻获一本版本相当不错的《项狄传》，附有罗布的插图，价格约合美金二元七十五分。同时我们也收到一册柏拉图的《苏格拉底四论》，译者是本杰明·乔伊特，一九〇三年在牛津出版。此书标价一美元，您是否要买?您在敝店\r\n户头内尚有美金一元二十二分的余额，如您两册都购买，仅\r\n需再付给我们美金二元五十三分。\r\n    我们仍翘首期盼您今夏能来，我家的两个女孩儿都离家住校，所以届时橡原巷3号将有两间卧房可任您挑选。很遗憾地向您报告:博尔顿老太太已被送到老人之家，我们都很难过，但毕竟她在那儿能得到比较好的照料。\r\n弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1956年6月1日\r\n亲爱的弗兰克:\r\n    布莱恩介绍我读肯尼思·格雷厄姆的《杨柳风》，因此我迷上了谢泼德的插图，决定自己也要买一本。但是!先别忙着寄来，帮我保留到九月，届时我会迁入新址。\r\n    原本住得舒舒服服的旧公寓老命不保喽。上个月所有住户都收到了搬迁通知。现址要盖新大楼。我想也是该为自己觅一间好公寓、买几件好家具的时候了。于是我打铁趁热地到第二大道的一处工地，订了一间连影儿都还没有的客卧两\r\n用预售屋。我现在忙着四处张罗新家具和书架、地毯，几乎把钱都花光了。可是，我一辈子都跟摇摇晃晃的桌椅、到处爬满蟑螂的厨房为伍，现在我想过点儿像样的日子。至于英国之旅，只好等着有人招待我去了。\r\n    与此同时，我的房东怕大家赖着不走，索性把门房给解雇了，害得垃圾和热水都没人打理;还打算要把信箱间、走道灯和我的厨房和卧室间的隔间墙全拆了(本周即将发生);这些烦心的事加上眼睁睁看着道奇队兵败如山倒，真是心酸谁\r\n人知噢……\r\n对了，新地址如下:\r\n纽约州，纽约市21，东七十二大街305号(九月一日启用)\r\n\r\n    伦敦中西二区，查令十字街84号\r\n    1957年5月3日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    预备好大震一惊:您上回信中提到的三本书，一口气全都找到了!而且已经在上周寄去给您，现在应该正在途中。别惊讶我们究竟是怎么办到的，这理应是本店的服务项目之一。账单附在此信中，扣除账户余额之后的书款为五美元。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('a34f1652-a27f-4138-a440-f88176ed2134', '测试2', '政治', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-25 10:37:40', '2017-09-02', '1', '200', '5', '1', '1', '0', '				少年特烦恼	\r\n	记忆中她是一个长得微黑、有点腼腆的女孩。留一头短发，平时不太爱出声，不多的几个好友都是从小学一起上来的。女伴们开玩笑地叫她“黑包公”，开始她还气恼，作势满教室追打她们。后来叫多了，也就默默认下了这个雅号。\r\n我和她坐过一段时间的同桌。初一到初二，不长不短。那个年代、那个年龄的男孩女孩，互相之间还很敏感，印象中没说过几句话，平时递个书本、卷子之类的，多是靠肢体语言和眼神交流。我俩私下里交往最深的也是唯一的一次主动交往，是我那时爱看各种闲书，而她刚好带了一本外国翻译小说，我实在手痒心痒到不行，便大起胆子向她借。她红着脸看了我一眼，没点头，也没摇头。等到第二天，上完课间操回来，我发现那本书已安静地躺在我的桌肚里，还细心地包好了书皮。用的是那种少见的挂历纸，四角都折压得十分熨帖、平整。\r\n那之后依旧风平浪静地坐着同桌。可是有一天班里忽然风云突变，大传特传起了我和她的流言蜚语。像所有类似桥段一样，女生们在背后窃窃私语，男生们则时不时地起哄，时不时高声怪气地叫着我俩的大名小号。\r\n无辜躺枪，让那时还是一枚地道、纯良好少年的我，第一次体尝到青春期小江湖的险恶。我每天像惊弓之鸟般不敢与她有半点瓜葛，却又躲无可躲，无可奈何！只好把这一切祸因归罪于她。借口找老师调了座，平时上学放学故意错开时间，偶尔不小心遇到，恨不得赶紧找个地洞逃掉。有一次她生病请假，我暗暗高兴了好几天，甚至在心里盼着她就此请长假，不再来上学。\r\n就这样一直到中考结束，彼此陌路，和她再未有过交集。\r\n上了高中，曾经那个羞涩单纯的傻小孩终于长大，开始懂得留意女生。于是某一天，一个长得白皙、梳着高高马尾，走路像骄傲的小鹿一样的女孩，忽然就一头撞进了眼睛里。那一瞬间说不清是什么感觉，就是喜欢看她那种既骄傲又活泼、大方，一副不谙世事的样子。从此无论她走到哪里，目光总是不自觉地尾随到哪里。\r\n	小鹿一度是男生寝室的话题女主之一。最得先机之利的是班长L。因为工作关系，他与小鹿经常有许多话要谈。从班务会谈到课间休，从课间休到自习课，再从自习课到操场。学校实行封闭式管理，曾经某天晚自习时，两个人在操场上顶着漫天飞雪，一圈又一圈地绕着跑道谈工作谈理想谈人生，可是很奇怪却不谈爱情。顶风冒雪回来的L为了自证清白，信誓旦旦地向一众室友宣布，他们的友谊就像外面的雪花一样纯洁无瑕。\r\n我在心里酸溜溜地差一点笑喷，雪化成了水，全是杂质的好吧！\r\nL和小鹿雪花般的纯洁友谊终究生死未卜，不知所终。后来听了用雪花来描摹爱情的歌，反而觉得真真切切、如梦似醉——“像雪花一样的柔柔，落到手心变水流，不相爱的人不会懂，柔柔的感受。像雪花一样的柔柔，虽没有永久却醉心头，拥有回忆我就足够……”\r\n像极了每个人的青春年少。\r\n时间在走。许多东西都会过去。唯有那些独属于自己的青春记忆，会越来越深地刻印在生命里。\r\n凡所经历，即所拥有；凡所拥有，足可珍惜。', null);
INSERT INTO `p_task` VALUES ('a38f41ef-288e-4081-802f-c7b6689e30e8', '测试6', '历史', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-21 16:12:24', '2017-09-06', '0', '600', '0', '0', '3', '0', '查令十字街84号\r\n\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n《查令十字街84号》\r\n\r\n\r\n\r\n\r\n\r\n\r\n***************\r\n*序言\r\n***************\r\n\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。						\r\n\r\n---------------\r\n序：书缘·情缘(1)\r\n---------------\r\n\r\n\r\n　　恺蒂\r\n　　如同每一个晴日的上午，阳光将这排歪歪斜斜的二手书店的影子投到街中心上，街上还少行人，穿着对襟毛衣，半秃着顶，行动悠缓的店主们正在将一切生意准备停当，掸一下桌面、橱窗中的灰尘，把书架上那排排参差的布面、皮面书摆正，再将一匣匣便宜的小本平装书移到门外，沿着窗前的墙根摆齐。不用吆喝生意，不用招揽顾客，这群书商们如同他们店中中层书架上的那些小羊皮装帧而成的上个世纪的书籍，虽并不昂贵，但却见过世面，口中叼着一枚烟斗，看着大红色的双层汽车在街上阳光屋影间叮咚过往。\r\n　　她跨下了一辆黑色的计程车，纤巧单薄的女人，游移的目光掠过那一家家摆着书的橱窗，68号，72号，76号，78号，82号，寻寻觅觅，像是丢失了件宝物。最终停了下来，但面前的84号却是空空如也。灰蒙蒙的玻璃窗里面蛛网遍织的书架东倒西歪，地上散落着些废纸，满是尘埃；推门进去，没有想像中的惊喜问候，空空的楼梯通向另一些同样废弃了的房间。孤身女人想张口告诉主人她已到来，她信守了诺言，但空屋中并无人回应，只有一阵冷风袭过，泪水顺着面颊静静地流淌下来。是一段书缘，还是一段情缘，竟让这纽约的独居女人千里迢迢为了伦敦小街这破落关门的书店而如此神伤？手中握着那本薄薄的小书，是为了还查令十字街(CharingCrossRoad)84号的哪一种心愿？\r\n　　一\r\n　　他约她出来聊天，选定的地方是孔乙己酒家，面前摆的是一樽绍兴花雕，自然少不了一碟五香豆，还有几样小菜。谈着各自喜欢的东西作家，纳博科夫、钱钟书、尤瑟纳尔、沈从文。谈着那本他最钟爱的书，《说吧，记忆》，在伦敦买到的初版本，自然便谈到那些古旧的书屋，里面的善本孤本初版本那些只有爱书人才能欣赏的古老气味。记得那条破街吗？我最爱做的是星期六早上睡个懒觉，约几个朋友去唐人街饮早茶，然后就去对面那条破街的老店中翻旧书。为什么我从未在那里遇见过你呢？回忆起从未共同经历过的伦敦往事，却怎么也想不起来查令十字街84号现在是做着什么样的买卖。知道那位纽约的老姑娘和那位一丝不苟的旧书商，他们通了二十多年的信，最终却仍未能谋面，是没有缘分？\r\n　　一九四九年十月，一切开始于一封很简单的从纽约到伦敦的商业性的信函：\r\n　　先生：\r\n　　你们在《星期六文学评论》的广告上说你们长于经营绝版的书籍，你们所用的“珍本书商”一词让我有些害怕，因为我总是把“珍本”与昂贵相联的。我是位穷作家，但对书却有一些“珍本”般的嗜好，我所要的书在这里都很难买到……寄上我最急需的书的名单，如果你们有干干净净不超过五美元一本的二手货，请将此函视作订购单，给我悉数寄来。(恺蒂女士的译文与本书正文译文行文不同，为尊重作者，特不作统一。下同。)\r\n　　(一九四九年十月五日)\r\n　　署名海莲·汉芙(HeleneHanff)，还特地注明了“小姐”。\r\n　　其实，这位小姐此年已三十有三，是一位以写电视、舞台剧本为生的自由撰稿人。汉芙出生于制衣人家庭，父亲原本是位民谣说唱艺人，虽为生活所迫做起了手艺活，但夫妻俩仍喜欢带着女儿去逛戏院。汉芙十九岁时进费城大学读英文，但家境贫困，一年后辍学，求职谋生，后来得一戏剧写作奖项，便以写作糊口。对书的热爱来自于在纽约市立图书馆中的刻苦自学，特别得益于英国剑桥大学一位阿Q教授(SirArthurQuillerCouch)的著作。然而美国书价昂贵，汉芙热爱英国文学，便将买书的对象转向英伦三岛，偶然选中一家小书店写了信去，第一次订货便得到价廉物美的圆满服务，海峡这边，查令十字街84号Marks&Co.书店的主管，弗兰克·德尔先生，则是汉芙二十年通信的对象。\r\n　　虽然三十有余，汉芙却仍是轻松活泼，特别是简牍之上，更善于以轻松调侃的笔墨，信手写来，天马行空，不拘格式。德尔先生给她回的第一封信中称之为“女士”，汉芙第二封信尾便加了注脚，“我希望‘女士’在你们那边的含义与这边不一样”。德尔先生下封信中便乖乖称之为“小姐”了。第五封信后，汉芙已将信首的尊称“先生”或“阁下”改为直呼其名，信的内容也像是写给一位相识已久的老友，且不乏亲昵、撒娇之态：\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(2)\r\n---------------\r\n\r\n\r\n　　弗兰克·德尔，你在那儿究竟干什么？你什么都没干，你只是闲坐着！\r\n　　我的利·亨特在哪里？我的《牛津诗集》在哪里？……\r\n　　你把我冷落在这里，坐在图书馆中，在那些不属于我的书上写着长长的眉批，总有一天他们会发现，会把我的图书卡收走！\r\n　　我已经安排了复活节的小兔子给你们带去礼物，等它到达时，你可能已慵懒而死了。\r\n　　春天到来之际，我要一本情诗集，不要济慈或雪莱，请寄给我一本不太煽情的情诗集，你自己挑选吧，要一本小开本的，可以放入裤兜中带到中央公园去。\r\n　　行了，不要只坐在那里，快去帮我找书吧，真不明白你们书店是怎么维持的。\r\n　　(一九五○年三月二十五日)\r\n　　汉芙性情率真，人更是善良，通信之初，她便得知战后的英国经济困难，肉类、鸡蛋等食品都是限量供应，女人的长统丝袜更是奢侈品。一九四九年圣诞节她将一块重六磅的火腿寄往伦敦，让德尔先生分给书店中的同事们，以后美式食品源源不断几年。汉芙本身手头并不宽裕，她的慷慨大度让书店的工作人员把她视作亲人，纷纷与她通信，聊天。只是德尔先生从未在信中对汉芙的轻松笑语做任何回报，他是正人君子，地道的好丈夫，典型的英国绅士，惟一的报答是兢兢业业地为汉芙寻觅好书。直到一九五二年，德尔太太登场写信给汉芙道谢这几年的礼物，并向汉芙介绍说德尔先生已有二女，德尔先生才在汉芙的强烈要求之下，在答谢汉芙所寄给他妻女的长统袜时，将例来一贯的信头的“汉芙小姐”的称呼，改为“亲爱的海莲”，写信的日期恰与情人节巧合，不过想必当时德尔先生压根没有注意到。\r\n　　亲爱的海莲：\r\n　　我同意，现在写信给你，是该把“小姐”放弃的时候了。我并不如你想像的那样古板，只是因为我所写给你的信，都得在办公室的卷宗中存档，所以我觉得正式的称呼更合适，但这封信与书没有关系，是不会被存档的。\r\n　　……\r\n　　真不知该如何报答你这么多好礼物，我能说的只是，如果有一天你来伦敦，橡原巷37号会有一张床给你，你爱待多久便待多久。\r\n　　(一九五二年二月十四日)\r\n　　去家里作客的邀请一直没有兑现，汉芙几次犹豫要去英格兰圆梦，但终因手头拮据而放弃。倒是德尔先生紧接着寄去的那本沃尔顿的《五人传》着实让汉芙惊喜不已：\r\n　　噢，天哪，老天感谢你沃尔顿的《五人传》，这本书出版于一八四○年，百年之后还能这样完美，真是奇迹！如此漂亮，久经摩挲的粗裁本！我真同情他，这位曾于一八四一年在书的扉页上签名的戈登先生。他那一群不肖子孙呀！几乎不值分文地便把它卖给了你！真希望在他们出卖图书馆之前，我曾去那边赤脚跑过！\r\n　　(一九五二年三月三日)\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。六十年代末，汉芙颇为潦倒，写出去的剧本屡遭拒绝，书的选题也无人感兴趣。一九六九年一月，纽约冬天很冷，汉芙从图书馆回到家中，已近六点。她手上捧着一摞书，把从门房中取来的信件放在书上，走向电梯。在电梯中，她发现在那一大堆账单之间，有一封薄薄的蓝色的从Marks&Co.寄来的航空信封。这信有些异样，因为德尔先生所寄的信，信封上的地址都是单行距打成，而且向来是把她连名带姓拼全的，而这封信上，地址是双行距，她的名字是由一个字母H代替的。她只道是德尔离开了书店，没太在意，夜深人静捧杯独坐时，她才打开此信。这一夜，她再没有睡着。因为信中的消息，是德尔的死讯。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(3)\r\n---------------\r\n\r\n\r\n　　二\r\n　　桌上的黄酒已过三巡。言谈嬉笑，话语投机，共同喜爱的书与作家们一时让他们觉得很亲近。然而，手中抚弄着那樽精致的铜酒壶，眼光却不敢对视，他早已与另一位美丽的女子谈婚论嫁，书缘与情缘，在现实生活中原本是风马牛不相干的事。\r\n　　然而，浪漫向来是作家们难以割舍的情怀。书中自有颜如玉，红袖添香夜读书，虽然这都是男人们近乎梦想的宣言，但是，自古以来的好书，大多都是激情之作，没有情的文章和书，是太过干涩枯燥，没有人愿读的。于是，温润的花雕虽只逗出跳跃键盘上往返数次的几行短语，将消除键轻轻按下便了无痕迹，查令十字街84号却被好心的好事者演绎成一曲情感故事。\r\n　　泪尽之后，汉芙觉得体内像被掏空了一样，一片冰凉。应该做些什么，但是又能做什么呢？想起这二十年来的通信，几次搬家，这丝带束成的一小扎竟还静静地躺在抽屉的底部。仿佛是为了了却一桩心愿，汉芙将它们结成一集，送到出版商的手中，也许是她时来运转，也许是德尔在天亡灵的保佑，此书一经出版，便受欢迎，英国出版商亦决定在英国推出此书，并邀请汉芙前往英国，下榻于大英博物馆旁布鲁姆斯伯里区的一家古旧的老饭店。\r\n　　英国是汉芙魂牵梦系的地方，从一九五○年开始她便屡次想去，但都因无川资而未成。《查令十字街84号》的最后一封信，是她于一九六九年四月写给一位前往伦敦度假的朋友的，读来让许多英国人觉得鼻子酸酸的：\r\n　　亲爱的凯瑟琳：\r\n　　我在家中打扫卫生，整理书架。偷闲坐在地上，四面地毯上散放的都是书。希望你与布莱恩在伦敦玩得愉快。他在电话上对我说：“如果你有路费的话，是否想和我们同去？”我几乎哭了。\r\n　　我不知道，可能对我来说去或不去那儿已是无所谓了。我梦到那儿的次数太多了。我常常是为了看那些宽街窄巷才去看那些英国电影。记得许久以前，有个人对我说，那些去过英国的人，都能在那儿找到他最想要的东西。我告诉他我想去英国，是为了找英国文学。他说：“它们就在那儿。”\r\n　　或许在那儿，或许不在。看着四周地毯上散乱的书籍，我知道，它们肯定在这儿。\r\n　　那位卖给我这所有书的好人几个月前去世了，书店的主人也死了，但是书店还在那里。如果你正巧经过查令十字街84号，能否为我吻它？我欠它的实在太多了。\r\n　　(一九六九年四月十一日)\r\n　　其实此时，查令十字街84号已准备关门大吉，书店主人的后代无心经营旧书，一年后汉芙的书的畅销也未让书店起死回生。此时的伦敦，经过六十年代文化革命和摇滚乐的洗礼，已与五十年代完全不同。七十年代初，英国是激进先锋、朋克即将形成势力的年代，关心旧书旧文化的人实在太少了。汉芙一九七一年前往英国，一心一意醉心于寻找的是维多利亚时代的情怀，触目所见，根本不是心中的英国，这位战战兢兢小心翼翼的老姑娘此时肯定已极难让一般人亲近，无奈大英帝国也确实有一批为数不多的怀旧之人，他们虽已过时，但却仍有生息，大洋彼岸这位老姑娘对英国潦倒二手书店的无限热爱，对英国旧文化旧文学旧传统的一片痴情，对这些怀旧的人来说，是一帖温润滋补的药，把他们熏得晕晕乎乎，舒舒服服，而且这是一副中国式的汤药，头剂、二剂、三剂，喝了十几年还不舍得把药渣子扔掉，这是这本书话式的信集能在英国成为畅销书的原因了。\r\n　　一九七五年，汉芙家中所有的鞋盒子都被腾出来装了英国各地的书迷们寄来的信件，BBC决定把《查令十字街84号》搬上荧屏；六年之后，素有盛名的英国戏剧界决定把它改编为舞台剧，在伦敦最好的剧院上演三月不衰；再过六年，此书又被改编成电影，由著名演员安妮·班克劳夫特及安东尼·霍普金斯领衔主演，电影介绍中称，“这部片子旨在反映两种爱情，一是汉芙对书的激情之爱，二是她对德尔的精神之爱”，终于在书缘与情缘之间系了根红线。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(4)\r\n---------------\r\n\r\n\r\n　　三\r\n　　现实生活中没有的缘分只能靠文学作品去演绎，然而他们最终未曾见面，电影中也没能让他们见面，没有缘分就是没有缘分。\r\n　　霍普金斯演德尔是最贴切不过的了，《霍华德庄园》、《长日将尽》，他最适合演的就是那种正经而又有些压抑的英国绅士。\r\n　　那部电影太干了，幸亏我不是英国男人。\r\n　　没有缘分的也会有感情，所以，并不能说是电影做作。\r\n　　这样的感情最好还是藏在心底。\r\n　　但是做妻子的总是会有所察觉的，每个人都很敏感，无论这个人爱不爱书，读不读文学作品，生活中的许多东西远比书要重要。\r\n　　有的时候，我并不忌讳告诉你我曾经很嫉妒过你，因为弗兰克对你的信如此喜欢，你的信与他的幽默感又如此相同！而且，我也嫉妒过你的写作能力。我与弗兰克在各方面都恰恰相反，他友善、温和，而我的爱尔兰的血性使我总是与人争斗。我很思念他，以前的生活太有意思了。他总在向我解释，也不住地教我些有关书的知识……\r\n　　(一九六九年一月二十九日寄出)\r\n　　与查令十字街84号有关的书另外还有两本。汉芙一九七一年初访英伦时每一天都记有日记，出版成《布鲁姆斯伯里的女公爵》(TheDuchessofBloomsburyStreet)；一九八五年又出版自传《Q的遗产》(Q’sLegacy)，介绍她爱书的起始，与德尔通信以及书、电视剧、舞台剧出品的前后经过。《八十四号》中的那些信件，大都是信手写来，原本并不是为给别人看的，如果不是因德尔不幸早亡，它们可能永远不会面世，它们是率情之作，有些有趣的书话，信函来往间更能看出美国老小姐与英国拘谨绅士间的不同性情风格，读来流畅而有趣。《女公爵》虽是日记，但原本便是为读者而记的，虽说笔法仍流利，但总归有些矫作牵强。汉芙这么多年对英国魂牵梦萦，仿佛一位怀春女子二十年后才得见梦中情人，一时手足无措，不知该如何讨好对方才行，便乱了章法，信函中所有的那种风趣幽默全被吓跑了。而且汉芙对英国传统过于热爱，一叶障目，她所见的只是她自己头脑中的百余年前的英国，实在是遗憾。到了《Q》一书，《八十四号》早已经历了大江大海，若干年后回忆往事，汉芙反而能心如止水，返璞归真，不动声容了。\r\n　　海莲·汉芙，一九一六年四月十五日出生，一九九七年四月九日去世。终身未嫁。\r\n　　一九九八年十二月二十五日于北京方星园\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(1)\r\n---------------\r\n\r\n\r\n　　陈建铭\r\n　　一九四九年，E.B.怀特窝居在纽约市中城的一家小旅馆里。他坐在“窄得让人透不过气、又热又闷”的房间内挥汗写下脍炙人口的《纽约漫谈》(HereIsNewYork，此书曾被《纽约时报》评选为“有史以来关于纽约市最佳的十本书之一”)，里头这么说：“任何人都不该搬到纽约来住，除非他下定决心让自己好运临头。”当然，这位曾写出《夏洛的网》、《精灵鼠小弟》、《天鹅的喇叭》等经典童话故事和无数优美、隽永散文的作家，依旧继续抱持他一贯的知命乐天，徜徉在纽约自由、愉悦的文化天空下。\r\n　　但是与此同时，另一位在这个城市住了将近半辈子的穷作家却没有这份好运。她略乏才气却嗜读好书——货真价实的好书；她嫌这个城市没有气质，害她老是买不到想读的书(在电影版((电影84CharingCrossRoad于一九八七年出品，由英国导演戴维·休·琼斯执导，休·怀特摩编剧；安妮·班克劳夫特饰演海莲·汉芙，安东尼·霍普金斯饰演弗兰克·德尔，茱迪·丹奇饰演诺拉·德尔。片中每位演员的演出均十分传神；编、导的成绩亦相当不恶(得了一九八八年英国电影学院的最佳女主角奖并提名最佳改编剧本奖；一九八九年，美国编剧协会更因此颁奖给海莲·汉芙与怀特摩)。坊间某些录像带租售店或许仍可寻获年代公司的授权版，要特别留意的是：台译片名居然成了《迷阵血影》，而影片对白字幕亦惨不忍睹，简直到了令人坐立难安的地步。我翻译这本书，多少也想为它赎点儿罪罢。\r\n　　除了电影，一九七五年由马克·库龄汉导演、休·怀特摩编剧，此书曾在英国以电视单元剧的形式上演。上述电影即是根据这一个剧本拍成的。时至今日，欧美地区仍有剧团不时公演由此书改编的舞台剧。))的《查令十字街84号》里，饰演海莲·汉芙的女演员一上场就开骂了：“全纽约市没人读英国文学啦？”)，她只好转而向伦敦的一家小旧书店邮购那些“这年头没人要买的英国佬写的英文书”(引电影一开始，被汉芙索书不成的美国某书店经理的话)。于是，一桩原本单纯的买卖关系竟成就了长达二十年、多人参与的越洋友谊。\r\n　　我的眼界不若汉芙那般高且深，但同为住在另一个没有气质的城市里的爱书人，也偶叹好书难寻的挑剔读者，一开始被这本书吸引的，自然是关于“旧书”的部分，但是旋即引我动容的，则是关于书写——隔着距离的书写(当然还有阅读)如何承建伟大的心灵构筑工程。\r\n　　我一直以为：把手写的信件装入信封，填了地址、贴上邮票，旷日费时投递的书信具有无可磨灭的魔力——对寄件人、收信者双方皆然。其中的奥义便在于“距离”——或者该说是“等待”——等待对方的信件寄达；也等待自己的信件送达对方手中。这来往之间因延迟所造成的时间差，大抵只有天然酵母的发菌时间之微妙差可比拟。\r\n　　我始终不愿也不甘臣服于转瞬出现在对方屏幕上的电子邮件；自然更视ICQ(线上实时对谈)为畏途。拜传统邮政犹运作不辍之赐，我至今仍与老友、至亲维持着以手写、投递信函的老把戏，全然是因为我由衷相信：致力消弭空间、时间的距离纯属不智亦无益。就在那些自以为省下来的时、空缝隙里，美好的事物大量流失。我指的不仅仅是亲笔书写时遗下的手泽无法取代；更重要的是：一旦交流变得太有效率，不再需要翘首引颈、两两相望，某些情意也将因而迅速贬值而不被察觉。我喜欢因不能立即传达而必须沉静耐心，句句寻思、字字落笔的过程；亦珍惜读着对方的前一封信、想着几日后对方读信时的景状和情绪。老电影《街角商店》(TheShopAroundtheCorner1940)晚近被改拍成《电子情书》(YouveGotMail1998)((TheShopAroundtheCorner，由殿堂级的大师厄尼斯·刘别谦执导；詹姆斯·斯图尔特与玛格丽特·萨拉文扮演故事中那两位每日错身却不对盘、频生龃龉而又各自暗自仰慕未谋面笔友的百货行员工。多年前我曾在专于夜间播映老电影的TNT频道上观赏过，至今难忘。\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(2)\r\n---------------\r\n\r\n\r\n　　改编后的《电子情书》仍依稀能看出向前作致意的影子，如：女主角匿名通信时的代号为“shopgirl”；而她经营的童书店店名赫然就是“ShopAroundTheCorner”！另外，我也怀疑，她迷恋《傲慢与偏见》一节的神来之笔，则俨然得自《查令十字街84号》。))，两者之间出现不少有趣的辩证关系，读者们不妨自行参酌。\r\n　　我自私地以为这才是《查令十字街84号》全书的题旨所系。\r\n　　从没“好运临头”的汉芙小姐，年届晚年终于有了一个仅有的机会，抱着酝酿二十载的怀想，坐在机舱里(这里也是引自电影)奔赴另一座魂牵梦萦的城市。邻座一名男士问她：“这是你头一回去伦敦？”接着他说：“听我奉劝：别相信出租车司机，明明三条街外的目的地，他会载你兜上五里路；还有，别白费力气读地图了，在伦敦没有人可以找得到路，即使是伦敦人也不例外。”不过，“你一定会爱上她的，伦敦实在太棒了。”\r\n　　当我伫立在伦敦街头，我实在也无法打心底恭维这座城市——街道窄仄、杂芜；气候湿冷、灰暗；市容脏乱、交通拥挤——比起台北不遑多让。但我也终究难免渐渐地——从查令十字街开始——爱上了这座城市(拜汉芙之赐，“查令十字街84号”这个门牌号码几乎快与伦敦市的另一个地址“贝克街221号B座”齐名了)。甚至笃定相信她也能让我事后——隔着距离——对她怀想、惦念个二十年不成问题。\r\n　　将这本书中译，想必可以聊偿许多爱书人多年以来的期盼。我知道：所有读过84CharingCrossRoad的爱书同好——就像我自己一样——总将这本小书珍藏在身边，屡屡重读，让汉芙的珠玑妙语和古道热肠不时温暖自己被冷硬现实尘覆的凡心；而我相信：中文世界之所以长年不见此书问世，一定是所有珍爱此书的人——也像我自己一样——不忍丝毫更动书中的每一句话、每一个字。\r\n　　多年前的某一个下午，在曾经任职的古书店里，我和钟芳玲聊起这本书真该有个中文版。对于这个工作，她自然是当仁不让，而且以她作为此书的头号死忠书迷，加上她与汉芙本人的私交，我也十分赞成她是担任中译者的不二人选，如今我却因苦等不及而掠占了她原先的任务。我相信芳玲也是因为顾及前述的原因，宁可维护汉芙的原貌而迟迟不谋此图。于是，我在翻译的过程中虽然尽可能地保留原书的滋味，但我仍须在此报告：我刻意做了极小的更动。除了让它更能适应中文环境外；我私下盼望这个须臾的“失真”也能转而成为让中文版的读者们动心发愿去读“货真价实的”汉芙原文的伏笔。\r\n　　这是我第一部翻译作品。倘使这个中译版本侥幸能稍稍不使原文蒙羞，则首先必须在此感谢小威和建兴，铭谢他们夫妻俩前仆后继，在追索此书版权归属的过程中上穷碧落下黄泉、锲而不舍；还有推广实体书店文化不遗余力的钟芳玲，他们才是承继汉芙精神的真正后人，也是让这本书能有机会进驻更多人心的幕后功臣。\r\n　　此外，代汉芙对唐诺首肯为中文版赐序((台湾版原唐诺序见附录。——编注))表示感激。我自己常为了读唐诺的序文而看了一堆原先没打算看的书，不过这并不全然是我对这个译本打的如意算盘，而是我晓得查令十字街是他每回在伦敦磨蹭得最久的一条街；何况，若汉芙仍在人间，一定也会找他写的。\r\n\r\n\r\n\r\n***************\r\n*查令十字街84号\r\n***************\r\n\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。						\r\n\r\n---------------\r\n1949年10月5日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年10月5日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。\r\n　　随信附上一份清单，上面列出我目前最想读而又遍寻不着的书。如果贵店有符合该书单所列，而每本又不高于五美元的话，可否径将此函视为订购单，并将书寄给我？\r\n　　你忠实的\r\n　　海莲·汉芙(小姐)\r\n\r\n\r\n\r\n---------------\r\n1949年10月25日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年10月25日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的夫人：\r\n　　谨在此回复您于本月五日的来函。敝店很荣幸能为您解除三分之二的困扰。您所列出的三种哈兹里特散文，均收录于这本典范出版社的《哈兹里特散文选》内；斯蒂文森的作品则在《致少女少男》中可以找到。我们挑出两本品相较好的书为您寄上，相信不久后即可送达您的手中，祈盼您会满意。随书附上发票，请查收。\r\n　　至于您提及的利·亨特的散文，目前颇不易得见，不过我们会留意是否能找到收罗齐全且装帧精良的版本，届时将再为您寄上。而您所描述的拉丁文圣经，目前敝店并无存书，仅有晚近出版、布面精装普通版的拉丁文和希腊文《新约全书》，不知您是否有兴趣？\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月3日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年11月3日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　今天收到你们寄来的书，斯蒂文森的书真是漂亮！把它放进我用水果箱权充的书架里，实在太委屈它。我捧着它，深怕污损它那细致的皮装封面和米黄色的厚实内页。看惯了那些用惨白纸张和硬纸板大量印制的美国书，我简直不晓得一本书竟也能这么迷人，光抚摸着就教人打心里头舒服。\r\n　　住在楼上的女孩儿凯特，她的英国男朋友布莱恩帮我将账单上列的书价一英镑十七先令六便士换算成美金五元三角，希望他没算错。我寄了五元和一元的钞票各一张，多出来的七角请用来支付《新约全书》，那两本我都要买。\r\n　　你们可否行行好？下回先将书价换算成美金。我连加减美金都一塌糊涂了，要我把英镑换算成美金真是阿弥陀佛。\r\n　　海莲·汉芙\r\n　　我希望在你们那边，“夫人”的意思和我们这边指的是两码事。\r\n\r\n\r\n\r\n---------------\r\n1949年11月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您寄来的六元书款已悉数收到，不过我们建议您不妨改以邮政划拨的方式付款，如此不但对你我双方都较为便利；亦比直接将钞票放入信封内要保险得多。\r\n　　我们非常高兴得知您如此喜欢那本斯蒂文森的书。两本《新约全书》已于今日付邮，账单亦一并附上，同时依照您的嘱咐，将书款分别以英镑与美金计价。我们期盼您也会喜欢此次寄去的两本书。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月18日\r\n---------------\r\n\r\n\r\n　　东九十五大街14号\r\n　　1949年11月18日\r\n　　这算哪门子的新约圣经啊！\r\n　　好心替我转告英国圣公会诸公，他们平白糟蹋了有史以来最优美的文字。是哪个家伙出馊主意把通俗拉丁文圣经整成这副德性？他们准会把他来活活烧死，不信记住我说的话。\r\n　　其实我犯不着火冒三丈，我本身是犹太人。不过我的嫂子是天主教徒；弟媳是卫理公会的；还有一票皈依长老教派的表亲(全是被我的亚伯拉罕叔公拉去改宗的)；还有一个到处宣扬基督信仰疗法的姑妈。他们要是知道有这么一本英国人搞出来的不三不四的拉丁文圣经，个个不暴跳如雷才怪！(话说回来，他们搞不好根本不晓得现在还有拉丁文哩。)\r\n　　哼，去他的！我手边还有一本从我的拉丁文老师那儿借来的圣经，暂且先不还他就是了，等你们找一本卖给我再说。\r\n　　寄去四元支付我欠你们的三元八角八分，你就拿多出来的一角二去买杯咖啡喝吧！我住的地方附近没有邮局，我才不要为了汇三元八角八分，大老远跑到洛克菲勒广场去大摆长龙呢。何况，如果要等到我哪天有空顺道去办事，口袋里的三元八角早就被我花得一毛不剩了。我对美国邮政和皇家邮政有十足的信心。\r\n　　你们有兰多的《假想对话录》吗？我想全套应该不止一本，我想读的是“希腊对话录”，如果里面有伊索和萝多彼的对话，就是那一本没错。\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年11月26日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月26日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您的书款已安全寄达，我们会将多出的一角二分计入您在敝店的专属账户中。\r\n　　很凑巧，敝店正好有收录“希腊对话录”的《沃尔特·萨维奇·兰多作品暨传记全集》中的第二卷，“罗马对话录”亦收录其中。本书由于是一八七六年出版的旧版本，并不是非常漂亮，但装订完好，书亦称干净。我们今日会将书与账单一并为您寄上。\r\n　　我在此为那本让您深感不满的拉丁文圣经向您致歉，我们将重新为您找一本正宗《通俗拉丁文圣经》；利·亨特的书仍持续密切留意中。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年12月8日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月8日\r\n　　敬启者：\r\n　　(老用“诸位”着实不智，我察觉到显然从头到尾都是同一个人为我服务。)\r\n　　萨维奇·兰多的书今天寄达，我迫不及待立即翻开“罗马对话录”——两座城市刚毁于兵燹战火，涂炭生灵被钉在十字架上，苦苦哀求列队行过的罗马士兵，干脆一戟刺死他们，好尽早结束这无止尽的折磨……再翻到“希腊对话录”，情境有了一百八十度的转换：读着伊索和萝多彼的娓娓对谈，这里惟一的忧虑只是怕饿着了肚子……我着实喜爱被前人翻读过无数回的旧书。上次《哈兹里特散文选》寄达时，一翻开就看到扉页上写着“我厌恶读新书”，我不禁对这位未曾谋面的前任书主肃然高呼：“同志！”\r\n　　随函附上一元，布莱恩(楼上女孩凯特的英国男朋友)说这够付我欠你的八先令，你忘了换算了。\r\n　　言归正传，布莱恩告诉我：你们每一户每个星期才配给到两盎司肉；而每个人每个月只分得一只鸡蛋！我一听简直吓坏了。他拿出一本目录给我看，这是一家设籍在美国的英国公司，专门代人从丹麦寄送补给物资到英国。所以我会寄给马克斯与科恩书店一份小小的圣诞礼物，希望数量足够让你们大家都能分得一些，因为布莱恩跟我说：查令十字街上的书店全都“小得很”。\r\n　　我会在包裹上注明由你——FPD——代转，天晓得你叫啥。\r\n　　祝佳节愉快\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月9日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月9日\r\n　　FPD！糟了！\r\n　　我刚把包裹寄走，里面主寄的是一条六磅重的火腿，我想你们应该可以自行拿去给肉贩，请他切片后再分给大家。\r\n　　不过我刚刚才发现你们寄来的账单上头印着“B.马克斯、M.科恩”。\r\n　　他们是犹太人吗？我该火速补寄点儿牛舌吗？\r\n　　快通知我该怎么办！\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年12月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您的礼品包裹于今日平安抵达，并已均分给大家。而马克斯先生和科恩先生则坚持只由员工均分即可，不用关照“老板”。再者，我想让您知道，您所寄来的物品，我们不是久未看到，就是只能偶尔在黑市匆匆一瞥。您能这样子顾虑我们，实在是太亲切也太慷慨了，我们都深怀感激。\r\n　　我们要在此表达对您的感谢，并祈祝您未来一年一切顺心。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年3月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年3月25日\r\n　　弗兰克·德尔！你在干吗？我啥也没收到！你该不是在打混吧？\r\n　　利·亨特呢？《牛津英语诗选》呢？《通俗拉丁文圣经》和书呆子约翰·亨利的书呢？我好整以暇，等着这些书来陪我过大斋节，结果你连个影儿也没寄来！\r\n　　你害我只好枯坐在家里，把密密麻麻的注记写在图书馆的书上。哪天要是让他们发现了，包准吊销我的借书证。\r\n　　我已经叫复活节兔子给你捎个“蛋”，希望它到达时不会看到你已经慵懒而死了！\r\n　　春意渐浓，我想读点儿情诗。别给我寄济慈或雪莱！我要那种款款深情而不是口沫横飞的。怀亚特还是琼森或谁的，该寄什么给我，你自己动点儿脑筋！最好是小小一本，可以让我轻松塞进口袋里，带到中央公园去读。\r\n　　行啦！别老坐着，快去把它找出来！真搞不懂你们是怎么做生意的！\r\n\r\n\r\n\r\n---------------\r\n1950年4月7日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　感谢您寄来的复活节礼物，包裹已于昨日平安寄达。看到这些罐头和那一盒生鸡蛋，大家都十分开心，全体同仁与我在此感激您对我们的亲切与慷慨。\r\n　　非常抱歉我们一直没能寄上您想要的书。关于您所提到的情诗集，敝店偶尔会收购到一些，可惜目前店内没有存书，但会竭力为您搜寻。\r\n　　再次感谢您寄来的礼物包裹。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　亲爱的汉芙小姐：\r\n　　请不要让弗兰克知道我写信给您。每回寄账单给您时，我都好想偷偷塞一张短笺到信封里。不过弗兰克一定会认为，以我的职务这么做并不适当。您听到我这么说，大概会以为他是个老古板吧？其实他是一个好得不能再好的人。只是每次您寄到书店的信或包裹都以他为收信人，而且他也将回信给您视为他的分内职责。不过我倒是一直很想自己给您写信。\r\n　　我们都好喜欢读您的来信，大伙儿也常凑在一块儿揣摩您的模样儿。我坚信您一定是一位年轻、有教养且长相聪慧的人；而老马丁先生竟无视您流露出来的绝顶幽默，硬是把您想成一个学究型的人。您愿意寄一张您的照片给我们吗？我们都很想瞧瞧呢！\r\n　　如果您也对弗兰克感到好奇的话，我偷偷告诉您：他年近四十，长得很帅，娶了一位漂亮的爱尔兰姑娘——好像是他的第二任太太。\r\n　　大家对您寄来的包裹都万分感激。我家那两个小家伙(女孩五岁、男孩四岁)简直乐翻了，因为有了您寄来的葡萄干和鸡蛋，我就可以为他们烤个蛋糕了！\r\n　　希望您不介意我私下写信给您，也请不要告诉弗兰克哟\r\n　　诚心祝福您\r\n　　塞西莉·法尔\r\n　　P.S.我会将家里的地址写在信封背后，万一您想到有什么我可以从这儿寄给您的，可以写信告诉我。\r\n\r\n\r\n\r\n---------------\r\n1950年4月10日\r\n---------------\r\n\r\n\r\n　　C.F.\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年4月10日\r\n　　亲爱的塞西莉：\r\n　　真是让老马丁先生大失所望了，请转告他：我非但一丁点儿学问都没有，连大学也没上过哩！我只不过碰巧喜欢看书罢了。说起来还得感谢一位剑桥的学者奎勒—库奇(一般都称他为Q)，是他让我在十七岁那一年一头栽进书堆里，从此不可自拔。至于我的长相，大概就跟百老汇街上的叫化子一样“聪慧”吧！\r\n　　我住在一幢白蚁丛生、摇摇欲坠、白天不供应暖气的老公寓里。整幢五层楼的其他住户早上九点出门，不到晚上六点不会回来，房东认为他犯不着为了一个窝在家里摇笔杆的小作家，而整天开着暖气。\r\n　　可怜的弗兰克，真是难为他了，我老是对他颐指气使的。我只是在打趣，不过就知道他会当真。我一直想要戳穿他那英国式的矜持。要是哪天他得了胃溃疡，都是我害的。\r\n　　请多来信告诉我关于伦敦的一切。我幻想着那一天快点到来——我步下轮船、火车，踩上布着尘灰的人行道……我要走遍柏克莱广场，逛尽温柏街；我要置身在约翰·多恩布道的圣保罗大教堂；我要趺坐在伊丽莎白拒不阶下为囚的伦敦塔前台阶上……我有一位战时派驻在伦敦的记者朋友，他曾经对我说：游客往往带着先入之见，所以他们总能在英国瞧见他们原先想看的。我告诉他，我到英国是为了探寻英国文学。而他这么告诉我：\r\n　　“去那儿准没错。”\r\n　　祝一切安好\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1950年9月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年9月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　自前封信以来，许久未向您报告，盼您不致认为我们因弛废店务而忘却了您交代我们该找的书。\r\n　　言归正传，《牛津英语诗选》新近到库，此书内页以印度纸印制，原版的蓝布精装，出版于一九○五年，扉页有前人签记，算是一本书况不错的二手书。标价两美元，我们认为在径自寄给您之前，应先向您略述此书的状况，以免您在这段时间内已另行购得一册。\r\n　　许久以前，您曾垂询纽曼的《大学论》一书的下落。您是否钟意首版书？最近我们收购到一册，谨描述如下：\r\n　　纽曼(约翰·亨利，神学博士)：《大学教育之目的及其本质——应都柏林天主教会之邀所作的演说之讲义稿》。首版，八开，小牛皮装帧。一八五二年于都柏林出版，若干页面稍有渍斑但装帧完好。\r\n　　价格：美金六元\r\n　　为了避免让别人捷足先登，这两本书我们会先为您保留。静候您的回复。\r\n　　祝身体健康\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年9月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年9月25日\r\n　　(他手上有只卖六美元的首版《大学论》，竟还问我要不要买！真不晓得该说他老实呢，还是憨？)\r\n　　亲爱的弗兰克：\r\n　　是的！我要！我真是快受不了我自己了，本来我并不特别讲究首版不首版的，可是，“那本书”的首版……！\r\n　　哗——我真迫不及待想看到它。\r\n　　也请把《牛津诗选》一并寄来。下回可别再纳闷我有没有跟别人买书了。既然我可以寸步不离书桌，就能向你们买到既干净又漂亮的书，我干吗跑到十七大街去买那些又脏又丑的？从我坐着的地方，伦敦可近得太多啦。\r\n　　附上“千万不能掉了”的八元钞票。我对你提过布莱恩正在打官司没？他向伦敦的一家理工专门书店订了一大套特贵的物理学书。他可不像我，既邋遢又老是漫不经心，他特地到洛克菲勒广场，乖乖地排队等着划拨那一大笔书款，该办的事一件也没漏。他精得很，聪明人自有聪明人按部就班的做法。\r\n　　结果你猜怎么着？那笔钱不知道给汇到哪儿去啦！\r\n　　皇家邮政，加把劲儿！\r\n　　HH\r\n　　为了庆祝我的第一本首版书，加上海外邮购公司终于给了我一本目录，我决定要寄一个小小的包裹送你们。\r\n\r\n\r\n\r\n---------------\r\n1950年10月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年10月2日\r\n　　亲爱的海莲：\r\n　　这些照片我带到店里好几个礼拜了，不过我们这阵子真是忙得昏天暗地，所以一直找不到空当寄给你看。这些都是我和道格(我的先生)在诺福克拍的，那儿是他所属的皇家空军驻地。里头我没有一张拍得漂亮的，不过这是我所能找到最好的了，孩子们和道格那几张倒都还不错。\r\n　　亲爱的海莲，我好盼望你真的能如愿到英国来，你何不省点儿买书钱，好让你能在明年夏天成行呢？我的爸爸妈妈在米德尔塞克斯有个房子，我们会很高兴接你来住的。\r\n　　梅甘·韦尔斯(老板的秘书)和我打算明年七月一起去泽西岛(在海峡群岛之中)度假一个礼拜，你可以来和我们一块儿玩，反正回到米德尔塞克斯不须花用你太多开销。\r\n　　本·马克斯先生在瞄我写些什么了，就此停笔。\r\n　　塞西莉谨上\r\n\r\n\r\n\r\n---------------\r\n1950年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年10月15日\r\n　　真是的！！！\r\n　　不是我爱唠叨，弗兰克·德尔！看到书店竟忍心把这么美的古书五马分尸，拿内页充当包装纸、填箱料，我真是觉得世道中落、万劫不复了。我向被包在里头的约翰·亨利告状：“主教阁下，斯文如此扫地，君岂信乎哉？”\r\n　　他说他也实在百思不得其解。更可恶的是你把书拆散了，随便抓来几页顺手就包，害我根本搞不清楚上头到底是在打哪一仗哪一役。\r\n　　这本书大约一个星期前寄达，现在气也慢慢消了。我把它端端正正地摆在案前，整天陪着我。我不时停下打字，伸手过去，无限爱怜地抚摸它。倒不全然因为这是首版书，主要是打出生起我从没见过这么标致的书。拥有这样的书，竟让我油然而生莫名的罪恶感。它那光可鉴人的皮装封面，古雅的烫金书名，秀丽的印刷铅字，它实在应该置身于英国乡间的一幢木造宅邸；由一位优雅的老绅士坐在炉火前的皮制摇椅里，慢条斯理地轻轻展读……而不该委身在一间寒酸破公寓里，让我坐在蹩脚旧沙发上翻阅。\r\n　　我要买那本Q的文集，可是忘了多少钱，我把你的上一封信搞丢了。好像是两块钱吧？附上两张一元钞票，若是不够就来信告诉我。\r\n　　下回要寄书来时，拿第五一二页和五一三页来包书怎么样？这样我才晓得最后哪一边打赢了，还有那到底是哪一场战役。\r\n　　HH\r\n　　P.S.你们那儿可有《佩皮斯日记》？我需要它来伴我度过漫漫冬夜。\r\n\r\n\r\n\r\n---------------\r\n1950年11月1日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年11月1日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您致歉，迟至今日才回信给您。我因公到外地出差了一个多星期，一回到办公室就被许多待办的事务耽搁，现在才有空提笔给您回信。\r\n　　首先，请您完全无须为我们拿旧书内页当包装纸用而感到忧心，那只是全套《英国叛乱和内战史》之中装订破散且多出来的一册复本，我想应该是无法当成商品卖给任何人的。\r\n　　奎勒—库奇的文集《朝圣之路》，已付邮寄给您。您的待付款项为一元八十五分，所以您新近寄来的两元用来支付该书仍绰绰有余。另，《佩皮斯日记》目前店内暂无存书，将为您留意。\r\n　　祝万事如意\r\n　　马克斯与科恩书店\r\n　　F.德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年2月2日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　很高兴听到您喜欢那本奎勒—库奇的书。《牛津英语散文选》此刻并无库存，我们将会为您留意。\r\n　　至于《罗杰·德·科弗利爵士正传》，我们手头上恰好有一本十八世纪的文集，除了包含该书中不少篇章之外，亦收入切斯特菲尔德与哥尔德斯密斯的文章。此书由奥斯汀·多布森精心编选，敝店仅标价一元十五分。我们已将书寄去给您。如果您想要更为完整的艾迪生与斯梯尔文集的话，请通知我，我将尽力为您寻找。\r\n　　敝店若不包括马克斯先生与科恩先生，共有职员六人。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月20日\r\n---------------\r\n\r\n\r\n　　米德尔塞克斯品内东厢\r\n　　1951年2月20日\r\n　　亲爱的海莲：\r\n　　做法不只一种，妈妈和我一致认为下面这个方法对你而言应该是最简单的：准备面粉一杯、鸡蛋一只、鲜奶半杯，撒入少许盐，在一个海碗里充分搅拌，直到变成浓稠的奶油状。搁进冰箱里摆几个钟头(所以一大早开始做，时间最刚好不过)。当你要把肉放进炉子时，挪个位子摆一个铁盘让它预热。在肉烤好前一个半钟头，浇一点儿肉汁在铁盘上，不用太多，浅浅的够铺满铁盘即可。记住！铁盘得烘烤得“非常热”才行。接着，把你先前准备好的布丁料全拨到铁盘上头，然后再放着继续烤。这样一来，肉和布丁就可以同时出炉上桌啦！\r\n　　对于从没见过的人，我实在不知道该怎么恰当地形容它。总之，一个大功告成的约克郡布丁应该会蓬松得高高的，表皮焦脆，当你切开它时，会发现里头其实是空心的。\r\n　　道格仍然随着皇家空军驻扎在诺福克。你寄来的圣诞节罐头，我们严格地实施管制囤积，以等他休假回家时再全家一起享用。亲爱的海莲，你一定不难想见，届时我们将会有个多么棒的庆祝餐会啊！不过，你实在不该这样子为我们破费的！\r\n　　该赶快把信寄出去了，这样你才来得及在布莱恩的生日晚宴上推出这道菜，成果如何一定要写信告诉我哟！\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年2月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年2月25日\r\n　　亲爱的塞西莉：\r\n　　约克郡布丁简直太棒了！因为我们这儿从没人见识过这玩意儿，我后来只好向别人形容成“一笼高高鼓起、松软细致、入口即化的特大号烤饼”！\r\n　　请别为我寄去的那些食品操心。我自己也觉得不可思议，那家海外邮购公司也不晓得是不是非营利机构，抑或是商品可以免税什么的吧？总之，他们的东西都便宜得很，我自己买的那只火鸡都还比寄给你们的那一大箱圣诞包裹贵哩。他们的确有一些价格比较高的商品，比如大块的烤肋排，或是一整只羊腿之类的。不过，即使是那些东西，也比跟这儿的肉贩买要便宜许多，如果真得那样，把我剁了也没法子寄东西给你们。现在正浏览着目录，我把它摊在地毯上，琢磨着两个旗鼓相当的商品组合：编号105的包裹(内含鸡蛋一打外加甜面饼一箱)和编号217B的包裹(内含鸡蛋两打、没有甜面饼)，我实在不甘心寄一打装的鸡蛋，让你们每人各分得两只能干吗？不过布莱恩跟我说，粉末干燥蛋吃起来味同嚼蜡，还真伤脑筋。\r\n　　有一位制作人刚打电话给我，说他蛮喜欢我写的剧本(还没喜欢到要把它搬上舞台的程度)。他正打算制作一出电视剧集，问我是否有兴趣编电视剧本，他漫不经心地说：“一集给两张！”搞了半天才弄明白他的意思是：每一集的稿费两百元。我原先为剧团修改剧本，一周的酬劳也才不过四十元！明天要去和他详谈，快祝我好运吧！\r\n　　祝福你\r\n　　海莲\r\n\r\n\r\n\r\n---------------\r\n1951年4月4日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月4日\r\n　　海莲亲爱的：\r\n　　你寄来的复活节包裹已经收到，这些礼物实在太棒了！不过，因为弗兰克第二天一早就出差去了，所以他没空回信向你道谢，大家都急得跳脚，而其他人全都不敢斗胆写信给“弗兰克的汉芙小姐”。\r\n　　包裹里的肉食实在太棒！我认为你真的不该再这样子为我们破费了，一定花了你不少钱吧？愿上帝保佑你的好心肠。\r\n　　本·马克斯先生走过来要找事给我做了，就此搁笔。\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　伦敦西八区肯辛顿高地街伯爵社区\r\n　　1951年4月5日\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您寄到马克斯与科恩书店的复活节礼物，已于几天前寄达。但弗兰克·德尔先生因公外出，错过与大家分享喜悦了。\r\n　　一见到包裹里头的肉，所有人的眼睛都看直了，而鸡蛋也大获欢迎。我觉得有必要给您写个信，向您报告：所有同仁对您的好意和慷慨都万分感激。\r\n　　我们所有同仁都期盼您能尽快来英国，届时我们一定会竭尽心力，让您有一趟愉快的英伦之旅。\r\n　　梅甘·韦尔斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　埃塞克斯滨海南庄桶桥路\r\n　　1951年4月5日\r\n　　敬爱的汉芙小姐：\r\n　　我是马克斯与科恩书店的编目员，已在书店任职即将届满两年。谨在此向您表达感激之意。谢谢您多次寄赠礼物包裹给我们。\r\n　　我现在与七十五岁的姨婆住在一起。当我带着您送的肉、牛舌罐头回到家里，我想如果您能在场目睹姨婆脸上惊喜万状的表情，您大概就不难想见我们满溢的感激之情了。知道远方有人竟能为从未谋面的一群人付出这么多关怀和慷慨，我的内心实在备感温暖。而我相信，所有同仁必定与我深有同感。\r\n　　如果您想到有什么事我可以代劳，或是从伦敦寄点儿什么给您的，请务必交代我去办，我将会引以为荣。\r\n　　比尔·汉弗莱斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　我猜您大概已经开始担心，我们竟然这么久都没写信谢谢您寄来的包裹，心里头一定正在嘀咕：真是一群不知好歹的家伙。实际上，我刚离开伦敦到乡间跑了一大圈，到处拜访私人宅第，搜寻待售的藏书，努力补充店里捉襟见肘的库存。我太太已经开始把我当成房客来招呼了——我总是只回家睡觉，一吃完早餐又不见人影。不过，当我带着您送的肉(鸡蛋、火腿就更不用说了)回到家里，她就会觉得我毕竟也非一无是处。当然，所有的不开心也就随之烟消云散。说实在的，我们已经太久没能见到一块完整的肉了。\r\n　　我们总得想点儿法子，表达我们对您的感激。于是，我们将另行寄上一本小书，希望您会喜欢它。我还记得您曾经想买一本情诗集，这是我所能找到尽可能符合您的要求的了。全体同仁为您献上此书，盼您笑纳。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　(附在《伊丽莎白时期情诗选》里的卡片：)\r\n　　谨以此书赠予\r\n　　海莲·汉芙\r\n　　并为其诸多美善情谊\r\n　　致上最诚挚的祝福\r\n　　与无尽的感激\r\n　　伦敦查令十字街84号\r\n　　全体员工一同\r\n　　一九五一年四月\r\n\r\n\r\n\r\n---------------\r\n1951年4月16日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年4月16日\r\n　　此致伦敦查令十字街84号全体同仁：\r\n　　谢谢你们送我这本书。我从没拥有过这么一本三边的页缘都上金的书。你们知道吗？我竟在生日当天收到这本书！\r\n　　你们另外写了一张卡片，而不直接题签在扉页上，我真希望你们不要这样过分拘谨。如果我猜得没错，这一定是你们的“书商本性”作祟使然吧，你们担心一旦写了字在书上，将会折损了它的价值。差矣，你们如果真能这么做，不仅对我而言，对未来的书主，都增添了无可估算的价值。我喜欢扉页上有题签、页边写满注记的旧书；我爱极了那种与心有灵犀的前人冥冥共读，时而戚戚于胸、时而被耳提面命的感觉。\r\n　　还有，为什么大家都不签上名字呢？我猜一定是弗兰克不准你们签的，他大概怕我会撇下他，一一给你们大家写情书吧！\r\n　　隔着汪洋，我在美国此端遥寄我对你们的祝福——“美国”，好一个“坚定的盟邦”！当她一掷千金帮日本、德国从败仗中“复苏”，却眼睁睁看着英国同胞饱受饥馑之苦！皇天为证，总有一天我要亲自去英国，当面为她向你们道歉。(等我回国后，我会叫她加倍向我赔罪！)\r\n　　再次感谢你们送我这本美丽的书，我一定会格外小心，免得让它溅到酒滴、沾了烟灰。这份礼物对我这种人来说实在太隆重了。\r\n　　海莲·汉芙上\r\n\r\n\r\n\r\n---------------\r\n1951年9月10日\r\n---------------\r\n\r\n\r\n　　(玛克辛写于后台)\r\n　　伦敦\r\n　　1951年9月10日\r\n　　甜心儿：\r\n　　这是一间活脱从狄更斯书里头蹦出来的可爱铺子，如果让你见到了，不爱死了才怪。\r\n　　店门口陈列了几架书，开门进去前，我先站在外头假装随意翻阅几本书，好让自己看起来像是若无其事地逛书店。一走进店内，喧嚣全被关在门外。一阵古书的陈旧气味扑鼻而来。我实在不知道该怎么形容：那是一种混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……店内左手边有张书桌，坐着一位年约五十、长着一只贺加斯式鼻子的男士。他站起身来，操着北方口音对我说：“日安。”我回答说我只是随意逛逛，而他则有礼地说：“请。”\r\n　　极目所见全是书架——高耸直抵到天花板的深色的古老书架，橡木架面经过漫长岁月的洗礼，虽已褪色仍径放光芒。接着是摆放画片的专区——应该说：一张叠放着许多画片的大桌台。上头有克鲁克香克、拉克姆矠、斯派和许许多多我叫不出名字的英国插画家的美丽画作；另一边还放着几叠迷人的古旧画刊。\r\n　　我在店内待了约莫半个钟头光景，期待着你的弗兰克或是哪个女孩儿翩翩现身。不过，因为我到达时已过了一点钟，我猜他们全都外出用餐去了，而我又不能待太久。\r\n　　就是这样咯！新戏的预告并没有造成万人空巷，不过据说对方人蛮好的，排给我们几个月的档期，所以我昨天出门找出租公寓，在骑士桥附近有一间小小的、蛮不错的小套房。现在还没确定，一旦定下来，我会写信告诉你，你也可以再打电话问我妈。\r\n　　三餐不成问题，我们都在餐厅或旅馆里用餐，像最高级的克拉里兹大饭店就能充分供应烤牛肉、排骨。价钱虽然贵得离谱，不过换算成美金倒是挺划算的，所以我们还吃得起。假使换成我是英国人，瞧见这光景一定会恨得牙痒痒的。但是他们却反而对我们好得不得了，到处都有人邀请我们去家里作客或上馆子。\r\n　　惟一短缺的东西就是糖。凡是甜的东西，一应俱缺。也许我反该谢天谢地，正好让我在这里瘦它个几磅。\r\n　　写信给我。\r\n　　爱你的\r\n　　玛克辛\r\n\r\n\r\n\r\n---------------\r\n1951年9月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年9月15日\r\n　　玛克辛：真多亏了你的慧心巧手，书店简直被你给写活了——你的文笔实在比我好得太多啦！\r\n　　我刚打了电话给你妈妈要你的住址，她要我转告你：方糖和巧克力棒依照你的交代，已经给你寄去了。你不是还跟我说你要趁机减肥的吗？\r\n　　我不想让你以为我是酸葡萄，不过我实在不明白，你究竟是何德何能？老天竟任由你饱览遍逛“我的书店”；而我为什么就只得乖乖蹲在九十五大街的破公寓里，埋头写着这劳什子《埃勒里·奎因的冒险》电视剧集脚本！“我不是告诉过你，不能安排一截沾着口红的烟蒂当做破案线索吗？”“我们这个节目是由百优雪茄公司赞助的，千万别给我编出“香烟”这个台词儿。”……就连在场景里安排一只道具烟灰缸也不许出现烟屁股；也不能摆雪茄屁股——厂商嫌不好看——所以，只要剧情出现烟灰缸，里头全好端端地搁着一管全新的、未拆封的百优雪茄！\r\n　　简直岂有此理！你却还能跟约翰·吉尔古德坐在克拉里兹的酒吧里打情骂俏！\r\n　　来信时多写些伦敦的事物——地铁、巷弄胡同、古宅大院……随便什么都好，写仔细点儿。告诉我骑士桥长什么模样，此刻我的耳畔似乎响起了科茨的《伦敦组曲》……听起来是那么绿意盎然、雄壮典雅。\r\n　　xxxx\r\n　　hh\r\n\r\n\r\n\r\n---------------\r\n1951年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年10月15日\r\n　　这哪是佩皮斯日记呢？你倒是给我交代清楚！\r\n　　这本书根本不够资格称之为《佩皮斯日记》，这只是哪个没事找事做的半吊子编辑，从佩皮斯日记里东挖西补、断章取义，存心让他死不瞑目！\r\n　　真想啐它一口！\r\n　　一六六八年一月十二日的日记跑到哪儿去了？记着他的老婆把他踹下床，抄了根烫红的拨火棍，追着他满屋子乱跑的那天的日记呢？\r\n　　记着W.佩恩爵士福至心灵的儿子成天揣着教谕，把大伙儿搞得七荤八素的日记呢？这些偷工减料的手脚可别想逃过我的法眼。\r\n　　附上两张皱巴巴的钞票。我想，用来付这本玩意儿，外加你将要为我找来的那本“货真价实的佩皮斯”，应该绰绰有余了！到时候，我会将这本烂书碎尸万段，然后，一页一页撕下来——拿来包东西！\r\n　　HH\r\n　　P.S.圣诞节快到了，到底是寄新鲜的鸡蛋，还是干燥蛋好呢？我当然明白干燥蛋可以放得比较久，但是，“丹麦空运直送的新鲜鸡蛋”光听都觉得香，你们快帮我拿主意吧！\r\n\r\n\r\n\r\n---------------\r\n1951年10月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年10月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　首先，在此为我们的疏忽向您致以十二万分的歉意。我一直错认为那是收录完整的布雷布鲁克版。我相当能够了解，当您发现该书阙漏了喜爱的章节时，会有何等的失落感受。\r\n　　我一定会尽力另找一本书价合理、完整收录您在信中提及的段落的《佩皮斯日记》，并尽快为您寄上。\r\n　　同时，很高兴地在此向您报告：本店最近将收购一批私人藏书，我已从该批书籍中捡选出一些您会喜欢的书，包括一本利·亨特的选集，收录大半您曾提及的文章；还有一册《通俗拉丁文新约全书》——希望这回不会再出错了；同时有一本也许对您相当实用的《通俗拉丁文圣经辞典》；另一本《二十世纪英国散文选》，内容虽收入希莱尔·贝洛克的文章，但并不是谈论厕所的那篇。随信附上发票，上列书款十七先令六便士，约合美金二元五角，此书账已扣除您在敝店账户中的二元余额。\r\n　　至于鸡蛋的问题——店内同仁商量的结果，大家似乎意见一致，均认为新鲜鸡蛋较好。诚如您所言：鲜蛋虽较难久放，但风味确实不同。\r\n　　我们都期盼大选后日子会好转。如果丘吉尔先生和他的政党能赢得选举——这也是我的衷心期望，将会是一件振奋民心的好事。\r\n　　衷心祝福您\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1951年1Z月2日\r\n    亲爱的急惊风:\r\n    你简直是“迅雷不及掩耳”，利·亨特的书和《通俗拉丁文新约全书》“倏忽”寄达。你大约还没弄明白吧——这不就正是我两年前向你们订购的书吗?如果你继续照着这种提心吊胆的步调干活儿，要不得心脏病也难。\r\n    我真恶毒。你为了帮我找书，忙东忙西的，我竟然不曾向你道过一声谢，我真是坏透了。其实，你在那头儿受苦受难，我都是铭感在心底的。附上三元钞票，抱歉，最上头那一张被我溅到了几滴咖啡，应该还不致于作废的地步，上头的面额还可以看得出来。\r\n    你们有精装版的合唱乐谱吗?比如说巴赫的《马太受难曲》或韩德尔的《弥赛亚》?虽然我顶着寒风，走到五十条街外就能买到，但我想我还是“就近”先问问你们吧。\r\n    恭贺丘吉尔先生和他所属的政党，希望他能让你们的日子好过些。\r\n    “德尔”?你的祖先是威尔士人吗?\r\n                                          HH\r\n\r\n    1951年12月7日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    希望您会高兴得知，两箱鸡蛋和牛舌罐头已平安寄达，而我们也要再度向您道谢。\r\n    本店的一位老同事——马丁先生，躺卧病榻已有一段时日了，因此，我们一致决定让他多分一些鸡蛋——其中一整箱。一如以往，对于这些难能可贵的物资，我们十分欣喜。牛舌罐头亦相当诱人，它们大大地补充了我们的贮粮。至于我\r\n自已，我会将它们好好地收藏起来，以备特殊场合再派上用场。\r\n    我询问了此间所有的乐谱店，不过全都没有干净的二手精装版《弥赛亚》或巴赫的《马太受难曲》，然而我发现新书目前仍持续发行。价格虽略嫌高了些，但我想了想，仍为您买了下来，并在几天前寄发出去了，最近几天您应该就会收到这两本乐谱。一英镑十先令(约合美金四元二角)的账单亦会随\r\n书寄上。\r\n    我们将寄给您一件小小的圣诞礼物。这是一件刺绣品，我们会在邮包上注明“内装礼品”，希望这样可以避免您被课上关税。所有人都希望您笑纳这份薄礼，大伙儿同声祝您圣诞快乐，并有一个美好的来年。\r\n    我的姓氏绝非源自威尔士，鉴于其发音近似法文“挪尔”，我想我的祖籍或许是法国也未可知。\r\n      马克斯与科恩书店\r\n      弗兰克·德尔谨上\r\n\r\n    195年l月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    首先，我们都十分高兴您喜欢那块桌布。总算能够回赠您一件礼物，亦带给我们一丝欣喜——虽然比起您过去这些年来为我们的奉献，那实在是太微不足道了。您或许想知道那件桌布的来历，其实这是最近刚完成的作品，出自我的邻\r\n居—一位八十多岁老太太的巧手。她一个人独居，平日以女红自娱。虽然她做了许多手工刺绣，却儿乎全都自己留着。她之所以愿意割爱，破例卖出这么一块桌布，乃是禁不住我太太的不断恳求。当然，您之前寄给我们的干燥蛋也使上不\r\n少助力。\r\n    如果您非到万不得已，一定要“清洗”您的《格罗里埃圣\r\n经》。的话，我们会建议您用一般的肥皂和清水即可。做法是:加一茶匙的苏打粉到一品脱的温水中，用蘸了肥皂的海绵轻轻擦拭。我相信您会发现，这样可以有效地除去污渍。经过上述的处理后，您可以再用少许绵羊油为它上光。\r\n    J.彭伯顿是一位女士，J是珍妮特的缩写。\r\n    祝您新的一年一切如意。\r\n弗兰克·德尔敬上\r\n\r\n\r\n    伦教北八区克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年1月20日\r\n    亲爱的汉芙小姐:\r\n    谢谢您长期不断地寄赠马克斯与科恩书店礼物包裹，并让我家也能有幸分得一部分。我长久以来就一直想写信给您，现在终于有正当理由可以动笔了。弗兰克跟我说，您很想知道绣了那块桌布的老太太的姓名、地址。那件刺绣的手工\r\n真的很漂亮吧？\r\n    这位博尔顿老太太就住在我们的公寓隔壁，地址是:橡原巷36号。当我告诉她，这件桌巾飘洋过海送给了一位好心的女士时，她好吃惊。如果能再亲耳听到您对她的手艺赞不绝口，我相信她一定会十分开心。\r\n    很感谢您说还要寄更多干燥蛋给我们。不过上回您送来的还剩一些，应该可以让我们享用到春天。四月到九月这段时间，我们并无须操心蛋的问题。如果偶尔会因物资短缺而临时减少配给量，我们就会拿别的东西去跟别人换罐头。我曾经用一双丝袜在黑市换得一罐干燥蛋，当然这么做不尽合法，却是非常时期不得已的变通办法。\r\n    过些日子我会寄一些全家福照片给您看，我们的小女儿玛莉上周刚度过她的四岁生日;大女儿去年八月满十二岁了，名叫希拉，是弗兰克的前妻生的——她不幸在战时丧生了。战后我嫁给弗，顺道有了个现成的女儿。去年五月，希拉在学校当着修女们的面(她念的是教会学校)，说她要送一张卡片祝福爸爸、妈妈的结婚四周年纪念日。您不难想像，这可怜的小姑娘花了多少唇舌才解释清楚。\r\n    最后，祝您新的一年一切顺遂，并期盼不久后我们可以在英国相会。\r\n诺拉·德尔谨上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年l月29日\r\n    亲爱的汉芙小姐:\r\n    谢谢您的来信。我很感激您如此好心地告诉我:您十分喜欢我绣的桌布。我当初真该多花点儿工夫在那上头。我想，德尔太太一定也对您提过了吧，我是一个上了年纪的人，双手也不太听使唤，能做的活儿也不像以前那么多了。我用这\r\n双老手做的东西有幸能交到喜欢它的人手上，这真是一件令人欣慰的事儿。\r\n    我常见到德尔太太，而她也不时向我提起您的事儿。您若到英国来，或许我也能有幸见您一面罢。\r\n    再次谢谢您。\r\n    玛丽·博尔顿谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n听好了!玛克辛:\r\n    我刚和你妈妈聊过。她说你们的戏也许下个月就会结束公演;她还告诉我。你带走了两打丝袜。帮我一个忙，趁你的戏下档前，拿四双去书店交给弗兰克·德尔，就说是送给店里的三个女孩和诺拉（他太太）。\r\n    你妈妈还特别交代，要我用不着付钱给你。她说那些丝袜全是她自已在去年夏天萨克斯百货店清仓大减价时便宜买到的，她决定要乐捐出来，好让她自个儿也能沾沾“共赴国难”的光。\r\n    等你回国就可以瞧见他们送我的圣诞节礼物了。这是一条漂亮的爱尔兰绣花桌巾，米黄色的底布上以手工绣着古典的花草图案——全是各自不同颜色、浓淡有致的花儿。保证你从没看过这么美的桌巾，我那张从旧货店买回来的破茶几\r\n就更肯定没见识过啦!我真是迫不及待想披上维多利亚时代的水袖，优雅地举起手，幻想自己执着一只乔治王朝的古董茶壶，轻轻地斟上一盏茗茶……你快点儿回来吧!我们可以在家里扮一出斯坦尼斯拉夫斯基！\r\n    埃勒里调高了我的剧本稿酬，现在是一集两百五十元。如果照着这样的调薪幅度。到了六月，也许我就可以启程赴英，自己去逛“我的书店”——如果我斗胆敢去的话。隔着三千英里的安全距离，我写了一堆没大没小的信，我大概只会\r\n悄悄溜进去又静静踱出来，而不敢告诉他们我是谁。\r\n    我实在不明白你怎么会在杂货铺里被搞得一楞一楞的呢?老板跟你说的不是“兔兜脚”，他说的是“土豆胶”!——我也觉得这才是惟一合理的称呼。你动点儿脑筋想想:豆子长在土里，叫它“土豆”合情人理;从土里头挖出来，一直“搅”、一直“搅”，搅成“胶”状，不就成了“土一豆一胶”!这个词儿不是要比“花生酱”更贴近事实吗?你真是不懂国语!\r\n    \r\n    h.汉芙(必也正名乎的海莲)\r\nP.S.你妈妈今天一早就打扮得花枝招展地出门，要为你在第八大道和五十大街一带找间公寓，因为你曾交代她去剧院区找。我说玛克辛啊，你该很清楚才对，她穿那一身行头在那种地方晃，谁还敢租房子给她啊?\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n大懒虫!\r\n    依我看若要等到你寄书来.我都不晓得要超渡几回了。我不如干脆直接冲进布伦塔诺书店，有什么就买什么，不管印得多糟!\r\n    你们不妨再加记一笔沃尔顿的《五人传》到我那份“该寄而未寄”的书单里头。老实说，订这本书实在是违背了我的购书原则。我从来不买没读过的书——否则，不就像买了一件没试穿过的衣服同样下场吗?可是，这儿竟然连图书馆也\r\n借不到这本书。\r\n    要读的话倒是有。四十二大街上的分馆有一本，但，恕不外借!坐镇在柜台的女馆员用力摇了摇头，盛气凌人地说:“仅供内用!”然后只准我窝在密不通风的315号阅览室里啃完整本书。既不能边读边喝咖啡，抽烟就更纯属妄想了。\r\n    没关系，反正Q多次引用这本书里的段子，所以我肯定也会喜欢它。只要是Q喜欢的，我都照单全收——小说除外，我就是没法儿喜欢那些根本不存在的虚构人物操演着不曾发生过的事儿。\r\n    你们成天都没事干吗?是不是都窝在店里头看书?何不起身做点儿生意呢?\r\n                                              汉芙小姐上\r\n                      (只有我的“朋友”才可以叫我“海莲”!)\r\np.s.转告女孩们和诺拉，如果一切顺利的话，她们在大斋节都有丝袜可穿。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年2月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我也十分同意，该是我们都摒弃无谓的“小姐”、“先生”敬称的时候了。不瞒您说，我本人实在并不像您长久以为的那样既木讷又严峻。只是我写给您的信都必须存放一份副本作为业务存档，所以我认为行礼如仪似乎比较妥当。不过，此封信既然与书店业务无关，自然毋须顾虑副本、存档的问题。\r\n    我们百思不得其解，不明白您如何隔海变戏法，让四双丝袜无中生有。我所知道的只是:今天中午我用过午餐回到书店，就赫然发现它们已经好端端地摆在我的办公桌上，上头还附着一张写着“海莲·汉芙赠”的卡片。没有人晓得它们是什么时候或是怎么来的。女孩们都吓呆了，我晓得她们正在打主意待会儿自行写信给您。\r\n    有一件令人遗憾的事要向您报告:久卧病榻的乔治·马丁先生上周在医院中病逝了。他在本书店工作已有多个年头。伴随着这个噩耗。国王的猝然驾崩亦使我们此刻都笼罩在一片哀戚之中。\r\n    我实在不知该如何回报您对我们的不断付出。我所能做到的，只是当您确定访问英国时，橡原巷37号将会有一个房间，可供您无限期地住宿。\r\n    全员共祝您一切美好\r\n    弗兰克。德尔\r\n\r\n 纽约市\r\n东九十五大街14号\r\n    1952年3月3日\r\n    噢!真感谢你寄来的《五人传》。实在难以置信，这本一八\r\n四〇年出版的书，经过了一百多年。竟然还能保持这么完好的书况!质地柔细、依旧带着毛边的书页尤其可人。我真为前\r\n任书主(扉页上有“威廉.T.戈登”的签名)感到悲哀，真是子孙不肖哟!竟把这么宝贵的东西一股脑儿全卖给你们。哼!我真想趁它们被称斤论两前，拎着鞋溜进他们的书房，先下手搜刮一番!\r\n    这真是一本引人入胜的书。你可知道，当约翰·多恩带着他主子的掌上明珠私奔(人家好不容易老来得女的)，他因此下狱被囚禁在伦敦塔，他在里头握饿受冻。饿着饿着……居然就“得道”了！——不是沃尔顿的原句啦，这是我自己改编的说法。\r\n    注意!我附寄五元钞票一张。由于这本《五人传》赫然登场，害我原来那本《垂钓者言》相形见绌(认识你们之前买的，这是一本硬邦邦的“美国大众经典文库”版)。《五人传》跟它不登对，总嫌它在跟前晃来晃去，看了就讨厌。所以，多出来的两块半就帮我找一本好点儿的《垂钓者言》——劳您大驾啦。\r\n    你得当心了，如果电视剧续签，明年我就会杀到你们那儿去。到时候我会蹬着古董木梯，掸去你们的书架顶层的陈年积垢，顺便也把你们的优雅端庄一并一扫而光。我跟你提过我帮埃勒里·奎因的电视节目编写巧艺谋杀吗?我所写的剧本总会安插艺术场景——芭蕾舞团啦，音乐厅啦，歌剧院什么的;连嫌疑犯或尸体也得写得文绉绉的。为了向你致敬，也许我该编一出发生在古书店里的谋杀案，怎么样?你要演行凶歹徒呢?还是要扮刀下亡魂?\r\n hh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年3月24日\r\n亲爱的汉芙小姐:\r\n    我实在不知如何表达我的感激，今天收到您寄给我的食物包裹，我有生以来未曾收到过包裹。您实在不须为我这个老太婆如此费心。我只能谢谢您的好意，并好好地享用您寄来的礼物。\r\n    感谢您为我设想如此周到。我拿您寄来的东西给德尔太太瞧，她也直夸您实在是一位大好人。\r\n    再次谢谢您，并析祷您一切安好。\r\n玛丽·博尔顿谨上\r\n\r\n    伦敦中西二区 令十字街84号.\r\n    1952年4月17日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲(您看，我已经不再“行礼如仪”了):\r\n    我相信您一定会高兴听到这个消息:我们刚购入一批私人藏书，里头有一册非常好的《垂钓者言》，待我将收购手续完成，本书可望于下周为您寄上。我们大概会标价二元二角五分，您账户中的余额仍够支付。\r\n    您为埃勒里·奎因写的剧本似乎挺有意思。我希望这部剧集也能在我们这儿播映，它也该改善其沉沉死气了(我是指我们的电视节目，不是您写的剧本)。\r\n    诺拉和所有人与我同祝您一切美好。\r\n弗兰克·德尔敬上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年5月4日星期日\r\n亲爱的海莲:\r\n    谢谢您寄赠的干燥蛋罐头，包裹在两天前收到了。尽管难掩喜悦之情，但我实在觉得很难为情，因为我竟向您提起蛋源短缺的事情。不过今年倒是还没发生减量配给的情形，所以我们就当成是上天的恩赐，用它们多烤了个蛋糕什么的。弗兰克还拿了一些去办公室，打算要寄给塞西莉，他老是忘了把她的地址带回家。我想您应该也听说了吧，塞西莉离职了，她正打算随丈夫调防到中亚去。\r\n    我附了几张相片，弗兰克说这些照片全没把他拍好，还说他本人比照片好看多了。不打紧，我们就让他说梦话好了。\r\n    希拉的学校放了一个月的假，以前我们全家偶尔会一起到海边走走或随处晃晃。但是现在恐怕也不能那么常出游了，因为交通费贵得吓人。我们一直有买车的念头，但车价也不便宜，物色一部性能尚可的二手车或许比较可行。大部分\r\n新车都出口到国外，国内只能分到少量的配额。我们有一些朋友，光是为了订一部新车，就等了五六年。\r\n    希拉现在要为您朗读一篇《丰收祈祷文》，祈祷您可以如愿到英国一游;而且在复活节时，我们也已经借花献佛，用您送给我们的咸肉罐头犒赏过神明了。所以，如果再加上《丰收祈祷文》奏效，老天将会保佑您获得一笔意外之财，这样您和我们就很快可以见面了!\r\n    就此停笔并再次感谢您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年5月11日\r\n亲爱的弗兰克:\r\n    本打算一收到书就写信给你的。就是想跟你道句谢谢，《垂钓者言》里的木刻版画太棒了，光这些插图的价值就十倍于书价。我们活在一个诡异的世界——这么漂亮，又能终生厮守的书，只须花相当于看场电影的代价就能拥有;上医院\r\n做一副牙套却要五十倍于此。\r\n    唉!如果你们依照每本书的实际价值去标价的话，我肯定一本也买不起。\r\n    如果你知道我这个一向厌恶小说的人终究回头读起简·奥斯汀来了，一定会大大地惊讶。《傲慢与偏见》深深掳获了我的心!我千不甘万不愿将我手头上这本送还给图书馆，所以快找一本卖我。\r\n    代我问候诺拉和办公室里可怜的上班族们。\r\nhh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年8月24日\r\n亲爱的海莲:\r\n    我要再一次对您寄给马克斯与科恩书店，并让我们家分享的物资向您道谢，我真希望能回寄点儿什么报答您。\r\n    对了，海莲，这个礼拜我们终于荣登有车阶级了。虽然不是一部新车，没什么好到处吹嘘的。不过，车子只要能跑就行了，您说是不?怎么样，您现在比较愿意来找我们玩了吧?\r\n    前一阵子，我有两位亲戚从苏格兰南下来看我们，博尔顿老太太心肠很好，肯让他们在她家叨扰了几个礼拜，他们说住在那儿很舒服。他们那一阵子就吃我家，睡博尔顿老太太家。如果您手头方便，可以赶明年儿，趁着女王登基大典时\r\n来一块儿凑凑热闹，博尔顿老太太说她会为您将房间准备好。\r\n    好了，不多说了，寄上我的祝福和感谢——谢谢您送的肉与鸡蛋。\r\n诺拉谨上\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年8月26日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我又要再度代表我们这儿所有人，写信向您道谢了，三件甚获欢迎的包裹几天前寄达。您实在太好心了，将辛苦挣来的钱花在我们身上。我们都非常感激您对我们的关怀。\r\n    这几天，店里进了三十几册“洛布经典文库”，可是，唉。里头不巧就缺了贺拉斯、萨福、卡图卢斯。\r\n    从九月一日起，我会休几个礼拜的假。可是因为我最近买了车，家中经济失血甚多，所以不能再大事铺张。诺拉有个姐姐住在海边，我们都冀望她会怜悯我们无处可去而邀我们去玩。这是我头一回买车，所以全家都非常兴奋——尽管这\r\n是一九三九型的老款车。但只要它不会老是半路抛锚，我们就该偷笑了。\r\n    全心祝福您\r\n    弗兰克·德尔\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年9月18日\r\n弗兰基，猜猜看，当你去度假时，谁上门来啦?萨姆·佩皮斯是也!不管是谁代你寄的书，别忘了要谢谢人家。这些书上个星期就到了，三册扎扎实实的海军蓝布面精装本，用四大页四开报纸裹着。我边吃午饭边读旧报;晚餐过后，开始和萨姆\r\n神交。\r\n    他要我转告你:他非常高兴能来到敝宝地，他的前任主人是个大草包，连书页都懒得裁开。我将它们一一裁开，内页用的是薄得几可透光的印度纸——我们这儿管这种纸叫“洋葱皮”，真是恰如其分。要是换成厚一点的纸张，难保不变成\r\n六册、甚至七册，印度纸果然功德无量。我只有三座书架，可是实在已经找不到可以让我清掉的书了。\r\n    每年一到春天，我就会“大清仓”，把一些我再也不会重读的书全丢掉，就像我也会把再也不穿的衣服扔了同样道理。倒是旁人都很惊讶、依我看，他们爱惜书本的方式才奇怪呢。他们买一堆新出版的畅销书，囫囵吞枣似的看完，我常\r\n想:他们也未免读得太潦草了吧。然后呢，因为他们从不重读那些书，不消一年，书里头的内容早就被他们抛到九霄云外!不过，当他们看见我一箱一箱地把书往外扔时，却又露出一副“这怎么得了!”的表情。要是照着他们的做法:买了一本\r\n书，好——读过了，好——上架，好——没事了，一辈子也不会再去碰它第二回，可是呢，“丢掉?万万使不得呀!”为什么使不得?我个人坚信:一本不好的书——哪怕它只是不够好，弃之毫不足惜!\r\n    你和诺拉过了一个不赖的假期吧?我自己则全消磨在中央公园里。我的宝贝牙医师放了我一个月的假，他却欢欢喜喜带着娇妻度蜜月去了，旅费是我出的——我有没有提过?好几个月前，我发现牙齿一颗接一颗全坏光了，我要么乖乖装上牙套。要不然就得全部拔光!因为还不想当个无齿之徒，我最后还是决定装牙套。可是诊疗费简直是天文数字!看来伊丽莎白只好在我没来的情况下登基了，我呢，今后几年里也只能留在这里看着我的牙齿一一加冕了。\r\n    不过我可没打算停止买书!连牙齿都弃我而去了。总该给自己留点儿什么呀!你能为我找到萧伯纳的剧评和乐评吗?我想他应该写了好几本，把你所能找到的都寄给我。还有!弗兰基，漫漫冬天眼看着又要来了，我兼差帮人带小孩时可不能闲着，所以，亟需读物!——快，别老坐着，起身帮我找找书!\r\n\r\nhh\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n  1952年12月12日\r\n此致“伦敦查令十字街84号的众好友们”:\r\n    一拆开包装，《爱书人文选》款款现身—镶金边的皮面、上金漆的上书口……轻而易举勇夺“我的藏书选美”的后冠，连首版的《大学论》也甘败下风。它看起来仍如此清新、纯朴，宛若从未遭染指——不过我知道，它的确曾被频繁地悉心翻阅过:因为一打开书页，总会落在某几个特定段落，冥冥之中似有前任书主的幽灵导引我，领我来到我未曾徜徉的优美辞藻。例如特里斯特拉姆·项狄描述他父亲富丽堂皇的书房:“架上罗列多善本，筐中广纳皆美卷。”(弗兰克!快去给我找一本《项狄传》！)\r\n    我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年;而你们送给我的礼物，却能和我朝夕相处，至死方休;我甚至还能将它遗爱人间而含笑以终。\r\n    谢谢你们，祝你们新年快乐。\r\n海莲\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n   1952年12月17日\r\n亲爱的海莲:\r\n    很抱歉这么久都没给您只字片语。希望阿德莱败选不致于让您太难过，也许下一回他可以东山再起。\r\n    博尔顿老太太跟我说，如果明年夏天您来英国时，她仍健在的话，很欢迎您住在她家。她是我所认识最长寿的人，我还不晓得有谁能像她活那么大岁数儿，我相信她一定可以活到一百岁的。无论如何，我们都会把您的住宿问题打点好。\r\n    谢谢您寄给我们这么好的圣诞礼物，您实在是个大好人。海莲!如果您明年来英国的时候，马克斯与科恩书店的人没好好地为您设宴接风的话，他们就太该死了。\r\n    我希望您将有个快乐的圣诞节。下回再聊了，献上我们\r\n的祝福和感激。\r\n    愿上帝保佑您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1953年5月3日\r\n弗兰基，告诉你个包准让你乐翻的消息-—\r\n    首先，随信寄上三元钞票。邮包已收到，这本书长得就像简·奥斯汀该有的模样儿——皮细骨瘦、清癯、完美无瑕。\r\n    好，进人正题——埃勒里电视剧集停播了。正当我青黄不接，又为了支付看牙的庞大开销而焦头烂额的当头，有人找我为一个新的节目写个草案——将名人轶事编成电视单元剧。所以我快马加鞭，完成一个故事大纲。送出之后，电视\r\n公司接受了;于是我又写了一个完整剧本，他们也颇为满意——所以再过个把月，新差事就有着落了。\r\n    而你猜我改编哪一个故事?“多恩与领主千金私奔记”—灵感来自沃尔顿的《五人传》!电视观众大概没几个人晓得约翰·多恩是谁，不过，拜海明威之赐，大家都听过“没有人是一座孤岛”。我只消将这句名言编进剧本里，便顺利\r\n卖出啦!\r\n    于是，约翰·多恩成功登上“不朽名人堂”。我也拿到一笔酬劳—价码大约是我前前后后花在你们店里的书款外加五颗牙!\r\n    我打算半夜爬起来听收音机的现场实况转播，和你们一块儿参加加冕典礼，同时惦念着你们所有人。\r\n    祝开心\r\n\r\n    伦敦中西二区，查令十字街84.\r\n    1953年6月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    您的包裹于六月一日平安抵达了，正好赶上加冕大典。当天家里来了不少朋友。和我们一起收看电视转播。我们用您寄来的火腿做了些三明治供大家享用，每个人都直说美味极了。于是我们全体举杯同祝您及女王都凤体康泰。\r\n    您实在太仁慈了，竟将您的辛苦所得拿来关照我们。所有同仁与我在此同声向您道句:万分感激。\r\n    祝福您\r\n弗兰克·德尔谨，上\r\n\r\n    米德尔塞克斯 品内东厢 包德米尔路\r\n    1953年9月23日\r\n亲爱的海莲:\r\n    我必须赶紧通知你:今年圣诞节可千万不许再寄礼物来。所有的东西都已经不用配给了，稍好一点的店里头也能买到丝袜。请攒下你的钱，现在最要紧的就是等你治好牙齿之后，能到英国来。只是别挑明年来。因为那时我不在国内。后年我才会回国，这样子你来了才能住在我们家。\r\n    道格写信告诉我，我们候补的家庭宿舍就快有着落了，孩子们和我希望能在圣诞节前就搬去和他团聚。他现在暂时被调到巴林岛(在波斯湾，你可以在地图上找找看)的基地，日子过得愉快惬意。等到家庭宿舍一分配下来，他便会回到哈巴尼亚(在伊拉克)的皇家空军基地和我们会合。一切都很顺利。\r\n    要写信给我哟，即使我暂时“出局”了，妈妈也会把你的信转寄给我的。\r\n思念并祝福您\r\n塞西莉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1955年9月2日\r\n你们店里一直发行这么棒的目录。却直到现在才寄给我!难道你还好意思跟我说你老是忘了吗?汝等无赖!\r\n    忘了哪个复辟时代的剧作家老爱用“汝等无赖”这个词儿数落别人，我好不容易终于逮到机会可以用它来造个句儿。\r\n    活说回来，这整本目录里头只有这本卡图卢斯我有点儿兴趣——虽然不是“洛布经典文库”版，不过看起来还算差强人意，如果这本书还在的话就寄给我。至于书价六先令两便士，只要你换算成美金，我马上付——凯特和布莱恩搬到郊\r\n区。这下子没有人可以帮我换算了。\r\n    如果你从下个月起每个星期都能携家带眷乖乖上教堂，我会十分感激你。请一起为吉廉、李斯、史奈德、坎帕内拉、罗宾逊、哈吉斯、费里罗、帕德瑞斯、纽坎姆与拉宾—布鲁克林道奇队全体球员祷告，祈祷他们身强体健并获天助神力。要是他们打输了世界大赛，我也不想活了，到时你再后悔就来不及了。\r\n    你们有德·托克维尔的《美洲见闻录》吗?有人把我原有的一本借走了赖着不还。我实在百思不解，再循规蹈矩的人怎么一霸占起书来都是一副理直气壮的气派。\r\n    代我问梅甘好，要是她还在店里头帮忙的话。还有。塞西莉现在怎么样了?从伊拉克回来了吗?\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1955年12月13日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    十分抱歉没能早一点写信给您。不过请您先别忙着动气，因为我生了一场小病，请了几个礼拜病假在家。一回到店里又被一堆待补办的公事绊住。\r\n    关于目录里登载的那本卡图卢斯，收到您的信之前就已经被买走。但我仍为您找到另一个版本寄去——这也是一本附拉丁原文的版本，韵诗部分由理查德·伯顿爵士翻译;内文则为伦纳德·史密瑟翻译，大字印刷，标价三美元七十八\r\n分。装帧并非十分漂亮，但品相尚称良好。我们目前没有德，托克维尔的书，将会为您留意。\r\n    梅甘仍在书店，但是她正打算搬到南非去，大家都还在劝她打俏念头;自从塞西莉随丈夫去了中亚之后，我们就再也没有她的消息，转眼间她竟然已经离职一年了。\r\n    我很乐意为布鲁克林道奇队加油——如果您也愿意为“热刺队”(托特纳姆足球队)打气的话。他们目前在联盟的排名敬陪末座，不过，球季尚未结束，同志仍须努力。希望他们在明年四月前能扭转颓势。\r\n    诺拉、所有人和我在此祝您圣诞快乐、恭贺新禧!\r\n弗兰克·德尔谨上\r\n\r\n     纽约市\r\n    东九十五大街14号\r\n    1956年l月4日\r\n    我现在趴在床脚下写信给你——这本卡图卢斯害我气得滚下来。\r\n译得洁屈赘牙的，真教人伤脑筋!\r\n到目前为止，我只听过一个名字也叫理查德·伯顿的帅哥演员——我曾在几部英国电影里头看过他，我想维持这么点交情也就够了。至于这个翻译者理查德·伯顿，他译得也未免太花俏了吧。\r\n    而可怜的史密瑟先生，他一定害怕他妈妈会读这本书，所以忍痛把那些原本应该活色生香的文章译得道貌岸然。咱们这么着吧，你索性找一本好的通俗拉丁文版的给我。我自己有一本卡塞尔氏拉丁文字典，那些难懂的段落，我自个\r\n儿去整明白得了!\r\n    梅甘头壳坏掉了吗?如果她真的那么厌烦文明世界，怎么不干脆搬去西伯利亚!\r\n    行行行!没问题!我会当热刺队的拉拉队!\r\n    正努力攒钱当中。如果电视台继续赏我饭吃，明年夏天我就可以成行了。我要去亲眼瞧瞧贵书店、圣保罗大教堂、国会殿堂、伦敦塔、柯芬园、老维克剧院. ....还要见见博尔顿老太太。\r\n    附寄十元大钞一张。这本白色软精装的卡图卢斯，居然还配着白色的丝质书签带……弗兰基，你打哪儿找来这些玩意儿啊?\r\n\r\n    伦敦中西二区 查令十字街84号.\r\n    1956年3月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    很抱歉又隔了好一段时间才回您的信。因为直到今天，我们才有好消息可以向您报告。原先我还在犹豫，是不是该等到卡图卢斯的书也一并我到时再写这封信比较好。\r\n    我们终于寻获一本版本相当不错的《项狄传》，附有罗布的插图，价格约合美金二元七十五分。同时我们也收到一册柏拉图的《苏格拉底四论》，译者是本杰明·乔伊特，一九〇三年在牛津出版。此书标价一美元，您是否要买?您在敝店\r\n户头内尚有美金一元二十二分的余额，如您两册都购买，仅\r\n需再付给我们美金二元五十三分。\r\n    我们仍翘首期盼您今夏能来，我家的两个女孩儿都离家住校，所以届时橡原巷3号将有两间卧房可任您挑选。很遗憾地向您报告:博尔顿老太太已被送到老人之家，我们都很难过，但毕竟她在那儿能得到比较好的照料。\r\n弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1956年6月1日\r\n亲爱的弗兰克:\r\n    布莱恩介绍我读肯尼思·格雷厄姆的《杨柳风》，因此我迷上了谢泼德的插图，决定自己也要买一本。但是!先别忙着寄来，帮我保留到九月，届时我会迁入新址。\r\n    原本住得舒舒服服的旧公寓老命不保喽。上个月所有住户都收到了搬迁通知。现址要盖新大楼。我想也是该为自己觅一间好公寓、买几件好家具的时候了。于是我打铁趁热地到第二大道的一处工地，订了一间连影儿都还没有的客卧两\r\n用预售屋。我现在忙着四处张罗新家具和书架、地毯，几乎把钱都花光了。可是，我一辈子都跟摇摇晃晃的桌椅、到处爬满蟑螂的厨房为伍，现在我想过点儿像样的日子。至于英国之旅，只好等着有人招待我去了。\r\n    与此同时，我的房东怕大家赖着不走，索性把门房给解雇了，害得垃圾和热水都没人打理;还打算要把信箱间、走道灯和我的厨房和卧室间的隔间墙全拆了(本周即将发生);这些烦心的事加上眼睁睁看着道奇队兵败如山倒，真是心酸谁\r\n人知噢……\r\n对了，新地址如下:\r\n纽约州，纽约市21，东七十二大街305号(九月一日启用)\r\n\r\n    伦敦中西二区，查令十字街84号\r\n    1957年5月3日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    预备好大震一惊:您上回信中提到的三本书，一口气全都找到了!而且已经在上周寄去给您，现在应该正在途中。别惊讶我们究竟是怎么办到的，这理应是本店的服务项目之一。账单附在此信中，扣除账户余额之后的书款为五美元。\r\n    几天前，有两位您的朋友到店里来探望我们——很抱歉我现在不记得他们的大名了，是一对很可爱的新婚夫妇。不过，可惜他们行程匆忙，只能在我们这儿稍坐片刻，抽根烟的光景，就必须告辞了。\r\n    今年的美国游客似乎较以往更多了。我曾见到由上百位律师组成的旅游团，每个人西装上都别着一块大大的名牌—上头写着他们的姓名和居住的城镇名。他们好像都玩得颇开心，所以明年您一定也要来一趟。\r\n致上全体的祝福\r\n弗兰克\r\n\r\n    海莲·汉芙 纽约州，纽约市21。东七十二大街305号\r\n    1958年1月10日\r\n嗨，弗兰基——\r\n    快叫诺拉更新通讯薄里的地址，你们家的圣诞卡寄到旧址去啦!她写成了东九十五大街14号。\r\n    忘了是不是说过了，我好喜欢那本《项狄传》，罗布的插图简直美得令人销魂，托比叔叔。地下有知也可含笑瞑目啦。我在信纸背面列了几本“麦克唐纳绣像经典”的书。其中有一本《伊利亚随笔》，有“麦克唐纳绣像经典”版最好，若是有其他不错的版本也行，但价格得“合理”我才会考虑——现在已经找不到“便宜”的东西了，所有的东西都标榜“价格公道”，要不然就是“收费合理”。对街又在盖大楼了，他们立了一块大招牌，上头写着斗大的字:\r\n    “一房一厅或两房一厅任君选择/租金合理”\r\n    房租什么时候“合理”过?其他东西也没好到哪儿去。尽管招牌上盖得天花乱坠，终究只是个广告文案。\r\n    我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天。就像米尼弗·奇维一样，余生也晚。而我也只能学他“干咳两声，自叹一句:奈何老天作弄。然后继续借酒浇愁。\r\nP.S. 喂，柏拉图的《苏格拉底四论》的下落呢？\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1958年3月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    在此向您致歉，无法及早回信。我们前一阵子都累垮了，诺拉生了一场病，在医院里待了几个月，而我只好暂时请假在家代理家务。她现在已经复元得差不多了，再过个把礼拜就能出院回家。这段时间真够折腾我们两人的，不过还真得\r\n感谢国民保健制度，我们几乎没花自己一毛钱。\r\n    关于“麦克唐纳绣像经典”，我们偶尔能收购到一些，不\r\n巧现在手头上都没有。而兰姆的《伊利亚随笔》本来也有好几本，但上回旅游旺季时全被买走了。下周我还会出一趟差，届时再尽力为您搜寻。另，柏拉图的书也会为您留意。\r\n    所有人齐祝您有一个愉快的春假。女孩儿们要我转告:她们对您感到很抱歉，粗心将圣诞卡寄到旧地址去了。\r\n弗兰克敬上\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1958年5月7日\r\n亲爱的海莲:\r\n    谢谢您接连寄来两封问候信。也很感激您的好意，不过说真的，海莲，我们现在什么都不缺。我真希望我们能自已开一家书店，这样我们就能送几本书给您，多少报答您的一片好心。\r\n    随信附上几张全家福照片。本想寄些拍得更好的，但是比较好一点的都被亲戚们要走了。你一定也会发现了吧，希拉和玛莉竟然长得那么像，这实在是一件很奇妙的事。弗兰克说。玛莉现在长得就像是希拉在她这个年纪时的模样。希拉的母亲是威尔士人，而我的老家则是在爱尔兰，所以她们一定得自弗兰克的遗传多些，不过她们都比弗兰克要漂亮多了，当然弗兰克死都不愿承认的!\r\n    要是您晓得我写这样一封信得绞尽多少脑汁，一定也会可怜我的。弗兰克老爱笑我:成天叽哩呱啦地说个不停，怎么一拿起纸笔就不灵光了呢?\r\n    再次感激您的问候和您的来信。\r\n上帝保佑您!\r\n诺拉\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1959年3月18日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我实在不知如何向您启齿。就在上一封信向您报告，为您的朋友找到了《简明牛津辞典》之后隔两天，我才一转身，这本书就被别人买走了。我之所以这么晚才回信，也是因为希望能尽快再另寻一本，可惜至今仍一无所获。辜负了您的\r\n朋友，我感到万分愧疚，当时我实在应该先将书收起来的。\r\n    我们今天会将约翰逊的《莎士比亚评传》寄出，正好敝店内有这本附沃尔特·雷利导言的牛津出版社版。定价为一元五分，您的账户内尚有余额足够支付此书。\r\n    您参与的电视节目将移师到好莱坞，大家都觉得很可惜;夏天又快到了，预料将会有更多美国游客到英国来，然而我们期盼的“那位美国游客”却仍独独教我们望穿秋水。我很能理解您不愿离开纽约搬到南加州的心情。我们也在此祝您好运，并希望您很快可以再找到类似的工作。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙 纽约州，纽约市21.东七十二大街305号\r\n    1959年8月15日\r\n仁兄：\r\n    告诉你吧，我又有活儿可干啦!\r\n    是我凭实力挣来的。我得到一笔CBS提供的编剧奖助金——为数五千美元，供我未来一年将美国历史编写成电视剧本。我头一个要写的题材是英国占领期间的纽约。我一边写一边踟蹰——贵国同胞于一七七六年到一七八三年在这儿的行径那般龌龊不堪，而我偏挑这一段来写，实在有点儿对不起你们现在待我如此温良友善，体谅慈悲。\r\n    你们可有白话版的《坎特伯雷故事集》?实在罪过，我从没读过乔叟。我想学古盎格鲁——撒克逊或〔和)中世纪英语的念头叫一位要拿它做博士论文的朋友给打消了。教授们对她说:关于古盎格鲁——撤克逊，想写什么题目都行。“乍听之下似乎很棒，”我的朋友哭丧着脸告诉我，“可是惟一能找到够多古盎格鲁——萨克逊语的文章，尽是些‘烧教堂、砍人头’的玩意儿。”\r\n    她还跟我讲了一堆贝奥武甫和他的私生子希卫斯(还是卫希斯?)的故事，她说那些也没啥看头。经她冷水一浇，我的兴致也全没了。所以给我白话版的《坎特伯雷故事集)就行了。\r\n代我问候诺拉。\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1959年9月2日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    人家都很高兴听到您得到一笔奖金，而且也有工作了。对于您所挑选的任何题材，我们完全不会介意。但我必须向您报告，店里的年轻同事们坦承:若不是您在信中提及，他们浑然不知英国竟然曾经“占领’过美国。\r\n    关于您的疑问，似乎所有最顶尖的研究学者对于将乔叟的文章译成白话文都敬而远之。但是，在一九三四年由希尔改写、朗文出版社出版的《坎特伯雷故事集》，应该是目前惟一能找到的白话版了。我认为还算是不错的书，此书现已绝\r\n版(这自然不在话下)，我将为您找一本书品较好的。\r\n弗兰克谨上\r\n\r\n        一九六〇年开春头一个糟糕透顶的周日夜\r\n我快没辙啦!弗兰基—\r\n    有人送我这么一本书充圣诞礼物。这是一本“巨匠现代文库”。你可曾见过这种版本?装帧比起纽约州议会公报好不到哪儿去，重量倒是略胜一筹。一个爱附庸风雅的家伙，听说我喜欢约翰·多恩。不知打哪儿找来送我。书名是:\r\n     约翰·多恩\r\n    诗全集、文选\r\n        与\r\n    威廉·布来克\r\n       诗全集?\r\n    问号是我自己加上去的。你学问大，能不能告诉我:这两个老小子浑身上下有哪点相同，得这样子凑合在一块儿?——只因他们俩都是英国佬，也都舞文弄墨?我努力读着导论，想找出些蛛丝马迹来。导论洋洋洒洒共分四大章:头两章巨细靡遗地描述了多恩的学术生涯;第三章开头是这么写的(我真不想引述):\r\n    “话说少年布来克，在茵茵夏日草原，于树下邂逅先知以西结，而遭母亲深责痛打。”我百分之百支持他娘。我的意思是:就算没瞻仰到上帝的后背，好歹领教一下圣母的尊容也不赖呀——谁叫他好死不死见到什么鬼先知以西结?\r\n    反正我不喜欢布来克，他还真是让这本书减色不少。看来我势必又得清掉一些书啦，弗兰基，你得伸出援手。\r\n    我现在蜷瘫在安乐椅里，聆听着收音机传出恬淡闲适的古典音乐——大概是科莱利吧... ...慵懒地享受这天下太平的短暂时刻。而这玩意儿——“巨匠现代文库”就站在桌上直盯着我瞧。我心想:好吧，不妨朗读读第十五篇布道词中的那\r\n三个标准段落好了。”多恩的文章合该大声朗读，它简直就像是巴赫的赋格!\r\n    可是你晓不晓得，这个无端之举给我自己惹了什么天大\r\n的麻烦?\r\n    打开“巨匠现代文库”，翻到“布道文第十五篇”——竟被肢解成三个选段。该到第一段末尾，才一发现“耶洗别篇”被删掉了;我赶紧找来另一本多恩的《布道文选编》(罗根·皮尔史·史密斯编)，花了二十来分钟拼命找“布道文第十五篇”……搞了半天才发现:原来，照史密斯的编法，它不叫“证道文第十五篇”，而是“第126节:人必有终”。好不容易找到了，却——同样没有“耶洗别篇”。再搬出《约翰·多恩诗全集暨文选》（典范版）.依然少了那段“耶洗别篇”;转而向《牛津英语诗选》求助，再度穷花二十分钟，因为在这本书里，既不叫“布道文第十五篇”，也不叫“第126节:人必有终”……好歹“耶洗\r\n别篇”总算是找着了，于是开始正襟危坐大声朗读……读到末尾，啥，又不行了——这本书没有第二、第三段!所以咯，我若要从头到尾朗读一篇完整的布道文，得将三巨册全摊开，翻到正确的页面，然后绕着它们来同奔波!\r\n    请好心地告诉我:一本完整收录约翰·多恩布道文的书有那么难找吗?我得花多少钱才买得到?\r\n    我得去睡了。我会做一个可怕的噩梦——披着道袍的妖魔鬼怪，拎着一把把血淋淋的屠刀—上面分别标示着\r\n“段”、‘’节”、“选”、“删”等字眼、霍霍朝我追来……\r\n        祝好\r\nhh\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    l960年3月5日\r\n    海莲· 汉芙小姐\r\n    美国\r\n    纽约州，纽约市2l\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我又迟回您的信了，因为我希望能等到有好消息再向您报告。我找到一本萧伯纳与埃伦·特丽的书信集，虽然装帧不特别吸引人，但书品完好。我想这一次我还是尽快将书寄给您为妙、因为这是一本颇抢手的书，要等到下一本出现，恐\r\n怕得花一段时日。扣掉书价的二元六十五分，您的账户余额尚余五角。\r\n    关于收录完整的多恩布道文，恐怕只能在《约翰·多恩全集》中才能找到，而这套书共有四十余册，若书况稍好些的也\r\n必然所费不赀。\r\n    我们希望您后来没耗费太多心神在那本“巨匠现代文库”上，好好地过了一个愉快的圣诞节和新年。\r\n    诺拉也要祝您一切顾遂。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1960年5月8日\r\n    德·托克维尔阁下在此致意，宣告他已安然渡抵美利坚。他顾盼满志地坐谈时事，而他的臧否都卓然成理。尤其当他论及“律师治国”时，我只有额手、点头的分儿。我参加了一个民主党的小型聚会，前儿天晚上，出席的十四个人里头有十一个是律师;回到家在报纸上读到一堆名人励志故事——有为青年怀抱鸿鸽之志，力争上游，终于晋身一国之尊——史蒂文森、汉弗莱丹、肯尼迪、史塔生.、尼克松……一整票人除了汉弗莱，全是干律师的。\r\n    我附上三块钱钞票，这是一本漂亮的书，实在不能算是“二手书”，连书页都还未裁开哩。我有没向你说过，我终于找到方便好用的裁页刀了?这是一把珍珠柄的水果刀，我母亲留给我一整打这种刀，我挑了一把搁在案头的笔筒里。大概\r\n是交游不力吧，我好像没有机会请来十二位宾客。让他们围坐在餐桌前集体切水果哩。\r\n    笑口常开\r\n\r\n    海莲·汉芙 纽约州，纽约市21，东七十二大街305号\r\n    1961年2月2日\r\n弗兰克?\r\n你还在吗?\r\n    我答应自己在找到工作前不再写信给你的。\r\n    终于卖了一篇稿子给《哈泼杂志》。被这篇稿子折腾了三个星期，他们付给我两百美元稿费。现在他们再度向我约稿，要我将生平事迹写成一本书，他们将“预付”给我一千五百美元!并预估我不用半年就能写得出来，我是无所谓啦，不过房东可又要头疼了。\r\n    所以这阵子暂且不能买书了。去年十月，有人介绍我读圣西蒙公爵路易的书，不过这是一本乏善可陈的节译本。于是我火速赶往学会图书馆，因为这个图书馆不仅全馆开架，还让我爱借多少就借多少，我在那里找到货真价实的路易。现在我已经对他不可自拔。正在读的这一套是六卷版，昨晚读到第六卷一半时，一想到等我把书归还后，家里就连一本路易也没有了，这实在令我难以接受。\r\n    我现在读的是弗朗西斯·阿克赖特的译本，他的译笔甚雅。不过我放手让你挑你信得过的版本。但先别忙着寄来!找到后暂时搁着，先报价，然后再一本一本卖给我。\r\n    希望诺拉和女孩儿们都一切安好—还有你和其他所有认识我的人。\r\n海莲\r\n\r\n    伦敦中西二区 查令十宇街84号.\r\n    1961年2月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    您听到一定会开心的:我们正好有一部《圣西蒙公爵回忆录》在店里，译者正是阿克赖特。六卷全帙，装帧精良且品相完好。我们将于今天寄给您，大约一两个礼拜可望寄达您的手中。这套书的价格约合十八元七十五分，但请勿挂怀书\r\n款，您在敝店的付款记录一向良好。\r\n    同时，很高兴又有了您的消息。我们大家都很好，依然盼望您能到英国一游。\r\n    所有人诚心祝福您。\r\n    弗兰克\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1961年3月10日\r\n亲爱的弗兰基:\r\n    附上“千万不能掉了”的十元钞票，它务必平安到达你们手里。倒不是最近发了什么横财，而是路易要我不能留他一个未赎之身。他在法庭上受够了俗不可耐的赖债痞子，可不希望保持了两百七十年的清誉毁于一旦。\r\n    《哈泼杂志》指派给我的编辑昨晚来家里吃饭，和我讨论“我的生平故事”，于是想起你。我们聊到我曾为电视节目“不朽名人堂”改写兰多的《伊索与萝多彼》(我好像告诉过你了?)——兰多笔下那个天真浪漫的萝多彼由萨拉·丘吉尔\r\n饰演。这一集节目在某个周日下午播出。就在播出前两个钟头，我翻着《纽约时报》周日书评版，在第三版中有一篇针对波莉·阿德勒关于娼妓业的新书《野花莫如家花香》的书评，附了一帧照片—一尊希腊的女人头像，图说这么写着:“萝\r\n多彼——希腊最艳名远播的娼妇。”兰多本人对此倒并未详加着墨，许多学者咸称：兰多笔下的萝多彼其实就是让萨福的兄弟千金散尽的众女子的综合体。我既非学者，而且多年前死记强背下的希腊文语尾变化，我也早忘得一干二净啦。\r\n    我对金(我的编辑)谈起这段轶闻，她问我:“兰多到底是何方神圣啊?”我不厌其详地为她细说从头——正当我苦心孤诣一头热滔滔不绝时，她竟不耐烦地插嘴说:“你还真的中毒不轻唉。”\r\n    唉，这下子你该明白了吧，弗兰基，这个世界上了解我的人只剩你一个了。\r\nP.S.金小姐乃中国人是也。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年10月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    当您收到弗吉尼亚·吴尔夫的《普通读者》。上、下两册时，一定会十分惊喜——它们已在寄往您的路上。如果您还有其他想要的书。我也会倾全力尽速为您服务。\r\n    我们都很好，仍是活蹦乱跳的。我的大女儿希拉(现在已经二十四岁了)两年前突然决定改行当老师，便辞掉原来的秘书工作。跑去念大学了，她还得待在学校一年。看来，要靠儿女供养我们这些老人家颐养天年的日子还有得等哩。\r\n    所有人诚心祝福您。\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年11月9日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    许久以前您曾询问过白话版《坎特伯雷故事集》，前几天我们收购了一部，心想您或许仍想买。虽然这并非是收录所有故事的完整版本，但价格十分便宜而且似乎编写还算严谨。我今天会将它寄给您，书价是一元三十五分。如果您读了\r\n之后，还想要更完整的乔叟作品，我会尽力去找。\r\n    弗兰克谨上\r\n\r\n星欺六\r\n    行了!白话版的乔叟真是够了!简直就像兰姆的《莎士比亚故事集》嘛——适合学龄儿童阅读!\r\n    充其量就是故事嘛，我讨厌虚构故事这事儿你是知道的。倒是里头描述一个吃相优雅、食不沾手的修女的那段还算有趣，换了我就不行，还是得动刀舞叉才成。其余内容全引不起我的兴趣，我就是不喜欢故事。如果乔叟能留下日记，里\r\n头规规矩矩记述他在理查三世的皇宫里当差的经过，那才是我真正该读的东西，否则我辛辛苦苦学文言文所为何来。\r\n    最近才刚扔了一本人家送的书——作者描述奥立弗·克伦威尔时代的社会状况。天晓得这个自作聪明的家伙是不是瞎瓣。他又不是那个时代的人，哪晓得那个时代的社会状况?如果我真想了解那个时代的社会状况，大可左阅弥尔顿，右\r\n读沃尔顿。这些货真价实的作品不仅能清楚明白告诉我那个时代的社会状况，还能引领我身历其境。\r\n    君不见沃尔顿尝曰:“若非身临现场、亲眼目睹，何以让读者尽信余言?”\r\n    这段话铿锵有力，深得吾心!我坚决拥护“亲身经历”的作者、作品。\r\n    附上两元支付这本乔臾，这样我在你们的户头里应该还会有六十五分钱的余额——比起我的其他任何一个户头都多。\r\n\r\n    海莲·汉芙 纽约州 纽约市21 东七十二大街305号\r\n    1964年3月30日\r\n亲爱的弗兰克——\r\n    我手头上正在编写给孩子们读的历史教材(已经写到第四本了，惊讶吧?)，忽然想起要帮一位朋友问你:你们有没有一套萧伯纳全集——他坚称书名前冠着“定本xxx”、暗红褐色的布面装帧——希望你有印象。我附上一张清单，上面列的是他已经有的几本。如果你可以为他补齐其他几本，先别全部寄来!他会分批购买，他和我一样——甲级贫户一个。你们可以直接寄给他，地址就写在清单上头。如果你嫌我的字太潦草看不懂的话，那是“第二十二大道”。\r\n    你可有塞西莉或梅甘的消息?\r\n祝一切顺利\r\n海莲\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1964年4月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    关于您的朋友想要的《定本萧伯纳作品全集》，原出版社目前仍有新书发行。红褐色布面精装——正如您所描述的。我想全套共有三十册。旧书反而不常见到，如果您的朋友不介意购买新书，我们可以安排对他方便的方式，每个月寄给\r\n他三或四册。\r\n    我们这几年来都没有塞西莉·法尔的消息，至于梅甘·韦尔斯，她在南非没多久就待不下去了，回国后曾到书店来看大家，给了大伙儿一个发“早跟你说了偏不听”牢骚的机会。不过她后来又搬去澳洲碰运气了。几年前曾收到她寄到书店的圣诞卡，最近则又断了音讯。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1965年10月4日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    很高兴再度收到您的来信。是的，我们都还健在如昔——老态益发龙钟，工作更加忙碌，口袋却没能加倍饱满。\r\n    我们购人了一本E.M.德拉菲尔德的《村姑日记》，一九四二年的麦克米兰版，书品很好，定价两美元。今天我会将书及账单为您寄去。\r\n    我们度过了一个“青春洋溢”的夏天——今年的游客比往年更多，大批年轻人全涌向卡纳比街朝圣。我们只能老远隔着安全距离打量着他们。老实说，我还蛮喜欢披头士的，只希望他们的歌迷们不要放声尖叫。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    海莲·汉芙  纽约州，纽约市21，东七十二大街305号\r\n    1968年9月30日\r\n我们都仍健在，可不是吗……\r\n    我为儿童编写美国历史读物已经长达四五年，得将这玩意儿告一段落了——为了写这些书，我自己还买了一大堆关于美国历史的书，全都是长相丑、装订差的美国书。我想，大概没有哪个循规蹈矩的英国人会在家里收藏詹姆斯·麦迪\r\n逊的制宪会议记录。或是T.杰弗逊写给J.亚当斯的书信吧。\r\n    你当上祖父没?告诉希拉和玛莉，她们的小孩将可以免费获赠有作者签名的《少年历史读本》，这样子应该能让她们比较愿意安定下来增产报国了吧。\r\n    我挑了一个细雨霏霏的星期天介绍一位年轻朋友读《傲慢与偏见》，她现在果然已经疯狂迷恋简·奥斯汀了。她的生日就在万圣节前后，你能帮我找几本奥斯汀的书当礼物吗?如果是一整套的话，先让我知道价钱，万一太贵，我会叫她的\r\n先生分摊，我和他各送半套。\r\n    祝诺拉和你周围所有人好。\r\n海莲\r\n\r\n    伦敦中西二区 查令十事街84号\r\n    1968年10月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市X\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    是的，我们依然健在，手脚也还勉强灵光。这个夏天真是把大家忙坏了。从美国、法国、北欧和其他各国来的大批观光客，几乎把我们比较好的皮面精装书全都搜刮一空。由于书源短缺。加上书价节节攀升，恐怕很难赶在您的朋友生日前找到任何奥斯汀的书，我们会设法在圣诞节之前为您办妥这件事。\r\n    诺拉和女孩儿们都很好。希拉已经开始执起教鞭;玛莉则和一位人品不错的小伙子订了亲—不过一年半载的还结不成婚，因为双方的经济条件都不太宽裕。所以。诺拉一心想当个福福泰泰的祖母，这希望恐怕愈来愈渺茫呢。\r\n    想念您\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1969年l月8日\r\n    H.汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街Los号\r\n敬爱的小姐:\r\n    我于近日整理公文档案时，偶然发现一封您于去年九月三十日寄给德尔先生的信。我在此非常遗憾地向您报告:德尔先生甫于上个礼拜天(十二月二十二日)去世了。丧礼则已在上周三(元月一日)举行。\r\n    德尔先生于十二月十五日因罹患急性盲肠炎被紧急送医，虽然立即施行手术，但他仍不幸因病情扩散，导致腹膜炎并发而于七日后不治。\r\n    德尔先生在本书店服务已超过四十年，加上马克斯先生也刚辞世未久，科恩先生对于这个不幸的事件自然万分悲坳。\r\n    您是否仍须本店为您寻找简·奥斯汀的书?\r\n         \r\n马克斯与科恩书店\r\n              秘书 琼·托德敬上\r\n\r\n(未署明日期，邮戳日期为1969年1月9日。无寄信地址〕\r\n\r\n亲爱的海莲:\r\n    感谢您寄来的慰问信，我完全不认为那冒犯了我。我真希望您在弗兰克在世时能够与他见面，并亲自结识他本人。我原先只知道他是一个处事严谨同时也很幽默的人;现在还了解了他在待人处事上更是一位谦冲的君子。我收到许许多多来\r\n自各地的信，都异口同声地赞杨他对古书业的贡献;许多人还说他是如何饱富学识而又不吝予与其他人分享……如果您想要看这些信，我可以将它们寄给您。\r\n    不瞒您说，我过去一直对您心存妒忌，因为弗兰克生前如此爱读您的来信，而你们俩似乎有许多共通点;我也羡慕您能写出那么好的信。弗兰克和我却是两个极端不同的人，他总温和和有耐性;而因为我的爱尔兰出身，我的脾气总是又倔又拗。生命就是选么爱捉弄人，他从前总是试图教导我书中的知识……我现在好想念他。\r\n    孩子们都很懂事、我为此深感欣慰。像我这样要一辈子孤寂以终的人想必大有人在吧。\r\n    希望您能原谅我的字迹潦草。\r\n                 祝福您\r\n                 诺拉\r\n    我盼望有一天您还是能来造访我们，两个孩子都很想见见您。\r\n    1969年4月11日\r\n\r\n亲爱的凯瑟琳——\r\n    我正在整理我的书架，现在抽空蹲在书堆中写信给你，祝你们一路顺风。我希望你和布莱恩在伦敦能玩得尽兴。布莱恩在电话中对我说:“如果你手头宽裕些就好了，这样子你就可以和我们一道去了。”我一听他这么说，眼泪差点儿要夺\r\n眶而出。\r\n    大概因为我长久以来就渴望能踏上那片土地……我曾经只为了瞧伦敦的街景而看了许多英国电影。记得好多年前有个朋友曾经说:人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我:“就在那儿!”\r\n    或许是吧。就算那儿没有，环顾我的四周……我很笃定:它们已在此驻足。\r\n    卖这些好书给我的那个好心人已在数月前去世了，书店老板马克斯先生也已不在人间。但是，书店还在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……\r\n海莲\r\n\r\n尾声1969年10月\r\n\r\n    伦敦北十一区 温顿道\r\n    1969年10月\r\n亲爱的海莲:\r\n    这是德尔家族第二号通讯员首次发言!\r\n    首先，我要对我们长时间的静默向您致歉。请相信我，其实我们心中一直惦记着您。只是不知如何将这样的意念用文辞表达。直到今天我们又收到您的来信，我们都感到万分惭愧，并决定应该立刻动笔回信给您。\r\n    我们很高兴得知您的出版计划，也同意并很愿意提供这些信件供您作为出书之用。\r\n    我们现在搬到可爱的新家，常常会想着:如果父亲依然健在，一定也会喜欢这儿。\r\n    再多的悲坳亦无济于事。虽然父亲生前从未拥有财富、权势，但他始终是一个快乐自得又具有丰富内涵的人，我们应该以拥有一位这样的亲人而深感欣慰。\r\n    也许只是为了冲淡愁思，我们都尽量让自己忙碌着。玛莉白天在大学图书馆辛勤工作，晚上则和朋友开车出游散心，深夜方归;我除了正常的教职外，还兼修一个学位;妈妈则整天忙上忙下，一刻也不教自己闲下来!所以，恐怕大家都无法好好地回信——不过，当然我们会很高兴能继续收到您的来信。无论如何，只要有空，我们还是会努力回信的，并期盼能再有您的消息。\r\n    希拉诚挚敬上\r\n\r\n\r\n**************\r\n*附录\r\n***************\r\n\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。						\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(1)\r\n---------------\r\n\r\n\r\n　　唐诺\r\n　　乍读这本书稿时，我一直努力在回想，查令十字街84号这家小书店究竟是长什么个模样(我坚信写书的海莲·汉芙不是胡诌的，在现实世界中必然有这么一家“坚实”存在的书店)，我一定不止一次从这家书店门口走过，甚至进去过，还取下架上的书翻阅过——《查令十字街84号》书中，通过一封1951年9月10日海莲·汉芙友人玛克辛的书店寻访后的信，我们看到它是“一间活脱从狄更斯书里头蹦出来的可爱铺子”，店门口陈列了几架书(一定是较廉价的)，店内则放眼全是直抵天花板的老橡木书架，扑鼻而来全是古书的气味，那是“混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……”，当然，还有一位五十开外年纪、以老英国腔老英国礼仪淡淡招呼你的男士(称店员好像不礼貌也不适切)。\r\n　　但这不也就是半世纪之后今天、查令十字街上一堆老书店的依然长相吗？——如此悬念，让我再次鼓起余勇、生出远志，很想再去查令十字街仔细查看一次，对一个有抽烟习性又加上轻微幽闭恐惧毛病如我者，这长达二十小时的飞行之旅，我自以为是个很大的冲动而且很英勇的企图不是吗？\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。\r\n　　至少，本书的译者一定会支持我的武断——陈建铭，就我个人的认识，正是书籍阅读世界的此道中人。一般，社会对他的粗浅身分辨识，是个优美、老英国典雅风味却内向不擅长议价的绝佳书版美术设计者，但这本《查令十字街84号》充分暴露了他的原形，他跳出来翻译了此书，而且还在没跟任何出版社联系且尚未跟国外购买版权的情况下就先译出了全书(因此，陈建铭其实正是本书的选书人)，以他对出版作业程序的理解，不可能不晓得其后只要一个环节没配合上，所有的心血当场成为白工，但安静有条理的陈建铭就可以因为查令十字街忽然疯狂起来。\r\n　　这是我熟悉、喜欢、也经常心生感激的疯子，在书籍和阅读的世界中，他们人数不多但代代有人，是这些人的持续存在，且持续进行他们一己“哈萨克人式的小小游击战”(借用赫尔岑的自况之言)，才让强大到几近无坚不摧的市场法则，始终无法放心地遂行其zhuanzhi统治，从而让书籍和阅读的世界，如汉娜·鄂兰谈本雅明时说的，总是在最边缘最异质的人身上，才得到自身最清晰的印记。\r\n　　在与不在的书街\r\n　　《查令十字街84号》这部美好的书，系以1949年至1969年长达廿年流光，往复于美国纽约和这家小书店的来往信函交织而成——住纽约的女剧作家买书，任职“马克斯与科恩书店”的经理弗兰克·德尔负责寻书寄书，原本是再乏味不过的商业往来，但很快的，书籍击败了商业，如约翰·房龙说“一个马槽击败了一个帝国”(当然，在书籍堆栈的基础之上，一开始是汉芙以她莽撞如火的白羊座人热情凿开缺口，尤其她不断寄送鸡蛋、火腿等食物包裹给彼时因战争物资短缺、仰赖配给和黑市的可怜英国人)，人的情感、心思乃至于咫尺天涯的友谊开始自由流窜漫溢开来。查令十字街那头，他们全体职员陆续加入(共六名)，然后是德尔自己的家人(妻子诺拉和两个女儿)，再来还有邻居的刺绣老太太玛丽·伯尔顿；至于纽约这边，则先后有舞台剧女演员玛克辛、友人金妮和埃德替代汉芙实地造访“她的书店”，惟遗憾且稍稍戏剧性的是，反倒汉芙本人终究没能在一切落幕之前踩上英国，实践她念念不忘的查令十字街之旅。全书结束于1969年10月德尔大女儿替代父亲的一封回信，德尔本人已于1968年底腹膜炎病逝。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(2)\r\n---------------\r\n\r\n\r\n　　一样产自英国的了不起小说家格林，在他的《哈瓦那特派员》中这么说：“人口研究报告可以印出各种统计数值、计算城市人口，借以描绘一个城市，但对城里的每个人而言，一个城市不过是几条巷道、几间房子和几个人的组合。没有了这些，一个城市如同陨落，只剩下悲凉的记忆。”——是的，1969年之后，对海莲·汉芙来说，这家书店、这道书街已不可能再一样了，如同陨落，只因为“卖这些好书给我的好心人已在数月前去世了，书店老板马克斯先生也已不在人间”，这本《查令十字街84号》于是是一本哀悼伤逝的书，纪念人心在二十年书籍时光中的一场奇遇。\r\n　　但海莲·汉芙把这一场写成书，这一切便不容易再失去一次了，甚至自此比她自身的生命有了更坚强抵御时间冲刷的力量——人类发明了文字，懂得写成并印制成书籍，我们便不再徒然无策地只受时间的摆弄宰制，我们甚至可以局部地、甚富意义地击败时间。\r\n　　书籍，确实是人类所成功拥有最好的记忆存留形式，记忆从此可置放于我们的身体之外，不随我们肉身朽坏。\r\n　　也因此，那家书店，当然更重要是用一本一本书铺起来的查令十字街便不会因这场人的奇遇戛然中止而跟着消失，事实上，它还会因多纳入海莲·汉芙的美好记忆而更添一分光晕色泽，就像它从不间断纳入所有思维者、纪念者、张望者、梦想者的书写一般，所以哀伤的汉芙仍能鼓起余勇地说：“但是，书店还是在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……”\r\n　　这是不会错的，今天，包括我个人在内，很多人都可以证实，查令十字街的确还在那儿，我是又过了十多年之后的八十年代、九十年代去的，即便84号的“马克斯与科恩书店”很遗憾如书末注释说的，没再撑下去，而成为“柯芬园唱片行”，但查令十字街的确还好好在那里。\r\n　　一道时间大河\r\n　　查令十字街，这个十字不是指十字路口，而是十字架的意思，事实上它是一道长约一公里许的蜿蜒市街，南端直抵泰晤士河，这里有最漂亮的查令十字街车站，如一个美丽的句点，往北路经国家艺廊，穿过苏活区和唐人街，旁及柯芬园，至牛津街为止，再往下走就成了托登罕路，很快就可看到著名的大英博物馆(大英博物馆一带又是另一个书店聚集处，但这里以精印的彩色大版本艺术书为主体)。\r\n　　老英国老伦敦遍地是好东西，这是老帝国长而辉煌的昔日一样样堆栈下来的，如书中汉芙说的(类似的话她说了不止一回)：“记得好多年前有个朋友曾经说：人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我：‘就在那儿！’”\r\n　　然而，和老英国其他如夕晖晚照荣光事物大大不同之处在于，查令十字街不是遗迹不是封存保护以待观光客拍照存念的古物，它源远流长，但它却是active，现役的，当下的，就在我们谈话这会儿仍孜孜勤勤劳动之中，我们可同时缅怀它并同时使用它，既是历史从来的又是此时此刻的，这样一种奇特的时间完整感受，仔细想起来，不正正好就是书籍这一人类最了不起发明成就的原来本质吗？我们之所以丧失了如此感受，可能是因为我们持续除魅的现实世界已成功一并驱除了时间，截去了过去未来，成为一种稍纵即逝却又驻留不去的所谓“永恒当下”——有生物学者告诉我们，人类而外的其他动物和时间的关系极可能只有这样，永恒的当下，记忆湮渺只留模糊的鬼影子，从而也就产生不来向前的有意义瞻望，只剩如此窄迫不容发的时间隙缝，于是很难容受得了人独有的持续思维和精致感受，只有不占时间的本能反射还能有效运作，这其实就是返祖。\r\n　　更正确地说，查令十字街的时间景观，指的不单单是它的经历、出身以及悠悠存在的岁月，而是更重要的，就算你不晓得它的历史沿革和昔日荣光，你仍可以在乍乍相见那一刻就清晰捕捉到的实时景观，由它林立的各个书店和店中各自藏书所自然构成——查令十字街的书店几乎每一家一个样，大小、陈列布置、书类书种、价格以及书店整体氛围所透出的难以言喻的鉴赏力、美学和心事。当然，书店又大体参差为一般新书书店和二手古书店的分别，拉开了时间的幅员，但其实就算卖新书的一般书店，彼此差异也是大的，各自收容着出版时日极不一致的各色书籍，呈现出极丰硕极细致的各自时间层次。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(3)\r\n---------------\r\n\r\n\r\n　　不太夸张地说，这于是成了最像时间大河的一条街，更像人类智识思维的完整化石层，你可以而且势必得一家一家地进出，行为上像进陈列室而不是卖场。\r\n　　相对来说，我们在台湾所谓的“逛书店”，便很难不是只让自我感觉良好的溢美之辞。一方面，进单一一家书店比较接近纯商业行为的“购买”，而不是带着本雅明式游手好闲意味的“逛”，一本书你在这家买不到，大概另一家也就休想；另一方面，“逛”，应该是不完全预设标的物的，你期待且预留着惊喜、发现、不期而遇的空间，但台湾既没二手书店，一般书店的书籍进退作业又积极，两三个月前出版的书，很可能和两三千年前的出土文物一样不好找。\r\n　　连书店及其图书景观都是永恒当下的，在我们台湾。\r\n　　永恒当下的灾难\r\n　　海莲·汉芙在书中说到过她看书买书的守则之一，对我们毋宁是极陌生到足以吓人一跳的，她正色告诉德尔，她绝不买一本没读过的书，那不是跟买衣服没试穿过一样冒失吗？当然我们没必要激烈如这位可敬的白羊座女士，但这其实是很有意思的话，说明旧书(广义的，不单指的珍版珍藏之书)的购买、收存和再阅读，不仅仅只是屯积居奇的讨人厌行为或附庸风雅的恶心行为而已。这根源于书籍的不易理解，不易完整掌握的恒定本质，尤其是愈好、内容愈丰硕、创见之路走得愈远的书，往往远远超过我们当下的知识准备、道德准备和情感准备，我们于是需要一段或长或短的回身空间与它相处。好书像真爱，可能一见钟情，但死生契阔与子成说，执子之手与子偕老的杳远理解和同情却总需要悠悠岁月。\r\n　　因此，从阅读的需求面来说，一本书的再阅读不仅仅只是可能，而是必要，你不能希冀自己一眼就洞穿它，而是你十五岁看，二十岁看，四十岁五十岁看，它都会因着你不同的询问、关注和困惑，开放给你不一样的东西，说真的，我努力回想，还想不出哪本我真心喜欢的书没有而且不需要再再重读的(你甚至深深记得其中片段，意思是你在记忆中持续重读)；也因此，从书籍取得的供给面来看，我们就应该聪明点给书籍多一点时间、给我们自己多一点机会，历史经验一再告诉我们，极多开创力十足且意义重大的书，我们当下的社会并没那个能力一眼就认得出来，不信的人可去翻阅大名鼎鼎的纽约时报历来书评(坊间有其结集成书的译本)，百年来，日后证明的经典著作，他们漏失掉的比他们慧眼捕捉到的何止十倍百倍，而少数捕捉到的书中又有诸如塞林格的《麦田里的守望者》或钱德勒的《大眠》被修理得一无是处(理由是脏话太多云云)。一个社会，若意图在两星期到一个月内就决定一本书的好坏去留，要求书籍打它不擅长的单败淘汰赛，这个社会不仅自大愚蠢，而且可悲的一步步向着灾难走去。\r\n　　一种只剩永恒当下的可悲灾难。\r\n　　部分远大于全体\r\n　　便是这个永恒当下的灾难启示，让我们得以在书籍暨阅读的世界中，推翻一项亘古的数学原理——这是柏拉图最爱引用的，全体永远大于部分，但我们晓得事实并不尽然，短短的一道查令十字街，的确只是我们居住世界的一个小小部分，但很多时候，我们却觉得查令十字街远比我们一整个世界还大，大太多了。\r\n　　最是在什么时候，我们会生出如此诡异的感觉呢？特别当我们满心迫切的困惑不能解之时。我们很容易在一本一本书中再再惊异到，原来我们所在的现实世界，相较于既有的书籍世界，懂得的事这么少，瞻望的视野这么窄，思维的续航能力这么差，人心又是这么封闭懒怠，诸多持续折磨我们的难题，包括公领域的和私领域的，不仅有人经历过受苦过认真思索过，甚至还把经验和睿智细腻的解答好好封存在书中。\r\n　　从形态上来看，我们眼前的世界往往只有当下这薄薄的一层，而查令十字街通过书籍所揭示的世界图像，却是无尽的时间层次叠合而成的，包括我们因失忆而遗失乃至于根本不知有过的无尽过去，以及我们无力也无意瞻望的无尽未来。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(4)\r\n---------------\r\n\r\n\r\n　　看看穆勒的《论自由》和《论代议政治》，这是足足一百五十年前就有的书，今天我们对自由社会和民主政治的建构、挫折、一再摔落的陷阱以及自以为聪明的恶意操弄，不好端端都写在书里头吗？\r\n　　看看李嘉图的《政治经济学原理》，这是两百年前的书，书中再清晰不过所揭示的经济学最基本道理和必要提醒，我们今天，尤其手握财经权力的决策者，不还在日日持续犯错吗？\r\n　　或者看看本雅明的《发达资本主义时代的抒情诗人》，这又是超过半个世纪以前的书，而今天，我们的大台北市才刚刚换好新的人行步道、才刚刚开始学习在城市走路并试图开始理解这个城市不是吗？\r\n　　还是我们要问宪法的问题(内阁制、总统制、双首长制、还有神秘的塞内加尔制)？要问民族主义和民粹主义的问题？问生态环保或仅仅只是整治一条基隆河的问题？问男女平权？问劳工和失业？问选举制度和选区规划？问媒体角色和自律他律？或更大哉问的问整体教育和社会价值暨道德危机等等问题？\r\n　　是的，如海莲·汉芙说的，书店还是在那儿。\r\n　　全世界最便宜的东西\r\n　　而查令十字街不仅比我们眼前的世界大，事实上，它做得更好——查令十字街不仅有着丰硕的时间层次，还呈现具体的空间分割；它是一道川流不息的时间之街，更是一个个书店、隔间、单一书籍所围拥成的自在小世界，让闲步其中的人柳暗花明。\r\n　　我猜，这一部分原因有历史的偶然渗入作用而成，比方说，老式的、动辄百年以上的老伦敦建筑物，厚实坚强的石墙风雨不动的制限了商业流窜的、拆毁一切夷平一切的侵略性格，因此，小书店各自盛开如繁花，即便是大型的综合性书店，内部格局也曲折回旋，每一区块往往是封闭的、隔绝的，自成洞天，毋宁更像书籍层层架起的读书阅览小房间而非卖场；而且，美国的霸权接收，让英文不随老帝国的坠落而衰败，仍是今天的“准世界语”，仍是普世书籍出版活动的总源头和荟萃之地，因此，你一旋身，才两步路便由持续挣扎的东欧世界出来，却马上误入古怪拼字，但极可能正是人类最远古家乡非洲黝暗世界，如同安博托·艾柯在《玫瑰之名》书中最(禁止)的惊心动魄一幕——第七天，威廉修士和见习僧艾森终于进入了大迷宫图书馆中一切秘密埋藏所在的非洲之末。\r\n　　一个无垠无边的智识世界，却是由一个个小洞窟构成的。\r\n　　我尤其喜欢查令十字街的一个个如此洞窟，一方面，这有可能正是人类亘古的记忆存留，是某种乡愁，像每一代小孩都有寻找洞窟打造洞窟置身洞窟的冲动，有某种安适安全之感，而读书，从阅读、思索到着迷，最根柢处，本来就是宛如置身一己洞窟的孤独活动；另一方面，我总时时想到列维—施特劳斯的话，这些自成天地般洞窟的存在，提供我们逃避的机会，逃避什么样的压迫呢？逃避一种列维—施特劳斯指称的大众化现象，意即一种愈发一致的、无趣的、再没性格可言的普世性可怖压逼(正是社会永恒当下的呈现)，而这些动人的洞窟，正像《爱丽丝漫游仙境》的树洞，你穿过它，便掉落到一个完全异质、完全始料未及的世界里去。\r\n　　于是，我遂也时时忧虑我们最终仍会失去属于我们这一代的查令十字街，如同汉芙早已失去她的查令十字街一般，我们的杞忧，一方面是现实中断续传来的不利信息(如商业的腐蚀性只是被减缓，并没真正被阻止)，更是人面对足够美好事物的很自然的神经质反应，你深知万事万物持续流变，珍爱的东西尤其不可能一直存留，如朝霞，如春花，如爱情。\r\n　　但你可以买它——当然不是整条查令十字街，而是它真正赖以存在、赖以得着意义的书籍，市街从不是有效抵御时间风蚀的形式，书籍才是，就像汉芙所说：“或许是吧，就算那儿没有(意指英国和查令十字街)，环顾我的四周(意指她从查令十字街买到的书)……我很笃定，它们已在此驻足。”\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(5)\r\n---------------\r\n\r\n\r\n　　从事出版已超过半辈子之久，我个人仍始终有个问题得不到满意的答案：我始终不真正明白人们为什么不买书？这不是全世界最便宜的一样东西吗？一个人类所曾拥有过最聪明最认真最富想像力最伟大的心灵，你不是极可能只用买一件看不上眼衣服的三千台币就可买下他奇迹一生所有吗(以一名作家，一生十本书，一本书三百元计，更何况这么买通常有折扣)？你不是用吃一顿平价午餐的支付，就可得到一个美好的洞窟、以及一个由此联通的完整世界吗？\r\n　　汉芙显然是同我一国的，她付钱买书，但自掏腰包寄食物还托朋友送丝袜，却仍觉得自己占便宜，在1952年12月12日，她说的是：“我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年；而你们送给我的礼物，却能和我朝夕相处、至死方休；我甚至还能将它遗爱人间而含笑以终。”而在1969年4月11日的最终决算，她仍得到“我亏欠它良多”的结论。\r\n　　美国当前最好的侦探小说家，同样也住纽约的劳伦斯·卜洛克也如此想，他在《麦田贼手》一书，通过一名仗义小偷之口对一名小说家(即塞林格)说：“这个人，写了这么一本书，改变了我们整整一代人，我总觉得我欠他点什么。”所以——买下它，我指的是书，好好读它，在读书时日里若省下花费，存起来找机会去一趟查令十字街，趁它还在，如果你真的成行并顺利到那儿，请代我们献上一吻，我们都亏欠它良多……\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(1)\r\n---------------\r\n\r\n\r\n　　张立宪\r\n　　“你们若恰好路经查令十字街84号，请代我献上一吻，我亏欠她良多……”\r\n　　生活中发生了一桩小小的笑话。一位朋友在英国，按照我的介绍，某一天逛伦敦的书店一条街——查令十字街，并到84号朝圣。奈何那里已经改为一家酒吧，只是在门边挂个铜牌，上书“查令十字街84号，马克斯与科恩书店的旧址，因为海莲·汉芙的书而闻名天下”。收银小姐看着她笑问：“为那本书来的吧？”她只好狠狠地点一瓶红酒，并为我买了期待已久的书《查令十字街84号》，然后寄往北京，还兴冲冲地先用数码相机将书拍了照mail过来，让我预热一下。结果，不幸的很，这本书在大英帝国的邮政系统兜了一个圈子，又回到了朋友的手上——她将收件人与寄件人的位置弄颠倒了。\r\n　　其实以我的英文修养，肯定啃不动原版书，但对于这本书，还是希望能保留一本，因为它被誉为“爱书人的《圣经》”。\r\n　　这本书讲的就是一个纽约爱书人通过书信往来在伦敦一家古旧书店(书名即是这家书店的地址)淘书并建立深厚友谊的故事。来往的书信被她汇集成此书，成为读书人的掌上明珠。\r\n　　得到这本书并非易事。好在，根据原著拍摄的同名电影已经有DVD出售，万幸的是，我买到了，并且看了。该片由美国哥伦比亚公司1986年拍就，片长100分钟，担纲主演的是演技派演员安妮·班克劳夫特和安东尼·霍普金斯，拍得真是无可挑剔。\r\n　　之所以说是“万幸”，是因为影碟被碟商译成了一个耸人听闻的动作片名字——《迷阵血影》。我敢担保，即使你看到这张影碟，最大的可能也是与其失之交臂。这个异想天开的译名惹恼了一个台湾人，他叫陈建铭，是诚品书店古书区的员工，在没与任何出版社联系出版且未跟国外购买版权的情况下，就先译出了全书。按照他的说法，这般牛劲发作，就是因为《迷阵血影》这个名字，而且影片的对白字幕也是惨不忍睹，“我翻译这本书，多少也想为它赎点儿罪罢”。\r\n　　恕我饶舌，复述一遍这个再简单不过的故事。\r\n　　穷困的女作家海莲受不了纽约昂贵庸俗的古旧书店，便按照《星期六文学评论》上的地址，给位于伦敦查令十字街84号的马克斯与科恩书店(后来被海莲派去伦敦侦察的好友形容成一家“活脱从狄更斯书里头蹦出来的可爱铺子”)写了一封信，求购一些绝版图书。这一天是1949年10月5日。\r\n　　很快，回信和她要的书就来了，那些书令海莲的书架相形见绌。双方的信任和欣喜很快达成，除了海莲有一点点麻烦，她是个连付账和找零都搞不清楚的女人，更不用说将英镑换算成美元了。马克斯与科恩书店的经理弗兰克除了满足她购书的要求外，还得给她准备英镑和美元两种发票。\r\n　　温暖的相知借助娓娓道来的书信，很快就俘获了远隔重洋的海莲和弗兰克。\r\n　　五十年代初期的英国百废待兴，物资实行配给制。海莲就从美国给书店的店员们寄来火腿、鸡蛋和香肠，让他们吃到很久没有见过的“完整而大块”的肉。而弗兰克并不是不知感恩的人，他开始在英国各地奔波，出入豪宅，为存货不多的书店添置新品，踏破铁鞋，为她寻觅难得一见的珍本。\r\n　　日子一天天地过去，书信成为他们平静流淌的生活中无时不在的旁白。\r\n　　海莲不是没想过去伦敦看看书店看看弗兰克。她终于有了自己的积蓄，而英女王的登基又使得赴英的费用打了折。眼看可以成行，但她的牙逼着她留在了纽约。她只好给弗兰克写信：“我陪着我的牙，而牙医却在度蜜月，他的结婚费用是我出的……”\r\n　　弗兰克只好为她和刚刚登基的伊丽莎白女王祝福。\r\n　　书照买，信照写。\r\n　　到了这一天，海莲的信三个月后才接到回音，她被告知：弗兰克于1968年12月22日病逝。\r\n　　海莲马上赶到查令十字街84号。走进即将被拆迁的马克斯与科恩书店时，距离她第一次给这里写信，已经过去了二十年。\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(2)\r\n---------------\r\n\r\n\r\n　　她笑着对空荡荡的书店说：“我来了，弗兰克，我终于来了。”\r\n　　通过胶片来诉说图书的故事，总显得不太解气。不过，看平静的生活围绕着他们的讨书买书谈书一幕幕展开，仿佛将惟一彩色的道具放在黑白环境里，使原本素朴的书本也显得绚丽，一如荒漠甘泉。\r\n　　事关读书的故事总是令人解颐。\r\n　　海莲对一本拉丁文版《圣经》极为不满，在给弗兰克的信中说翻译简直是想毁掉这本世界上“最美的散文”，建议拿正宗的拉丁文版对照来读，才不致暴殄天物，并出卖了她七大姑八大姨的说法加以佐证。可爱的女人，总是将自己试图保守的机密在另一种心情下泄露无遗。\r\n　　弗兰克看到纽曼的《大学论》，写信问海莲：“有兴趣买初版的吗？”同时叮嘱店员为她留下来。镜头马上从伦敦切到纽约，海莲对着空气质问：“你有初版的《大学论》，只要六美元，居然还傻傻地问我‘你要吗’？”“亲爱的弗兰克：是的，我要。我本不在乎是不是初版，可这本书的初版！……”\r\n　　等她收到这本有百年历史的初版书后，写信对他说：“我占有它有一种罪恶感，那么漂亮的封面和烫金，它理应属于某幢英国乡间的木造宅邸才对。”\r\n　　这个莽撞如火的白羊座女人颇有豪侠之风，不但体现在她为书店员工邮寄生活用品的慷慨上，甚至她的性格也凸显在信纸上：“我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天，就像米尼弗·奇维一样，余生也晚。而我也只能学他‘干咳两声，自叹一句：奈何老天作弄’，然后继续借酒浇愁。”\r\n　　“这是个堕落颓废的年代，他们居然把漂亮的旧书页撕下来当包装纸。上面描述的是一场战役的中段，但我已经看不出是哪场战役了……”海莲在信中抱怨，又该可怜的弗兰克忙活了。\r\n　　在一家豪宅，弗兰克见到了帮海莲遍寻不着的伊丽莎白一世时期的情诗集，以书店全体员工的名义寄给她。“你们相信它是在我生日那天寄达的吗？这是我拥有的第一本镶金边的书。可惜你们太客气了，将字句写在卡片上，而非扉页上。你们全都是爱书人，惟恐会减损书的价值，其实你们已经为书的主人甚至书未来的主人提升了它的价值。”海莲在回信中兴奋地絮叨。\r\n　　……\r\n　　1969年1月8日，海莲收到马克斯与科恩书店通报弗兰克的死讯，那封信的最后一句是：\r\n　　“你还要我们寻找你所订的书吗？”\r\n　　该说说海莲和弗兰克之间的事儿了。\r\n　　海莲的爱人死于二战，她终身未嫁。弗兰克则有妻子和两个女儿。一大两小三个女人会收到海莲寄自美国的尼龙袜，弗兰克的太太也会和海莲雅谑几句：“弗兰克给你的照片够难看的，但他狡辩说本人比照片帅多了，我们就让他臭美去吧。”\r\n　　一切看来都那么正常，正常到两人相识二十年却缘悭一面，正常到两人通信数百封而未涉一个“爱”字。\r\n　　但是，弗兰克死后，他的太太写信给海莲说：“不怕你见笑，有时候我还会嫉妒你。”\r\n　　马克斯与科恩书店的店员们把海莲想像成一个“年轻，成熟，时髦”的女人，海莲老实告诉他们，自己“和百老汇的乞丐一样时髦”。就是这样一个执拗邋遢的女人，将骄蛮趣致的女性一面全都呈现给弗兰克。她会为一本欺世盗名的书而冲弗兰克发飙，将满腔怨气倾泄到打字机上，然后突然收起霸道，对着空气娇媚地笑了：“弗兰克，你是惟一了解我的人。”\r\n　　独身的海莲是自由的，而弗兰克眼前连这团自由的空气也没有。他只能努力让自己正常地度过二十年的光阴，只是在某一刻，他会注意到书店中驻足的一个女子，大概就是他想像中那个女人的模样？她说她来自美国，他的眼光一下子变得热切，却又不是，他好像习惯了这种失望和等待。电视机里在转播纽约元旦嘉年华的情景，广场上人多如织，他的眼睛在搜寻什么？\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(3)\r\n---------------\r\n\r\n\r\n　　只是到了打烊的时候，书店里再没有别人，最柔软的情思才在这一刻展开，他会让自己的眼睛盯住某一处，款款道来。此刻，那个女人正躺在自己的床上，身罩破旧的睡衣，翻看着他抚摩过的书，点燃一支烟，不时发出一声声咳嗽。\r\n　　弗兰克死后，海莲来到查令十字街84号，站立的地方，正是他深情凝视的所在。\r\n　　经过了二十年岁月的打磨，他们的眼神都那么一致。\r\n　　海莲所推崇的英国玄学诗人、散文家多恩有一句话：“全体人类就是一本书。当一个人死亡，这并非有一章被从书中撕去，而是被翻译成一种更好的语言。”\r\n　　我想，当爱情以另外一种方式展现铺陈时，也并非被撕去，而是翻译成了一种更好的语言。上帝派来的那几个译者，名叫机缘，名叫责任，名叫蕴藉，名叫沉默。\r\n　　还有一位，名叫怀恋。\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n', null);
INSERT INTO `p_task` VALUES ('a73fada1-2894-4a81-8bbf-9ebee066253d', '测试8[卷30]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:46', '2017-09-08', '0', '80', '0', '0', '1', '30', '　　日子一天天地过去，书信成为他们平静流淌的生活中无时不在的旁白。\r\n　　海莲不是没想过去伦敦看看书店看看弗兰克。她终于有了自己的积蓄，而英女王的登基又使得赴英的费用打了折。眼看可以成行，但她的牙逼着她留在了纽约。她只好给弗兰克写信：“我陪着我的牙，而牙医却在度蜜月，他的结婚费用是我出的……”\r\n　　弗兰克只好为她和刚刚登基的伊丽莎白女王祝福。\r\n　　书照买，信照写。\r\n　　到了这一天，海莲的信三个月后才接到回音，她被告知：弗兰克于1968年12月22日病逝。\r\n　　海莲马上赶到查令十字街84号。走进即将被拆迁的马克斯与科恩书店时，距离她第一次给这里写信，已经过去了二十年。\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(2)\r\n---------------\r\n\r\n\r\n　　她笑着对空荡荡的书店说：“我来了，弗兰克，我终于来了。”\r\n　　通过胶片来诉说图书的故事，总显得不太解气。不过，看平静的生活围绕着他们的讨书买书谈书一幕幕展开，仿佛将惟一彩色的道具放在黑白环境里，使原本素朴的书本也显得绚丽，一如荒漠甘泉。\r\n　　事关读书的故事总是令人解颐。\r\n　　海莲对一本拉丁文版《圣经》极为不满，在给弗兰克的信中说翻译简直是想毁掉这本世界上“最美的散文”，建议拿正宗的拉丁文版对照来读，才不致暴殄天物，并出卖了她七大姑八大姨的说法加以佐证。可爱的女人，总是将自己试图保守的机密在另一种心情下泄露无遗。\r\n　　弗兰克看到纽曼的《大学论》，写信问海莲：“有兴趣买初版的吗？”同时叮嘱店员为她留下来。镜头马上从伦敦切到纽约，海莲对着空气质问：“你有初版的《大学论》，只要六美元，居然还傻傻地问我‘你要吗’？”“亲爱的弗兰克：是的，我要。我本不在乎是不是初版，可这本书的初版！……”\r\n　　等她收到这本有百年历史的初版书后，写信对他说：“我占有它有一种罪恶感，那么漂亮的封面和烫金，它理应属于某幢英国乡间的木造宅邸才对。”\r\n　　这个莽撞如火的白羊座女人颇有豪侠之风，不但体现在她为书店员工邮寄生活用品的慷慨上，甚至她的性格也凸显在信纸上：“我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天，就像米尼弗·奇维一样，余生也晚。而我也只能学他‘干咳两声，自叹一句：奈何老天作弄’，然后继续借酒浇愁。”\r\n　　“这是个堕落颓废的年代，他们居然把漂亮的旧书页撕下来当包装纸。上面描述的是一场战役的中段，但我已经看不出是哪场战役了……”海莲在信中抱怨，又该可怜的弗兰克忙活了。\r\n　　在一家豪宅，弗兰克见到了帮海莲遍寻不着的伊丽莎白一世时期的情诗集，以书店全体员工的名义寄给她。“你们相信它是在我生日那天寄达的吗？这是我拥有的第一本镶金边的书。可惜你们太客气了，将字句写在卡片上，而非扉页上。你们全都是爱书人，惟恐会减损书的价值，其实你们已经为书的主人甚至书未来的主人提升了它的价值。”海莲在回信中兴奋地絮叨。\r\n　　……\r\n　　1969年1月8日，海莲收到马克斯与科恩书店通报弗兰克的死讯，那封信的最后一句是：\r\n　　“你还要我们寻找你所订的书吗？”\r\n　　该说说海莲和弗兰克之间的事儿了。\r\n　　海莲的爱人死于二战，她终身未嫁。弗兰克则有妻子和两个女儿。一大两小三个女人会收到海莲寄自美国的尼龙袜，弗兰克的太太也会和海莲雅谑几句：“弗兰克给你的照片够难看的，但他狡辩说本人比照片帅多了，我们就让他臭美去吧。”\r\n　　一切看来都那么正常，正常到两人相识二十年却缘悭一面，正常到两人通信数百封而未涉一个“爱”字。\r\n　　但是，弗兰克死后，他的太太写信给海莲说：“不怕你见笑，有时候我还会嫉妒你。”\r\n　　马克斯与科恩书店的店员们把海莲想像成一个“年轻，成熟，时髦”的女人，海莲老实告诉他们，自己“和百老汇的乞丐一样时髦”。就是这样一个执拗邋遢的女人，将骄蛮趣致的女性一面全都呈现给弗兰克。她会为一本欺世盗名的书而冲弗兰克发飙，将满腔怨气倾泄到打字机上，然后突然收起霸道，对着空气娇媚地笑了：“弗兰克，你是惟一了解我的人。”\r\n　　独身的海莲是自由的，而弗兰克眼前连这团自由的空气也没有。他只能努力让自己正常地度过二十年的光阴，只是在某一刻，他会注意到书店中驻足的一个女子，大概就是他想像中那个女人的模样？她说她来自美国，他的眼光一下子变得热切，却又不是，他好像习惯了这种失望和等待。电视机里在转播纽约元旦嘉年华的情景，广场上人多如织，他的眼睛在搜寻什么？\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(3)\r\n---------------\r\n\r\n\r\n　　只是到了打烊的时候，书店里再没有别人，最柔软的情思才在这一刻展开，他会让自己的眼睛盯住某一处，款款道来。此刻，那个女人正躺在自己的床上，身罩破旧的睡衣，翻看着他抚摩过的书，点燃一支烟，不时发出一声声咳嗽。\r\n　　弗兰克死后，海莲来到查令十字街84号，站立的地方，正是他深情凝视的所在。\r\n　　经过了二十年岁月的打磨，他们的眼神都那么一致。\r\n　　海莲所推崇的英国玄学诗人、散文家多恩有一句话：“全体人类就是一本书。当一个人死亡，这并非有一章被从书中撕去，而是被翻译成一种更好的语言。”\r\n　　我想，当爱情以另外一种方式展现铺陈时，也并非被撕去，而是翻译成了一种更好的语言。上帝派来的那几个译者，名叫机缘，名叫责任，名叫蕴藉，名叫沉默。\r\n　　还有一位，名叫怀恋。\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('aed2ddaa-7cd0-489f-8bea-bc27965118c3', '测试8[卷20]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '20', '    而你猜我改编哪一个故事?“多恩与领主千金私奔记”—灵感来自沃尔顿的《五人传》!电视观众大概没几个人晓得约翰·多恩是谁，不过，拜海明威之赐，大家都听过“没有人是一座孤岛”。我只消将这句名言编进剧本里，便顺利\r\n卖出啦!\r\n    于是，约翰·多恩成功登上“不朽名人堂”。我也拿到一笔酬劳—价码大约是我前前后后花在你们店里的书款外加五颗牙!\r\n    我打算半夜爬起来听收音机的现场实况转播，和你们一块儿参加加冕典礼，同时惦念着你们所有人。\r\n    祝开心\r\n\r\n    伦敦中西二区，查令十字街84.\r\n    1953年6月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    您的包裹于六月一日平安抵达了，正好赶上加冕大典。当天家里来了不少朋友。和我们一起收看电视转播。我们用您寄来的火腿做了些三明治供大家享用，每个人都直说美味极了。于是我们全体举杯同祝您及女王都凤体康泰。\r\n    您实在太仁慈了，竟将您的辛苦所得拿来关照我们。所有同仁与我在此同声向您道句:万分感激。\r\n    祝福您\r\n弗兰克·德尔谨，上\r\n\r\n    米德尔塞克斯 品内东厢 包德米尔路\r\n    1953年9月23日\r\n亲爱的海莲:\r\n    我必须赶紧通知你:今年圣诞节可千万不许再寄礼物来。所有的东西都已经不用配给了，稍好一点的店里头也能买到丝袜。请攒下你的钱，现在最要紧的就是等你治好牙齿之后，能到英国来。只是别挑明年来。因为那时我不在国内。后年我才会回国，这样子你来了才能住在我们家。\r\n    道格写信告诉我，我们候补的家庭宿舍就快有着落了，孩子们和我希望能在圣诞节前就搬去和他团聚。他现在暂时被调到巴林岛(在波斯湾，你可以在地图上找找看)的基地，日子过得愉快惬意。等到家庭宿舍一分配下来，他便会回到哈巴尼亚(在伊拉克)的皇家空军基地和我们会合。一切都很顺利。\r\n    要写信给我哟，即使我暂时“出局”了，妈妈也会把你的信转寄给我的。\r\n思念并祝福您\r\n塞西莉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1955年9月2日\r\n你们店里一直发行这么棒的目录。却直到现在才寄给我!难道你还好意思跟我说你老是忘了吗?汝等无赖!\r\n    忘了哪个复辟时代的剧作家老爱用“汝等无赖”这个词儿数落别人，我好不容易终于逮到机会可以用它来造个句儿。\r\n    活说回来，这整本目录里头只有这本卡图卢斯我有点儿兴趣——虽然不是“洛布经典文库”版，不过看起来还算差强人意，如果这本书还在的话就寄给我。至于书价六先令两便士，只要你换算成美金，我马上付——凯特和布莱恩搬到郊\r\n区。这下子没有人可以帮我换算了。\r\n    如果你从下个月起每个星期都能携家带眷乖乖上教堂，我会十分感激你。请一起为吉廉、李斯、史奈德、坎帕内拉、罗宾逊、哈吉斯、费里罗、帕德瑞斯、纽坎姆与拉宾—布鲁克林道奇队全体球员祷告，祈祷他们身强体健并获天助神力。要是他们打输了世界大赛，我也不想活了，到时你再后悔就来不及了。\r\n    你们有德·托克维尔的《美洲见闻录》吗?有人把我原有的一本借走了赖着不还。我实在百思不解，再循规蹈矩的人怎么一霸占起书来都是一副理直气壮的气派。\r\n    代我问梅甘好，要是她还在店里头帮忙的话。还有。塞西莉现在怎么样了?从伊拉克回来了吗?\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1955年12月13日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    十分抱歉没能早一点写信给您。不过请您先别忙着动气，因为我生了一场小病，请了几个礼拜病假在家。一回到店里又被一堆待补办的公事绊住。\r\n    关于目录里登载的那本卡图卢斯，收到您的信之前就已经被买走。但我仍为您找到另一个版本寄去——这也是一本附拉丁原文的版本，韵诗部分由理查德·伯顿爵士翻译;内文则为伦纳德·史密瑟翻译，大字印刷，标价三美元七十八\r\n分。装帧并非十分漂亮，但品相尚称良好。我们目前没有德，托克维尔的书，将会为您留意。\r\n    梅甘仍在书店，但是她正打算搬到南非去，大家都还在劝她打俏念头;自从塞西莉随丈夫去了中亚之后，我们就再也没有她的消息，转眼间她竟然已经离职一年了。\r\n    我很乐意为布鲁克林道奇队加油——如果您也愿意为“热刺队”(托特纳姆足球队)打气的话。他们目前在联盟的排名敬陪末座，不过，球季尚未结束，同志仍须努力。希望他们在明年四月前能扭转颓势。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('bd89e0ff-9575-43a5-8d26-e27286fa8bcd', '测试6[卷2]', '历史', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-21 22:54:36', '2017-09-06', '1', '600', '2', '0', '1', '2', '其他人全都不敢斗胆写信给“弗兰克的汉芙小姐”。\r\n　　包裹里的肉食实在太棒！我认为你真的不该再这样子为我们破费了，一定花了你不少钱吧？愿上帝保佑你的好心肠。\r\n　　本·马克斯先生走过来要找事给我做了，就此搁笔。\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　伦敦西八区肯辛顿高地街伯爵社区\r\n　　1951年4月5日\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您寄到马克斯与科恩书店的复活节礼物，已于几天前寄达。但弗兰克·德尔先生因公外出，错过与大家分享喜悦了。\r\n　　一见到包裹里头的肉，所有人的眼睛都看直了，而鸡蛋也大获欢迎。我觉得有必要给您写个信，向您报告：所有同仁对您的好意和慷慨都万分感激。\r\n　　我们所有同仁都期盼您能尽快来英国，届时我们一定会竭尽心力，让您有一趟愉快的英伦之旅。\r\n　　梅甘·韦尔斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　埃塞克斯滨海南庄桶桥路\r\n　　1951年4月5日\r\n　　敬爱的汉芙小姐：\r\n　　我是马克斯与科恩书店的编目员，已在书店任职即将届满两年。谨在此向您表达感激之意。谢谢您多次寄赠礼物包裹给我们。\r\n　　我现在与七十五岁的姨婆住在一起。当我带着您送的肉、牛舌罐头回到家里，我想如果您能在场目睹姨婆脸上惊喜万状的表情，您大概就不难想见我们满溢的感激之情了。知道远方有人竟能为从未谋面的一群人付出这么多关怀和慷慨，我的内心实在备感温暖。而我相信，所有同仁必定与我深有同感。\r\n　　如果您想到有什么事我可以代劳，或是从伦敦寄点儿什么给您的，请务必交代我去办，我将会引以为荣。\r\n　　比尔·汉弗莱斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　我猜您大概已经开始担心，我们竟然这么久都没写信谢谢您寄来的包裹，心里头一定正在嘀咕：真是一群不知好歹的家伙。实际上，我刚离开伦敦到乡间跑了一大圈，到处拜访私人宅第，搜寻待售的藏书，努力补充店里捉襟见肘的库存。我太太已经开始把我当成房客来招呼了——我总是只回家睡觉，一吃完早餐又不见人影。不过，当我带着您送的肉(鸡蛋、火腿就更不用说了)回到家里，她就会觉得我毕竟也非一无是处。当然，所有的不开心也就随之烟消云散。说实在的，我们已经太久没能见到一块完整的肉了。\r\n　　我们总得想点儿法子，表达我们对您的感激。于是，我们将另行寄上一本小书，希望您会喜欢它。我还记得您曾经想买一本情诗集，这是我所能找到尽可能符合您的要求的了。全体同仁为您献上此书，盼您笑纳。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　(附在《伊丽莎白时期情诗选》里的卡片：)\r\n　　谨以此书赠予\r\n　　海莲·汉芙\r\n　　并为其诸多美善情谊\r\n　　致上最诚挚的祝福\r\n　　与无尽的感激\r\n　　伦敦查令十字街84号\r\n　　全体员工一同\r\n　　一九五一年四月\r\n\r\n\r\n\r\n---------------\r\n1951年4月16日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年4月16日\r\n　　此致伦敦查令十字街84号全体同仁：\r\n　　谢谢你们送我这本书。我从没拥有过这么一本三边的页缘都上金的书。你们知道吗？我竟在生日当天收到这本书！\r\n　　你们另外写了一张卡片，而不直接题签在扉页上，我真希望你们不要这样过分拘谨。如果我猜得没错，这一定是你们的“书商本性”作祟使然吧，你们担心一旦写了字在书上，将会折损了它的价值。差矣，你们如果真能这么做，不仅对我而言，对未来的书主，都增添了无可估算的价值。我喜欢扉页上有题签、页边写满注记的旧书；我爱极了那种与心有灵犀的前人冥冥共读，时而戚戚于胸、时而被耳提面命的感觉。\r\n　　还有，为什么大家都不签上名字呢？我猜一定是弗兰克不准你们签的，他大概怕我会撇下他，一一给你们大家写情书吧！\r\n　　隔着汪洋，我在美国此端遥寄我对你们的祝福——“美国”，好一个“坚定的盟邦”！当她一掷千金帮日本、德国从败仗中“复苏”，却眼睁睁看着英国同胞饱受饥馑之苦！皇天为证，总有一天我要亲自去英国，当面为她向你们道歉。(等我回国后，我会叫她加倍向我赔罪！)\r\n　　再次感谢你们送我这本美丽的书，我一定会格外小心，免得让它溅到酒滴、沾了烟灰。这份礼物对我这种人来说实在太隆重了。\r\n　　海莲·汉芙上\r\n\r\n\r\n\r\n---------------\r\n1951年9月10日\r\n---------------\r\n\r\n\r\n　　(玛克辛写于后台)\r\n　　伦敦\r\n　　1951年9月10日\r\n　　甜心儿：\r\n　　这是一间活脱从狄更斯书里头蹦出来的可爱铺子，如果让你见到了，不爱死了才怪。\r\n　　店门口陈列了几架书，开门进去前，我先站在外头假装随意翻阅几本书，好让自己看起来像是若无其事地逛书店。一走进店内，喧嚣全被关在门外。一阵古书的陈旧气味扑鼻而来。我实在不知道该怎么形容：那是一种混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……店内左手边有张书桌，坐着一位年约五十、长着一只贺加斯式鼻子的男士。他站起身来，操着北方口音对我说：“日安。”我回答说我只是随意逛逛，而他则有礼地说：“请。”\r\n　　极目所见全是书架——高耸直抵到天花板的深色的古老书架，橡木架面经过漫长岁月的洗礼，虽已褪色仍径放光芒。接着是摆放画片的专区——应该说：一张叠放着许多画片的大桌台。上头有克鲁克香克、拉克姆矠、斯派和许许多多我叫不出名字的英国插画家的美丽画作；另一边还放着几叠迷人的古旧画刊。\r\n　　我在店内待了约莫半个钟头光景，期待着你的弗兰克或是哪个女孩儿翩翩现身。不过，因为我到达时已过了一点钟，我猜他们全都外出用餐去了，而我又不能待太久。\r\n　　就是这样咯！新戏的预告并没有造成万人空巷，不过据说对方人蛮好的，排给我们几个月的档期，所以我昨天出门找出租公寓，在骑士桥附近有一间小小的、蛮不错的小套房。现在还没确定，一旦定下来，我会写信告诉你，你也可以再打电话问我妈。\r\n　　三餐不成问题，我们都在餐厅或旅馆里用餐，像最高级的克拉里兹大饭店就能充分供应烤牛肉、排骨。价钱虽然贵得离谱，不过换算成美金倒是挺划算的，所以我们还吃得起。假使换成我是英国人，瞧见这光景一定会恨得牙痒痒的。但是他们却反而对我们好得不得了，到处都有人邀请我们去家里作客或上馆子。\r\n　　惟一短缺的东西就是糖。凡是甜的东西，一应俱缺。也许我反该谢天谢地，正好让我在这里瘦它个几磅。\r\n　　写信给我。\r\n　　爱你的\r\n　　玛克辛\r\n\r\n\r\n\r\n---------------\r\n1951年9月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年9月15日\r\n　　玛克辛：真多亏了你的慧心巧手，书店简直被你给写活了——你的文笔实在比我好得太多啦！\r\n　　我刚打了电话给你妈妈要你的住址，她要我转告你：方糖和巧克力棒依照你的交代，已经给你寄去了。你不是还跟我说你要趁机减肥的吗？\r\n　　我不想让你以为我是酸葡萄，不过我实在不明白，你究竟是何德何能？老天竟任由你饱览遍逛“我的书店”；而我为什么就只得乖乖蹲在九十五大街的破公寓里，埋头写着这劳什子《埃勒里·奎因的冒险》电视剧集脚本！“我不是告诉过你，不能安排一截沾着口红的烟蒂当做破案线索吗？”“我们这个节目是由百优雪茄公司赞助的，千万别给我编出“香烟”这个台词儿。”……就连在场景里安排一只道具烟灰缸也不许出现烟屁股；也不能摆雪茄屁股——厂商嫌不好看——所以，只要剧情出现烟灰缸，里头全好端端地搁着一管全新的、未拆封的百优雪茄！\r\n　　简直岂有此理！你却还能跟约翰·吉尔古德坐在克拉里兹的酒吧里打情骂俏！\r\n　　来信时多写些伦敦的事物——地铁、巷弄胡同、古宅大院……随便什么都好，写仔细点儿。告诉我骑士桥长什么模样，此刻我的耳畔似乎响起了科茨的《伦敦组曲》……听起来是那么绿意盎然、雄壮典雅。\r\n　　xxxx\r\n　　hh\r\n\r\n\r\n\r\n---------------\r\n1951年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年10月15日\r\n　　这哪是佩皮斯日记呢？你倒是给我交代清楚！\r\n　　这本书根本不够资格称之为《佩皮斯日记》，这只是哪个没事找事做的半吊子编辑，从佩皮斯日记里东挖西补、断章取义，存心让他死不瞑目！\r\n　　真想啐它一口！\r\n　　一六六八年一月十二日的日记跑到哪儿去了？记着他的老婆把他踹下床，抄了根烫红的拨火棍，追着他满屋子乱跑的那天的日记呢？\r\n　　记着W.佩恩爵士福至心灵的儿子成天揣着教谕，把大伙儿搞得七荤八素的日记呢？这些偷工减料的手脚可别想逃过我的法眼。\r\n　　附上两张皱巴巴的钞票。我想，用来付这本玩意儿，外加你将要为我找来的那本“货真价实的佩皮斯”，应该绰绰有余了！到时候，我会将这本烂书碎尸万段，然后，一页一页撕下来——拿来包东西！\r\n　　HH\r\n　　P.S.圣诞节快到了，到底是寄新鲜的鸡蛋，还是干燥蛋好呢？我当然明白干燥蛋可以放得比较久，但是，“丹麦空运直送的新鲜鸡蛋”光听都觉得香，你们快帮我拿主意吧！\r\n\r\n\r\n\r\n---------------\r\n1951年10月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年10月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　首先，在此为我们的疏忽向您致以十二万分的歉意。我一直错认为那是收录完整的布雷布鲁克版。我相当能够了解，当您发现该书阙漏了喜爱的章节时，会有何等的失落感受。\r\n　　我一定会尽力另找一本书价合理、完整收录您在信中提及的段落的《佩皮斯日记》，并尽快为您寄上。\r\n　　同时，很高兴地在此向您报告：本店最近将收购一批私人藏书，我已从该批书籍中捡选出一些您会喜欢的书，包括一本利·亨特的选集，收录大半您曾提及的文章；还有一册《通俗拉丁文新约全书》——希望这回不会再出错了；同时有一本也许对您相当实用的《通俗拉丁文圣经辞典》；另一本《二十世纪英国散文选》，内容虽收入希莱尔·贝洛克的文章，但并不是谈论厕所的那篇。随信附上发票，上列书款十七先令六便士，约合美金二元五角，此书账已扣除您在敝店账户中的二元余额。\r\n　　至于鸡蛋的问题——店内同仁商量的结果，大家似乎意见一致，均认为新鲜鸡蛋较好。诚如您所言：鲜蛋虽较难久放，但风味确实不同。\r\n　　我们都期盼大选后日子会好转。如果丘吉尔先生和他的政党能赢得选举——这也是我的衷心期望，将会是一件振奋民心的好事。\r\n　　衷心祝福您\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1951年1Z月2日\r\n    亲爱的急惊风:\r\n    你简直是“迅雷不及掩耳”，利·亨特的书和《通俗拉丁文新约全书》“倏忽”寄达。你大约还没弄明白吧——这不就正是我两年前向你们订购的书吗?如果你继续照着这种提心吊胆的步调干活儿，要不得心脏病也难。\r\n    我真恶毒。你为了帮我找书，忙东忙西的，我竟然不曾向你道过一声谢，我真是坏透了。其实，你在那头儿受苦受难，我都是铭感在心底的。附上三元钞票，抱歉，最上头那一张被我溅到了几滴咖啡，应该还不致于作废的地步，上头的面额还可以看得出来。\r\n    你们有精装版的合唱乐谱吗?比如说巴赫的《马太受难曲》或韩德尔的《弥赛亚》?虽然我顶着寒风，走到五十条街外就能买到，但我想我还是“就近”先问问你们吧。\r\n    恭贺丘吉尔先生和他所属的政党，希望他能让你们的日子好过些。\r\n    “德尔”?你的祖先是威尔士人吗?\r\n                                          HH\r\n\r\n    1951年12月7日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    希望您会高兴得知，两箱鸡蛋和牛舌罐头已平安寄达，而我们也要再度向您道谢。\r\n    本店的一位老同事——马丁先生，躺卧病榻已有一段时日了，因此，我们一致决定让他多分一些鸡蛋——其中一整箱。一如以往，对于这些难能可贵的物资，我们十分欣喜。牛舌罐头亦相当诱人，它们大大地补充了我们的贮粮。至于我\r\n自已，我会将它们好好地收藏起来，以备特殊场合再派上用场。\r\n    我询问了此间所有的乐谱店，不过全都没有干净的二手精装版《弥赛亚》或巴赫的《马太受难曲》，然而我发现新书目前仍持续发行。价格虽略嫌高了些，但我想了想，仍为您买了下来，并在几天前寄发出去了，最近几天您应该就会收到这两本乐谱。一英镑十先令(约合美金四元二角)的账单亦会随\r\n书寄上。\r\n    我们将寄给您一件小小的圣诞礼物。这是一件刺绣品，我们会在邮包上注明“内装礼品”，希望这样可以避免您被课上关税。所有人都希望您笑纳这份薄礼，大伙儿同声祝您圣诞快乐，并有一个美好的来年。\r\n    我的姓氏绝非源自威尔士，鉴于其发音近似法文“挪尔”，我想我的祖籍或许是法国也未可知。\r\n      马克斯与科恩书店\r\n      弗兰克·德尔谨上\r\n\r\n    195年l月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    首先，我们都十分高兴您喜欢那块桌布。总算能够回赠您一件礼物，亦带给我们一丝欣喜——虽然比起您过去这些年来为我们的奉献，那实在是太微不足道了。您或许想知道那件桌布的来历，其实这是最近刚完成的作品，出自我的邻\r\n居—一位八十多岁老太太的巧手。她一个人独居，平日以女红自娱。虽然她做了许多手工刺绣，却儿乎全都自己留着。她之所以愿意割爱，破例卖出这么一块桌布，乃是禁不住我太太的不断恳求。当然，您之前寄给我们的干燥蛋也使上不\r\n少助力。\r\n    如果您非到万不得已，一定要“清洗”您的《格罗里埃圣\r\n经》。的话，我们会建议您用一般的肥皂和清水即可。做法是:加一茶匙的苏打粉到一品脱的温水中，用蘸了肥皂的海绵轻轻擦拭。我相信您会发现，这样可以有效地除去污渍。经过上述的处理后，您可以再用少许绵羊油为它上光。\r\n    J.彭伯顿是一位女士，J是珍妮特的缩写。\r\n    祝您新的一年一切如意。\r\n弗兰克·德尔敬上\r\n\r\n\r\n    伦教北八区克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年1月20日\r\n    亲爱的汉芙小姐:\r\n    谢谢您长期不断地寄赠马克斯与科恩书店礼物包裹，并让我家也能有幸分得一部分。我长久以来就一直想写信给您，现在终于有正当理由可以动笔了。弗兰克跟我说，您很想知道绣了那块桌布的老太太的姓名、地址。那件刺绣的手工\r\n真的很漂亮吧？\r\n    这位博尔顿老太太就住在我们的公寓隔壁，地址是:橡原巷36号。当我告诉她，这件桌巾飘洋过海送给了一位好心的女士时，她好吃惊。如果能再亲耳听到您对她的手艺赞不绝口，我相信她一定会十分开心。\r\n    很感谢您说还要寄更多干燥蛋给我们。不过上回您送来的还剩一些，应该可以让我们享用到春天。四月到九月这段时间，我们并无须操心蛋的问题。如果偶尔会因物资短缺而临时减少配给量，我们就会拿别的东西去跟别人换罐头。我曾经用一双丝袜在黑市换得一罐干燥蛋，当然这么做不尽合法，却是非常时期不得已的变通办法。\r\n    过些日子我会寄一些全家福照片给您看，我们的小女儿玛莉上周刚度过她的四岁生日;大女儿去年八月满十二岁了，名叫希拉，是弗兰克的前妻生的——她不幸在战时丧生了。战后我嫁给弗，顺道有了个现成的女儿。去年五月，希拉在学校当着修女们的面(她念的是教会学校)，说她要送一张卡片祝福爸爸、妈妈的结婚四周年纪念日。您不难想像，这可怜的小姑娘花了多少唇舌才解释清楚。\r\n    最后，祝您新的一年一切顺遂，并期盼不久后我们可以在英国相会。\r\n诺拉·德尔谨上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年l月29日\r\n    亲爱的汉芙小姐:\r\n    谢谢您的来信。我很感激您如此好心地告诉我:您十分喜欢我绣的桌布。我当初真该多花点儿工夫在那上头。我想，德尔太太一定也对您提过了吧，我是一个上了年纪的人，双手也不太听使唤，能做的活儿也不像以前那么多了。我用这\r\n双老手做的东西有幸能交到喜欢它的人手上，这真是一件令人欣慰的事儿。\r\n    我常见到德尔太太，而她也不时向我提起您的事儿。您若到英国来，或许我也能有幸见您一面罢。\r\n    再次谢谢您。\r\n    玛丽·博尔顿谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n听好了!玛克辛:\r\n    我刚和你妈妈聊过。她说你们的戏也许下个月就会结束公演;她还告诉我。你带走了两打丝袜。帮我一个忙，趁你的戏下档前，拿四双去书店交给弗兰克·德尔，就说是送给店里的三个女孩和诺拉（他太太）。\r\n    你妈妈还特别交代，要我用不着付钱给你。她说那些丝袜全是她自已在去年夏天萨克斯百货店清仓大减价时便宜买到的，她决定要乐捐出来，好让她自个儿也能沾沾“共赴国难”的光。\r\n    等你回国就可以瞧见他们送我的圣诞节礼物了。这是一条漂亮的爱尔兰绣花桌巾，米黄色的底布上以手工绣着古典的花草图案——全是各自不同颜色、浓淡有致的花儿。保证你从没看过这么美的桌巾，我那张从旧货店买回来的破茶几\r\n就更肯定没见识过啦!我真是迫不及待想披上维多利亚时代的水袖，优雅地举起手，幻想自己执着一只乔治王朝的古董茶壶，轻轻地斟上一盏茗茶……你快点儿回来吧!我们可以在家里扮一出斯坦尼斯拉夫斯基！\r\n    埃勒里调高了我的剧本稿酬，现在是一集两百五十元。如果照着这样的调薪幅度。到了六月，也许我就可以启程赴英，自己去逛“我的书店”——如果我斗胆敢去的话。隔着三千英里的安全距离，我写了一堆没大没小的信，我大概只会\r\n悄悄溜进去又静静踱出来，而不敢告诉他们我是谁。\r\n    我实在不明白你怎么会在杂货铺里被搞得一楞一楞的呢?老板跟你说的不是“兔兜脚”，他说的是“土豆胶”!——我也觉得这才是惟一合理的称呼。你动点儿脑筋想想:豆子长在土里，叫它“土豆”合情人理;从土里头挖出来，一直“搅”、一直“搅”，搅成“胶”状，不就成了“土一豆一胶”!这个词儿不是要比“花生酱”更贴近事实吗?你真是不懂国语!\r\n    \r\n    h.汉芙(必也正名乎的海莲)\r\nP.S.你妈妈今天一早就打扮得花枝招展地出门，要为你在第八大道和五十大街一带找间公寓，因为你曾交代她去剧院区找。我说玛克辛啊，你该很清楚才对，她穿那一身行头在那种地方晃，谁还敢租房子给她啊?\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n大懒虫!\r\n    依我看若要等到你寄书来.我都不晓得要超渡几回了。我不如干脆直接冲进布伦塔诺书店，有什么就买什么，不管印得多糟!\r\n    你们不妨再加记一笔沃尔顿的《五人传》到我那份“该寄而未寄”的书单里头。老实说，订这本书实在是违背了我的购书原则。我从来不买没读过的书——否则，不就像买了一件没试穿过的衣服同样下场吗?可是，这儿竟然连图书馆也\r\n借不到这本书。\r\n    要读的话倒是有。四十二大街上的分馆有一本，但，恕不外借!坐镇在柜台的女馆员用力摇了摇头，盛气凌人地说:“仅供内用!”然后只准我窝在密不通风的315号阅览室里啃完整本书。既不能边读边喝咖啡，抽烟就更纯属妄想了。\r\n    没关系，反正Q多次引用这本书里的段子，所以我肯定也会喜欢它。只要是Q喜欢的，我都照单全收——小说除外，我就是没法儿喜欢那些根本不存在的虚构人物操演着不曾发生过的事儿。\r\n    你们成天都没事干吗?是不是都窝在店里头看书?何不起身做点儿生意呢?\r\n                                              汉芙小姐上\r\n                      (只有我的“朋友”才可以叫我“海莲”!)\r\np.s.转告女孩们和诺拉，如果一切顺利的话，她们在大斋节都有丝袜可穿。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年2月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我也十分同意，该是我们都摒弃无谓的“小姐”、“先生”敬称的时候了。不瞒您说，我本人实在并不像您长久以为的那样既木讷又严峻。只是我写给您的信都必须存放一份副本作为业务存档，所以我认为行礼如仪似乎比较妥当。不过，此封信既然与书店业务无关，自然毋须顾虑副本、存档的问题。\r\n    我们百思不得其解，不明白您如何隔海变戏法，让四双丝袜无中生有。我所知道的只是:今天中午我用过午餐回到书店，就赫然发现它们已经好端端地摆在我的办公桌上，上头还附着一张写着“海莲·汉芙赠”的卡片。没有人晓得它们是什么时候或是怎么来的。女孩们都吓呆了，我晓得她们正在打主意待会儿自行写信给您。\r\n    有一件令人遗憾的事要向您报告:久卧病榻的乔治·马丁先生上周在医院中病逝了。他在本书店工作已有多个年头。伴随着这个噩耗。国王的猝然驾崩亦使我们此刻都笼罩在一片哀戚之中。\r\n    我实在不知该如何回报您对我们的不断付出。我所能做到的，只是当您确定访问英国时，橡原巷37号将会有一个房间，可供您无限期地住宿。\r\n    全员共祝您一切美好\r\n    弗兰克。德尔\r\n\r\n 纽约市\r\n东九十五大街14号\r\n    1952年3月3日\r\n    噢!真感谢你寄来的《五人传》。实在难以置信，这本一八\r\n四〇年出版的书，经过了一百多年。竟然还能保持这么完好的书况!质地柔细、依旧带着毛边的书页尤其可人。我真为前\r\n任书主(扉页上有“威廉.T.戈登”的签名)感到悲哀，真是子孙不肖哟!竟把这么宝贵的东西一股脑儿全卖给你们。哼!我真想趁它们被称斤论两前，拎着鞋溜进他们的书房，先下手搜刮一番!\r\n    这真是一本引人入胜的书。你可知道，当约翰·多恩带着他主子的掌上明珠私奔(人家好不容易老来得女的)，他因此下狱被囚禁在伦敦塔，他在里头握饿受冻。饿着饿着……居然就“得道”了！——不是沃尔顿的原句啦，这是我自己改编的说法。\r\n    注意!我附寄五元钞票一张。由于这本《五人传》赫然登场，害我原来那本《垂钓者言》相形见绌(认识你们之前买的，这是一本硬邦邦的“美国大众经典文库”版)。《五人传》跟它不登对，总嫌它在跟前晃来晃去，看了就讨厌。所以，多出来的两块半就帮我找一本好点儿的《垂钓者言》——劳您大驾啦。\r\n    你得当心了，如果电视剧续签，明年我就会杀到你们那儿去。到时候我会蹬着古董木梯，掸去你们的书架顶层的陈年积垢，顺便也把你们的优雅端庄一并一扫而光。我跟你提过我帮埃勒里·奎因的电视节目编写巧艺谋杀吗?我所写的剧本总会安插艺术场景——芭蕾舞团啦，音乐厅啦，歌剧院什么的;连嫌疑犯或尸体也得写得文绉绉的。为了向你致敬，也许我该编一出发生在古书店里的谋杀案，怎么样?你要演行凶歹徒呢?还是要扮刀下亡魂?\r\n hh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年3月24日\r\n亲爱的汉芙小姐:\r\n    我实在不知如何表达我的感激，今天收到您寄给我的食物包裹，我有生以来未曾收到过包裹。您实在不须为我这个老太婆如此费心。我只能谢谢您的好意，并好好地享用您寄来的礼物。\r\n    感谢您为我设想如此周到。我拿您寄来的东西给德尔太太瞧，她也直夸您实在是一位大好人。\r\n    再次谢谢您，并析祷您一切安好。\r\n玛丽·博尔顿谨上\r\n\r\n    伦敦中西二区 令十字街84号.\r\n    1952年4月17日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲(您看，我已经不再“行礼如仪”了):\r\n    我相信您一定会高兴听到这个消息:我们刚购入一批私人藏书，里头有一册非常好的《垂钓者言》，待我将收购手续完成，本书可望于下周为您寄上。我们大概会标价二元二角五分，您账户中的余额仍够支付。\r\n    您为埃勒里·奎因写的剧本似乎挺有意思。我希望这部剧集也能在我们这儿播映，它也该改善其沉沉死气了(我是指我们的电视节目，不是您写的剧本)。\r\n    诺拉和所有人与我同祝您一切美好。\r\n弗兰克·德尔敬上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年5月4日星期日\r\n亲爱的海莲:\r\n    谢谢您寄赠的干燥蛋罐头，包裹在两天前收到了。尽管难掩喜悦之情，但我实在觉得很难为情，因为我竟向您提起蛋源短缺的事情。不过今年倒是还没发生减量配给的情形，所以我们就当成是上天的恩赐，用它们多烤了个蛋糕什么的。弗兰克还拿了一些去办公室，打算要寄给塞西莉，他老是忘了把她的地址带回家。我想您应该也听说了吧，塞西莉离职了，她正打算随丈夫调防到中亚去。\r\n    我附了几张相片，弗兰克说这些照片全没把他拍好，还说他本人比照片好看多了。不打紧，我们就让他说梦话好了。\r\n    希拉的学校放了一个月的假，以前我们全家偶尔会一起到海边走走或随处晃晃。但是现在恐怕也不能那么常出游了，因为交通费贵得吓人。我们一直有买车的念头，但车价也不便宜，物色一部性能尚可的二手车或许比较可行。大部分\r\n新车都出口到国外，国内只能分到少量的配额。我们有一些朋友，光是为了订一部新车，就等了五六年。\r\n    希拉现在要为您朗读一篇《丰收祈祷文》，祈祷您可以如愿到英国一游;而且在复活节时，我们也已经借花献佛，用您送给我们的咸肉罐头犒赏过神明了。所以，如果再加上《丰收祈祷文》奏效，老天将会保佑您获得一笔意外之财，这样您和我们就很快可以见面了!\r\n    就此停笔并再次感谢您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年5月11日\r\n亲爱的弗兰克:\r\n    本打算一收到书就写信给你的。就是想跟你道句谢谢，《垂钓者言》里的木刻版画太棒了，光这些插图的价值就十倍于书价。我们活在一个诡异的世界——这么漂亮，又能终生厮守的书，只须花相当于看场电影的代价就能拥有;上医院\r\n做一副牙套却要五十倍于此。\r\n    唉!如果你们依照每本书的实际价值去标价的话，我肯定一本也买不起。\r\n    如果你知道我这个一向厌恶小说的人终究回头读起简·奥斯汀来了，一定会大大地惊讶。《傲慢与偏见》深深掳获了我的心!我千不甘万不愿将我手头上这本送还给图书馆，所以快找一本卖我。\r\n    代我问候诺拉和办公室里可怜的上班族们。\r\nhh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年8月24日\r\n亲爱的海莲:\r\n    我要再一次对您寄给马克斯与科恩书店，并让我们家分享的物资向您道谢，我真希望能回寄点儿什么报答您。\r\n    对了，海莲，这个礼拜我们终于荣登有车阶级了。虽然不是一部新车，没什么好到处吹嘘的。不过，车子只要能跑就行了，您说是不?怎么样，您现在比较愿意来找我们玩了吧?\r\n    前一阵子，我有两位亲戚从苏格兰南下来看我们，博尔顿老太太心肠很好，肯让他们在她家叨扰了几个礼拜，他们说住在那儿很舒服。他们那一阵子就吃我家，睡博尔顿老太太家。如果您手头方便，可以赶明年儿，趁着女王登基大典时\r\n来一块儿凑凑热闹，博尔顿老太太说她会为您将房间准备好。\r\n    好了，不多说了，寄上我的祝福和感谢——谢谢您送的肉与鸡蛋。\r\n诺拉谨上\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年8月26日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我又要再度代表我们这儿所有人，写信向您道谢了，三件甚获欢迎的包裹几天前寄达。您实在太好心了，将辛苦挣来的钱花在我们身上。我们都非常感激您对我们的关怀。\r\n    这几天，店里进了三十几册“洛布经典文库”，可是，唉。里头不巧就缺了贺拉斯、萨福、卡图卢斯。\r\n    从九月一日起，我会休几个礼拜的假。可是因为我最近买了车，家中经济失血甚多，所以不能再大事铺张。诺拉有个姐姐住在海边，我们都冀望她会怜悯我们无处可去而邀我们去玩。这是我头一回买车，所以全家都非常兴奋——尽管这\r\n是一九三九型的老款车。但只要它不会老是半路抛锚，我们就该偷笑了。\r\n    全心祝福您\r\n    弗兰克·德尔\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年9月18日\r\n弗兰基，猜猜看，当你去度假时，谁上门来啦?萨姆·佩皮斯是也!不管是谁代你寄的书，别忘了要谢谢人家。这些书上个星期就到了，三册扎扎实实的海军蓝布面精装本，用四大页四开报纸裹着。我边吃午饭边读旧报;晚餐过后，开始和萨姆\r\n神交。\r\n    他要我转告你:他非常高兴能来到敝宝地，他的前任主人是个大草包，连书页都懒得裁开。我将它们一一裁开，内页用的是薄得几可透光的印度纸——我们这儿管这种纸叫“洋葱皮”，真是恰如其分。要是换成厚一点的纸张，难保不变成\r\n六册、甚至七册，印度纸果然功德无量。我只有三座书架，可是实在已经找不到可以让我清掉的书了。\r\n    每年一到春天，我就会“大清仓”，把一些我再也不会重读的书全丢掉，就像我也会把再也不穿的衣服扔了同样道理。倒是旁人都很惊讶、依我看，他们爱惜书本的方式才奇怪呢。他们买一堆新出版的畅销书，囫囵吞枣似的看完，我常\r\n想:他们也未免读得太潦草了吧。然后呢，因为他们从不重读那些书，不消一年，书里头的内容早就被他们抛到九霄云外!不过，当他们看见我一箱一箱地把书往外扔时，却又露出一副“这怎么得了!”的表情。要是照着他们的做法:买了一本\r\n书，好——读过了，好——上架，好——没事了，一辈子也不会再去碰它第二回，可是呢，“丢掉?万万使不得呀!”为什么使不得?我个人坚信:一本不好的书——哪怕它只是不够好，弃之毫不足惜!\r\n    你和诺拉过了一个不赖的假期吧?我自己则全消磨在中央公园里。我的宝贝牙医师放了我一个月的假，他却欢欢喜喜带着娇妻度蜜月去了，旅费是我出的——我有没有提过?好几个月前，我发现牙齿一颗接一颗全坏光了，我要么乖乖装上牙套。要不然就得全部拔光!因为还不想当个无齿之徒，我最后还是决定装牙套。可是诊疗费简直是天文数字!看来伊丽莎白只好在我没来的情况下登基了，我呢，今后几年里也只能留在这里看着我的牙齿一一加冕了。\r\n    不过我可没打算停止买书!连牙齿都弃我而去了。总该给自己留点儿什么呀!你能为我找到萧伯纳的剧评和乐评吗?我想他应该写了好几本，把你所能找到的都寄给我。还有!弗兰基，漫漫冬天眼看着又要来了，我兼差帮人带小孩时可不能闲着，所以，亟需读物!——快，别老坐着，起身帮我找找书!\r\n\r\nhh\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n  1952年12月12日\r\n此致“伦敦查令十字街84号的众好友们”:\r\n    一拆开包装，《爱书人文选》款款现身—镶金边的皮面、上金漆的上书口……轻而易举勇夺“我的藏书选美”的后冠，连首版的《大学论》也甘败下风。它看起来仍如此清新、纯朴，宛若从未遭染指——不过我知道，它的确曾被频繁地悉心翻阅过:因为一打开书页，总会落在某几个特定段落，冥冥之中似有前任书主的幽灵导引我，领我来到我未曾徜徉的优美辞藻。例如特里斯特拉姆·项狄描述他父亲富丽堂皇的书房:“架上罗列多善本，筐中广纳皆美卷。”(弗兰克!快去给我找一本《项狄传》！)\r\n    我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年;而你们送给我的礼物，却能和我朝夕相处，至死方休;我甚至还能将它遗爱人间而含笑以终。\r\n    谢谢你们，祝你们新年快乐。\r\n海莲\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n   1952年12月17日\r\n亲爱的海莲:\r\n    很抱歉这么久都没给您只字片语。希望阿德莱败选不致于让您太难过，也许下一回他可以东山再起。\r\n    博尔顿老太太跟我说，如果明年夏天您来英国时，她仍健在的话，很欢迎您住在她家。她是我所认识最长寿的人，我还不晓得有谁能像她活那么大岁数儿，我相信她一定可以活到一百岁的。无论如何，我们都会把您的住宿问题打点好。\r\n    谢谢您寄给我们这么好的圣诞礼物，您实在是个大好人。海莲!如果您明年来英国的时候，马克斯与科恩书店的人没好好地为您设宴接风的话，他们就太该死了。\r\n    我希望您将有个快乐的圣诞节。下回再聊了，献上我们\r\n的祝福和感激。\r\n    愿上帝保佑您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1953年5月3日\r\n弗兰基，告诉你个包准让你乐翻的消息-—\r\n    首先，随信寄上三元钞票。邮包已收到，这本书长得就像简·奥斯汀该有的模样儿——皮细骨瘦、清癯、完美无瑕。\r\n    好，进人正题——埃勒里电视剧集停播了。正当我青黄不接，又为了支付看牙的庞大开销而焦头烂额的当头，有人找我为一个新的节目写个草案——将名人轶事编成电视单元剧。所以我快马加鞭，完成一个故事大纲。送出之后，电视\r\n公司接受了;于是我又写了一个完整剧本，他们也颇为满意——所以再过个把月，新差事就有着落了。\r\n    而你猜我改编哪一个故事?“多恩与领主千金私奔记”—灵感来自沃尔顿的《五人传》!电视观众大概没几个人晓得约翰·多恩是谁，不过，拜海明威之赐，大家都听过“没有人是一座孤岛”。我只消将这句名言编进剧本里，便顺利\r\n卖出啦!\r\n    于是，约翰·多恩成功登上“不朽名人堂”。我也拿到一笔酬劳—价码大约是我前前后后花在你们店里的书款外加五颗牙!\r\n    我打算半夜爬起来听收音机的现场实况转播，和你们一块儿参加加冕典礼，同时惦念着你们所有人。\r\n    祝开心\r\n\r\n    伦敦中西二区，查令十字街84.\r\n    1953年6月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    您的包裹于六月一日平安抵达了，正好赶上加冕大典。当天家里来了不少朋友。和我们一起收看电视转播。我们用您寄来的火腿做了些三明治供大家享用，每个人都直说美味极了。于是我们全体举杯同祝您及女王都凤体康泰。\r\n    您实在太仁慈了，竟将您的辛苦所得拿来关照我们。所有同仁与我在此同声向您道句:万分感激。\r\n    祝福您\r\n弗兰克·德尔谨，上\r\n\r\n    米德尔塞克斯 品内东厢 包德米尔路\r\n    1953年9月23日\r\n亲爱的海莲:\r\n    我必须赶紧通知你:今年圣诞节可千万不许再寄礼物来。所有的东西都已经不用配给了，稍好一点的店里头也能买到丝袜。请攒下你的钱，现在最要紧的就是等你治好牙齿之后，能到英国来。只是别挑明年来。因为那时我不在国内。后年我才会回国，这样子你来了才能住在我们家。\r\n    道格写信告诉我，我们候补的家庭宿舍就快有着落了，孩子们和我希望能在圣诞节前就搬去和他团聚。他现在暂时被调到巴林岛(在波斯湾，你可以在地图上找找看)的基地，日子过得愉快惬意。等到家庭宿舍一分配下来，他便会回到哈巴尼亚(在伊拉克)的皇家空军基地和我们会合。一切都很顺利。\r\n    要写信给我哟，即使我暂时“出局”了，妈妈也会把你的信转寄给我的。\r\n思念并祝福您\r\n塞西莉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1955年9月2日\r\n你们店里一直发行这么棒的目录。却直到现在才寄给我!难道你还好意思跟我说你老是忘了吗?汝等无赖!\r\n    忘了哪个复辟时代的剧作家老爱用“汝等无赖”这个词儿数落别人，我好不容易终于逮到机会可以用它来造个句儿。\r\n    活说回来，这整本目录里头只有这本卡图卢斯我有点儿兴趣——虽然不是“洛布经典文库”版，不过看起来还算差强人意，如果这本书还在的话就寄给我。至于书价六先令两便士，只要你换算成美金，我马上付——凯特和布莱恩搬到郊\r\n区。这下子没有人可以帮我换算了。\r\n    如果你从下个月起每个星期都能携家带眷乖乖上教堂，我会十分感激你。请一起为吉廉、李斯、史奈德、坎帕内拉、罗宾逊、哈吉斯、费里罗、帕德瑞斯、纽坎姆与拉宾—布鲁克林道奇队全体球员祷告，祈祷他们身强体健并获天助神力。要是他们打输了世界大赛，我也不想活了，到时你再后悔就来不及了。\r\n    你们有德·托克维尔的《美洲见闻录》吗?有人把我原有的一本借走了赖着不还。我实在百思不解，再循规蹈矩的人怎么一霸占起书来都是一副理直气壮的气派。\r\n    代我问梅甘好，要是她还在店里头帮忙的话。还有。塞西莉现在怎么样了?从伊拉克回来了吗?\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1955年12月13日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    十分抱歉没能早一点写信给您。不过请您先别忙着动气，因为我生了一场小病，请了几个礼拜病假在家。一回到店里又被一堆待补办的公事绊住。\r\n    关于目录里登载的那本卡图卢斯，收到您的信之前就已经被买走。但我仍为您找到另一个版本寄去——这也是一本附拉丁原文的版本，韵诗部分由理查德·伯顿爵士翻译;内文则为伦纳德·史密瑟翻译，大字印刷，标价三美元七十八\r\n分。装帧并非十分漂亮，但品相尚称良好。我们目前没有德，托克维尔的书，将会为您留意。\r\n    梅甘仍在书店，但是她正打算搬到南非去，大家都还在劝她打俏念头;自从塞西莉随丈夫去了中亚之后，我们就再也没有她的消息，转眼间她竟然已经离职一年了。\r\n    我很乐意为布鲁克林道奇队加油——如果您也愿意为“热刺队”(托特纳姆足球队)打气的话。他们目前在联盟的排名敬陪末座，不过，球季尚未结束，同志仍须努力。希望他们在明年四月前能扭转颓势。\r\n    诺拉、所有人和我在此祝您圣诞快乐、恭贺新禧!\r\n弗兰克·德尔谨上\r\n\r\n     纽约市\r\n    东九十五大街14号\r\n    1956年l月4日\r\n    我现在趴在床脚下写信给你——这本卡图卢斯害我气得滚下来。\r\n译得洁屈赘牙的，真教人伤脑筋!\r\n到目前为止，我只听过一个名字也叫理查德·伯顿的帅哥演员——我曾在几部英国电影里头看过他，我想维持这么点交情也就够了。至于这个翻译者理查德·伯顿，他译得也未免太花俏了吧。\r\n    而可怜的史密瑟先生，他一定害怕他妈妈会读这本书，所以忍痛把那些原本应该活色生香的文章译得道貌岸然。咱们这么着吧，你索性找一本好的通俗拉丁文版的给我。我自己有一本卡塞尔氏拉丁文字典，那些难懂的段落，我自个\r\n儿去整明白得了!\r\n    梅甘头壳坏掉了吗?如果她真的那么厌烦文明世界，怎么不干脆搬去西伯利亚!\r\n    行行行!没问题!我会当热刺队的拉拉队!\r\n    正努力攒钱当中。如果电视台继续赏我饭吃，明年夏天我就可以成行了。我要去亲眼瞧瞧贵书店、圣保罗大教堂、国会殿堂、伦敦塔、柯芬园、老维克剧院. ....还要见见博尔顿老太太。\r\n    附寄十元大钞一张。这本白色软精装的卡图卢斯，居然还配着白色的丝质书签带……弗兰基，你打哪儿找来这些玩意儿啊?\r\n\r\n    伦敦中西二区 查令十字街84号.\r\n    1956年3月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    很抱歉又隔了好一段时间才回您的信。因为直到今天，我们才有好消息可以向您报告。原先我还在犹豫，是不是该等到卡图卢斯的书也一并我到时再写这封信比较好。\r\n    我们终于寻获一本版本相当不错的《项狄传》，附有罗布的插图，价格约合美金二元七十五分。同时我们也收到一册柏拉图的《苏格拉底四论》，译者是本杰明·乔伊特，一九〇三年在牛津出版。此书标价一美元，您是否要买?您在敝店\r\n户头内尚有美金一元二十二分的余额，如您两册都购买，仅\r\n需再付给我们美金二元五十三分。\r\n    我们仍翘首期盼您今夏能来，我家的两个女孩儿都离家住校，所以届时橡原巷3号将有两间卧房可任您挑选。很遗憾地向您报告:博尔顿老太太已被送到老人之家，我们都很难过，但毕竟她在那儿能得到比较好的照料。\r\n弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1956年6月1日\r\n亲爱的弗兰克:\r\n    布莱恩介绍我读肯尼思·格雷厄姆的《杨柳风》，因此我迷上了谢泼德的插图，决定自己也要买一本。但是!先别忙着寄来，帮我保留到九月，届时我会迁入新址。\r\n    原本住得舒舒服服的旧公寓老命不保喽。上个月所有住户都收到了搬迁通知。现址要盖新大楼。我想也是该为自己觅一间好公寓、买几件好家具的时候了。于是我打铁趁热地到第二大道的一处工地，订了一间连影儿都还没有的客卧两\r\n用预售屋。我现在忙着四处张罗新家具和书架、地毯，几乎把钱都花光了。可是，我一辈子都跟摇摇晃晃的桌椅、到处爬满蟑螂的厨房为伍，现在我想过点儿像样的日子。至于英国之旅，只好等着有人招待我去了。\r\n    与此同时，我的房东怕大家赖着不走，索性把门房给解雇了，害得垃圾和热水都没人打理;还打算要把信箱间、走道灯和我的厨房和卧室间的隔间墙全拆了(本周即将发生);这些烦心的事加上眼睁睁看着道奇队兵败如山倒，真是心酸谁\r\n人知噢……\r\n对了，新地址如下:\r\n纽约州，纽约市21，东七十二大街305号(九月一日启用)\r\n\r\n    伦敦中西二区，查令十字街84号\r\n    1957年5月3日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    预备好大震一惊:您上回信中提到的三本书，一口气全都找到了!而且已经在上周寄去给您，现在应该正在途中。别惊讶我们究竟是怎么办到的，这理应是本店的服务项目之一。账单附在此信中，扣除账户余额之后的书款为五美元。\r\n    几天前，有两位您的朋友到店里来探望我们——很抱歉我现在不记得他们的大名了，是一对很可爱的新婚夫妇。不过，可惜他们行程匆忙，只能在我们这儿稍坐片刻，抽根烟的光景，就必须告辞了。\r\n    今年的美国游客似乎较以往更多了。我曾见到由上百位律师组成的旅游团，每个人西装上都别着一块大大的名牌—上头写着他们的姓名和居住的城镇名。他们好像都玩得颇开心，所以明年您一定也要来一趟。\r\n致上全体的祝福\r\n弗兰克\r\n\r\n    海莲·汉芙 纽约州，纽约市21。东七十二大街305号\r\n    1958年1月10日\r\n嗨，弗兰基——\r\n    快叫诺拉更新通讯薄里的地址，你们家的圣诞卡寄到旧址去啦!她写成了东九十五大街14号。\r\n    忘了是不是说过了，我好喜欢那本《项狄传》，罗布的插图简直美得令人销魂，托比叔叔。地下有知也可含笑瞑目啦。我在信纸背面列了几本“麦克唐纳绣像经典”的书。其中有一本《伊利亚随笔》，有“麦克唐纳绣像经典”版最好，若是有其他不错的版本也行，但价格得“合理”我才会考虑——现在已经找不到“便宜”的东西了，所有的东西都标榜“价格公道”，要不然就是“收费合理”。对街又在盖大楼了，他们立了一块大招牌，上头写着斗大的字:\r\n    “一房一厅或两房一厅任君选择/租金合理”\r\n    房租什么时候“合理”过?其他东西也没好到哪儿去。尽管招牌上盖得天花乱坠，终究只是个广告文案。\r\n    我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天。就像米尼弗·奇维一样，余生也晚。而我也只能学他“干咳两声，自叹一句:奈何老天作弄。然后继续借酒浇愁。\r\nP.S. 喂，柏拉图的《苏格拉底四论》的下落呢？\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1958年3月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    在此向您致歉，无法及早回信。我们前一阵子都累垮了，诺拉生了一场病，在医院里待了几个月，而我只好暂时请假在家代理家务。她现在已经复元得差不多了，再过个把礼拜就能出院回家。这段时间真够折腾我们两人的，不过还真得\r\n感谢国民保健制度，我们几乎没花自己一毛钱。\r\n    关于“麦克唐纳绣像经典”，我们偶尔能收购到一些，不\r\n巧现在手头上都没有。而兰姆的《伊利亚随笔》本来也有好几本，但上回旅游旺季时全被买走了。下周我还会出一趟差，届时再尽力为您搜寻。另，柏拉图的书也会为您留意。\r\n    所有人齐祝您有一个愉快的春假。女孩儿们要我转告:她们对您感到很抱歉，粗心将圣诞卡寄到旧地址去了。\r\n弗兰克敬上\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1958年5月7日\r\n亲爱的海莲:\r\n    谢谢您接连寄来两封问候信。也很感激您的好意，不过说真的，海莲，我们现在什么都不缺。我真希望我们能自已开一家书店，这样我们就能送几本书给您，多少报答您的一片好心。\r\n    随信附上几张全家福照片。本想寄些拍得更好的，但是比较好一点的都被亲戚们要走了。你一定也会发现了吧，希拉和玛莉竟然长得那么像，这实在是一件很奇妙的事。弗兰克说。玛莉现在长得就像是希拉在她这个年纪时的模样。希拉的母亲是威尔士人，而我的老家则是在爱尔兰，所以她们一定得自弗兰克的遗传多些，不过她们都比弗兰克要漂亮多了，当然弗兰克死都不愿承认的!\r\n    要是您晓得我写这样一封信得绞尽多少脑汁，一定也会可怜我的。弗兰克老爱笑我:成天叽哩呱啦地说个不停，怎么一拿起纸笔就不灵光了呢?\r\n    再次感激您的问候和您的来信。\r\n上帝保佑您!\r\n诺拉\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1959年3月18日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我实在不知如何向您启齿。就在上一封信向您报告，为您的朋友找到了《简明牛津辞典》之后隔两天，我才一转身，这本书就被别人买走了。我之所以这么晚才回信，也是因为希望能尽快再另寻一本，可惜至今仍一无所获。辜负了您的\r\n朋友，我感到万分愧疚，当时我实在应该先将书收起来的。\r\n    我们今天会将约翰逊的《莎士比亚评传》寄出，正好敝店内有这本附沃尔特·雷利导言的牛津出版社版。定价为一元五分，您的账户内尚有余额足够支付此书。\r\n    您参与的电视节目将移师到好莱坞，大家都觉得很可惜;夏天又快到了，预料将会有更多美国游客到英国来，然而我们期盼的“那位美国游客”却仍独独教我们望穿秋水。我很能理解您不愿离开纽约搬到南加州的心情。我们也在此祝您好运，并希望您很快可以再找到类似的工作。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙 纽约州，纽约市21.东七十二大街305号\r\n    1959年8月15日\r\n仁兄：\r\n    告诉你吧，我又有活儿可干啦!\r\n    是我凭实力挣来的。我得到一笔CBS提供的编剧奖助金——为数五千美元，供我未来一年将美国历史编写成电视剧本。我头一个要写的题材是英国占领期间的纽约。我一边写一边踟蹰——贵国同胞于一七七六年到一七八三年在这儿的行径那般龌龊不堪，而我偏挑这一段来写，实在有点儿对不起你们现在待我如此温良友善，体谅慈悲。\r\n    你们可有白话版的《坎特伯雷故事集》?实在罪过，我从没读过乔叟。我想学古盎格鲁——撒克逊', 'a38f41ef-288e-4081-802f-c7b6689e30e8');
INSERT INTO `p_task` VALUES ('c1500470-52e1-4852-8206-23bd51366d62', '测试8[卷22]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '22', '    几天前，有两位您的朋友到店里来探望我们——很抱歉我现在不记得他们的大名了，是一对很可爱的新婚夫妇。不过，可惜他们行程匆忙，只能在我们这儿稍坐片刻，抽根烟的光景，就必须告辞了。\r\n    今年的美国游客似乎较以往更多了。我曾见到由上百位律师组成的旅游团，每个人西装上都别着一块大大的名牌—上头写着他们的姓名和居住的城镇名。他们好像都玩得颇开心，所以明年您一定也要来一趟。\r\n致上全体的祝福\r\n弗兰克\r\n\r\n    海莲·汉芙 纽约州，纽约市21。东七十二大街305号\r\n    1958年1月10日\r\n嗨，弗兰基——\r\n    快叫诺拉更新通讯薄里的地址，你们家的圣诞卡寄到旧址去啦!她写成了东九十五大街14号。\r\n    忘了是不是说过了，我好喜欢那本《项狄传》，罗布的插图简直美得令人销魂，托比叔叔。地下有知也可含笑瞑目啦。我在信纸背面列了几本“麦克唐纳绣像经典”的书。其中有一本《伊利亚随笔》，有“麦克唐纳绣像经典”版最好，若是有其他不错的版本也行，但价格得“合理”我才会考虑——现在已经找不到“便宜”的东西了，所有的东西都标榜“价格公道”，要不然就是“收费合理”。对街又在盖大楼了，他们立了一块大招牌，上头写着斗大的字:\r\n    “一房一厅或两房一厅任君选择/租金合理”\r\n    房租什么时候“合理”过?其他东西也没好到哪儿去。尽管招牌上盖得天花乱坠，终究只是个广告文案。\r\n    我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天。就像米尼弗·奇维一样，余生也晚。而我也只能学他“干咳两声，自叹一句:奈何老天作弄。然后继续借酒浇愁。\r\nP.S. 喂，柏拉图的《苏格拉底四论》的下落呢？\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1958年3月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    在此向您致歉，无法及早回信。我们前一阵子都累垮了，诺拉生了一场病，在医院里待了几个月，而我只好暂时请假在家代理家务。她现在已经复元得差不多了，再过个把礼拜就能出院回家。这段时间真够折腾我们两人的，不过还真得\r\n感谢国民保健制度，我们几乎没花自己一毛钱。\r\n    关于“麦克唐纳绣像经典”，我们偶尔能收购到一些，不\r\n巧现在手头上都没有。而兰姆的《伊利亚随笔》本来也有好几本，但上回旅游旺季时全被买走了。下周我还会出一趟差，届时再尽力为您搜寻。另，柏拉图的书也会为您留意。\r\n    所有人齐祝您有一个愉快的春假。女孩儿们要我转告:她们对您感到很抱歉，粗心将圣诞卡寄到旧地址去了。\r\n弗兰克敬上\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1958年5月7日\r\n亲爱的海莲:\r\n    谢谢您接连寄来两封问候信。也很感激您的好意，不过说真的，海莲，我们现在什么都不缺。我真希望我们能自已开一家书店，这样我们就能送几本书给您，多少报答您的一片好心。\r\n    随信附上几张全家福照片。本想寄些拍得更好的，但是比较好一点的都被亲戚们要走了。你一定也会发现了吧，希拉和玛莉竟然长得那么像，这实在是一件很奇妙的事。弗兰克说。玛莉现在长得就像是希拉在她这个年纪时的模样。希拉的母亲是威尔士人，而我的老家则是在爱尔兰，所以她们一定得自弗兰克的遗传多些，不过她们都比弗兰克要漂亮多了，当然弗兰克死都不愿承认的!\r\n    要是您晓得我写这样一封信得绞尽多少脑汁，一定也会可怜我的。弗兰克老爱笑我:成天叽哩呱啦地说个不停，怎么一拿起纸笔就不灵光了呢?\r\n    再次感激您的问候和您的来信。\r\n上帝保佑您!\r\n诺拉\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1959年3月18日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我实在不知如何向您启齿。就在上一封信向您报告，为您的朋友找到了《简明牛津辞典》之后隔两天，我才一转身，这本书就被别人买走了。我之所以这么晚才回信，也是因为希望能尽快再另寻一本，可惜至今仍一无所获。辜负了您的\r\n朋友，我感到万分愧疚，当时我实在应该先将书收起来的。\r\n    我们今天会将约翰逊的《莎士比亚评传》寄出，正好敝店内有这本附沃尔特·雷利导言的牛津出版社版。定价为一元五分，您的账户内尚有余额足够支付此书。\r\n    您参与的电视节目将移师到好莱坞，大家都觉得很可惜;夏天又快到了，预料将会有更多美国游客到英国来，然而我们期盼的“那位美国游客”却仍独独教我们望穿秋水。我很能理解您不愿离开纽约搬到南加州的心情。我们也在此祝您好运，并希望您很快可以再找到类似的工作。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('c5510016-1ac6-476f-b548-595dbbe00c56', '测试8[卷23]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '23', '弗兰克谨上\r\n\r\n    海莲·汉芙 纽约州，纽约市21.东七十二大街305号\r\n    1959年8月15日\r\n仁兄：\r\n    告诉你吧，我又有活儿可干啦!\r\n    是我凭实力挣来的。我得到一笔CBS提供的编剧奖助金——为数五千美元，供我未来一年将美国历史编写成电视剧本。我头一个要写的题材是英国占领期间的纽约。我一边写一边踟蹰——贵国同胞于一七七六年到一七八三年在这儿的行径那般龌龊不堪，而我偏挑这一段来写，实在有点儿对不起你们现在待我如此温良友善，体谅慈悲。\r\n    你们可有白话版的《坎特伯雷故事集》?实在罪过，我从没读过乔叟。我想学古盎格鲁——撒克逊或〔和)中世纪英语的念头叫一位要拿它做博士论文的朋友给打消了。教授们对她说:关于古盎格鲁——撤克逊，想写什么题目都行。“乍听之下似乎很棒，”我的朋友哭丧着脸告诉我，“可是惟一能找到够多古盎格鲁——萨克逊语的文章，尽是些‘烧教堂、砍人头’的玩意儿。”\r\n    她还跟我讲了一堆贝奥武甫和他的私生子希卫斯(还是卫希斯?)的故事，她说那些也没啥看头。经她冷水一浇，我的兴致也全没了。所以给我白话版的《坎特伯雷故事集)就行了。\r\n代我问候诺拉。\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1959年9月2日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    人家都很高兴听到您得到一笔奖金，而且也有工作了。对于您所挑选的任何题材，我们完全不会介意。但我必须向您报告，店里的年轻同事们坦承:若不是您在信中提及，他们浑然不知英国竟然曾经“占领’过美国。\r\n    关于您的疑问，似乎所有最顶尖的研究学者对于将乔叟的文章译成白话文都敬而远之。但是，在一九三四年由希尔改写、朗文出版社出版的《坎特伯雷故事集》，应该是目前惟一能找到的白话版了。我认为还算是不错的书，此书现已绝\r\n版(这自然不在话下)，我将为您找一本书品较好的。\r\n弗兰克谨上\r\n\r\n        一九六〇年开春头一个糟糕透顶的周日夜\r\n我快没辙啦!弗兰基—\r\n    有人送我这么一本书充圣诞礼物。这是一本“巨匠现代文库”。你可曾见过这种版本?装帧比起纽约州议会公报好不到哪儿去，重量倒是略胜一筹。一个爱附庸风雅的家伙，听说我喜欢约翰·多恩。不知打哪儿找来送我。书名是:\r\n     约翰·多恩\r\n    诗全集、文选\r\n        与\r\n    威廉·布来克\r\n       诗全集?\r\n    问号是我自己加上去的。你学问大，能不能告诉我:这两个老小子浑身上下有哪点相同，得这样子凑合在一块儿?——只因他们俩都是英国佬，也都舞文弄墨?我努力读着导论，想找出些蛛丝马迹来。导论洋洋洒洒共分四大章:头两章巨细靡遗地描述了多恩的学术生涯;第三章开头是这么写的(我真不想引述):\r\n    “话说少年布来克，在茵茵夏日草原，于树下邂逅先知以西结，而遭母亲深责痛打。”我百分之百支持他娘。我的意思是:就算没瞻仰到上帝的后背，好歹领教一下圣母的尊容也不赖呀——谁叫他好死不死见到什么鬼先知以西结?\r\n    反正我不喜欢布来克，他还真是让这本书减色不少。看来我势必又得清掉一些书啦，弗兰基，你得伸出援手。\r\n    我现在蜷瘫在安乐椅里，聆听着收音机传出恬淡闲适的古典音乐——大概是科莱利吧... ...慵懒地享受这天下太平的短暂时刻。而这玩意儿——“巨匠现代文库”就站在桌上直盯着我瞧。我心想:好吧，不妨朗读读第十五篇布道词中的那\r\n三个标准段落好了。”多恩的文章合该大声朗读，它简直就像是巴赫的赋格!\r\n    可是你晓不晓得，这个无端之举给我自己惹了什么天大\r\n的麻烦?\r\n    打开“巨匠现代文库”，翻到“布道文第十五篇”——竟被肢解成三个选段。该到第一段末尾，才一发现“耶洗别篇”被删掉了;我赶紧找来另一本多恩的《布道文选编》(罗根·皮尔史·史密斯编)，花了二十来分钟拼命找“布道文第十五篇”……搞了半天才发现:原来，照史密斯的编法，它不叫“证道文第十五篇”，而是“第126节:人必有终”。好不容易找到了，却——同样没有“耶洗别篇”。再搬出《约翰·多恩诗全集暨文选》（典范版）.依然少了那段“耶洗别篇”;转而向《牛津英语诗选》求助，再度穷花二十分钟，因为在这本书里，既不叫“布道文第十五篇”，也不叫“第126节:人必有终”……好歹“耶洗\r\n别篇”总算是找着了，于是开始正襟危坐大声朗读……读到末尾，啥，又不行了——这本书没有第二、第三段!所以咯，我若要从头到尾朗读一篇完整的布道文，得将三巨册全摊开，翻到正确的页面，然后绕着它们来同奔波!\r\n    请好心地告诉我:一本完整收录约翰·多恩布道文的书有那么难找吗?我得花多少钱才买得到?\r\n    我得去睡了。我会做一个可怕的噩梦——披着道袍的妖魔鬼怪，拎着一把把血淋淋的屠刀—上面分别标示着\r\n“段”、‘’节”、“选”、“删”等字眼、霍霍朝我追来……\r\n        祝好\r\nhh\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    l960年3月5日\r\n    海莲· 汉芙小姐\r\n    美国\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('c6a1803b-b60b-45dc-ac3a-0aa5606f2bb5', '测试8[卷2]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '2', '　　你把我冷落在这里，坐在图书馆中，在那些不属于我的书上写着长长的眉批，总有一天他们会发现，会把我的图书卡收走！\r\n　　我已经安排了复活节的小兔子给你们带去礼物，等它到达时，你可能已慵懒而死了。\r\n　　春天到来之际，我要一本情诗集，不要济慈或雪莱，请寄给我一本不太煽情的情诗集，你自己挑选吧，要一本小开本的，可以放入裤兜中带到中央公园去。\r\n　　行了，不要只坐在那里，快去帮我找书吧，真不明白你们书店是怎么维持的。\r\n　　(一九五○年三月二十五日)\r\n　　汉芙性情率真，人更是善良，通信之初，她便得知战后的英国经济困难，肉类、鸡蛋等食品都是限量供应，女人的长统丝袜更是奢侈品。一九四九年圣诞节她将一块重六磅的火腿寄往伦敦，让德尔先生分给书店中的同事们，以后美式食品源源不断几年。汉芙本身手头并不宽裕，她的慷慨大度让书店的工作人员把她视作亲人，纷纷与她通信，聊天。只是德尔先生从未在信中对汉芙的轻松笑语做任何回报，他是正人君子，地道的好丈夫，典型的英国绅士，惟一的报答是兢兢业业地为汉芙寻觅好书。直到一九五二年，德尔太太登场写信给汉芙道谢这几年的礼物，并向汉芙介绍说德尔先生已有二女，德尔先生才在汉芙的强烈要求之下，在答谢汉芙所寄给他妻女的长统袜时，将例来一贯的信头的“汉芙小姐”的称呼，改为“亲爱的海莲”，写信的日期恰与情人节巧合，不过想必当时德尔先生压根没有注意到。\r\n　　亲爱的海莲：\r\n　　我同意，现在写信给你，是该把“小姐”放弃的时候了。我并不如你想像的那样古板，只是因为我所写给你的信，都得在办公室的卷宗中存档，所以我觉得正式的称呼更合适，但这封信与书没有关系，是不会被存档的。\r\n　　……\r\n　　真不知该如何报答你这么多好礼物，我能说的只是，如果有一天你来伦敦，橡原巷37号会有一张床给你，你爱待多久便待多久。\r\n　　(一九五二年二月十四日)\r\n　　去家里作客的邀请一直没有兑现，汉芙几次犹豫要去英格兰圆梦，但终因手头拮据而放弃。倒是德尔先生紧接着寄去的那本沃尔顿的《五人传》着实让汉芙惊喜不已：\r\n　　噢，天哪，老天感谢你沃尔顿的《五人传》，这本书出版于一八四○年，百年之后还能这样完美，真是奇迹！如此漂亮，久经摩挲的粗裁本！我真同情他，这位曾于一八四一年在书的扉页上签名的戈登先生。他那一群不肖子孙呀！几乎不值分文地便把它卖给了你！真希望在他们出卖图书馆之前，我曾去那边赤脚跑过！\r\n　　(一九五二年三月三日)\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。六十年代末，汉芙颇为潦倒，写出去的剧本屡遭拒绝，书的选题也无人感兴趣。一九六九年一月，纽约冬天很冷，汉芙从图书馆回到家中，已近六点。她手上捧着一摞书，把从门房中取来的信件放在书上，走向电梯。在电梯中，她发现在那一大堆账单之间，有一封薄薄的蓝色的从Marks&Co.寄来的航空信封。这信有些异样，因为德尔先生所寄的信，信封上的地址都是单行距打成，而且向来是把她连名带姓拼全的，而这封信上，地址是双行距，她的名字是由一个字母H代替的。她只道是德尔离开了书店，没太在意，夜深人静捧杯独坐时，她才打开此信。这一夜，她再没有睡着。因为信中的消息，是德尔的死讯。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(3)\r\n---------------\r\n\r\n\r\n　　二\r\n　　桌上的黄酒已过三巡。言谈嬉笑，话语投机，共同喜爱的书与作家们一时让他们觉得很亲近。然而，手中抚弄着那樽精致的铜酒壶，眼光却不敢对视，他早已与另一位美丽的女子谈婚论嫁，书缘与情缘，在现实生活中原本是风马牛不相干的事。\r\n　　然而，浪漫向来是作家们难以割舍的情怀。书中自有颜如玉，红袖添香夜读书，虽然这都是男人们近乎梦想的宣言，但是，自古以来的好书，大多都是激情之作，没有情的文章和书，是太过干涩枯燥，没有人愿读的。于是，温润的花雕虽只逗出跳跃键盘上往返数次的几行短语，将消除键轻轻按下便了无痕迹，查令十字街84号却被好心的好事者演绎成一曲情感故事。\r\n　　泪尽之后，汉芙觉得体内像被掏空了一样，一片冰凉。应该做些什么，但是又能做什么呢？想起这二十年来的通信，几次搬家，这丝带束成的一小扎竟还静静地躺在抽屉的底部。仿佛是为了了却一桩心愿，汉芙将它们结成一集，送到出版商的手中，也许是她时来运转，也许是德尔在天亡灵的保佑，此书一经出版，便受欢迎，英国出版商亦决定在英国推出此书，并邀请汉芙前往英国，下榻于大英博物馆旁布鲁姆斯伯里区的一家古旧的老饭店。\r\n　　英国是汉芙魂牵梦系的地方，从一九五○年开始她便屡次想去，但都因无川资而未成。《查令十字街84号》的最后一封信，是她于一九六九年四月写给一位前往伦敦度假的朋友的，读来让许多英国人觉得鼻子酸酸的：\r\n　　亲爱的凯瑟琳：\r\n　　我在家中打扫卫生，整理书架。偷闲坐在地上，四面地毯上散放的都是书。希望你与布莱恩在伦敦玩得愉快。他在电话上对我说：“如果你有路费的话，是否想和我们同去？”我几乎哭了。\r\n　　我不知道，可能对我来说去或不去那儿已是无所谓了。我梦到那儿的次数太多了。我常常是为了看那些宽街窄巷才去看那些英国电影。记得许久以前，有个人对我说，那些去过英国的人，都能在那儿找到他最想要的东西。我告诉他我想去英国，是为了找英国文学。他说：“它们就在那儿。”\r\n　　或许在那儿，或许不在。看着四周地毯上散乱的书籍，我知道，它们肯定在这儿。\r\n　　那位卖给我这所有书的好人几个月前去世了，书店的主人也死了，但是书店还在那里。如果你正巧经过查令十字街84号，能否为我吻它？我欠它的实在太多了。\r\n　　(一九六九年四月十一日)\r\n　　其实此时，查令十字街84号已准备关门大吉，书店主人的后代无心经营旧书，一年后汉芙的书的畅销也未让书店起死回生。此时的伦敦，经过六十年代文化革命和摇滚乐的洗礼，已与五十年代完全不同。七十年代初，英国是激进先锋、朋克即将形成势力的年代，关心旧书旧文化的人实在太少了。汉芙一九七一年前往英国，一心一意醉心于寻找的是维多利亚时代的情怀，触目所见，根本不是心中的英国，这位战战兢兢小心翼翼的老姑娘此时肯定已极难让一般人亲近，无奈大英帝国也确实有一批为数不多的怀旧之人，他们虽已过时，但却仍有生息，大洋彼岸这位老姑娘对英国潦倒二手书店的无限热爱，对英国旧文化旧文学旧传统的一片痴情，对这些怀旧的人来说，是一帖温润滋补的药，把他们熏得晕晕乎乎，舒舒服服，而且这是一副中国式的汤药，头剂、二剂、三剂，喝了十几年还不舍得把药渣子扔掉，这是这本书话式的信集能在英国成为畅销书的原因了。\r\n　　一九七五年，汉芙家中所有的鞋盒子都被腾出来装了英国各地的书迷们寄来的信件，BBC决定把《查令十字街84号》搬上荧屏；六年之后，素有盛名的英国戏剧界决定把它改编为舞台剧，在伦敦最好的剧院上演三月不衰；再过六年，此书又被改编成电影，由著名演员安妮·班克劳夫特及安东尼·霍普金斯领衔主演，电影介绍中称，“这部片子旨在反映两种爱情，一是汉芙对书的激情之爱，二是她对德尔的精神之爱”，终于在书缘与情缘之间系了根红线。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(4)\r\n---------------\r\n\r\n\r\n　　三\r\n　　现实生活中没有的缘分只能靠文学作品去演绎，然而他们最终未曾见面，电影中也没能让他们见面，没有缘分就是没有缘分。\r\n　　霍普金斯演德尔是最贴切不过的了，《霍华德庄园》、《长日将尽》，他最适合演的就是那种正经而又有些压抑的英国绅士。\r\n　　那部电影太干了，幸亏我不是英国男人。\r\n　　没有缘分的也会有感情，所以，并不能说是电影做作。\r\n　　这样的感情最好还是藏在心底。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('cbd7cc58-8d4e-43f1-9cd1-862f14c4c0d8', '测试8[卷4]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '4', '\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年10月5日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。\r\n　　随信附上一份清单，上面列出我目前最想读而又遍寻不着的书。如果贵店有符合该书单所列，而每本又不高于五美元的话，可否径将此函视为订购单，并将书寄给我？\r\n　　你忠实的\r\n　　海莲·汉芙(小姐)\r\n\r\n\r\n\r\n---------------\r\n1949年10月25日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年10月25日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的夫人：\r\n　　谨在此回复您于本月五日的来函。敝店很荣幸能为您解除三分之二的困扰。您所列出的三种哈兹里特散文，均收录于这本典范出版社的《哈兹里特散文选》内；斯蒂文森的作品则在《致少女少男》中可以找到。我们挑出两本品相较好的书为您寄上，相信不久后即可送达您的手中，祈盼您会满意。随书附上发票，请查收。\r\n　　至于您提及的利·亨特的散文，目前颇不易得见，不过我们会留意是否能找到收罗齐全且装帧精良的版本，届时将再为您寄上。而您所描述的拉丁文圣经，目前敝店并无存书，仅有晚近出版、布面精装普通版的拉丁文和希腊文《新约全书》，不知您是否有兴趣？\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月3日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年11月3日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　今天收到你们寄来的书，斯蒂文森的书真是漂亮！把它放进我用水果箱权充的书架里，实在太委屈它。我捧着它，深怕污损它那细致的皮装封面和米黄色的厚实内页。看惯了那些用惨白纸张和硬纸板大量印制的美国书，我简直不晓得一本书竟也能这么迷人，光抚摸着就教人打心里头舒服。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('ce4c276c-ef0b-410f-99b7-7b843e375ef4', '测试8[卷25]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '25', '    唉，这下子你该明白了吧，弗兰基，这个世界上了解我的人只剩你一个了。\r\nP.S.金小姐乃中国人是也。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年10月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    当您收到弗吉尼亚·吴尔夫的《普通读者》。上、下两册时，一定会十分惊喜——它们已在寄往您的路上。如果您还有其他想要的书。我也会倾全力尽速为您服务。\r\n    我们都很好，仍是活蹦乱跳的。我的大女儿希拉(现在已经二十四岁了)两年前突然决定改行当老师，便辞掉原来的秘书工作。跑去念大学了，她还得待在学校一年。看来，要靠儿女供养我们这些老人家颐养天年的日子还有得等哩。\r\n    所有人诚心祝福您。\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年11月9日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    许久以前您曾询问过白话版《坎特伯雷故事集》，前几天我们收购了一部，心想您或许仍想买。虽然这并非是收录所有故事的完整版本，但价格十分便宜而且似乎编写还算严谨。我今天会将它寄给您，书价是一元三十五分。如果您读了\r\n之后，还想要更完整的乔叟作品，我会尽力去找。\r\n    弗兰克谨上\r\n\r\n星欺六\r\n    行了!白话版的乔叟真是够了!简直就像兰姆的《莎士比亚故事集》嘛——适合学龄儿童阅读!\r\n    充其量就是故事嘛，我讨厌虚构故事这事儿你是知道的。倒是里头描述一个吃相优雅、食不沾手的修女的那段还算有趣，换了我就不行，还是得动刀舞叉才成。其余内容全引不起我的兴趣，我就是不喜欢故事。如果乔叟能留下日记，里\r\n头规规矩矩记述他在理查三世的皇宫里当差的经过，那才是我真正该读的东西，否则我辛辛苦苦学文言文所为何来。\r\n    最近才刚扔了一本人家送的书——作者描述奥立弗·克伦威尔时代的社会状况。天晓得这个自作聪明的家伙是不是瞎瓣。他又不是那个时代的人，哪晓得那个时代的社会状况?如果我真想了解那个时代的社会状况，大可左阅弥尔顿，右\r\n读沃尔顿。这些货真价实的作品不仅能清楚明白告诉我那个时代的社会状况，还能引领我身历其境。\r\n    君不见沃尔顿尝曰:“若非身临现场、亲眼目睹，何以让读者尽信余言?”\r\n    这段话铿锵有力，深得吾心!我坚决拥护“亲身经历”的作者、作品。\r\n    附上两元支付这本乔臾，这样我在你们的户头里应该还会有六十五分钱的余额——比起我的其他任何一个户头都多。\r\n\r\n    海莲·汉芙 纽约州 纽约市21 东七十二大街305号\r\n    1964年3月30日\r\n亲爱的弗兰克——\r\n    我手头上正在编写给孩子们读的历史教材(已经写到第四本了，惊讶吧?)，忽然想起要帮一位朋友问你:你们有没有一套萧伯纳全集——他坚称书名前冠着“定本xxx”、暗红褐色的布面装帧——希望你有印象。我附上一张清单，上面列的是他已经有的几本。如果你可以为他补齐其他几本，先别全部寄来!他会分批购买，他和我一样——甲级贫户一个。你们可以直接寄给他，地址就写在清单上头。如果你嫌我的字太潦草看不懂的话，那是“第二十二大道”。\r\n    你可有塞西莉或梅甘的消息?\r\n祝一切顺利\r\n海莲\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1964年4月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('e2efb82d-fb7c-4552-a435-4817e909a167', '测试8[卷16]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:45', '2017-09-08', '0', '80', '0', '0', '1', '16', '      弗兰克·德尔谨上\r\n\r\n    195年l月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    首先，我们都十分高兴您喜欢那块桌布。总算能够回赠您一件礼物，亦带给我们一丝欣喜——虽然比起您过去这些年来为我们的奉献，那实在是太微不足道了。您或许想知道那件桌布的来历，其实这是最近刚完成的作品，出自我的邻\r\n居—一位八十多岁老太太的巧手。她一个人独居，平日以女红自娱。虽然她做了许多手工刺绣，却儿乎全都自己留着。她之所以愿意割爱，破例卖出这么一块桌布，乃是禁不住我太太的不断恳求。当然，您之前寄给我们的干燥蛋也使上不\r\n少助力。\r\n    如果您非到万不得已，一定要“清洗”您的《格罗里埃圣\r\n经》。的话，我们会建议您用一般的肥皂和清水即可。做法是:加一茶匙的苏打粉到一品脱的温水中，用蘸了肥皂的海绵轻轻擦拭。我相信您会发现，这样可以有效地除去污渍。经过上述的处理后，您可以再用少许绵羊油为它上光。\r\n    J.彭伯顿是一位女士，J是珍妮特的缩写。\r\n    祝您新的一年一切如意。\r\n弗兰克·德尔敬上\r\n\r\n\r\n    伦教北八区克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年1月20日\r\n    亲爱的汉芙小姐:\r\n    谢谢您长期不断地寄赠马克斯与科恩书店礼物包裹，并让我家也能有幸分得一部分。我长久以来就一直想写信给您，现在终于有正当理由可以动笔了。弗兰克跟我说，您很想知道绣了那块桌布的老太太的姓名、地址。那件刺绣的手工\r\n真的很漂亮吧？\r\n    这位博尔顿老太太就住在我们的公寓隔壁，地址是:橡原巷36号。当我告诉她，这件桌巾飘洋过海送给了一位好心的女士时，她好吃惊。如果能再亲耳听到您对她的手艺赞不绝口，我相信她一定会十分开心。\r\n    很感谢您说还要寄更多干燥蛋给我们。不过上回您送来的还剩一些，应该可以让我们享用到春天。四月到九月这段时间，我们并无须操心蛋的问题。如果偶尔会因物资短缺而临时减少配给量，我们就会拿别的东西去跟别人换罐头。我曾经用一双丝袜在黑市换得一罐干燥蛋，当然这么做不尽合法，却是非常时期不得已的变通办法。\r\n    过些日子我会寄一些全家福照片给您看，我们的小女儿玛莉上周刚度过她的四岁生日;大女儿去年八月满十二岁了，名叫希拉，是弗兰克的前妻生的——她不幸在战时丧生了。战后我嫁给弗，顺道有了个现成的女儿。去年五月，希拉在学校当着修女们的面(她念的是教会学校)，说她要送一张卡片祝福爸爸、妈妈的结婚四周年纪念日。您不难想像，这可怜的小姑娘花了多少唇舌才解释清楚。\r\n    最后，祝您新的一年一切顺遂，并期盼不久后我们可以在英国相会。\r\n诺拉·德尔谨上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年l月29日\r\n    亲爱的汉芙小姐:\r\n    谢谢您的来信。我很感激您如此好心地告诉我:您十分喜欢我绣的桌布。我当初真该多花点儿工夫在那上头。我想，德尔太太一定也对您提过了吧，我是一个上了年纪的人，双手也不太听使唤，能做的活儿也不像以前那么多了。我用这\r\n双老手做的东西有幸能交到喜欢它的人手上，这真是一件令人欣慰的事儿。\r\n    我常见到德尔太太，而她也不时向我提起您的事儿。您若到英国来，或许我也能有幸见您一面罢。\r\n    再次谢谢您。\r\n    玛丽·博尔顿谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n听好了!玛克辛:\r\n    我刚和你妈妈聊过。她说你们的戏也许下个月就会结束公演;她还告诉我。你带走了两打丝袜。帮我一个忙，趁你的戏下档前，拿四双去书店交给弗兰克·德尔，就说是送给店里的三个女孩和诺拉（他太太）。\r\n    你妈妈还特别交代，要我用不着付钱给你。她说那些丝袜全是她自已在去年夏天萨克斯百货店清仓大减价时便宜买到的，她决定要乐捐出来，好让她自个儿也能沾沾“共赴国难”的光。\r\n    等你回国就可以瞧见他们送我的圣诞节礼物了。这是一条漂亮的爱尔兰绣花桌巾，米黄色的底布上以手工绣着古典的花草图案——全是各自不同颜色、浓淡有致的花儿。保证你从没看过这么美的桌巾，我那张从旧货店买回来的破茶几\r\n就更肯定没见识过啦!我真是迫不及待想披上维多利亚时代的水袖，优雅地举起手，幻想自己执着一只乔治王朝的古董茶壶，轻轻地斟上一盏茗茶……你快点儿回来吧!我们可以在家里扮一出斯坦尼斯拉夫斯基！\r\n    埃勒里调高了我的剧本稿酬，现在是一集两百五十元。如果照着这样的调薪幅度。到了六月，也许我就可以启程赴英，自己去逛“我的书店”——如果我斗胆敢去的话。隔着三千英里的安全距离，我写了一堆没大没小的信，我大概只会\r\n悄悄溜进去又静静踱出来，而不敢告诉他们我是谁。\r\n    我实在不明白你怎么会在杂货铺里被搞得一楞一楞的呢?老板跟你说的不是“兔兜脚”，他说的是“土豆胶”!——我也觉得这才是惟一合理的称呼。你动点儿脑筋想想:豆子长在土里，叫它“土豆”合情人理;从土里头挖出来，一直“搅”、一直“搅”，搅成“胶”状，不就成了“土一豆一胶”!这个词儿不是要比“花生酱”更贴近事实吗?你真是不懂国语!\r\n    \r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('e39c5a8e-de93-4d31-a396-b0f7b6ff2e32', '测试6[卷1]', '历史', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-21 22:54:40', '2017-09-06', '1', '600', '2', '0', '1', '1', '查令十字街84号\r\n\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n《查令十字街84号》\r\n\r\n\r\n\r\n\r\n\r\n\r\n***************\r\n*序言\r\n***************\r\n\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。						\r\n\r\n---------------\r\n序：书缘·情缘(1)\r\n---------------\r\n\r\n\r\n　　恺蒂\r\n　　如同每一个晴日的上午，阳光将这排歪歪斜斜的二手书店的影子投到街中心上，街上还少行人，穿着对襟毛衣，半秃着顶，行动悠缓的店主们正在将一切生意准备停当，掸一下桌面、橱窗中的灰尘，把书架上那排排参差的布面、皮面书摆正，再将一匣匣便宜的小本平装书移到门外，沿着窗前的墙根摆齐。不用吆喝生意，不用招揽顾客，这群书商们如同他们店中中层书架上的那些小羊皮装帧而成的上个世纪的书籍，虽并不昂贵，但却见过世面，口中叼着一枚烟斗，看着大红色的双层汽车在街上阳光屋影间叮咚过往。\r\n　　她跨下了一辆黑色的计程车，纤巧单薄的女人，游移的目光掠过那一家家摆着书的橱窗，68号，72号，76号，78号，82号，寻寻觅觅，像是丢失了件宝物。最终停了下来，但面前的84号却是空空如也。灰蒙蒙的玻璃窗里面蛛网遍织的书架东倒西歪，地上散落着些废纸，满是尘埃；推门进去，没有想像中的惊喜问候，空空的楼梯通向另一些同样废弃了的房间。孤身女人想张口告诉主人她已到来，她信守了诺言，但空屋中并无人回应，只有一阵冷风袭过，泪水顺着面颊静静地流淌下来。是一段书缘，还是一段情缘，竟让这纽约的独居女人千里迢迢为了伦敦小街这破落关门的书店而如此神伤？手中握着那本薄薄的小书，是为了还查令十字街(CharingCrossRoad)84号的哪一种心愿？\r\n　　一\r\n　　他约她出来聊天，选定的地方是孔乙己酒家，面前摆的是一樽绍兴花雕，自然少不了一碟五香豆，还有几样小菜。谈着各自喜欢的东西作家，纳博科夫、钱钟书、尤瑟纳尔、沈从文。谈着那本他最钟爱的书，《说吧，记忆》，在伦敦买到的初版本，自然便谈到那些古旧的书屋，里面的善本孤本初版本那些只有爱书人才能欣赏的古老气味。记得那条破街吗？我最爱做的是星期六早上睡个懒觉，约几个朋友去唐人街饮早茶，然后就去对面那条破街的老店中翻旧书。为什么我从未在那里遇见过你呢？回忆起从未共同经历过的伦敦往事，却怎么也想不起来查令十字街84号现在是做着什么样的买卖。知道那位纽约的老姑娘和那位一丝不苟的旧书商，他们通了二十多年的信，最终却仍未能谋面，是没有缘分？\r\n　　一九四九年十月，一切开始于一封很简单的从纽约到伦敦的商业性的信函：\r\n　　先生：\r\n　　你们在《星期六文学评论》的广告上说你们长于经营绝版的书籍，你们所用的“珍本书商”一词让我有些害怕，因为我总是把“珍本”与昂贵相联的。我是位穷作家，但对书却有一些“珍本”般的嗜好，我所要的书在这里都很难买到……寄上我最急需的书的名单，如果你们有干干净净不超过五美元一本的二手货，请将此函视作订购单，给我悉数寄来。(恺蒂女士的译文与本书正文译文行文不同，为尊重作者，特不作统一。下同。)\r\n　　(一九四九年十月五日)\r\n　　署名海莲·汉芙(HeleneHanff)，还特地注明了“小姐”。\r\n　　其实，这位小姐此年已三十有三，是一位以写电视、舞台剧本为生的自由撰稿人。汉芙出生于制衣人家庭，父亲原本是位民谣说唱艺人，虽为生活所迫做起了手艺活，但夫妻俩仍喜欢带着女儿去逛戏院。汉芙十九岁时进费城大学读英文，但家境贫困，一年后辍学，求职谋生，后来得一戏剧写作奖项，便以写作糊口。对书的热爱来自于在纽约市立图书馆中的刻苦自学，特别得益于英国剑桥大学一位阿Q教授(SirArthurQuillerCouch)的著作。然而美国书价昂贵，汉芙热爱英国文学，便将买书的对象转向英伦三岛，偶然选中一家小书店写了信去，第一次订货便得到价廉物美的圆满服务，海峡这边，查令十字街84号Marks&Co.书店的主管，弗兰克·德尔先生，则是汉芙二十年通信的对象。\r\n　　虽然三十有余，汉芙却仍是轻松活泼，特别是简牍之上，更善于以轻松调侃的笔墨，信手写来，天马行空，不拘格式。德尔先生给她回的第一封信中称之为“女士”，汉芙第二封信尾便加了注脚，“我希望‘女士’在你们那边的含义与这边不一样”。德尔先生下封信中便乖乖称之为“小姐”了。第五封信后，汉芙已将信首的尊称“先生”或“阁下”改为直呼其名，信的内容也像是写给一位相识已久的老友，且不乏亲昵、撒娇之态：\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(2)\r\n---------------\r\n\r\n\r\n　　弗兰克·德尔，你在那儿究竟干什么？你什么都没干，你只是闲坐着！\r\n　　我的利·亨特在哪里？我的《牛津诗集》在哪里？……\r\n　　你把我冷落在这里，坐在图书馆中，在那些不属于我的书上写着长长的眉批，总有一天他们会发现，会把我的图书卡收走！\r\n　　我已经安排了复活节的小兔子给你们带去礼物，等它到达时，你可能已慵懒而死了。\r\n　　春天到来之际，我要一本情诗集，不要济慈或雪莱，请寄给我一本不太煽情的情诗集，你自己挑选吧，要一本小开本的，可以放入裤兜中带到中央公园去。\r\n　　行了，不要只坐在那里，快去帮我找书吧，真不明白你们书店是怎么维持的。\r\n　　(一九五○年三月二十五日)\r\n　　汉芙性情率真，人更是善良，通信之初，她便得知战后的英国经济困难，肉类、鸡蛋等食品都是限量供应，女人的长统丝袜更是奢侈品。一九四九年圣诞节她将一块重六磅的火腿寄往伦敦，让德尔先生分给书店中的同事们，以后美式食品源源不断几年。汉芙本身手头并不宽裕，她的慷慨大度让书店的工作人员把她视作亲人，纷纷与她通信，聊天。只是德尔先生从未在信中对汉芙的轻松笑语做任何回报，他是正人君子，地道的好丈夫，典型的英国绅士，惟一的报答是兢兢业业地为汉芙寻觅好书。直到一九五二年，德尔太太登场写信给汉芙道谢这几年的礼物，并向汉芙介绍说德尔先生已有二女，德尔先生才在汉芙的强烈要求之下，在答谢汉芙所寄给他妻女的长统袜时，将例来一贯的信头的“汉芙小姐”的称呼，改为“亲爱的海莲”，写信的日期恰与情人节巧合，不过想必当时德尔先生压根没有注意到。\r\n　　亲爱的海莲：\r\n　　我同意，现在写信给你，是该把“小姐”放弃的时候了。我并不如你想像的那样古板，只是因为我所写给你的信，都得在办公室的卷宗中存档，所以我觉得正式的称呼更合适，但这封信与书没有关系，是不会被存档的。\r\n　　……\r\n　　真不知该如何报答你这么多好礼物，我能说的只是，如果有一天你来伦敦，橡原巷37号会有一张床给你，你爱待多久便待多久。\r\n　　(一九五二年二月十四日)\r\n　　去家里作客的邀请一直没有兑现，汉芙几次犹豫要去英格兰圆梦，但终因手头拮据而放弃。倒是德尔先生紧接着寄去的那本沃尔顿的《五人传》着实让汉芙惊喜不已：\r\n　　噢，天哪，老天感谢你沃尔顿的《五人传》，这本书出版于一八四○年，百年之后还能这样完美，真是奇迹！如此漂亮，久经摩挲的粗裁本！我真同情他，这位曾于一八四一年在书的扉页上签名的戈登先生。他那一群不肖子孙呀！几乎不值分文地便把它卖给了你！真希望在他们出卖图书馆之前，我曾去那边赤脚跑过！\r\n　　(一九五二年三月三日)\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。六十年代末，汉芙颇为潦倒，写出去的剧本屡遭拒绝，书的选题也无人感兴趣。一九六九年一月，纽约冬天很冷，汉芙从图书馆回到家中，已近六点。她手上捧着一摞书，把从门房中取来的信件放在书上，走向电梯。在电梯中，她发现在那一大堆账单之间，有一封薄薄的蓝色的从Marks&Co.寄来的航空信封。这信有些异样，因为德尔先生所寄的信，信封上的地址都是单行距打成，而且向来是把她连名带姓拼全的，而这封信上，地址是双行距，她的名字是由一个字母H代替的。她只道是德尔离开了书店，没太在意，夜深人静捧杯独坐时，她才打开此信。这一夜，她再没有睡着。因为信中的消息，是德尔的死讯。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(3)\r\n---------------\r\n\r\n\r\n　　二\r\n　　桌上的黄酒已过三巡。言谈嬉笑，话语投机，共同喜爱的书与作家们一时让他们觉得很亲近。然而，手中抚弄着那樽精致的铜酒壶，眼光却不敢对视，他早已与另一位美丽的女子谈婚论嫁，书缘与情缘，在现实生活中原本是风马牛不相干的事。\r\n　　然而，浪漫向来是作家们难以割舍的情怀。书中自有颜如玉，红袖添香夜读书，虽然这都是男人们近乎梦想的宣言，但是，自古以来的好书，大多都是激情之作，没有情的文章和书，是太过干涩枯燥，没有人愿读的。于是，温润的花雕虽只逗出跳跃键盘上往返数次的几行短语，将消除键轻轻按下便了无痕迹，查令十字街84号却被好心的好事者演绎成一曲情感故事。\r\n　　泪尽之后，汉芙觉得体内像被掏空了一样，一片冰凉。应该做些什么，但是又能做什么呢？想起这二十年来的通信，几次搬家，这丝带束成的一小扎竟还静静地躺在抽屉的底部。仿佛是为了了却一桩心愿，汉芙将它们结成一集，送到出版商的手中，也许是她时来运转，也许是德尔在天亡灵的保佑，此书一经出版，便受欢迎，英国出版商亦决定在英国推出此书，并邀请汉芙前往英国，下榻于大英博物馆旁布鲁姆斯伯里区的一家古旧的老饭店。\r\n　　英国是汉芙魂牵梦系的地方，从一九五○年开始她便屡次想去，但都因无川资而未成。《查令十字街84号》的最后一封信，是她于一九六九年四月写给一位前往伦敦度假的朋友的，读来让许多英国人觉得鼻子酸酸的：\r\n　　亲爱的凯瑟琳：\r\n　　我在家中打扫卫生，整理书架。偷闲坐在地上，四面地毯上散放的都是书。希望你与布莱恩在伦敦玩得愉快。他在电话上对我说：“如果你有路费的话，是否想和我们同去？”我几乎哭了。\r\n　　我不知道，可能对我来说去或不去那儿已是无所谓了。我梦到那儿的次数太多了。我常常是为了看那些宽街窄巷才去看那些英国电影。记得许久以前，有个人对我说，那些去过英国的人，都能在那儿找到他最想要的东西。我告诉他我想去英国，是为了找英国文学。他说：“它们就在那儿。”\r\n　　或许在那儿，或许不在。看着四周地毯上散乱的书籍，我知道，它们肯定在这儿。\r\n　　那位卖给我这所有书的好人几个月前去世了，书店的主人也死了，但是书店还在那里。如果你正巧经过查令十字街84号，能否为我吻它？我欠它的实在太多了。\r\n　　(一九六九年四月十一日)\r\n　　其实此时，查令十字街84号已准备关门大吉，书店主人的后代无心经营旧书，一年后汉芙的书的畅销也未让书店起死回生。此时的伦敦，经过六十年代文化革命和摇滚乐的洗礼，已与五十年代完全不同。七十年代初，英国是激进先锋、朋克即将形成势力的年代，关心旧书旧文化的人实在太少了。汉芙一九七一年前往英国，一心一意醉心于寻找的是维多利亚时代的情怀，触目所见，根本不是心中的英国，这位战战兢兢小心翼翼的老姑娘此时肯定已极难让一般人亲近，无奈大英帝国也确实有一批为数不多的怀旧之人，他们虽已过时，但却仍有生息，大洋彼岸这位老姑娘对英国潦倒二手书店的无限热爱，对英国旧文化旧文学旧传统的一片痴情，对这些怀旧的人来说，是一帖温润滋补的药，把他们熏得晕晕乎乎，舒舒服服，而且这是一副中国式的汤药，头剂、二剂、三剂，喝了十几年还不舍得把药渣子扔掉，这是这本书话式的信集能在英国成为畅销书的原因了。\r\n　　一九七五年，汉芙家中所有的鞋盒子都被腾出来装了英国各地的书迷们寄来的信件，BBC决定把《查令十字街84号》搬上荧屏；六年之后，素有盛名的英国戏剧界决定把它改编为舞台剧，在伦敦最好的剧院上演三月不衰；再过六年，此书又被改编成电影，由著名演员安妮·班克劳夫特及安东尼·霍普金斯领衔主演，电影介绍中称，“这部片子旨在反映两种爱情，一是汉芙对书的激情之爱，二是她对德尔的精神之爱”，终于在书缘与情缘之间系了根红线。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(4)\r\n---------------\r\n\r\n\r\n　　三\r\n　　现实生活中没有的缘分只能靠文学作品去演绎，然而他们最终未曾见面，电影中也没能让他们见面，没有缘分就是没有缘分。\r\n　　霍普金斯演德尔是最贴切不过的了，《霍华德庄园》、《长日将尽》，他最适合演的就是那种正经而又有些压抑的英国绅士。\r\n　　那部电影太干了，幸亏我不是英国男人。\r\n　　没有缘分的也会有感情，所以，并不能说是电影做作。\r\n　　这样的感情最好还是藏在心底。\r\n　　但是做妻子的总是会有所察觉的，每个人都很敏感，无论这个人爱不爱书，读不读文学作品，生活中的许多东西远比书要重要。\r\n　　有的时候，我并不忌讳告诉你我曾经很嫉妒过你，因为弗兰克对你的信如此喜欢，你的信与他的幽默感又如此相同！而且，我也嫉妒过你的写作能力。我与弗兰克在各方面都恰恰相反，他友善、温和，而我的爱尔兰的血性使我总是与人争斗。我很思念他，以前的生活太有意思了。他总在向我解释，也不住地教我些有关书的知识……\r\n　　(一九六九年一月二十九日寄出)\r\n　　与查令十字街84号有关的书另外还有两本。汉芙一九七一年初访英伦时每一天都记有日记，出版成《布鲁姆斯伯里的女公爵》(TheDuchessofBloomsburyStreet)；一九八五年又出版自传《Q的遗产》(Q’sLegacy)，介绍她爱书的起始，与德尔通信以及书、电视剧、舞台剧出品的前后经过。《八十四号》中的那些信件，大都是信手写来，原本并不是为给别人看的，如果不是因德尔不幸早亡，它们可能永远不会面世，它们是率情之作，有些有趣的书话，信函来往间更能看出美国老小姐与英国拘谨绅士间的不同性情风格，读来流畅而有趣。《女公爵》虽是日记，但原本便是为读者而记的，虽说笔法仍流利，但总归有些矫作牵强。汉芙这么多年对英国魂牵梦萦，仿佛一位怀春女子二十年后才得见梦中情人，一时手足无措，不知该如何讨好对方才行，便乱了章法，信函中所有的那种风趣幽默全被吓跑了。而且汉芙对英国传统过于热爱，一叶障目，她所见的只是她自己头脑中的百余年前的英国，实在是遗憾。到了《Q》一书，《八十四号》早已经历了大江大海，若干年后回忆往事，汉芙反而能心如止水，返璞归真，不动声容了。\r\n　　海莲·汉芙，一九一六年四月十五日出生，一九九七年四月九日去世。终身未嫁。\r\n　　一九九八年十二月二十五日于北京方星园\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(1)\r\n---------------\r\n\r\n\r\n　　陈建铭\r\n　　一九四九年，E.B.怀特窝居在纽约市中城的一家小旅馆里。他坐在“窄得让人透不过气、又热又闷”的房间内挥汗写下脍炙人口的《纽约漫谈》(HereIsNewYork，此书曾被《纽约时报》评选为“有史以来关于纽约市最佳的十本书之一”)，里头这么说：“任何人都不该搬到纽约来住，除非他下定决心让自己好运临头。”当然，这位曾写出《夏洛的网》、《精灵鼠小弟》、《天鹅的喇叭》等经典童话故事和无数优美、隽永散文的作家，依旧继续抱持他一贯的知命乐天，徜徉在纽约自由、愉悦的文化天空下。\r\n　　但是与此同时，另一位在这个城市住了将近半辈子的穷作家却没有这份好运。她略乏才气却嗜读好书——货真价实的好书；她嫌这个城市没有气质，害她老是买不到想读的书(在电影版((电影84CharingCrossRoad于一九八七年出品，由英国导演戴维·休·琼斯执导，休·怀特摩编剧；安妮·班克劳夫特饰演海莲·汉芙，安东尼·霍普金斯饰演弗兰克·德尔，茱迪·丹奇饰演诺拉·德尔。片中每位演员的演出均十分传神；编、导的成绩亦相当不恶(得了一九八八年英国电影学院的最佳女主角奖并提名最佳改编剧本奖；一九八九年，美国编剧协会更因此颁奖给海莲·汉芙与怀特摩)。坊间某些录像带租售店或许仍可寻获年代公司的授权版，要特别留意的是：台译片名居然成了《迷阵血影》，而影片对白字幕亦惨不忍睹，简直到了令人坐立难安的地步。我翻译这本书，多少也想为它赎点儿罪罢。\r\n　　除了电影，一九七五年由马克·库龄汉导演、休·怀特摩编剧，此书曾在英国以电视单元剧的形式上演。上述电影即是根据这一个剧本拍成的。时至今日，欧美地区仍有剧团不时公演由此书改编的舞台剧。))的《查令十字街84号》里，饰演海莲·汉芙的女演员一上场就开骂了：“全纽约市没人读英国文学啦？”)，她只好转而向伦敦的一家小旧书店邮购那些“这年头没人要买的英国佬写的英文书”(引电影一开始，被汉芙索书不成的美国某书店经理的话)。于是，一桩原本单纯的买卖关系竟成就了长达二十年、多人参与的越洋友谊。\r\n　　我的眼界不若汉芙那般高且深，但同为住在另一个没有气质的城市里的爱书人，也偶叹好书难寻的挑剔读者，一开始被这本书吸引的，自然是关于“旧书”的部分，但是旋即引我动容的，则是关于书写——隔着距离的书写(当然还有阅读)如何承建伟大的心灵构筑工程。\r\n　　我一直以为：把手写的信件装入信封，填了地址、贴上邮票，旷日费时投递的书信具有无可磨灭的魔力——对寄件人、收信者双方皆然。其中的奥义便在于“距离”——或者该说是“等待”——等待对方的信件寄达；也等待自己的信件送达对方手中。这来往之间因延迟所造成的时间差，大抵只有天然酵母的发菌时间之微妙差可比拟。\r\n　　我始终不愿也不甘臣服于转瞬出现在对方屏幕上的电子邮件；自然更视ICQ(线上实时对谈)为畏途。拜传统邮政犹运作不辍之赐，我至今仍与老友、至亲维持着以手写、投递信函的老把戏，全然是因为我由衷相信：致力消弭空间、时间的距离纯属不智亦无益。就在那些自以为省下来的时、空缝隙里，美好的事物大量流失。我指的不仅仅是亲笔书写时遗下的手泽无法取代；更重要的是：一旦交流变得太有效率，不再需要翘首引颈、两两相望，某些情意也将因而迅速贬值而不被察觉。我喜欢因不能立即传达而必须沉静耐心，句句寻思、字字落笔的过程；亦珍惜读着对方的前一封信、想着几日后对方读信时的景状和情绪。老电影《街角商店》(TheShopAroundtheCorner1940)晚近被改拍成《电子情书》(YouveGotMail1998)((TheShopAroundtheCorner，由殿堂级的大师厄尼斯·刘别谦执导；詹姆斯·斯图尔特与玛格丽特·萨拉文扮演故事中那两位每日错身却不对盘、频生龃龉而又各自暗自仰慕未谋面笔友的百货行员工。多年前我曾在专于夜间播映老电影的TNT频道上观赏过，至今难忘。\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(2)\r\n---------------\r\n\r\n\r\n　　改编后的《电子情书》仍依稀能看出向前作致意的影子，如：女主角匿名通信时的代号为“shopgirl”；而她经营的童书店店名赫然就是“ShopAroundTheCorner”！另外，我也怀疑，她迷恋《傲慢与偏见》一节的神来之笔，则俨然得自《查令十字街84号》。))，两者之间出现不少有趣的辩证关系，读者们不妨自行参酌。\r\n　　我自私地以为这才是《查令十字街84号》全书的题旨所系。\r\n　　从没“好运临头”的汉芙小姐，年届晚年终于有了一个仅有的机会，抱着酝酿二十载的怀想，坐在机舱里(这里也是引自电影)奔赴另一座魂牵梦萦的城市。邻座一名男士问她：“这是你头一回去伦敦？”接着他说：“听我奉劝：别相信出租车司机，明明三条街外的目的地，他会载你兜上五里路；还有，别白费力气读地图了，在伦敦没有人可以找得到路，即使是伦敦人也不例外。”不过，“你一定会爱上她的，伦敦实在太棒了。”\r\n　　当我伫立在伦敦街头，我实在也无法打心底恭维这座城市——街道窄仄、杂芜；气候湿冷、灰暗；市容脏乱、交通拥挤——比起台北不遑多让。但我也终究难免渐渐地——从查令十字街开始——爱上了这座城市(拜汉芙之赐，“查令十字街84号”这个门牌号码几乎快与伦敦市的另一个地址“贝克街221号B座”齐名了)。甚至笃定相信她也能让我事后——隔着距离——对她怀想、惦念个二十年不成问题。\r\n　　将这本书中译，想必可以聊偿许多爱书人多年以来的期盼。我知道：所有读过84CharingCrossRoad的爱书同好——就像我自己一样——总将这本小书珍藏在身边，屡屡重读，让汉芙的珠玑妙语和古道热肠不时温暖自己被冷硬现实尘覆的凡心；而我相信：中文世界之所以长年不见此书问世，一定是所有珍爱此书的人——也像我自己一样——不忍丝毫更动书中的每一句话、每一个字。\r\n　　多年前的某一个下午，在曾经任职的古书店里，我和钟芳玲聊起这本书真该有个中文版。对于这个工作，她自然是当仁不让，而且以她作为此书的头号死忠书迷，加上她与汉芙本人的私交，我也十分赞成她是担任中译者的不二人选，如今我却因苦等不及而掠占了她原先的任务。我相信芳玲也是因为顾及前述的原因，宁可维护汉芙的原貌而迟迟不谋此图。于是，我在翻译的过程中虽然尽可能地保留原书的滋味，但我仍须在此报告：我刻意做了极小的更动。除了让它更能适应中文环境外；我私下盼望这个须臾的“失真”也能转而成为让中文版的读者们动心发愿去读“货真价实的”汉芙原文的伏笔。\r\n　　这是我第一部翻译作品。倘使这个中译版本侥幸能稍稍不使原文蒙羞，则首先必须在此感谢小威和建兴，铭谢他们夫妻俩前仆后继，在追索此书版权归属的过程中上穷碧落下黄泉、锲而不舍；还有推广实体书店文化不遗余力的钟芳玲，他们才是承继汉芙精神的真正后人，也是让这本书能有机会进驻更多人心的幕后功臣。\r\n　　此外，代汉芙对唐诺首肯为中文版赐序((台湾版原唐诺序见附录。——编注))表示感激。我自己常为了读唐诺的序文而看了一堆原先没打算看的书，不过这并不全然是我对这个译本打的如意算盘，而是我晓得查令十字街是他每回在伦敦磨蹭得最久的一条街；何况，若汉芙仍在人间，一定也会找他写的。\r\n\r\n\r\n\r\n***************\r\n*查令十字街84号\r\n***************\r\n\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。						\r\n\r\n---------------\r\n1949年10月5日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年10月5日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。\r\n　　随信附上一份清单，上面列出我目前最想读而又遍寻不着的书。如果贵店有符合该书单所列，而每本又不高于五美元的话，可否径将此函视为订购单，并将书寄给我？\r\n　　你忠实的\r\n　　海莲·汉芙(小姐)\r\n\r\n\r\n\r\n---------------\r\n1949年10月25日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年10月25日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的夫人：\r\n　　谨在此回复您于本月五日的来函。敝店很荣幸能为您解除三分之二的困扰。您所列出的三种哈兹里特散文，均收录于这本典范出版社的《哈兹里特散文选》内；斯蒂文森的作品则在《致少女少男》中可以找到。我们挑出两本品相较好的书为您寄上，相信不久后即可送达您的手中，祈盼您会满意。随书附上发票，请查收。\r\n　　至于您提及的利·亨特的散文，目前颇不易得见，不过我们会留意是否能找到收罗齐全且装帧精良的版本，届时将再为您寄上。而您所描述的拉丁文圣经，目前敝店并无存书，仅有晚近出版、布面精装普通版的拉丁文和希腊文《新约全书》，不知您是否有兴趣？\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月3日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年11月3日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　今天收到你们寄来的书，斯蒂文森的书真是漂亮！把它放进我用水果箱权充的书架里，实在太委屈它。我捧着它，深怕污损它那细致的皮装封面和米黄色的厚实内页。看惯了那些用惨白纸张和硬纸板大量印制的美国书，我简直不晓得一本书竟也能这么迷人，光抚摸着就教人打心里头舒服。\r\n　　住在楼上的女孩儿凯特，她的英国男朋友布莱恩帮我将账单上列的书价一英镑十七先令六便士换算成美金五元三角，希望他没算错。我寄了五元和一元的钞票各一张，多出来的七角请用来支付《新约全书》，那两本我都要买。\r\n　　你们可否行行好？下回先将书价换算成美金。我连加减美金都一塌糊涂了，要我把英镑换算成美金真是阿弥陀佛。\r\n　　海莲·汉芙\r\n　　我希望在你们那边，“夫人”的意思和我们这边指的是两码事。\r\n\r\n\r\n\r\n---------------\r\n1949年11月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您寄来的六元书款已悉数收到，不过我们建议您不妨改以邮政划拨的方式付款，如此不但对你我双方都较为便利；亦比直接将钞票放入信封内要保险得多。\r\n　　我们非常高兴得知您如此喜欢那本斯蒂文森的书。两本《新约全书》已于今日付邮，账单亦一并附上，同时依照您的嘱咐，将书款分别以英镑与美金计价。我们期盼您也会喜欢此次寄去的两本书。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月18日\r\n---------------\r\n\r\n\r\n　　东九十五大街14号\r\n　　1949年11月18日\r\n　　这算哪门子的新约圣经啊！\r\n　　好心替我转告英国圣公会诸公，他们平白糟蹋了有史以来最优美的文字。是哪个家伙出馊主意把通俗拉丁文圣经整成这副德性？他们准会把他来活活烧死，不信记住我说的话。\r\n　　其实我犯不着火冒三丈，我本身是犹太人。不过我的嫂子是天主教徒；弟媳是卫理公会的；还有一票皈依长老教派的表亲(全是被我的亚伯拉罕叔公拉去改宗的)；还有一个到处宣扬基督信仰疗法的姑妈。他们要是知道有这么一本英国人搞出来的不三不四的拉丁文圣经，个个不暴跳如雷才怪！(话说回来，他们搞不好根本不晓得现在还有拉丁文哩。)\r\n　　哼，去他的！我手边还有一本从我的拉丁文老师那儿借来的圣经，暂且先不还他就是了，等你们找一本卖给我再说。\r\n　　寄去四元支付我欠你们的三元八角八分，你就拿多出来的一角二去买杯咖啡喝吧！我住的地方附近没有邮局，我才不要为了汇三元八角八分，大老远跑到洛克菲勒广场去大摆长龙呢。何况，如果要等到我哪天有空顺道去办事，口袋里的三元八角早就被我花得一毛不剩了。我对美国邮政和皇家邮政有十足的信心。\r\n　　你们有兰多的《假想对话录》吗？我想全套应该不止一本，我想读的是“希腊对话录”，如果里面有伊索和萝多彼的对话，就是那一本没错。\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年11月26日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月26日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您的书款已安全寄达，我们会将多出的一角二分计入您在敝店的专属账户中。\r\n　　很凑巧，敝店正好有收录“希腊对话录”的《沃尔特·萨维奇·兰多作品暨传记全集》中的第二卷，“罗马对话录”亦收录其中。本书由于是一八七六年出版的旧版本，并不是非常漂亮，但装订完好，书亦称干净。我们今日会将书与账单一并为您寄上。\r\n　　我在此为那本让您深感不满的拉丁文圣经向您致歉，我们将重新为您找一本正宗《通俗拉丁文圣经》；利·亨特的书仍持续密切留意中。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年12月8日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月8日\r\n　　敬启者：\r\n　　(老用“诸位”着实不智，我察觉到显然从头到尾都是同一个人为我服务。)\r\n　　萨维奇·兰多的书今天寄达，我迫不及待立即翻开“罗马对话录”——两座城市刚毁于兵燹战火，涂炭生灵被钉在十字架上，苦苦哀求列队行过的罗马士兵，干脆一戟刺死他们，好尽早结束这无止尽的折磨……再翻到“希腊对话录”，情境有了一百八十度的转换：读着伊索和萝多彼的娓娓对谈，这里惟一的忧虑只是怕饿着了肚子……我着实喜爱被前人翻读过无数回的旧书。上次《哈兹里特散文选》寄达时，一翻开就看到扉页上写着“我厌恶读新书”，我不禁对这位未曾谋面的前任书主肃然高呼：“同志！”\r\n　　随函附上一元，布莱恩(楼上女孩凯特的英国男朋友)说这够付我欠你的八先令，你忘了换算了。\r\n　　言归正传，布莱恩告诉我：你们每一户每个星期才配给到两盎司肉；而每个人每个月只分得一只鸡蛋！我一听简直吓坏了。他拿出一本目录给我看，这是一家设籍在美国的英国公司，专门代人从丹麦寄送补给物资到英国。所以我会寄给马克斯与科恩书店一份小小的圣诞礼物，希望数量足够让你们大家都能分得一些，因为布莱恩跟我说：查令十字街上的书店全都“小得很”。\r\n　　我会在包裹上注明由你——FPD——代转，天晓得你叫啥。\r\n　　祝佳节愉快\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月9日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月9日\r\n　　FPD！糟了！\r\n　　我刚把包裹寄走，里面主寄的是一条六磅重的火腿，我想你们应该可以自行拿去给肉贩，请他切片后再分给大家。\r\n　　不过我刚刚才发现你们寄来的账单上头印着“B.马克斯、M.科恩”。\r\n　　他们是犹太人吗？我该火速补寄点儿牛舌吗？\r\n　　快通知我该怎么办！\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年12月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您的礼品包裹于今日平安抵达，并已均分给大家。而马克斯先生和科恩先生则坚持只由员工均分即可，不用关照“老板”。再者，我想让您知道，您所寄来的物品，我们不是久未看到，就是只能偶尔在黑市匆匆一瞥。您能这样子顾虑我们，实在是太亲切也太慷慨了，我们都深怀感激。\r\n　　我们要在此表达对您的感谢，并祈祝您未来一年一切顺心。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年3月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年3月25日\r\n　　弗兰克·德尔！你在干吗？我啥也没收到！你该不是在打混吧？\r\n　　利·亨特呢？《牛津英语诗选》呢？《通俗拉丁文圣经》和书呆子约翰·亨利的书呢？我好整以暇，等着这些书来陪我过大斋节，结果你连个影儿也没寄来！\r\n　　你害我只好枯坐在家里，把密密麻麻的注记写在图书馆的书上。哪天要是让他们发现了，包准吊销我的借书证。\r\n　　我已经叫复活节兔子给你捎个“蛋”，希望它到达时不会看到你已经慵懒而死了！\r\n　　春意渐浓，我想读点儿情诗。别给我寄济慈或雪莱！我要那种款款深情而不是口沫横飞的。怀亚特还是琼森或谁的，该寄什么给我，你自己动点儿脑筋！最好是小小一本，可以让我轻松塞进口袋里，带到中央公园去读。\r\n　　行啦！别老坐着，快去把它找出来！真搞不懂你们是怎么做生意的！\r\n\r\n\r\n\r\n---------------\r\n1950年4月7日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　感谢您寄来的复活节礼物，包裹已于昨日平安寄达。看到这些罐头和那一盒生鸡蛋，大家都十分开心，全体同仁与我在此感激您对我们的亲切与慷慨。\r\n　　非常抱歉我们一直没能寄上您想要的书。关于您所提到的情诗集，敝店偶尔会收购到一些，可惜目前店内没有存书，但会竭力为您搜寻。\r\n　　再次感谢您寄来的礼物包裹。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　亲爱的汉芙小姐：\r\n　　请不要让弗兰克知道我写信给您。每回寄账单给您时，我都好想偷偷塞一张短笺到信封里。不过弗兰克一定会认为，以我的职务这么做并不适当。您听到我这么说，大概会以为他是个老古板吧？其实他是一个好得不能再好的人。只是每次您寄到书店的信或包裹都以他为收信人，而且他也将回信给您视为他的分内职责。不过我倒是一直很想自己给您写信。\r\n　　我们都好喜欢读您的来信，大伙儿也常凑在一块儿揣摩您的模样儿。我坚信您一定是一位年轻、有教养且长相聪慧的人；而老马丁先生竟无视您流露出来的绝顶幽默，硬是把您想成一个学究型的人。您愿意寄一张您的照片给我们吗？我们都很想瞧瞧呢！\r\n　　如果您也对弗兰克感到好奇的话，我偷偷告诉您：他年近四十，长得很帅，娶了一位漂亮的爱尔兰姑娘——好像是他的第二任太太。\r\n　　大家对您寄来的包裹都万分感激。我家那两个小家伙(女孩五岁、男孩四岁)简直乐翻了，因为有了您寄来的葡萄干和鸡蛋，我就可以为他们烤个蛋糕了！\r\n　　希望您不介意我私下写信给您，也请不要告诉弗兰克哟\r\n　　诚心祝福您\r\n　　塞西莉·法尔\r\n　　P.S.我会将家里的地址写在信封背后，万一您想到有什么我可以从这儿寄给您的，可以写信告诉我。\r\n\r\n\r\n\r\n---------------\r\n1950年4月10日\r\n---------------\r\n\r\n\r\n　　C.F.\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年4月10日\r\n　　亲爱的塞西莉：\r\n　　真是让老马丁先生大失所望了，请转告他：我非但一丁点儿学问都没有，连大学也没上过哩！我只不过碰巧喜欢看书罢了。说起来还得感谢一位剑桥的学者奎勒—库奇(一般都称他为Q)，是他让我在十七岁那一年一头栽进书堆里，从此不可自拔。至于我的长相，大概就跟百老汇街上的叫化子一样“聪慧”吧！\r\n　　我住在一幢白蚁丛生、摇摇欲坠、白天不供应暖气的老公寓里。整幢五层楼的其他住户早上九点出门，不到晚上六点不会回来，房东认为他犯不着为了一个窝在家里摇笔杆的小作家，而整天开着暖气。\r\n　　可怜的弗兰克，真是难为他了，我老是对他颐指气使的。我只是在打趣，不过就知道他会当真。我一直想要戳穿他那英国式的矜持。要是哪天他得了胃溃疡，都是我害的。\r\n　　请多来信告诉我关于伦敦的一切。我幻想着那一天快点到来——我步下轮船、火车，踩上布着尘灰的人行道……我要走遍柏克莱广场，逛尽温柏街；我要置身在约翰·多恩布道的圣保罗大教堂；我要趺坐在伊丽莎白拒不阶下为囚的伦敦塔前台阶上……我有一位战时派驻在伦敦的记者朋友，他曾经对我说：游客往往带着先入之见，所以他们总能在英国瞧见他们原先想看的。我告诉他，我到英国是为了探寻英国文学。而他这么告诉我：\r\n　　“去那儿准没错。”\r\n　　祝一切安好\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1950年9月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年9月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　自前封信以来，许久未向您报告，盼您不致认为我们因弛废店务而忘却了您交代我们该找的书。\r\n　　言归正传，《牛津英语诗选》新近到库，此书内页以印度纸印制，原版的蓝布精装，出版于一九○五年，扉页有前人签记，算是一本书况不错的二手书。标价两美元，我们认为在径自寄给您之前，应先向您略述此书的状况，以免您在这段时间内已另行购得一册。\r\n　　许久以前，您曾垂询纽曼的《大学论》一书的下落。您是否钟意首版书？最近我们收购到一册，谨描述如下：\r\n　　纽曼(约翰·亨利，神学博士)：《大学教育之目的及其本质——应都柏林天主教会之邀所作的演说之讲义稿》。首版，八开，小牛皮装帧。一八五二年于都柏林出版，若干页面稍有渍斑但装帧完好。\r\n　　价格：美金六元\r\n　　为了避免让别人捷足先登，这两本书我们会先为您保留。静候您的回复。\r\n　　祝身体健康\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年9月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年9月25日\r\n　　(他手上有只卖六美元的首版《大学论》，竟还问我要不要买！真不晓得该说他老实呢，还是憨？)\r\n　　亲爱的弗兰克：\r\n　　是的！我要！我真是快受不了我自己了，本来我并不特别讲究首版不首版的，可是，“那本书”的首版……！\r\n　　哗——我真迫不及待想看到它。\r\n　　也请把《牛津诗选》一并寄来。下回可别再纳闷我有没有跟别人买书了。既然我可以寸步不离书桌，就能向你们买到既干净又漂亮的书，我干吗跑到十七大街去买那些又脏又丑的？从我坐着的地方，伦敦可近得太多啦。\r\n　　附上“千万不能掉了”的八元钞票。我对你提过布莱恩正在打官司没？他向伦敦的一家理工专门书店订了一大套特贵的物理学书。他可不像我，既邋遢又老是漫不经心，他特地到洛克菲勒广场，乖乖地排队等着划拨那一大笔书款，该办的事一件也没漏。他精得很，聪明人自有聪明人按部就班的做法。\r\n　　结果你猜怎么着？那笔钱不知道给汇到哪儿去啦！\r\n　　皇家邮政，加把劲儿！\r\n　　HH\r\n　　为了庆祝我的第一本首版书，加上海外邮购公司终于给了我一本目录，我决定要寄一个小小的包裹送你们。\r\n\r\n\r\n\r\n---------------\r\n1950年10月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年10月2日\r\n　　亲爱的海莲：\r\n　　这些照片我带到店里好几个礼拜了，不过我们这阵子真是忙得昏天暗地，所以一直找不到空当寄给你看。这些都是我和道格(我的先生)在诺福克拍的，那儿是他所属的皇家空军驻地。里头我没有一张拍得漂亮的，不过这是我所能找到最好的了，孩子们和道格那几张倒都还不错。\r\n　　亲爱的海莲，我好盼望你真的能如愿到英国来，你何不省点儿买书钱，好让你能在明年夏天成行呢？我的爸爸妈妈在米德尔塞克斯有个房子，我们会很高兴接你来住的。\r\n　　梅甘·韦尔斯(老板的秘书)和我打算明年七月一起去泽西岛(在海峡群岛之中)度假一个礼拜，你可以来和我们一块儿玩，反正回到米德尔塞克斯不须花用你太多开销。\r\n　　本·马克斯先生在瞄我写些什么了，就此停笔。\r\n　　塞西莉谨上\r\n\r\n\r\n\r\n---------------\r\n1950年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年10月15日\r\n　　真是的！！！\r\n　　不是我爱唠叨，弗兰克·德尔！看到书店竟忍心把这么美的古书五马分尸，拿内页充当包装纸、填箱料，我真是觉得世道中落、万劫不复了。我向被包在里头的约翰·亨利告状：“主教阁下，斯文如此扫地，君岂信乎哉？”\r\n　　他说他也实在百思不得其解。更可恶的是你把书拆散了，随便抓来几页顺手就包，害我根本搞不清楚上头到底是在打哪一仗哪一役。\r\n　　这本书大约一个星期前寄达，现在气也慢慢消了。我把它端端正正地摆在案前，整天陪着我。我不时停下打字，伸手过去，无限爱怜地抚摸它。倒不全然因为这是首版书，主要是打出生起我从没见过这么标致的书。拥有这样的书，竟让我油然而生莫名的罪恶感。它那光可鉴人的皮装封面，古雅的烫金书名，秀丽的印刷铅字，它实在应该置身于英国乡间的一幢木造宅邸；由一位优雅的老绅士坐在炉火前的皮制摇椅里，慢条斯理地轻轻展读……而不该委身在一间寒酸破公寓里，让我坐在蹩脚旧沙发上翻阅。\r\n　　我要买那本Q的文集，可是忘了多少钱，我把你的上一封信搞丢了。好像是两块钱吧？附上两张一元钞票，若是不够就来信告诉我。\r\n　　下回要寄书来时，拿第五一二页和五一三页来包书怎么样？这样我才晓得最后哪一边打赢了，还有那到底是哪一场战役。\r\n　　HH\r\n　　P.S.你们那儿可有《佩皮斯日记》？我需要它来伴我度过漫漫冬夜。\r\n\r\n\r\n\r\n---------------\r\n1950年11月1日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年11月1日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您致歉，迟至今日才回信给您。我因公到外地出差了一个多星期，一回到办公室就被许多待办的事务耽搁，现在才有空提笔给您回信。\r\n　　首先，请您完全无须为我们拿旧书内页当包装纸用而感到忧心，那只是全套《英国叛乱和内战史》之中装订破散且多出来的一册复本，我想应该是无法当成商品卖给任何人的。\r\n　　奎勒—库奇的文集《朝圣之路》，已付邮寄给您。您的待付款项为一元八十五分，所以您新近寄来的两元用来支付该书仍绰绰有余。另，《佩皮斯日记》目前店内暂无存书，将为您留意。\r\n　　祝万事如意\r\n　　马克斯与科恩书店\r\n　　F.德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年2月2日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　很高兴听到您喜欢那本奎勒—库奇的书。《牛津英语散文选》此刻并无库存，我们将会为您留意。\r\n　　至于《罗杰·德·科弗利爵士正传》，我们手头上恰好有一本十八世纪的文集，除了包含该书中不少篇章之外，亦收入切斯特菲尔德与哥尔德斯密斯的文章。此书由奥斯汀·多布森精心编选，敝店仅标价一元十五分。我们已将书寄去给您。如果您想要更为完整的艾迪生与斯梯尔文集的话，请通知我，我将尽力为您寻找。\r\n　　敝店若不包括马克斯先生与科恩先生，共有职员六人。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月20日\r\n---------------\r\n\r\n\r\n　　米德尔塞克斯品内东厢\r\n　　1951年2月20日\r\n　　亲爱的海莲：\r\n　　做法不只一种，妈妈和我一致认为下面这个方法对你而言应该是最简单的：准备面粉一杯、鸡蛋一只、鲜奶半杯，撒入少许盐，在一个海碗里充分搅拌，直到变成浓稠的奶油状。搁进冰箱里摆几个钟头(所以一大早开始做，时间最刚好不过)。当你要把肉放进炉子时，挪个位子摆一个铁盘让它预热。在肉烤好前一个半钟头，浇一点儿肉汁在铁盘上，不用太多，浅浅的够铺满铁盘即可。记住！铁盘得烘烤得“非常热”才行。接着，把你先前准备好的布丁料全拨到铁盘上头，然后再放着继续烤。这样一来，肉和布丁就可以同时出炉上桌啦！\r\n　　对于从没见过的人，我实在不知道该怎么恰当地形容它。总之，一个大功告成的约克郡布丁应该会蓬松得高高的，表皮焦脆，当你切开它时，会发现里头其实是空心的。\r\n　　道格仍然随着皇家空军驻扎在诺福克。你寄来的圣诞节罐头，我们严格地实施管制囤积，以等他休假回家时再全家一起享用。亲爱的海莲，你一定不难想见，届时我们将会有个多么棒的庆祝餐会啊！不过，你实在不该这样子为我们破费的！\r\n　　该赶快把信寄出去了，这样你才来得及在布莱恩的生日晚宴上推出这道菜，成果如何一定要写信告诉我哟！\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年2月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年2月25日\r\n　　亲爱的塞西莉：\r\n　　约克郡布丁简直太棒了！因为我们这儿从没人见识过这玩意儿，我后来只好向别人形容成“一笼高高鼓起、松软细致、入口即化的特大号烤饼”！\r\n　　请别为我寄去的那些食品操心。我自己也觉得不可思议，那家海外邮购公司也不晓得是不是非营利机构，抑或是商品可以免税什么的吧？总之，他们的东西都便宜得很，我自己买的那只火鸡都还比寄给你们的那一大箱圣诞包裹贵哩。他们的确有一些价格比较高的商品，比如大块的烤肋排，或是一整只羊腿之类的。不过，即使是那些东西，也比跟这儿的肉贩买要便宜许多，如果真得那样，把我剁了也没法子寄东西给你们。现在正浏览着目录，我把它摊在地毯上，琢磨着两个旗鼓相当的商品组合：编号105的包裹(内含鸡蛋一打外加甜面饼一箱)和编号217B的包裹(内含鸡蛋两打、没有甜面饼)，我实在不甘心寄一打装的鸡蛋，让你们每人各分得两只能干吗？不过布莱恩跟我说，粉末干燥蛋吃起来味同嚼蜡，还真伤脑筋。\r\n　　有一位制作人刚打电话给我，说他蛮喜欢我写的剧本(还没喜欢到要把它搬上舞台的程度)。他正打算制作一出电视剧集，问我是否有兴趣编电视剧本，他漫不经心地说：“一集给两张！”搞了半天才弄明白他的意思是：每一集的稿费两百元。我原先为剧团修改剧本，一周的酬劳也才不过四十元！明天要去和他详谈，快祝我好运吧！\r\n　　祝福你\r\n　　海莲\r\n\r\n\r\n\r\n---------------\r\n1951年4月4日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月4日\r\n　　海莲亲爱的：\r\n　　你寄来的复活节包裹已经收到，这些礼物实在太棒了！不过，因为弗兰克第二天一早就出差去了，所以他没空回信向你道谢，大家都急得跳脚，而', 'a38f41ef-288e-4081-802f-c7b6689e30e8');
INSERT INTO `p_task` VALUES ('e48d3fd2-8b48-4bce-9970-85eb5c01146c', '测试8[卷9]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '9', '　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　自前封信以来，许久未向您报告，盼您不致认为我们因弛废店务而忘却了您交代我们该找的书。\r\n　　言归正传，《牛津英语诗选》新近到库，此书内页以印度纸印制，原版的蓝布精装，出版于一九○五年，扉页有前人签记，算是一本书况不错的二手书。标价两美元，我们认为在径自寄给您之前，应先向您略述此书的状况，以免您在这段时间内已另行购得一册。\r\n　　许久以前，您曾垂询纽曼的《大学论》一书的下落。您是否钟意首版书？最近我们收购到一册，谨描述如下：\r\n　　纽曼(约翰·亨利，神学博士)：《大学教育之目的及其本质——应都柏林天主教会之邀所作的演说之讲义稿》。首版，八开，小牛皮装帧。一八五二年于都柏林出版，若干页面稍有渍斑但装帧完好。\r\n　　价格：美金六元\r\n　　为了避免让别人捷足先登，这两本书我们会先为您保留。静候您的回复。\r\n　　祝身体健康\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年9月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年9月25日\r\n　　(他手上有只卖六美元的首版《大学论》，竟还问我要不要买！真不晓得该说他老实呢，还是憨？)\r\n　　亲爱的弗兰克：\r\n　　是的！我要！我真是快受不了我自己了，本来我并不特别讲究首版不首版的，可是，“那本书”的首版……！\r\n　　哗——我真迫不及待想看到它。\r\n　　也请把《牛津诗选》一并寄来。下回可别再纳闷我有没有跟别人买书了。既然我可以寸步不离书桌，就能向你们买到既干净又漂亮的书，我干吗跑到十七大街去买那些又脏又丑的？从我坐着的地方，伦敦可近得太多啦。\r\n　　附上“千万不能掉了”的八元钞票。我对你提过布莱恩正在打官司没？他向伦敦的一家理工专门书店订了一大套特贵的物理学书。他可不像我，既邋遢又老是漫不经心，他特地到洛克菲勒广场，乖乖地排队等着划拨那一大笔书款，该办的事一件也没漏。他精得很，聪明人自有聪明人按部就班的做法。\r\n　　结果你猜怎么着？那笔钱不知道给汇到哪儿去啦！\r\n　　皇家邮政，加把劲儿！\r\n　　HH\r\n　　为了庆祝我的第一本首版书，加上海外邮购公司终于给了我一本目录，我决定要寄一个小小的包裹送你们。\r\n\r\n\r\n\r\n---------------\r\n1950年10月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年10月2日\r\n　　亲爱的海莲：\r\n　　这些照片我带到店里好几个礼拜了，不过我们这阵子真是忙得昏天暗地，所以一直找不到空当寄给你看。这些都是我和道格(我的先生)在诺福克拍的，那儿是他所属的皇家空军驻地。里头我没有一张拍得漂亮的，不过这是我所能找到最好的了，孩子们和道格那几张倒都还不错。\r\n　　亲爱的海莲，我好盼望你真的能如愿到英国来，你何不省点儿买书钱，好让你能在明年夏天成行呢？我的爸爸妈妈在米德尔塞克斯有个房子，我们会很高兴接你来住的。\r\n　　梅甘·韦尔斯(老板的秘书)和我打算明年七月一起去泽西岛(在海峡群岛之中)度假一个礼拜，你可以来和我们一块儿玩，反正回到米德尔塞克斯不须花用你太多开销。\r\n　　本·马克斯先生在瞄我写些什么了，就此停笔。\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('f2331c50-c89e-4fa3-8dd5-86291edda593', '测试8[卷6]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '6', '　　伦敦中西二区查令十字街84号\r\n　　1949年11月26日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您的书款已安全寄达，我们会将多出的一角二分计入您在敝店的专属账户中。\r\n　　很凑巧，敝店正好有收录“希腊对话录”的《沃尔特·萨维奇·兰多作品暨传记全集》中的第二卷，“罗马对话录”亦收录其中。本书由于是一八七六年出版的旧版本，并不是非常漂亮，但装订完好，书亦称干净。我们今日会将书与账单一并为您寄上。\r\n　　我在此为那本让您深感不满的拉丁文圣经向您致歉，我们将重新为您找一本正宗《通俗拉丁文圣经》；利·亨特的书仍持续密切留意中。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年12月8日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月8日\r\n　　敬启者：\r\n　　(老用“诸位”着实不智，我察觉到显然从头到尾都是同一个人为我服务。)\r\n　　萨维奇·兰多的书今天寄达，我迫不及待立即翻开“罗马对话录”——两座城市刚毁于兵燹战火，涂炭生灵被钉在十字架上，苦苦哀求列队行过的罗马士兵，干脆一戟刺死他们，好尽早结束这无止尽的折磨……再翻到“希腊对话录”，情境有了一百八十度的转换：读着伊索和萝多彼的娓娓对谈，这里惟一的忧虑只是怕饿着了肚子……我着实喜爱被前人翻读过无数回的旧书。上次《哈兹里特散文选》寄达时，一翻开就看到扉页上写着“我厌恶读新书”，我不禁对这位未曾谋面的前任书主肃然高呼：“同志！”\r\n　　随函附上一元，布莱恩(楼上女孩凯特的英国男朋友)说这够付我欠你的八先令，你忘了换算了。\r\n　　言归正传，布莱恩告诉我：你们每一户每个星期才配给到两盎司肉；而每个人每个月只分得一只鸡蛋！我一听简直吓坏了。他拿出一本目录给我看，这是一家设籍在美国的英国公司，专门代人从丹麦寄送补给物资到英国。所以我会寄给马克斯与科恩书店一份小小的圣诞礼物，希望数量足够让你们大家都能分得一些，因为布莱恩跟我说：查令十字街上的书店全都“小得很”。\r\n　　我会在包裹上注明由你——FPD——代转，天晓得你叫啥。\r\n　　祝佳节愉快\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月9日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月9日\r\n　　FPD！糟了！\r\n　　我刚把包裹寄走，里面主寄的是一条六磅重的火腿，我想你们应该可以自行拿去给肉贩，请他切片后再分给大家。\r\n　　不过我刚刚才发现你们寄来的账单上头印着“B.马克斯、M.科恩”。\r\n　　他们是犹太人吗？我该火速补寄点儿牛舌吗？\r\n　　快通知我该怎么办！\r\n　　海莲·汉芙\r\n\r\n\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');
INSERT INTO `p_task` VALUES ('f65bf5df-ba31-4d45-8c15-868ffdd3f423', '测试3', '地理', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-27 16:35:12', '2017-09-03', '1', '300', '9', '1', '1', '0', '					忠犬大黄\r\n    一天半夜，正在熟睡的我被一阵剧烈的撞门声和狗的狂吠声吵醒，睡眼惺忪的我披衣下床，打开门。一只棕黄色的大狗咬着我的裤管向外拖，这是邻居刘奶奶家的狗大黄。刘奶奶孀居多年，两个儿子一个在国外，一个在上海，接她去他们那儿住，刘奶奶不肯，坚持要独居。随着大黄的引领，我来到刘奶奶家里。躺在床上的刘奶奶脸色紫黑，头歪在一边，奄奄一息。我吓坏了，立即叫来老公，拨打120，救护车很快赶到，但是医生称是脑出血，回天无术。后来我打电话通知刘奶奶的两个儿子，前来料理丧事。大家的心思都在丧事上，没人注意到大黄。刘奶奶的儿子走之前，说是将大黄扔到乡下，由它自生自灭去吧。可是那天晚上下班，我看到刘奶奶紧锁的家门前趴着大黄，我蹲下细瞧，大黄瘦多了。我不知道这么长的路程，大黄是如何认得路又如何跑来的。它该饿了吧？我到宠物店买了专门的狗粮。但是大黄一口没动。我走近大黄，抚摸它有些干枯的毛皮，我发现有泪溢出了它的眼眶。它是在悼念它的故主啊，我的心颤抖了，为一只狗的忠诚。\r\n    过了七八天，我看见那碗里的狗粮没了，心中一喜。大黄用软软的身体摩挲着我的腿，很是依恋，我把它带回家，我这个从来不正眼看狗的人对大黄却有了深深的挂念。大黄很爱干净，大小便都是到卫生间。渐渐地，我发现大黄身体发福了，我以为这是我饲养的功劳，很是沾沾自喜。有一天下班，我看见大黄一瘸一拐地跟着我，嘴里还流着血，血滴了一路，它的腹部有一个钢圈紧紧地卡着，勒得血迹斑斑。我猜测，可能有人把它当成流浪狗，想套去换钱。我心疼地抱着它，试图解开那钢圈。大黄似触电般拼命挣脱，可能是受惊了。老公说，到兽医站打点麻药，再慢慢解开。我觉得言之有理，就去了兽医站。兽医手一摸说，这只狗快要临盆了。我大惊失色，医生打了麻药，用老虎钳仔细扳断钢圈，钢圈深深地嵌进肉里，大黄的腹部血肉模糊，我和医生双手沾满了大黄的鲜血，医生在伤口上涂上药粉。我回家买来骨头给大黄滋补身体。三天后，大黄趴在床肚下，辗转反侧，痛苦呻吟，不一会儿，一只只小狗就下来了，一连生了九只，我发现大黄刚有点愈合的伤口也被产痛挣裂开，渗出缕缕血丝。可怜的大黄啊。\r\n    我看大黄独自哺乳九只小狗，很是吃力，就和老公商量，把九只小狗送人。大黄像听懂我的话似的，时时刻刻警惕地看着我。终于趁大黄吃食时，我瞅准一个空当，将九只小狗装在早就准备好的纸箱内送到二桥一个熟识的理发店的老板，托他送人。没有了小狗的大黄，神色戚然，泪水盈眶，我心中实在不忍，抱着大黄去那家理发店探望它的孩子。大黄与它的孩子搂作一团，喔喔地欢呼，我的眼眶有点湿润。短暂的团聚后，大黄咬着我的裤管示意离开，我千叮咛万嘱咐，要理发店老板务必找几个妥当的人家领养。大黄回家的第二天晚上，我没有看到大黄，一连很多天。一天早上，我去买早点，发现刘奶奶的门口卧着大黄，我见神色不对，一摸鼻息，已经香消玉殒了。\r\n    所谓的进食发福，原来都是为了自己的孩子。', null);
INSERT INTO `p_task` VALUES ('f9355508-164c-45b6-96d8-7e2c82127834', '测试5', '历史', 'dam', '9e6422cc-d562-4684-a561-342273d490c2', '2017-09-27 16:20:24', '2017-09-05', '1', '500', '6', '0', '1', '0', '查令十字街84号\r\n\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n《查令十字街84号》\r\n\r\n\r\n\r\n\r\n\r\n\r\n***************\r\n*序言\r\n***************\r\n\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。						\r\n\r\n---------------\r\n序：书缘·情缘(1)\r\n---------------\r\n\r\n\r\n　　恺蒂\r\n　　如同每一个晴日的上午，阳光将这排歪歪斜斜的二手书店的影子投到街中心上，街上还少行人，穿着对襟毛衣，半秃着顶，行动悠缓的店主们正在将一切生意准备停当，掸一下桌面、橱窗中的灰尘，把书架上那排排参差的布面、皮面书摆正，再将一匣匣便宜的小本平装书移到门外，沿着窗前的墙根摆齐。不用吆喝生意，不用招揽顾客，这群书商们如同他们店中中层书架上的那些小羊皮装帧而成的上个世纪的书籍，虽并不昂贵，但却见过世面，口中叼着一枚烟斗，看着大红色的双层汽车在街上阳光屋影间叮咚过往。\r\n　　她跨下了一辆黑色的计程车，纤巧单薄的女人，游移的目光掠过那一家家摆着书的橱窗，68号，72号，76号，78号，82号，寻寻觅觅，像是丢失了件宝物。最终停了下来，但面前的84号却是空空如也。灰蒙蒙的玻璃窗里面蛛网遍织的书架东倒西歪，地上散落着些废纸，满是尘埃；推门进去，没有想像中的惊喜问候，空空的楼梯通向另一些同样废弃了的房间。孤身女人想张口告诉主人她已到来，她信守了诺言，但空屋中并无人回应，只有一阵冷风袭过，泪水顺着面颊静静地流淌下来。是一段书缘，还是一段情缘，竟让这纽约的独居女人千里迢迢为了伦敦小街这破落关门的书店而如此神伤？手中握着那本薄薄的小书，是为了还查令十字街(CharingCrossRoad)84号的哪一种心愿？\r\n　　一\r\n　　他约她出来聊天，选定的地方是孔乙己酒家，面前摆的是一樽绍兴花雕，自然少不了一碟五香豆，还有几样小菜。谈着各自喜欢的东西作家，纳博科夫、钱钟书、尤瑟纳尔、沈从文。谈着那本他最钟爱的书，《说吧，记忆》，在伦敦买到的初版本，自然便谈到那些古旧的书屋，里面的善本孤本初版本那些只有爱书人才能欣赏的古老气味。记得那条破街吗？我最爱做的是星期六早上睡个懒觉，约几个朋友去唐人街饮早茶，然后就去对面那条破街的老店中翻旧书。为什么我从未在那里遇见过你呢？回忆起从未共同经历过的伦敦往事，却怎么也想不起来查令十字街84号现在是做着什么样的买卖。知道那位纽约的老姑娘和那位一丝不苟的旧书商，他们通了二十多年的信，最终却仍未能谋面，是没有缘分？\r\n　　一九四九年十月，一切开始于一封很简单的从纽约到伦敦的商业性的信函：\r\n　　先生：\r\n　　你们在《星期六文学评论》的广告上说你们长于经营绝版的书籍，你们所用的“珍本书商”一词让我有些害怕，因为我总是把“珍本”与昂贵相联的。我是位穷作家，但对书却有一些“珍本”般的嗜好，我所要的书在这里都很难买到……寄上我最急需的书的名单，如果你们有干干净净不超过五美元一本的二手货，请将此函视作订购单，给我悉数寄来。(恺蒂女士的译文与本书正文译文行文不同，为尊重作者，特不作统一。下同。)\r\n　　(一九四九年十月五日)\r\n　　署名海莲·汉芙(HeleneHanff)，还特地注明了“小姐”。\r\n　　其实，这位小姐此年已三十有三，是一位以写电视、舞台剧本为生的自由撰稿人。汉芙出生于制衣人家庭，父亲原本是位民谣说唱艺人，虽为生活所迫做起了手艺活，但夫妻俩仍喜欢带着女儿去逛戏院。汉芙十九岁时进费城大学读英文，但家境贫困，一年后辍学，求职谋生，后来得一戏剧写作奖项，便以写作糊口。对书的热爱来自于在纽约市立图书馆中的刻苦自学，特别得益于英国剑桥大学一位阿Q教授(SirArthurQuillerCouch)的著作。然而美国书价昂贵，汉芙热爱英国文学，便将买书的对象转向英伦三岛，偶然选中一家小书店写了信去，第一次订货便得到价廉物美的圆满服务，海峡这边，查令十字街84号Marks&Co.书店的主管，弗兰克·德尔先生，则是汉芙二十年通信的对象。\r\n　　虽然三十有余，汉芙却仍是轻松活泼，特别是简牍之上，更善于以轻松调侃的笔墨，信手写来，天马行空，不拘格式。德尔先生给她回的第一封信中称之为“女士”，汉芙第二封信尾便加了注脚，“我希望‘女士’在你们那边的含义与这边不一样”。德尔先生下封信中便乖乖称之为“小姐”了。第五封信后，汉芙已将信首的尊称“先生”或“阁下”改为直呼其名，信的内容也像是写给一位相识已久的老友，且不乏亲昵、撒娇之态：\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(2)\r\n---------------\r\n\r\n\r\n　　弗兰克·德尔，你在那儿究竟干什么？你什么都没干，你只是闲坐着！\r\n　　我的利·亨特在哪里？我的《牛津诗集》在哪里？……\r\n　　你把我冷落在这里，坐在图书馆中，在那些不属于我的书上写着长长的眉批，总有一天他们会发现，会把我的图书卡收走！\r\n　　我已经安排了复活节的小兔子给你们带去礼物，等它到达时，你可能已慵懒而死了。\r\n　　春天到来之际，我要一本情诗集，不要济慈或雪莱，请寄给我一本不太煽情的情诗集，你自己挑选吧，要一本小开本的，可以放入裤兜中带到中央公园去。\r\n　　行了，不要只坐在那里，快去帮我找书吧，真不明白你们书店是怎么维持的。\r\n　　(一九五○年三月二十五日)\r\n　　汉芙性情率真，人更是善良，通信之初，她便得知战后的英国经济困难，肉类、鸡蛋等食品都是限量供应，女人的长统丝袜更是奢侈品。一九四九年圣诞节她将一块重六磅的火腿寄往伦敦，让德尔先生分给书店中的同事们，以后美式食品源源不断几年。汉芙本身手头并不宽裕，她的慷慨大度让书店的工作人员把她视作亲人，纷纷与她通信，聊天。只是德尔先生从未在信中对汉芙的轻松笑语做任何回报，他是正人君子，地道的好丈夫，典型的英国绅士，惟一的报答是兢兢业业地为汉芙寻觅好书。直到一九五二年，德尔太太登场写信给汉芙道谢这几年的礼物，并向汉芙介绍说德尔先生已有二女，德尔先生才在汉芙的强烈要求之下，在答谢汉芙所寄给他妻女的长统袜时，将例来一贯的信头的“汉芙小姐”的称呼，改为“亲爱的海莲”，写信的日期恰与情人节巧合，不过想必当时德尔先生压根没有注意到。\r\n　　亲爱的海莲：\r\n　　我同意，现在写信给你，是该把“小姐”放弃的时候了。我并不如你想像的那样古板，只是因为我所写给你的信，都得在办公室的卷宗中存档，所以我觉得正式的称呼更合适，但这封信与书没有关系，是不会被存档的。\r\n　　……\r\n　　真不知该如何报答你这么多好礼物，我能说的只是，如果有一天你来伦敦，橡原巷37号会有一张床给你，你爱待多久便待多久。\r\n　　(一九五二年二月十四日)\r\n　　去家里作客的邀请一直没有兑现，汉芙几次犹豫要去英格兰圆梦，但终因手头拮据而放弃。倒是德尔先生紧接着寄去的那本沃尔顿的《五人传》着实让汉芙惊喜不已：\r\n　　噢，天哪，老天感谢你沃尔顿的《五人传》，这本书出版于一八四○年，百年之后还能这样完美，真是奇迹！如此漂亮，久经摩挲的粗裁本！我真同情他，这位曾于一八四一年在书的扉页上签名的戈登先生。他那一群不肖子孙呀！几乎不值分文地便把它卖给了你！真希望在他们出卖图书馆之前，我曾去那边赤脚跑过！\r\n　　(一九五二年三月三日)\r\n　　二十年间，汉芙总共在查令十字街84号购书近五十种，这个数目并不大，算不得是位好顾客，但保持着与书店的德尔先生及其他人的通信来往，却成了她生活中必不可少的一部分。特别是她在五十年代初英国困难时期慷慨出手的豪情，为她带来了英伦的真挚的友谊，也是这扎书信的人情味所在。六十年代末，汉芙颇为潦倒，写出去的剧本屡遭拒绝，书的选题也无人感兴趣。一九六九年一月，纽约冬天很冷，汉芙从图书馆回到家中，已近六点。她手上捧着一摞书，把从门房中取来的信件放在书上，走向电梯。在电梯中，她发现在那一大堆账单之间，有一封薄薄的蓝色的从Marks&Co.寄来的航空信封。这信有些异样，因为德尔先生所寄的信，信封上的地址都是单行距打成，而且向来是把她连名带姓拼全的，而这封信上，地址是双行距，她的名字是由一个字母H代替的。她只道是德尔离开了书店，没太在意，夜深人静捧杯独坐时，她才打开此信。这一夜，她再没有睡着。因为信中的消息，是德尔的死讯。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(3)\r\n---------------\r\n\r\n\r\n　　二\r\n　　桌上的黄酒已过三巡。言谈嬉笑，话语投机，共同喜爱的书与作家们一时让他们觉得很亲近。然而，手中抚弄着那樽精致的铜酒壶，眼光却不敢对视，他早已与另一位美丽的女子谈婚论嫁，书缘与情缘，在现实生活中原本是风马牛不相干的事。\r\n　　然而，浪漫向来是作家们难以割舍的情怀。书中自有颜如玉，红袖添香夜读书，虽然这都是男人们近乎梦想的宣言，但是，自古以来的好书，大多都是激情之作，没有情的文章和书，是太过干涩枯燥，没有人愿读的。于是，温润的花雕虽只逗出跳跃键盘上往返数次的几行短语，将消除键轻轻按下便了无痕迹，查令十字街84号却被好心的好事者演绎成一曲情感故事。\r\n　　泪尽之后，汉芙觉得体内像被掏空了一样，一片冰凉。应该做些什么，但是又能做什么呢？想起这二十年来的通信，几次搬家，这丝带束成的一小扎竟还静静地躺在抽屉的底部。仿佛是为了了却一桩心愿，汉芙将它们结成一集，送到出版商的手中，也许是她时来运转，也许是德尔在天亡灵的保佑，此书一经出版，便受欢迎，英国出版商亦决定在英国推出此书，并邀请汉芙前往英国，下榻于大英博物馆旁布鲁姆斯伯里区的一家古旧的老饭店。\r\n　　英国是汉芙魂牵梦系的地方，从一九五○年开始她便屡次想去，但都因无川资而未成。《查令十字街84号》的最后一封信，是她于一九六九年四月写给一位前往伦敦度假的朋友的，读来让许多英国人觉得鼻子酸酸的：\r\n　　亲爱的凯瑟琳：\r\n　　我在家中打扫卫生，整理书架。偷闲坐在地上，四面地毯上散放的都是书。希望你与布莱恩在伦敦玩得愉快。他在电话上对我说：“如果你有路费的话，是否想和我们同去？”我几乎哭了。\r\n　　我不知道，可能对我来说去或不去那儿已是无所谓了。我梦到那儿的次数太多了。我常常是为了看那些宽街窄巷才去看那些英国电影。记得许久以前，有个人对我说，那些去过英国的人，都能在那儿找到他最想要的东西。我告诉他我想去英国，是为了找英国文学。他说：“它们就在那儿。”\r\n　　或许在那儿，或许不在。看着四周地毯上散乱的书籍，我知道，它们肯定在这儿。\r\n　　那位卖给我这所有书的好人几个月前去世了，书店的主人也死了，但是书店还在那里。如果你正巧经过查令十字街84号，能否为我吻它？我欠它的实在太多了。\r\n　　(一九六九年四月十一日)\r\n　　其实此时，查令十字街84号已准备关门大吉，书店主人的后代无心经营旧书，一年后汉芙的书的畅销也未让书店起死回生。此时的伦敦，经过六十年代文化革命和摇滚乐的洗礼，已与五十年代完全不同。七十年代初，英国是激进先锋、朋克即将形成势力的年代，关心旧书旧文化的人实在太少了。汉芙一九七一年前往英国，一心一意醉心于寻找的是维多利亚时代的情怀，触目所见，根本不是心中的英国，这位战战兢兢小心翼翼的老姑娘此时肯定已极难让一般人亲近，无奈大英帝国也确实有一批为数不多的怀旧之人，他们虽已过时，但却仍有生息，大洋彼岸这位老姑娘对英国潦倒二手书店的无限热爱，对英国旧文化旧文学旧传统的一片痴情，对这些怀旧的人来说，是一帖温润滋补的药，把他们熏得晕晕乎乎，舒舒服服，而且这是一副中国式的汤药，头剂、二剂、三剂，喝了十几年还不舍得把药渣子扔掉，这是这本书话式的信集能在英国成为畅销书的原因了。\r\n　　一九七五年，汉芙家中所有的鞋盒子都被腾出来装了英国各地的书迷们寄来的信件，BBC决定把《查令十字街84号》搬上荧屏；六年之后，素有盛名的英国戏剧界决定把它改编为舞台剧，在伦敦最好的剧院上演三月不衰；再过六年，此书又被改编成电影，由著名演员安妮·班克劳夫特及安东尼·霍普金斯领衔主演，电影介绍中称，“这部片子旨在反映两种爱情，一是汉芙对书的激情之爱，二是她对德尔的精神之爱”，终于在书缘与情缘之间系了根红线。\r\n\r\n\r\n\r\n---------------\r\n序：书缘·情缘(4)\r\n---------------\r\n\r\n\r\n　　三\r\n　　现实生活中没有的缘分只能靠文学作品去演绎，然而他们最终未曾见面，电影中也没能让他们见面，没有缘分就是没有缘分。\r\n　　霍普金斯演德尔是最贴切不过的了，《霍华德庄园》、《长日将尽》，他最适合演的就是那种正经而又有些压抑的英国绅士。\r\n　　那部电影太干了，幸亏我不是英国男人。\r\n　　没有缘分的也会有感情，所以，并不能说是电影做作。\r\n　　这样的感情最好还是藏在心底。\r\n　　但是做妻子的总是会有所察觉的，每个人都很敏感，无论这个人爱不爱书，读不读文学作品，生活中的许多东西远比书要重要。\r\n　　有的时候，我并不忌讳告诉你我曾经很嫉妒过你，因为弗兰克对你的信如此喜欢，你的信与他的幽默感又如此相同！而且，我也嫉妒过你的写作能力。我与弗兰克在各方面都恰恰相反，他友善、温和，而我的爱尔兰的血性使我总是与人争斗。我很思念他，以前的生活太有意思了。他总在向我解释，也不住地教我些有关书的知识……\r\n　　(一九六九年一月二十九日寄出)\r\n　　与查令十字街84号有关的书另外还有两本。汉芙一九七一年初访英伦时每一天都记有日记，出版成《布鲁姆斯伯里的女公爵》(TheDuchessofBloomsburyStreet)；一九八五年又出版自传《Q的遗产》(Q’sLegacy)，介绍她爱书的起始，与德尔通信以及书、电视剧、舞台剧出品的前后经过。《八十四号》中的那些信件，大都是信手写来，原本并不是为给别人看的，如果不是因德尔不幸早亡，它们可能永远不会面世，它们是率情之作，有些有趣的书话，信函来往间更能看出美国老小姐与英国拘谨绅士间的不同性情风格，读来流畅而有趣。《女公爵》虽是日记，但原本便是为读者而记的，虽说笔法仍流利，但总归有些矫作牵强。汉芙这么多年对英国魂牵梦萦，仿佛一位怀春女子二十年后才得见梦中情人，一时手足无措，不知该如何讨好对方才行，便乱了章法，信函中所有的那种风趣幽默全被吓跑了。而且汉芙对英国传统过于热爱，一叶障目，她所见的只是她自己头脑中的百余年前的英国，实在是遗憾。到了《Q》一书，《八十四号》早已经历了大江大海，若干年后回忆往事，汉芙反而能心如止水，返璞归真，不动声容了。\r\n　　海莲·汉芙，一九一六年四月十五日出生，一九九七年四月九日去世。终身未嫁。\r\n　　一九九八年十二月二十五日于北京方星园\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(1)\r\n---------------\r\n\r\n\r\n　　陈建铭\r\n　　一九四九年，E.B.怀特窝居在纽约市中城的一家小旅馆里。他坐在“窄得让人透不过气、又热又闷”的房间内挥汗写下脍炙人口的《纽约漫谈》(HereIsNewYork，此书曾被《纽约时报》评选为“有史以来关于纽约市最佳的十本书之一”)，里头这么说：“任何人都不该搬到纽约来住，除非他下定决心让自己好运临头。”当然，这位曾写出《夏洛的网》、《精灵鼠小弟》、《天鹅的喇叭》等经典童话故事和无数优美、隽永散文的作家，依旧继续抱持他一贯的知命乐天，徜徉在纽约自由、愉悦的文化天空下。\r\n　　但是与此同时，另一位在这个城市住了将近半辈子的穷作家却没有这份好运。她略乏才气却嗜读好书——货真价实的好书；她嫌这个城市没有气质，害她老是买不到想读的书(在电影版((电影84CharingCrossRoad于一九八七年出品，由英国导演戴维·休·琼斯执导，休·怀特摩编剧；安妮·班克劳夫特饰演海莲·汉芙，安东尼·霍普金斯饰演弗兰克·德尔，茱迪·丹奇饰演诺拉·德尔。片中每位演员的演出均十分传神；编、导的成绩亦相当不恶(得了一九八八年英国电影学院的最佳女主角奖并提名最佳改编剧本奖；一九八九年，美国编剧协会更因此颁奖给海莲·汉芙与怀特摩)。坊间某些录像带租售店或许仍可寻获年代公司的授权版，要特别留意的是：台译片名居然成了《迷阵血影》，而影片对白字幕亦惨不忍睹，简直到了令人坐立难安的地步。我翻译这本书，多少也想为它赎点儿罪罢。\r\n　　除了电影，一九七五年由马克·库龄汉导演、休·怀特摩编剧，此书曾在英国以电视单元剧的形式上演。上述电影即是根据这一个剧本拍成的。时至今日，欧美地区仍有剧团不时公演由此书改编的舞台剧。))的《查令十字街84号》里，饰演海莲·汉芙的女演员一上场就开骂了：“全纽约市没人读英国文学啦？”)，她只好转而向伦敦的一家小旧书店邮购那些“这年头没人要买的英国佬写的英文书”(引电影一开始，被汉芙索书不成的美国某书店经理的话)。于是，一桩原本单纯的买卖关系竟成就了长达二十年、多人参与的越洋友谊。\r\n　　我的眼界不若汉芙那般高且深，但同为住在另一个没有气质的城市里的爱书人，也偶叹好书难寻的挑剔读者，一开始被这本书吸引的，自然是关于“旧书”的部分，但是旋即引我动容的，则是关于书写——隔着距离的书写(当然还有阅读)如何承建伟大的心灵构筑工程。\r\n　　我一直以为：把手写的信件装入信封，填了地址、贴上邮票，旷日费时投递的书信具有无可磨灭的魔力——对寄件人、收信者双方皆然。其中的奥义便在于“距离”——或者该说是“等待”——等待对方的信件寄达；也等待自己的信件送达对方手中。这来往之间因延迟所造成的时间差，大抵只有天然酵母的发菌时间之微妙差可比拟。\r\n　　我始终不愿也不甘臣服于转瞬出现在对方屏幕上的电子邮件；自然更视ICQ(线上实时对谈)为畏途。拜传统邮政犹运作不辍之赐，我至今仍与老友、至亲维持着以手写、投递信函的老把戏，全然是因为我由衷相信：致力消弭空间、时间的距离纯属不智亦无益。就在那些自以为省下来的时、空缝隙里，美好的事物大量流失。我指的不仅仅是亲笔书写时遗下的手泽无法取代；更重要的是：一旦交流变得太有效率，不再需要翘首引颈、两两相望，某些情意也将因而迅速贬值而不被察觉。我喜欢因不能立即传达而必须沉静耐心，句句寻思、字字落笔的过程；亦珍惜读着对方的前一封信、想着几日后对方读信时的景状和情绪。老电影《街角商店》(TheShopAroundtheCorner1940)晚近被改拍成《电子情书》(YouveGotMail1998)((TheShopAroundtheCorner，由殿堂级的大师厄尼斯·刘别谦执导；詹姆斯·斯图尔特与玛格丽特·萨拉文扮演故事中那两位每日错身却不对盘、频生龃龉而又各自暗自仰慕未谋面笔友的百货行员工。多年前我曾在专于夜间播映老电影的TNT频道上观赏过，至今难忘。\r\n\r\n\r\n\r\n---------------\r\n译序：关乎书写，更关乎距离(2)\r\n---------------\r\n\r\n\r\n　　改编后的《电子情书》仍依稀能看出向前作致意的影子，如：女主角匿名通信时的代号为“shopgirl”；而她经营的童书店店名赫然就是“ShopAroundTheCorner”！另外，我也怀疑，她迷恋《傲慢与偏见》一节的神来之笔，则俨然得自《查令十字街84号》。))，两者之间出现不少有趣的辩证关系，读者们不妨自行参酌。\r\n　　我自私地以为这才是《查令十字街84号》全书的题旨所系。\r\n　　从没“好运临头”的汉芙小姐，年届晚年终于有了一个仅有的机会，抱着酝酿二十载的怀想，坐在机舱里(这里也是引自电影)奔赴另一座魂牵梦萦的城市。邻座一名男士问她：“这是你头一回去伦敦？”接着他说：“听我奉劝：别相信出租车司机，明明三条街外的目的地，他会载你兜上五里路；还有，别白费力气读地图了，在伦敦没有人可以找得到路，即使是伦敦人也不例外。”不过，“你一定会爱上她的，伦敦实在太棒了。”\r\n　　当我伫立在伦敦街头，我实在也无法打心底恭维这座城市——街道窄仄、杂芜；气候湿冷、灰暗；市容脏乱、交通拥挤——比起台北不遑多让。但我也终究难免渐渐地——从查令十字街开始——爱上了这座城市(拜汉芙之赐，“查令十字街84号”这个门牌号码几乎快与伦敦市的另一个地址“贝克街221号B座”齐名了)。甚至笃定相信她也能让我事后——隔着距离——对她怀想、惦念个二十年不成问题。\r\n　　将这本书中译，想必可以聊偿许多爱书人多年以来的期盼。我知道：所有读过84CharingCrossRoad的爱书同好——就像我自己一样——总将这本小书珍藏在身边，屡屡重读，让汉芙的珠玑妙语和古道热肠不时温暖自己被冷硬现实尘覆的凡心；而我相信：中文世界之所以长年不见此书问世，一定是所有珍爱此书的人——也像我自己一样——不忍丝毫更动书中的每一句话、每一个字。\r\n　　多年前的某一个下午，在曾经任职的古书店里，我和钟芳玲聊起这本书真该有个中文版。对于这个工作，她自然是当仁不让，而且以她作为此书的头号死忠书迷，加上她与汉芙本人的私交，我也十分赞成她是担任中译者的不二人选，如今我却因苦等不及而掠占了她原先的任务。我相信芳玲也是因为顾及前述的原因，宁可维护汉芙的原貌而迟迟不谋此图。于是，我在翻译的过程中虽然尽可能地保留原书的滋味，但我仍须在此报告：我刻意做了极小的更动。除了让它更能适应中文环境外；我私下盼望这个须臾的“失真”也能转而成为让中文版的读者们动心发愿去读“货真价实的”汉芙原文的伏笔。\r\n　　这是我第一部翻译作品。倘使这个中译版本侥幸能稍稍不使原文蒙羞，则首先必须在此感谢小威和建兴，铭谢他们夫妻俩前仆后继，在追索此书版权归属的过程中上穷碧落下黄泉、锲而不舍；还有推广实体书店文化不遗余力的钟芳玲，他们才是承继汉芙精神的真正后人，也是让这本书能有机会进驻更多人心的幕后功臣。\r\n　　此外，代汉芙对唐诺首肯为中文版赐序((台湾版原唐诺序见附录。——编注))表示感激。我自己常为了读唐诺的序文而看了一堆原先没打算看的书，不过这并不全然是我对这个译本打的如意算盘，而是我晓得查令十字街是他每回在伦敦磨蹭得最久的一条街；何况，若汉芙仍在人间，一定也会找他写的。\r\n\r\n\r\n\r\n***************\r\n*查令十字街84号\r\n***************\r\n\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。						\r\n\r\n---------------\r\n1949年10月5日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年10月5日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　我在《星期六文学评论》上看到你们刊登的广告，上头说你们“专营绝版书”。另一个字眼“古书商”总是令我望之却步，因为我总认为：既然“古”，一定也很“贵”吧。而我只不过是一名对书本有着“古老”胃口的穷作家罢了。在我住的地方，总买不到我想读的书，要不是索价奇昂的珍本，就是巴诺书店里头那些被小鬼涂得乱七八糟的邋遢书。\r\n　　随信附上一份清单，上面列出我目前最想读而又遍寻不着的书。如果贵店有符合该书单所列，而每本又不高于五美元的话，可否径将此函视为订购单，并将书寄给我？\r\n　　你忠实的\r\n　　海莲·汉芙(小姐)\r\n\r\n\r\n\r\n---------------\r\n1949年10月25日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年10月25日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的夫人：\r\n　　谨在此回复您于本月五日的来函。敝店很荣幸能为您解除三分之二的困扰。您所列出的三种哈兹里特散文，均收录于这本典范出版社的《哈兹里特散文选》内；斯蒂文森的作品则在《致少女少男》中可以找到。我们挑出两本品相较好的书为您寄上，相信不久后即可送达您的手中，祈盼您会满意。随书附上发票，请查收。\r\n　　至于您提及的利·亨特的散文，目前颇不易得见，不过我们会留意是否能找到收罗齐全且装帧精良的版本，届时将再为您寄上。而您所描述的拉丁文圣经，目前敝店并无存书，仅有晚近出版、布面精装普通版的拉丁文和希腊文《新约全书》，不知您是否有兴趣？\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月3日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年11月3日\r\n　　马克斯与科恩书店\r\n　　英国\r\n　　伦敦中西二区\r\n　　查令十字街84号\r\n　　诸位先生：\r\n　　今天收到你们寄来的书，斯蒂文森的书真是漂亮！把它放进我用水果箱权充的书架里，实在太委屈它。我捧着它，深怕污损它那细致的皮装封面和米黄色的厚实内页。看惯了那些用惨白纸张和硬纸板大量印制的美国书，我简直不晓得一本书竟也能这么迷人，光抚摸着就教人打心里头舒服。\r\n　　住在楼上的女孩儿凯特，她的英国男朋友布莱恩帮我将账单上列的书价一英镑十七先令六便士换算成美金五元三角，希望他没算错。我寄了五元和一元的钞票各一张，多出来的七角请用来支付《新约全书》，那两本我都要买。\r\n　　你们可否行行好？下回先将书价换算成美金。我连加减美金都一塌糊涂了，要我把英镑换算成美金真是阿弥陀佛。\r\n　　海莲·汉芙\r\n　　我希望在你们那边，“夫人”的意思和我们这边指的是两码事。\r\n\r\n\r\n\r\n---------------\r\n1949年11月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您寄来的六元书款已悉数收到，不过我们建议您不妨改以邮政划拨的方式付款，如此不但对你我双方都较为便利；亦比直接将钞票放入信封内要保险得多。\r\n　　我们非常高兴得知您如此喜欢那本斯蒂文森的书。两本《新约全书》已于今日付邮，账单亦一并附上，同时依照您的嘱咐，将书款分别以英镑与美金计价。我们期盼您也会喜欢此次寄去的两本书。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年11月18日\r\n---------------\r\n\r\n\r\n　　东九十五大街14号\r\n　　1949年11月18日\r\n　　这算哪门子的新约圣经啊！\r\n　　好心替我转告英国圣公会诸公，他们平白糟蹋了有史以来最优美的文字。是哪个家伙出馊主意把通俗拉丁文圣经整成这副德性？他们准会把他来活活烧死，不信记住我说的话。\r\n　　其实我犯不着火冒三丈，我本身是犹太人。不过我的嫂子是天主教徒；弟媳是卫理公会的；还有一票皈依长老教派的表亲(全是被我的亚伯拉罕叔公拉去改宗的)；还有一个到处宣扬基督信仰疗法的姑妈。他们要是知道有这么一本英国人搞出来的不三不四的拉丁文圣经，个个不暴跳如雷才怪！(话说回来，他们搞不好根本不晓得现在还有拉丁文哩。)\r\n　　哼，去他的！我手边还有一本从我的拉丁文老师那儿借来的圣经，暂且先不还他就是了，等你们找一本卖给我再说。\r\n　　寄去四元支付我欠你们的三元八角八分，你就拿多出来的一角二去买杯咖啡喝吧！我住的地方附近没有邮局，我才不要为了汇三元八角八分，大老远跑到洛克菲勒广场去大摆长龙呢。何况，如果要等到我哪天有空顺道去办事，口袋里的三元八角早就被我花得一毛不剩了。我对美国邮政和皇家邮政有十足的信心。\r\n　　你们有兰多的《假想对话录》吗？我想全套应该不止一本，我想读的是“希腊对话录”，如果里面有伊索和萝多彼的对话，就是那一本没错。\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年11月26日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年11月26日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　敬爱的汉芙小姐：\r\n　　您的书款已安全寄达，我们会将多出的一角二分计入您在敝店的专属账户中。\r\n　　很凑巧，敝店正好有收录“希腊对话录”的《沃尔特·萨维奇·兰多作品暨传记全集》中的第二卷，“罗马对话录”亦收录其中。本书由于是一八七六年出版的旧版本，并不是非常漂亮，但装订完好，书亦称干净。我们今日会将书与账单一并为您寄上。\r\n　　我在此为那本让您深感不满的拉丁文圣经向您致歉，我们将重新为您找一本正宗《通俗拉丁文圣经》；利·亨特的书仍持续密切留意中。\r\n　　马克斯与科恩书店\r\n　　FPD敬上\r\n\r\n\r\n\r\n---------------\r\n1949年12月8日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月8日\r\n　　敬启者：\r\n　　(老用“诸位”着实不智，我察觉到显然从头到尾都是同一个人为我服务。)\r\n　　萨维奇·兰多的书今天寄达，我迫不及待立即翻开“罗马对话录”——两座城市刚毁于兵燹战火，涂炭生灵被钉在十字架上，苦苦哀求列队行过的罗马士兵，干脆一戟刺死他们，好尽早结束这无止尽的折磨……再翻到“希腊对话录”，情境有了一百八十度的转换：读着伊索和萝多彼的娓娓对谈，这里惟一的忧虑只是怕饿着了肚子……我着实喜爱被前人翻读过无数回的旧书。上次《哈兹里特散文选》寄达时，一翻开就看到扉页上写着“我厌恶读新书”，我不禁对这位未曾谋面的前任书主肃然高呼：“同志！”\r\n　　随函附上一元，布莱恩(楼上女孩凯特的英国男朋友)说这够付我欠你的八先令，你忘了换算了。\r\n　　言归正传，布莱恩告诉我：你们每一户每个星期才配给到两盎司肉；而每个人每个月只分得一只鸡蛋！我一听简直吓坏了。他拿出一本目录给我看，这是一家设籍在美国的英国公司，专门代人从丹麦寄送补给物资到英国。所以我会寄给马克斯与科恩书店一份小小的圣诞礼物，希望数量足够让你们大家都能分得一些，因为布莱恩跟我说：查令十字街上的书店全都“小得很”。\r\n　　我会在包裹上注明由你——FPD——代转，天晓得你叫啥。\r\n　　祝佳节愉快\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月9日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1949年12月9日\r\n　　FPD！糟了！\r\n　　我刚把包裹寄走，里面主寄的是一条六磅重的火腿，我想你们应该可以自行拿去给肉贩，请他切片后再分给大家。\r\n　　不过我刚刚才发现你们寄来的账单上头印着“B.马克斯、M.科恩”。\r\n　　他们是犹太人吗？我该火速补寄点儿牛舌吗？\r\n　　快通知我该怎么办！\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1949年12月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1949年12月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您的礼品包裹于今日平安抵达，并已均分给大家。而马克斯先生和科恩先生则坚持只由员工均分即可，不用关照“老板”。再者，我想让您知道，您所寄来的物品，我们不是久未看到，就是只能偶尔在黑市匆匆一瞥。您能这样子顾虑我们，实在是太亲切也太慷慨了，我们都深怀感激。\r\n　　我们要在此表达对您的感谢，并祈祝您未来一年一切顺心。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年3月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年3月25日\r\n　　弗兰克·德尔！你在干吗？我啥也没收到！你该不是在打混吧？\r\n　　利·亨特呢？《牛津英语诗选》呢？《通俗拉丁文圣经》和书呆子约翰·亨利的书呢？我好整以暇，等着这些书来陪我过大斋节，结果你连个影儿也没寄来！\r\n　　你害我只好枯坐在家里，把密密麻麻的注记写在图书馆的书上。哪天要是让他们发现了，包准吊销我的借书证。\r\n　　我已经叫复活节兔子给你捎个“蛋”，希望它到达时不会看到你已经慵懒而死了！\r\n　　春意渐浓，我想读点儿情诗。别给我寄济慈或雪莱！我要那种款款深情而不是口沫横飞的。怀亚特还是琼森或谁的，该寄什么给我，你自己动点儿脑筋！最好是小小一本，可以让我轻松塞进口袋里，带到中央公园去读。\r\n　　行啦！别老坐着，快去把它找出来！真搞不懂你们是怎么做生意的！\r\n\r\n\r\n\r\n---------------\r\n1950年4月7日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　感谢您寄来的复活节礼物，包裹已于昨日平安寄达。看到这些罐头和那一盒生鸡蛋，大家都十分开心，全体同仁与我在此感激您对我们的亲切与慷慨。\r\n　　非常抱歉我们一直没能寄上您想要的书。关于您所提到的情诗集，敝店偶尔会收购到一些，可惜目前店内没有存书，但会竭力为您搜寻。\r\n　　再次感谢您寄来的礼物包裹。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年4月7日\r\n　　亲爱的汉芙小姐：\r\n　　请不要让弗兰克知道我写信给您。每回寄账单给您时，我都好想偷偷塞一张短笺到信封里。不过弗兰克一定会认为，以我的职务这么做并不适当。您听到我这么说，大概会以为他是个老古板吧？其实他是一个好得不能再好的人。只是每次您寄到书店的信或包裹都以他为收信人，而且他也将回信给您视为他的分内职责。不过我倒是一直很想自己给您写信。\r\n　　我们都好喜欢读您的来信，大伙儿也常凑在一块儿揣摩您的模样儿。我坚信您一定是一位年轻、有教养且长相聪慧的人；而老马丁先生竟无视您流露出来的绝顶幽默，硬是把您想成一个学究型的人。您愿意寄一张您的照片给我们吗？我们都很想瞧瞧呢！\r\n　　如果您也对弗兰克感到好奇的话，我偷偷告诉您：他年近四十，长得很帅，娶了一位漂亮的爱尔兰姑娘——好像是他的第二任太太。\r\n　　大家对您寄来的包裹都万分感激。我家那两个小家伙(女孩五岁、男孩四岁)简直乐翻了，因为有了您寄来的葡萄干和鸡蛋，我就可以为他们烤个蛋糕了！\r\n　　希望您不介意我私下写信给您，也请不要告诉弗兰克哟\r\n　　诚心祝福您\r\n　　塞西莉·法尔\r\n　　P.S.我会将家里的地址写在信封背后，万一您想到有什么我可以从这儿寄给您的，可以写信告诉我。\r\n\r\n\r\n\r\n---------------\r\n1950年4月10日\r\n---------------\r\n\r\n\r\n　　C.F.\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年4月10日\r\n　　亲爱的塞西莉：\r\n　　真是让老马丁先生大失所望了，请转告他：我非但一丁点儿学问都没有，连大学也没上过哩！我只不过碰巧喜欢看书罢了。说起来还得感谢一位剑桥的学者奎勒—库奇(一般都称他为Q)，是他让我在十七岁那一年一头栽进书堆里，从此不可自拔。至于我的长相，大概就跟百老汇街上的叫化子一样“聪慧”吧！\r\n　　我住在一幢白蚁丛生、摇摇欲坠、白天不供应暖气的老公寓里。整幢五层楼的其他住户早上九点出门，不到晚上六点不会回来，房东认为他犯不着为了一个窝在家里摇笔杆的小作家，而整天开着暖气。\r\n　　可怜的弗兰克，真是难为他了，我老是对他颐指气使的。我只是在打趣，不过就知道他会当真。我一直想要戳穿他那英国式的矜持。要是哪天他得了胃溃疡，都是我害的。\r\n　　请多来信告诉我关于伦敦的一切。我幻想着那一天快点到来——我步下轮船、火车，踩上布着尘灰的人行道……我要走遍柏克莱广场，逛尽温柏街；我要置身在约翰·多恩布道的圣保罗大教堂；我要趺坐在伊丽莎白拒不阶下为囚的伦敦塔前台阶上……我有一位战时派驻在伦敦的记者朋友，他曾经对我说：游客往往带着先入之见，所以他们总能在英国瞧见他们原先想看的。我告诉他，我到英国是为了探寻英国文学。而他这么告诉我：\r\n　　“去那儿准没错。”\r\n　　祝一切安好\r\n　　海莲·汉芙\r\n\r\n\r\n\r\n---------------\r\n1950年9月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年9月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　自前封信以来，许久未向您报告，盼您不致认为我们因弛废店务而忘却了您交代我们该找的书。\r\n　　言归正传，《牛津英语诗选》新近到库，此书内页以印度纸印制，原版的蓝布精装，出版于一九○五年，扉页有前人签记，算是一本书况不错的二手书。标价两美元，我们认为在径自寄给您之前，应先向您略述此书的状况，以免您在这段时间内已另行购得一册。\r\n　　许久以前，您曾垂询纽曼的《大学论》一书的下落。您是否钟意首版书？最近我们收购到一册，谨描述如下：\r\n　　纽曼(约翰·亨利，神学博士)：《大学教育之目的及其本质——应都柏林天主教会之邀所作的演说之讲义稿》。首版，八开，小牛皮装帧。一八五二年于都柏林出版，若干页面稍有渍斑但装帧完好。\r\n　　价格：美金六元\r\n　　为了避免让别人捷足先登，这两本书我们会先为您保留。静候您的回复。\r\n　　祝身体健康\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1950年9月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年9月25日\r\n　　(他手上有只卖六美元的首版《大学论》，竟还问我要不要买！真不晓得该说他老实呢，还是憨？)\r\n　　亲爱的弗兰克：\r\n　　是的！我要！我真是快受不了我自己了，本来我并不特别讲究首版不首版的，可是，“那本书”的首版……！\r\n　　哗——我真迫不及待想看到它。\r\n　　也请把《牛津诗选》一并寄来。下回可别再纳闷我有没有跟别人买书了。既然我可以寸步不离书桌，就能向你们买到既干净又漂亮的书，我干吗跑到十七大街去买那些又脏又丑的？从我坐着的地方，伦敦可近得太多啦。\r\n　　附上“千万不能掉了”的八元钞票。我对你提过布莱恩正在打官司没？他向伦敦的一家理工专门书店订了一大套特贵的物理学书。他可不像我，既邋遢又老是漫不经心，他特地到洛克菲勒广场，乖乖地排队等着划拨那一大笔书款，该办的事一件也没漏。他精得很，聪明人自有聪明人按部就班的做法。\r\n　　结果你猜怎么着？那笔钱不知道给汇到哪儿去啦！\r\n　　皇家邮政，加把劲儿！\r\n　　HH\r\n　　为了庆祝我的第一本首版书，加上海外邮购公司终于给了我一本目录，我决定要寄一个小小的包裹送你们。\r\n\r\n\r\n\r\n---------------\r\n1950年10月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年10月2日\r\n　　亲爱的海莲：\r\n　　这些照片我带到店里好几个礼拜了，不过我们这阵子真是忙得昏天暗地，所以一直找不到空当寄给你看。这些都是我和道格(我的先生)在诺福克拍的，那儿是他所属的皇家空军驻地。里头我没有一张拍得漂亮的，不过这是我所能找到最好的了，孩子们和道格那几张倒都还不错。\r\n　　亲爱的海莲，我好盼望你真的能如愿到英国来，你何不省点儿买书钱，好让你能在明年夏天成行呢？我的爸爸妈妈在米德尔塞克斯有个房子，我们会很高兴接你来住的。\r\n　　梅甘·韦尔斯(老板的秘书)和我打算明年七月一起去泽西岛(在海峡群岛之中)度假一个礼拜，你可以来和我们一块儿玩，反正回到米德尔塞克斯不须花用你太多开销。\r\n　　本·马克斯先生在瞄我写些什么了，就此停笔。\r\n　　塞西莉谨上\r\n\r\n\r\n\r\n---------------\r\n1950年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1950年10月15日\r\n　　真是的！！！\r\n　　不是我爱唠叨，弗兰克·德尔！看到书店竟忍心把这么美的古书五马分尸，拿内页充当包装纸、填箱料，我真是觉得世道中落、万劫不复了。我向被包在里头的约翰·亨利告状：“主教阁下，斯文如此扫地，君岂信乎哉？”\r\n　　他说他也实在百思不得其解。更可恶的是你把书拆散了，随便抓来几页顺手就包，害我根本搞不清楚上头到底是在打哪一仗哪一役。\r\n　　这本书大约一个星期前寄达，现在气也慢慢消了。我把它端端正正地摆在案前，整天陪着我。我不时停下打字，伸手过去，无限爱怜地抚摸它。倒不全然因为这是首版书，主要是打出生起我从没见过这么标致的书。拥有这样的书，竟让我油然而生莫名的罪恶感。它那光可鉴人的皮装封面，古雅的烫金书名，秀丽的印刷铅字，它实在应该置身于英国乡间的一幢木造宅邸；由一位优雅的老绅士坐在炉火前的皮制摇椅里，慢条斯理地轻轻展读……而不该委身在一间寒酸破公寓里，让我坐在蹩脚旧沙发上翻阅。\r\n　　我要买那本Q的文集，可是忘了多少钱，我把你的上一封信搞丢了。好像是两块钱吧？附上两张一元钞票，若是不够就来信告诉我。\r\n　　下回要寄书来时，拿第五一二页和五一三页来包书怎么样？这样我才晓得最后哪一边打赢了，还有那到底是哪一场战役。\r\n　　HH\r\n　　P.S.你们那儿可有《佩皮斯日记》？我需要它来伴我度过漫漫冬夜。\r\n\r\n\r\n\r\n---------------\r\n1950年11月1日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1950年11月1日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您致歉，迟至今日才回信给您。我因公到外地出差了一个多星期，一回到办公室就被许多待办的事务耽搁，现在才有空提笔给您回信。\r\n　　首先，请您完全无须为我们拿旧书内页当包装纸用而感到忧心，那只是全套《英国叛乱和内战史》之中装订破散且多出来的一册复本，我想应该是无法当成商品卖给任何人的。\r\n　　奎勒—库奇的文集《朝圣之路》，已付邮寄给您。您的待付款项为一元八十五分，所以您新近寄来的两元用来支付该书仍绰绰有余。另，《佩皮斯日记》目前店内暂无存书，将为您留意。\r\n　　祝万事如意\r\n　　马克斯与科恩书店\r\n　　F.德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月2日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年2月2日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　很高兴听到您喜欢那本奎勒—库奇的书。《牛津英语散文选》此刻并无库存，我们将会为您留意。\r\n　　至于《罗杰·德·科弗利爵士正传》，我们手头上恰好有一本十八世纪的文集，除了包含该书中不少篇章之外，亦收入切斯特菲尔德与哥尔德斯密斯的文章。此书由奥斯汀·多布森精心编选，敝店仅标价一元十五分。我们已将书寄去给您。如果您想要更为完整的艾迪生与斯梯尔文集的话，请通知我，我将尽力为您寻找。\r\n　　敝店若不包括马克斯先生与科恩先生，共有职员六人。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月20日\r\n---------------\r\n\r\n\r\n　　米德尔塞克斯品内东厢\r\n　　1951年2月20日\r\n　　亲爱的海莲：\r\n　　做法不只一种，妈妈和我一致认为下面这个方法对你而言应该是最简单的：准备面粉一杯、鸡蛋一只、鲜奶半杯，撒入少许盐，在一个海碗里充分搅拌，直到变成浓稠的奶油状。搁进冰箱里摆几个钟头(所以一大早开始做，时间最刚好不过)。当你要把肉放进炉子时，挪个位子摆一个铁盘让它预热。在肉烤好前一个半钟头，浇一点儿肉汁在铁盘上，不用太多，浅浅的够铺满铁盘即可。记住！铁盘得烘烤得“非常热”才行。接着，把你先前准备好的布丁料全拨到铁盘上头，然后再放着继续烤。这样一来，肉和布丁就可以同时出炉上桌啦！\r\n　　对于从没见过的人，我实在不知道该怎么恰当地形容它。总之，一个大功告成的约克郡布丁应该会蓬松得高高的，表皮焦脆，当你切开它时，会发现里头其实是空心的。\r\n　　道格仍然随着皇家空军驻扎在诺福克。你寄来的圣诞节罐头，我们严格地实施管制囤积，以等他休假回家时再全家一起享用。亲爱的海莲，你一定不难想见，届时我们将会有个多么棒的庆祝餐会啊！不过，你实在不该这样子为我们破费的！\r\n　　该赶快把信寄出去了，这样你才来得及在布莱恩的生日晚宴上推出这道菜，成果如何一定要写信告诉我哟！\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年2月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年2月25日\r\n　　亲爱的塞西莉：\r\n　　约克郡布丁简直太棒了！因为我们这儿从没人见识过这玩意儿，我后来只好向别人形容成“一笼高高鼓起、松软细致、入口即化的特大号烤饼”！\r\n　　请别为我寄去的那些食品操心。我自己也觉得不可思议，那家海外邮购公司也不晓得是不是非营利机构，抑或是商品可以免税什么的吧？总之，他们的东西都便宜得很，我自己买的那只火鸡都还比寄给你们的那一大箱圣诞包裹贵哩。他们的确有一些价格比较高的商品，比如大块的烤肋排，或是一整只羊腿之类的。不过，即使是那些东西，也比跟这儿的肉贩买要便宜许多，如果真得那样，把我剁了也没法子寄东西给你们。现在正浏览着目录，我把它摊在地毯上，琢磨着两个旗鼓相当的商品组合：编号105的包裹(内含鸡蛋一打外加甜面饼一箱)和编号217B的包裹(内含鸡蛋两打、没有甜面饼)，我实在不甘心寄一打装的鸡蛋，让你们每人各分得两只能干吗？不过布莱恩跟我说，粉末干燥蛋吃起来味同嚼蜡，还真伤脑筋。\r\n　　有一位制作人刚打电话给我，说他蛮喜欢我写的剧本(还没喜欢到要把它搬上舞台的程度)。他正打算制作一出电视剧集，问我是否有兴趣编电视剧本，他漫不经心地说：“一集给两张！”搞了半天才弄明白他的意思是：每一集的稿费两百元。我原先为剧团修改剧本，一周的酬劳也才不过四十元！明天要去和他详谈，快祝我好运吧！\r\n　　祝福你\r\n　　海莲\r\n\r\n\r\n\r\n---------------\r\n1951年4月4日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月4日\r\n　　海莲亲爱的：\r\n　　你寄来的复活节包裹已经收到，这些礼物实在太棒了！不过，因为弗兰克第二天一早就出差去了，所以他没空回信向你道谢，大家都急得跳脚，而其他人全都不敢斗胆写信给“弗兰克的汉芙小姐”。\r\n　　包裹里的肉食实在太棒！我认为你真的不该再这样子为我们破费了，一定花了你不少钱吧？愿上帝保佑你的好心肠。\r\n　　本·马克斯先生走过来要找事给我做了，就此搁笔。\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　伦敦西八区肯辛顿高地街伯爵社区\r\n　　1951年4月5日\r\n　　亲爱的汉芙小姐：\r\n　　谨在此向您报告，您寄到马克斯与科恩书店的复活节礼物，已于几天前寄达。但弗兰克·德尔先生因公外出，错过与大家分享喜悦了。\r\n　　一见到包裹里头的肉，所有人的眼睛都看直了，而鸡蛋也大获欢迎。我觉得有必要给您写个信，向您报告：所有同仁对您的好意和慷慨都万分感激。\r\n　　我们所有同仁都期盼您能尽快来英国，届时我们一定会竭尽心力，让您有一趟愉快的英伦之旅。\r\n　　梅甘·韦尔斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月5日\r\n---------------\r\n\r\n\r\n　　埃塞克斯滨海南庄桶桥路\r\n　　1951年4月5日\r\n　　敬爱的汉芙小姐：\r\n　　我是马克斯与科恩书店的编目员，已在书店任职即将届满两年。谨在此向您表达感激之意。谢谢您多次寄赠礼物包裹给我们。\r\n　　我现在与七十五岁的姨婆住在一起。当我带着您送的肉、牛舌罐头回到家里，我想如果您能在场目睹姨婆脸上惊喜万状的表情，您大概就不难想见我们满溢的感激之情了。知道远方有人竟能为从未谋面的一群人付出这么多关怀和慷慨，我的内心实在备感温暖。而我相信，所有同仁必定与我深有同感。\r\n　　如果您想到有什么事我可以代劳，或是从伦敦寄点儿什么给您的，请务必交代我去办，我将会引以为荣。\r\n　　比尔·汉弗莱斯谨上\r\n\r\n\r\n\r\n---------------\r\n1951年4月9日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年4月9日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　我猜您大概已经开始担心，我们竟然这么久都没写信谢谢您寄来的包裹，心里头一定正在嘀咕：真是一群不知好歹的家伙。实际上，我刚离开伦敦到乡间跑了一大圈，到处拜访私人宅第，搜寻待售的藏书，努力补充店里捉襟见肘的库存。我太太已经开始把我当成房客来招呼了——我总是只回家睡觉，一吃完早餐又不见人影。不过，当我带着您送的肉(鸡蛋、火腿就更不用说了)回到家里，她就会觉得我毕竟也非一无是处。当然，所有的不开心也就随之烟消云散。说实在的，我们已经太久没能见到一块完整的肉了。\r\n　　我们总得想点儿法子，表达我们对您的感激。于是，我们将另行寄上一本小书，希望您会喜欢它。我还记得您曾经想买一本情诗集，这是我所能找到尽可能符合您的要求的了。全体同仁为您献上此书，盼您笑纳。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n　　(附在《伊丽莎白时期情诗选》里的卡片：)\r\n　　谨以此书赠予\r\n　　海莲·汉芙\r\n　　并为其诸多美善情谊\r\n　　致上最诚挚的祝福\r\n　　与无尽的感激\r\n　　伦敦查令十字街84号\r\n　　全体员工一同\r\n　　一九五一年四月\r\n\r\n\r\n\r\n---------------\r\n1951年4月16日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年4月16日\r\n　　此致伦敦查令十字街84号全体同仁：\r\n　　谢谢你们送我这本书。我从没拥有过这么一本三边的页缘都上金的书。你们知道吗？我竟在生日当天收到这本书！\r\n　　你们另外写了一张卡片，而不直接题签在扉页上，我真希望你们不要这样过分拘谨。如果我猜得没错，这一定是你们的“书商本性”作祟使然吧，你们担心一旦写了字在书上，将会折损了它的价值。差矣，你们如果真能这么做，不仅对我而言，对未来的书主，都增添了无可估算的价值。我喜欢扉页上有题签、页边写满注记的旧书；我爱极了那种与心有灵犀的前人冥冥共读，时而戚戚于胸、时而被耳提面命的感觉。\r\n　　还有，为什么大家都不签上名字呢？我猜一定是弗兰克不准你们签的，他大概怕我会撇下他，一一给你们大家写情书吧！\r\n　　隔着汪洋，我在美国此端遥寄我对你们的祝福——“美国”，好一个“坚定的盟邦”！当她一掷千金帮日本、德国从败仗中“复苏”，却眼睁睁看着英国同胞饱受饥馑之苦！皇天为证，总有一天我要亲自去英国，当面为她向你们道歉。(等我回国后，我会叫她加倍向我赔罪！)\r\n　　再次感谢你们送我这本美丽的书，我一定会格外小心，免得让它溅到酒滴、沾了烟灰。这份礼物对我这种人来说实在太隆重了。\r\n　　海莲·汉芙上\r\n\r\n\r\n\r\n---------------\r\n1951年9月10日\r\n---------------\r\n\r\n\r\n　　(玛克辛写于后台)\r\n　　伦敦\r\n　　1951年9月10日\r\n　　甜心儿：\r\n　　这是一间活脱从狄更斯书里头蹦出来的可爱铺子，如果让你见到了，不爱死了才怪。\r\n　　店门口陈列了几架书，开门进去前，我先站在外头假装随意翻阅几本书，好让自己看起来像是若无其事地逛书店。一走进店内，喧嚣全被关在门外。一阵古书的陈旧气味扑鼻而来。我实在不知道该怎么形容：那是一种混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……店内左手边有张书桌，坐着一位年约五十、长着一只贺加斯式鼻子的男士。他站起身来，操着北方口音对我说：“日安。”我回答说我只是随意逛逛，而他则有礼地说：“请。”\r\n　　极目所见全是书架——高耸直抵到天花板的深色的古老书架，橡木架面经过漫长岁月的洗礼，虽已褪色仍径放光芒。接着是摆放画片的专区——应该说：一张叠放着许多画片的大桌台。上头有克鲁克香克、拉克姆矠、斯派和许许多多我叫不出名字的英国插画家的美丽画作；另一边还放着几叠迷人的古旧画刊。\r\n　　我在店内待了约莫半个钟头光景，期待着你的弗兰克或是哪个女孩儿翩翩现身。不过，因为我到达时已过了一点钟，我猜他们全都外出用餐去了，而我又不能待太久。\r\n　　就是这样咯！新戏的预告并没有造成万人空巷，不过据说对方人蛮好的，排给我们几个月的档期，所以我昨天出门找出租公寓，在骑士桥附近有一间小小的、蛮不错的小套房。现在还没确定，一旦定下来，我会写信告诉你，你也可以再打电话问我妈。\r\n　　三餐不成问题，我们都在餐厅或旅馆里用餐，像最高级的克拉里兹大饭店就能充分供应烤牛肉、排骨。价钱虽然贵得离谱，不过换算成美金倒是挺划算的，所以我们还吃得起。假使换成我是英国人，瞧见这光景一定会恨得牙痒痒的。但是他们却反而对我们好得不得了，到处都有人邀请我们去家里作客或上馆子。\r\n　　惟一短缺的东西就是糖。凡是甜的东西，一应俱缺。也许我反该谢天谢地，正好让我在这里瘦它个几磅。\r\n　　写信给我。\r\n　　爱你的\r\n　　玛克辛\r\n\r\n\r\n\r\n---------------\r\n1951年9月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年9月15日\r\n　　玛克辛：真多亏了你的慧心巧手，书店简直被你给写活了——你的文笔实在比我好得太多啦！\r\n　　我刚打了电话给你妈妈要你的住址，她要我转告你：方糖和巧克力棒依照你的交代，已经给你寄去了。你不是还跟我说你要趁机减肥的吗？\r\n　　我不想让你以为我是酸葡萄，不过我实在不明白，你究竟是何德何能？老天竟任由你饱览遍逛“我的书店”；而我为什么就只得乖乖蹲在九十五大街的破公寓里，埋头写着这劳什子《埃勒里·奎因的冒险》电视剧集脚本！“我不是告诉过你，不能安排一截沾着口红的烟蒂当做破案线索吗？”“我们这个节目是由百优雪茄公司赞助的，千万别给我编出“香烟”这个台词儿。”……就连在场景里安排一只道具烟灰缸也不许出现烟屁股；也不能摆雪茄屁股——厂商嫌不好看——所以，只要剧情出现烟灰缸，里头全好端端地搁着一管全新的、未拆封的百优雪茄！\r\n　　简直岂有此理！你却还能跟约翰·吉尔古德坐在克拉里兹的酒吧里打情骂俏！\r\n　　来信时多写些伦敦的事物——地铁、巷弄胡同、古宅大院……随便什么都好，写仔细点儿。告诉我骑士桥长什么模样，此刻我的耳畔似乎响起了科茨的《伦敦组曲》……听起来是那么绿意盎然、雄壮典雅。\r\n　　xxxx\r\n　　hh\r\n\r\n\r\n\r\n---------------\r\n1951年10月15日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年10月15日\r\n　　这哪是佩皮斯日记呢？你倒是给我交代清楚！\r\n　　这本书根本不够资格称之为《佩皮斯日记》，这只是哪个没事找事做的半吊子编辑，从佩皮斯日记里东挖西补、断章取义，存心让他死不瞑目！\r\n　　真想啐它一口！\r\n　　一六六八年一月十二日的日记跑到哪儿去了？记着他的老婆把他踹下床，抄了根烫红的拨火棍，追着他满屋子乱跑的那天的日记呢？\r\n　　记着W.佩恩爵士福至心灵的儿子成天揣着教谕，把大伙儿搞得七荤八素的日记呢？这些偷工减料的手脚可别想逃过我的法眼。\r\n　　附上两张皱巴巴的钞票。我想，用来付这本玩意儿，外加你将要为我找来的那本“货真价实的佩皮斯”，应该绰绰有余了！到时候，我会将这本烂书碎尸万段，然后，一页一页撕下来——拿来包东西！\r\n　　HH\r\n　　P.S.圣诞节快到了，到底是寄新鲜的鸡蛋，还是干燥蛋好呢？我当然明白干燥蛋可以放得比较久，但是，“丹麦空运直送的新鲜鸡蛋”光听都觉得香，你们快帮我拿主意吧！\r\n\r\n\r\n\r\n---------------\r\n1951年10月20日\r\n---------------\r\n\r\n\r\n　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年10月20日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　首先，在此为我们的疏忽向您致以十二万分的歉意。我一直错认为那是收录完整的布雷布鲁克版。我相当能够了解，当您发现该书阙漏了喜爱的章节时，会有何等的失落感受。\r\n　　我一定会尽力另找一本书价合理、完整收录您在信中提及的段落的《佩皮斯日记》，并尽快为您寄上。\r\n　　同时，很高兴地在此向您报告：本店最近将收购一批私人藏书，我已从该批书籍中捡选出一些您会喜欢的书，包括一本利·亨特的选集，收录大半您曾提及的文章；还有一册《通俗拉丁文新约全书》——希望这回不会再出错了；同时有一本也许对您相当实用的《通俗拉丁文圣经辞典》；另一本《二十世纪英国散文选》，内容虽收入希莱尔·贝洛克的文章，但并不是谈论厕所的那篇。随信附上发票，上列书款十七先令六便士，约合美金二元五角，此书账已扣除您在敝店账户中的二元余额。\r\n　　至于鸡蛋的问题——店内同仁商量的结果，大家似乎意见一致，均认为新鲜鸡蛋较好。诚如您所言：鲜蛋虽较难久放，但风味确实不同。\r\n　　我们都期盼大选后日子会好转。如果丘吉尔先生和他的政党能赢得选举——这也是我的衷心期望，将会是一件振奋民心的好事。\r\n　　衷心祝福您\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1951年1Z月2日\r\n    亲爱的急惊风:\r\n    你简直是“迅雷不及掩耳”，利·亨特的书和《通俗拉丁文新约全书》“倏忽”寄达。你大约还没弄明白吧——这不就正是我两年前向你们订购的书吗?如果你继续照着这种提心吊胆的步调干活儿，要不得心脏病也难。\r\n    我真恶毒。你为了帮我找书，忙东忙西的，我竟然不曾向你道过一声谢，我真是坏透了。其实，你在那头儿受苦受难，我都是铭感在心底的。附上三元钞票，抱歉，最上头那一张被我溅到了几滴咖啡，应该还不致于作废的地步，上头的面额还可以看得出来。\r\n    你们有精装版的合唱乐谱吗?比如说巴赫的《马太受难曲》或韩德尔的《弥赛亚》?虽然我顶着寒风，走到五十条街外就能买到，但我想我还是“就近”先问问你们吧。\r\n    恭贺丘吉尔先生和他所属的政党，希望他能让你们的日子好过些。\r\n    “德尔”?你的祖先是威尔士人吗?\r\n                                          HH\r\n\r\n    1951年12月7日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    希望您会高兴得知，两箱鸡蛋和牛舌罐头已平安寄达，而我们也要再度向您道谢。\r\n    本店的一位老同事——马丁先生，躺卧病榻已有一段时日了，因此，我们一致决定让他多分一些鸡蛋——其中一整箱。一如以往，对于这些难能可贵的物资，我们十分欣喜。牛舌罐头亦相当诱人，它们大大地补充了我们的贮粮。至于我\r\n自已，我会将它们好好地收藏起来，以备特殊场合再派上用场。\r\n    我询问了此间所有的乐谱店，不过全都没有干净的二手精装版《弥赛亚》或巴赫的《马太受难曲》，然而我发现新书目前仍持续发行。价格虽略嫌高了些，但我想了想，仍为您买了下来，并在几天前寄发出去了，最近几天您应该就会收到这两本乐谱。一英镑十先令(约合美金四元二角)的账单亦会随\r\n书寄上。\r\n    我们将寄给您一件小小的圣诞礼物。这是一件刺绣品，我们会在邮包上注明“内装礼品”，希望这样可以避免您被课上关税。所有人都希望您笑纳这份薄礼，大伙儿同声祝您圣诞快乐，并有一个美好的来年。\r\n    我的姓氏绝非源自威尔士，鉴于其发音近似法文“挪尔”，我想我的祖籍或许是法国也未可知。\r\n      马克斯与科恩书店\r\n      弗兰克·德尔谨上\r\n\r\n    195年l月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n    亲爱的汉芙小姐:\r\n    首先，我们都十分高兴您喜欢那块桌布。总算能够回赠您一件礼物，亦带给我们一丝欣喜——虽然比起您过去这些年来为我们的奉献，那实在是太微不足道了。您或许想知道那件桌布的来历，其实这是最近刚完成的作品，出自我的邻\r\n居—一位八十多岁老太太的巧手。她一个人独居，平日以女红自娱。虽然她做了许多手工刺绣，却儿乎全都自己留着。她之所以愿意割爱，破例卖出这么一块桌布，乃是禁不住我太太的不断恳求。当然，您之前寄给我们的干燥蛋也使上不\r\n少助力。\r\n    如果您非到万不得已，一定要“清洗”您的《格罗里埃圣\r\n经》。的话，我们会建议您用一般的肥皂和清水即可。做法是:加一茶匙的苏打粉到一品脱的温水中，用蘸了肥皂的海绵轻轻擦拭。我相信您会发现，这样可以有效地除去污渍。经过上述的处理后，您可以再用少许绵羊油为它上光。\r\n    J.彭伯顿是一位女士，J是珍妮特的缩写。\r\n    祝您新的一年一切如意。\r\n弗兰克·德尔敬上\r\n\r\n\r\n    伦教北八区克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年1月20日\r\n    亲爱的汉芙小姐:\r\n    谢谢您长期不断地寄赠马克斯与科恩书店礼物包裹，并让我家也能有幸分得一部分。我长久以来就一直想写信给您，现在终于有正当理由可以动笔了。弗兰克跟我说，您很想知道绣了那块桌布的老太太的姓名、地址。那件刺绣的手工\r\n真的很漂亮吧？\r\n    这位博尔顿老太太就住在我们的公寓隔壁，地址是:橡原巷36号。当我告诉她，这件桌巾飘洋过海送给了一位好心的女士时，她好吃惊。如果能再亲耳听到您对她的手艺赞不绝口，我相信她一定会十分开心。\r\n    很感谢您说还要寄更多干燥蛋给我们。不过上回您送来的还剩一些，应该可以让我们享用到春天。四月到九月这段时间，我们并无须操心蛋的问题。如果偶尔会因物资短缺而临时减少配给量，我们就会拿别的东西去跟别人换罐头。我曾经用一双丝袜在黑市换得一罐干燥蛋，当然这么做不尽合法，却是非常时期不得已的变通办法。\r\n    过些日子我会寄一些全家福照片给您看，我们的小女儿玛莉上周刚度过她的四岁生日;大女儿去年八月满十二岁了，名叫希拉，是弗兰克的前妻生的——她不幸在战时丧生了。战后我嫁给弗，顺道有了个现成的女儿。去年五月，希拉在学校当着修女们的面(她念的是教会学校)，说她要送一张卡片祝福爸爸、妈妈的结婚四周年纪念日。您不难想像，这可怜的小姑娘花了多少唇舌才解释清楚。\r\n    最后，祝您新的一年一切顺遂，并期盼不久后我们可以在英国相会。\r\n诺拉·德尔谨上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年l月29日\r\n    亲爱的汉芙小姐:\r\n    谢谢您的来信。我很感激您如此好心地告诉我:您十分喜欢我绣的桌布。我当初真该多花点儿工夫在那上头。我想，德尔太太一定也对您提过了吧，我是一个上了年纪的人，双手也不太听使唤，能做的活儿也不像以前那么多了。我用这\r\n双老手做的东西有幸能交到喜欢它的人手上，这真是一件令人欣慰的事儿。\r\n    我常见到德尔太太，而她也不时向我提起您的事儿。您若到英国来，或许我也能有幸见您一面罢。\r\n    再次谢谢您。\r\n    玛丽·博尔顿谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n听好了!玛克辛:\r\n    我刚和你妈妈聊过。她说你们的戏也许下个月就会结束公演;她还告诉我。你带走了两打丝袜。帮我一个忙，趁你的戏下档前，拿四双去书店交给弗兰克·德尔，就说是送给店里的三个女孩和诺拉（他太太）。\r\n    你妈妈还特别交代，要我用不着付钱给你。她说那些丝袜全是她自已在去年夏天萨克斯百货店清仓大减价时便宜买到的，她决定要乐捐出来，好让她自个儿也能沾沾“共赴国难”的光。\r\n    等你回国就可以瞧见他们送我的圣诞节礼物了。这是一条漂亮的爱尔兰绣花桌巾，米黄色的底布上以手工绣着古典的花草图案——全是各自不同颜色、浓淡有致的花儿。保证你从没看过这么美的桌巾，我那张从旧货店买回来的破茶几\r\n就更肯定没见识过啦!我真是迫不及待想披上维多利亚时代的水袖，优雅地举起手，幻想自己执着一只乔治王朝的古董茶壶，轻轻地斟上一盏茗茶……你快点儿回来吧!我们可以在家里扮一出斯坦尼斯拉夫斯基！\r\n    埃勒里调高了我的剧本稿酬，现在是一集两百五十元。如果照着这样的调薪幅度。到了六月，也许我就可以启程赴英，自己去逛“我的书店”——如果我斗胆敢去的话。隔着三千英里的安全距离，我写了一堆没大没小的信，我大概只会\r\n悄悄溜进去又静静踱出来，而不敢告诉他们我是谁。\r\n    我实在不明白你怎么会在杂货铺里被搞得一楞一楞的呢?老板跟你说的不是“兔兜脚”，他说的是“土豆胶”!——我也觉得这才是惟一合理的称呼。你动点儿脑筋想想:豆子长在土里，叫它“土豆”合情人理;从土里头挖出来，一直“搅”、一直“搅”，搅成“胶”状，不就成了“土一豆一胶”!这个词儿不是要比“花生酱”更贴近事实吗?你真是不懂国语!\r\n    \r\n    h.汉芙(必也正名乎的海莲)\r\nP.S.你妈妈今天一早就打扮得花枝招展地出门，要为你在第八大道和五十大街一带找间公寓，因为你曾交代她去剧院区找。我说玛克辛啊，你该很清楚才对，她穿那一身行头在那种地方晃，谁还敢租房子给她啊?\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年2月9日\r\n大懒虫!\r\n    依我看若要等到你寄书来.我都不晓得要超渡几回了。我不如干脆直接冲进布伦塔诺书店，有什么就买什么，不管印得多糟!\r\n    你们不妨再加记一笔沃尔顿的《五人传》到我那份“该寄而未寄”的书单里头。老实说，订这本书实在是违背了我的购书原则。我从来不买没读过的书——否则，不就像买了一件没试穿过的衣服同样下场吗?可是，这儿竟然连图书馆也\r\n借不到这本书。\r\n    要读的话倒是有。四十二大街上的分馆有一本，但，恕不外借!坐镇在柜台的女馆员用力摇了摇头，盛气凌人地说:“仅供内用!”然后只准我窝在密不通风的315号阅览室里啃完整本书。既不能边读边喝咖啡，抽烟就更纯属妄想了。\r\n    没关系，反正Q多次引用这本书里的段子，所以我肯定也会喜欢它。只要是Q喜欢的，我都照单全收——小说除外，我就是没法儿喜欢那些根本不存在的虚构人物操演着不曾发生过的事儿。\r\n    你们成天都没事干吗?是不是都窝在店里头看书?何不起身做点儿生意呢?\r\n                                              汉芙小姐上\r\n                      (只有我的“朋友”才可以叫我“海莲”!)\r\np.s.转告女孩们和诺拉，如果一切顺利的话，她们在大斋节都有丝袜可穿。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年2月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我也十分同意，该是我们都摒弃无谓的“小姐”、“先生”敬称的时候了。不瞒您说，我本人实在并不像您长久以为的那样既木讷又严峻。只是我写给您的信都必须存放一份副本作为业务存档，所以我认为行礼如仪似乎比较妥当。不过，此封信既然与书店业务无关，自然毋须顾虑副本、存档的问题。\r\n    我们百思不得其解，不明白您如何隔海变戏法，让四双丝袜无中生有。我所知道的只是:今天中午我用过午餐回到书店，就赫然发现它们已经好端端地摆在我的办公桌上，上头还附着一张写着“海莲·汉芙赠”的卡片。没有人晓得它们是什么时候或是怎么来的。女孩们都吓呆了，我晓得她们正在打主意待会儿自行写信给您。\r\n    有一件令人遗憾的事要向您报告:久卧病榻的乔治·马丁先生上周在医院中病逝了。他在本书店工作已有多个年头。伴随着这个噩耗。国王的猝然驾崩亦使我们此刻都笼罩在一片哀戚之中。\r\n    我实在不知该如何回报您对我们的不断付出。我所能做到的，只是当您确定访问英国时，橡原巷37号将会有一个房间，可供您无限期地住宿。\r\n    全员共祝您一切美好\r\n    弗兰克。德尔\r\n\r\n 纽约市\r\n东九十五大街14号\r\n    1952年3月3日\r\n    噢!真感谢你寄来的《五人传》。实在难以置信，这本一八\r\n四〇年出版的书，经过了一百多年。竟然还能保持这么完好的书况!质地柔细、依旧带着毛边的书页尤其可人。我真为前\r\n任书主(扉页上有“威廉.T.戈登”的签名)感到悲哀，真是子孙不肖哟!竟把这么宝贵的东西一股脑儿全卖给你们。哼!我真想趁它们被称斤论两前，拎着鞋溜进他们的书房，先下手搜刮一番!\r\n    这真是一本引人入胜的书。你可知道，当约翰·多恩带着他主子的掌上明珠私奔(人家好不容易老来得女的)，他因此下狱被囚禁在伦敦塔，他在里头握饿受冻。饿着饿着……居然就“得道”了！——不是沃尔顿的原句啦，这是我自己改编的说法。\r\n    注意!我附寄五元钞票一张。由于这本《五人传》赫然登场，害我原来那本《垂钓者言》相形见绌(认识你们之前买的，这是一本硬邦邦的“美国大众经典文库”版)。《五人传》跟它不登对，总嫌它在跟前晃来晃去，看了就讨厌。所以，多出来的两块半就帮我找一本好点儿的《垂钓者言》——劳您大驾啦。\r\n    你得当心了，如果电视剧续签，明年我就会杀到你们那儿去。到时候我会蹬着古董木梯，掸去你们的书架顶层的陈年积垢，顺便也把你们的优雅端庄一并一扫而光。我跟你提过我帮埃勒里·奎因的电视节目编写巧艺谋杀吗?我所写的剧本总会安插艺术场景——芭蕾舞团啦，音乐厅啦，歌剧院什么的;连嫌疑犯或尸体也得写得文绉绉的。为了向你致敬，也许我该编一出发生在古书店里的谋杀案，怎么样?你要演行凶歹徒呢?还是要扮刀下亡魂?\r\n hh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷36号\r\n    1952年3月24日\r\n亲爱的汉芙小姐:\r\n    我实在不知如何表达我的感激，今天收到您寄给我的食物包裹，我有生以来未曾收到过包裹。您实在不须为我这个老太婆如此费心。我只能谢谢您的好意，并好好地享用您寄来的礼物。\r\n    感谢您为我设想如此周到。我拿您寄来的东西给德尔太太瞧，她也直夸您实在是一位大好人。\r\n    再次谢谢您，并析祷您一切安好。\r\n玛丽·博尔顿谨上\r\n\r\n    伦敦中西二区 令十字街84号.\r\n    1952年4月17日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲(您看，我已经不再“行礼如仪”了):\r\n    我相信您一定会高兴听到这个消息:我们刚购入一批私人藏书，里头有一册非常好的《垂钓者言》，待我将收购手续完成，本书可望于下周为您寄上。我们大概会标价二元二角五分，您账户中的余额仍够支付。\r\n    您为埃勒里·奎因写的剧本似乎挺有意思。我希望这部剧集也能在我们这儿播映，它也该改善其沉沉死气了(我是指我们的电视节目，不是您写的剧本)。\r\n    诺拉和所有人与我同祝您一切美好。\r\n弗兰克·德尔敬上\r\n\r\n    伦教北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年5月4日星期日\r\n亲爱的海莲:\r\n    谢谢您寄赠的干燥蛋罐头，包裹在两天前收到了。尽管难掩喜悦之情，但我实在觉得很难为情，因为我竟向您提起蛋源短缺的事情。不过今年倒是还没发生减量配给的情形，所以我们就当成是上天的恩赐，用它们多烤了个蛋糕什么的。弗兰克还拿了一些去办公室，打算要寄给塞西莉，他老是忘了把她的地址带回家。我想您应该也听说了吧，塞西莉离职了，她正打算随丈夫调防到中亚去。\r\n    我附了几张相片，弗兰克说这些照片全没把他拍好，还说他本人比照片好看多了。不打紧，我们就让他说梦话好了。\r\n    希拉的学校放了一个月的假，以前我们全家偶尔会一起到海边走走或随处晃晃。但是现在恐怕也不能那么常出游了，因为交通费贵得吓人。我们一直有买车的念头，但车价也不便宜，物色一部性能尚可的二手车或许比较可行。大部分\r\n新车都出口到国外，国内只能分到少量的配额。我们有一些朋友，光是为了订一部新车，就等了五六年。\r\n    希拉现在要为您朗读一篇《丰收祈祷文》，祈祷您可以如愿到英国一游;而且在复活节时，我们也已经借花献佛，用您送给我们的咸肉罐头犒赏过神明了。所以，如果再加上《丰收祈祷文》奏效，老天将会保佑您获得一笔意外之财，这样您和我们就很快可以见面了!\r\n    就此停笔并再次感谢您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年5月11日\r\n亲爱的弗兰克:\r\n    本打算一收到书就写信给你的。就是想跟你道句谢谢，《垂钓者言》里的木刻版画太棒了，光这些插图的价值就十倍于书价。我们活在一个诡异的世界——这么漂亮，又能终生厮守的书，只须花相当于看场电影的代价就能拥有;上医院\r\n做一副牙套却要五十倍于此。\r\n    唉!如果你们依照每本书的实际价值去标价的话，我肯定一本也买不起。\r\n    如果你知道我这个一向厌恶小说的人终究回头读起简·奥斯汀来了，一定会大大地惊讶。《傲慢与偏见》深深掳获了我的心!我千不甘万不愿将我手头上这本送还给图书馆，所以快找一本卖我。\r\n    代我问候诺拉和办公室里可怜的上班族们。\r\nhh\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1952年8月24日\r\n亲爱的海莲:\r\n    我要再一次对您寄给马克斯与科恩书店，并让我们家分享的物资向您道谢，我真希望能回寄点儿什么报答您。\r\n    对了，海莲，这个礼拜我们终于荣登有车阶级了。虽然不是一部新车，没什么好到处吹嘘的。不过，车子只要能跑就行了，您说是不?怎么样，您现在比较愿意来找我们玩了吧?\r\n    前一阵子，我有两位亲戚从苏格兰南下来看我们，博尔顿老太太心肠很好，肯让他们在她家叨扰了几个礼拜，他们说住在那儿很舒服。他们那一阵子就吃我家，睡博尔顿老太太家。如果您手头方便，可以赶明年儿，趁着女王登基大典时\r\n来一块儿凑凑热闹，博尔顿老太太说她会为您将房间准备好。\r\n    好了，不多说了，寄上我的祝福和感谢——谢谢您送的肉与鸡蛋。\r\n诺拉谨上\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1952年8月26日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    我又要再度代表我们这儿所有人，写信向您道谢了，三件甚获欢迎的包裹几天前寄达。您实在太好心了，将辛苦挣来的钱花在我们身上。我们都非常感激您对我们的关怀。\r\n    这几天，店里进了三十几册“洛布经典文库”，可是，唉。里头不巧就缺了贺拉斯、萨福、卡图卢斯。\r\n    从九月一日起，我会休几个礼拜的假。可是因为我最近买了车，家中经济失血甚多，所以不能再大事铺张。诺拉有个姐姐住在海边，我们都冀望她会怜悯我们无处可去而邀我们去玩。这是我头一回买车，所以全家都非常兴奋——尽管这\r\n是一九三九型的老款车。但只要它不会老是半路抛锚，我们就该偷笑了。\r\n    全心祝福您\r\n    弗兰克·德尔\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1952年9月18日\r\n弗兰基，猜猜看，当你去度假时，谁上门来啦?萨姆·佩皮斯是也!不管是谁代你寄的书，别忘了要谢谢人家。这些书上个星期就到了，三册扎扎实实的海军蓝布面精装本，用四大页四开报纸裹着。我边吃午饭边读旧报;晚餐过后，开始和萨姆\r\n神交。\r\n    他要我转告你:他非常高兴能来到敝宝地，他的前任主人是个大草包，连书页都懒得裁开。我将它们一一裁开，内页用的是薄得几可透光的印度纸——我们这儿管这种纸叫“洋葱皮”，真是恰如其分。要是换成厚一点的纸张，难保不变成\r\n六册、甚至七册，印度纸果然功德无量。我只有三座书架，可是实在已经找不到可以让我清掉的书了。\r\n    每年一到春天，我就会“大清仓”，把一些我再也不会重读的书全丢掉，就像我也会把再也不穿的衣服扔了同样道理。倒是旁人都很惊讶、依我看，他们爱惜书本的方式才奇怪呢。他们买一堆新出版的畅销书，囫囵吞枣似的看完，我常\r\n想:他们也未免读得太潦草了吧。然后呢，因为他们从不重读那些书，不消一年，书里头的内容早就被他们抛到九霄云外!不过，当他们看见我一箱一箱地把书往外扔时，却又露出一副“这怎么得了!”的表情。要是照着他们的做法:买了一本\r\n书，好——读过了，好——上架，好——没事了，一辈子也不会再去碰它第二回，可是呢，“丢掉?万万使不得呀!”为什么使不得?我个人坚信:一本不好的书——哪怕它只是不够好，弃之毫不足惜!\r\n    你和诺拉过了一个不赖的假期吧?我自己则全消磨在中央公园里。我的宝贝牙医师放了我一个月的假，他却欢欢喜喜带着娇妻度蜜月去了，旅费是我出的——我有没有提过?好几个月前，我发现牙齿一颗接一颗全坏光了，我要么乖乖装上牙套。要不然就得全部拔光!因为还不想当个无齿之徒，我最后还是决定装牙套。可是诊疗费简直是天文数字!看来伊丽莎白只好在我没来的情况下登基了，我呢，今后几年里也只能留在这里看着我的牙齿一一加冕了。\r\n    不过我可没打算停止买书!连牙齿都弃我而去了。总该给自己留点儿什么呀!你能为我找到萧伯纳的剧评和乐评吗?我想他应该写了好几本，把你所能找到的都寄给我。还有!弗兰基，漫漫冬天眼看着又要来了，我兼差帮人带小孩时可不能闲着，所以，亟需读物!——快，别老坐着，起身帮我找找书!\r\n\r\nhh\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n  1952年12月12日\r\n此致“伦敦查令十字街84号的众好友们”:\r\n    一拆开包装，《爱书人文选》款款现身—镶金边的皮面、上金漆的上书口……轻而易举勇夺“我的藏书选美”的后冠，连首版的《大学论》也甘败下风。它看起来仍如此清新、纯朴，宛若从未遭染指——不过我知道，它的确曾被频繁地悉心翻阅过:因为一打开书页，总会落在某几个特定段落，冥冥之中似有前任书主的幽灵导引我，领我来到我未曾徜徉的优美辞藻。例如特里斯特拉姆·项狄描述他父亲富丽堂皇的书房:“架上罗列多善本，筐中广纳皆美卷。”(弗兰克!快去给我找一本《项狄传》！)\r\n    我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年;而你们送给我的礼物，却能和我朝夕相处，至死方休;我甚至还能将它遗爱人间而含笑以终。\r\n    谢谢你们，祝你们新年快乐。\r\n海莲\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n   1952年12月17日\r\n亲爱的海莲:\r\n    很抱歉这么久都没给您只字片语。希望阿德莱败选不致于让您太难过，也许下一回他可以东山再起。\r\n    博尔顿老太太跟我说，如果明年夏天您来英国时，她仍健在的话，很欢迎您住在她家。她是我所认识最长寿的人，我还不晓得有谁能像她活那么大岁数儿，我相信她一定可以活到一百岁的。无论如何，我们都会把您的住宿问题打点好。\r\n    谢谢您寄给我们这么好的圣诞礼物，您实在是个大好人。海莲!如果您明年来英国的时候，马克斯与科恩书店的人没好好地为您设宴接风的话，他们就太该死了。\r\n    我希望您将有个快乐的圣诞节。下回再聊了，献上我们\r\n的祝福和感激。\r\n    愿上帝保佑您。\r\n诺拉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1953年5月3日\r\n弗兰基，告诉你个包准让你乐翻的消息-—\r\n    首先，随信寄上三元钞票。邮包已收到，这本书长得就像简·奥斯汀该有的模样儿——皮细骨瘦、清癯、完美无瑕。\r\n    好，进人正题——埃勒里电视剧集停播了。正当我青黄不接，又为了支付看牙的庞大开销而焦头烂额的当头，有人找我为一个新的节目写个草案——将名人轶事编成电视单元剧。所以我快马加鞭，完成一个故事大纲。送出之后，电视\r\n公司接受了;于是我又写了一个完整剧本，他们也颇为满意——所以再过个把月，新差事就有着落了。\r\n    而你猜我改编哪一个故事?“多恩与领主千金私奔记”—灵感来自沃尔顿的《五人传》!电视观众大概没几个人晓得约翰·多恩是谁，不过，拜海明威之赐，大家都听过“没有人是一座孤岛”。我只消将这句名言编进剧本里，便顺利\r\n卖出啦!\r\n    于是，约翰·多恩成功登上“不朽名人堂”。我也拿到一笔酬劳—价码大约是我前前后后花在你们店里的书款外加五颗牙!\r\n    我打算半夜爬起来听收音机的现场实况转播，和你们一块儿参加加冕典礼，同时惦念着你们所有人。\r\n    祝开心\r\n\r\n    伦敦中西二区，查令十字街84.\r\n    1953年6月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    您的包裹于六月一日平安抵达了，正好赶上加冕大典。当天家里来了不少朋友。和我们一起收看电视转播。我们用您寄来的火腿做了些三明治供大家享用，每个人都直说美味极了。于是我们全体举杯同祝您及女王都凤体康泰。\r\n    您实在太仁慈了，竟将您的辛苦所得拿来关照我们。所有同仁与我在此同声向您道句:万分感激。\r\n    祝福您\r\n弗兰克·德尔谨，上\r\n\r\n    米德尔塞克斯 品内东厢 包德米尔路\r\n    1953年9月23日\r\n亲爱的海莲:\r\n    我必须赶紧通知你:今年圣诞节可千万不许再寄礼物来。所有的东西都已经不用配给了，稍好一点的店里头也能买到丝袜。请攒下你的钱，现在最要紧的就是等你治好牙齿之后，能到英国来。只是别挑明年来。因为那时我不在国内。后年我才会回国，这样子你来了才能住在我们家。\r\n    道格写信告诉我，我们候补的家庭宿舍就快有着落了，孩子们和我希望能在圣诞节前就搬去和他团聚。他现在暂时被调到巴林岛(在波斯湾，你可以在地图上找找看)的基地，日子过得愉快惬意。等到家庭宿舍一分配下来，他便会回到哈巴尼亚(在伊拉克)的皇家空军基地和我们会合。一切都很顺利。\r\n    要写信给我哟，即使我暂时“出局”了，妈妈也会把你的信转寄给我的。\r\n思念并祝福您\r\n塞西莉\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1955年9月2日\r\n你们店里一直发行这么棒的目录。却直到现在才寄给我!难道你还好意思跟我说你老是忘了吗?汝等无赖!\r\n    忘了哪个复辟时代的剧作家老爱用“汝等无赖”这个词儿数落别人，我好不容易终于逮到机会可以用它来造个句儿。\r\n    活说回来，这整本目录里头只有这本卡图卢斯我有点儿兴趣——虽然不是“洛布经典文库”版，不过看起来还算差强人意，如果这本书还在的话就寄给我。至于书价六先令两便士，只要你换算成美金，我马上付——凯特和布莱恩搬到郊\r\n区。这下子没有人可以帮我换算了。\r\n    如果你从下个月起每个星期都能携家带眷乖乖上教堂，我会十分感激你。请一起为吉廉、李斯、史奈德、坎帕内拉、罗宾逊、哈吉斯、费里罗、帕德瑞斯、纽坎姆与拉宾—布鲁克林道奇队全体球员祷告，祈祷他们身强体健并获天助神力。要是他们打输了世界大赛，我也不想活了，到时你再后悔就来不及了。\r\n    你们有德·托克维尔的《美洲见闻录》吗?有人把我原有的一本借走了赖着不还。我实在百思不解，再循规蹈矩的人怎么一霸占起书来都是一副理直气壮的气派。\r\n    代我问梅甘好，要是她还在店里头帮忙的话。还有。塞西莉现在怎么样了?从伊拉克回来了吗?\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1955年12月13日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    十分抱歉没能早一点写信给您。不过请您先别忙着动气，因为我生了一场小病，请了几个礼拜病假在家。一回到店里又被一堆待补办的公事绊住。\r\n    关于目录里登载的那本卡图卢斯，收到您的信之前就已经被买走。但我仍为您找到另一个版本寄去——这也是一本附拉丁原文的版本，韵诗部分由理查德·伯顿爵士翻译;内文则为伦纳德·史密瑟翻译，大字印刷，标价三美元七十八\r\n分。装帧并非十分漂亮，但品相尚称良好。我们目前没有德，托克维尔的书，将会为您留意。\r\n    梅甘仍在书店，但是她正打算搬到南非去，大家都还在劝她打俏念头;自从塞西莉随丈夫去了中亚之后，我们就再也没有她的消息，转眼间她竟然已经离职一年了。\r\n    我很乐意为布鲁克林道奇队加油——如果您也愿意为“热刺队”(托特纳姆足球队)打气的话。他们目前在联盟的排名敬陪末座，不过，球季尚未结束，同志仍须努力。希望他们在明年四月前能扭转颓势。\r\n    诺拉、所有人和我在此祝您圣诞快乐、恭贺新禧!\r\n弗兰克·德尔谨上\r\n\r\n     纽约市\r\n    东九十五大街14号\r\n    1956年l月4日\r\n    我现在趴在床脚下写信给你——这本卡图卢斯害我气得滚下来。\r\n译得洁屈赘牙的，真教人伤脑筋!\r\n到目前为止，我只听过一个名字也叫理查德·伯顿的帅哥演员——我曾在几部英国电影里头看过他，我想维持这么点交情也就够了。至于这个翻译者理查德·伯顿，他译得也未免太花俏了吧。\r\n    而可怜的史密瑟先生，他一定害怕他妈妈会读这本书，所以忍痛把那些原本应该活色生香的文章译得道貌岸然。咱们这么着吧，你索性找一本好的通俗拉丁文版的给我。我自己有一本卡塞尔氏拉丁文字典，那些难懂的段落，我自个\r\n儿去整明白得了!\r\n    梅甘头壳坏掉了吗?如果她真的那么厌烦文明世界，怎么不干脆搬去西伯利亚!\r\n    行行行!没问题!我会当热刺队的拉拉队!\r\n    正努力攒钱当中。如果电视台继续赏我饭吃，明年夏天我就可以成行了。我要去亲眼瞧瞧贵书店、圣保罗大教堂、国会殿堂、伦敦塔、柯芬园、老维克剧院. ....还要见见博尔顿老太太。\r\n    附寄十元大钞一张。这本白色软精装的卡图卢斯，居然还配着白色的丝质书签带……弗兰基，你打哪儿找来这些玩意儿啊?\r\n\r\n    伦敦中西二区 查令十字街84号.\r\n    1956年3月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市28\r\n    东九十五大街14号\r\n亲爱的海莲:\r\n    很抱歉又隔了好一段时间才回您的信。因为直到今天，我们才有好消息可以向您报告。原先我还在犹豫，是不是该等到卡图卢斯的书也一并我到时再写这封信比较好。\r\n    我们终于寻获一本版本相当不错的《项狄传》，附有罗布的插图，价格约合美金二元七十五分。同时我们也收到一册柏拉图的《苏格拉底四论》，译者是本杰明·乔伊特，一九〇三年在牛津出版。此书标价一美元，您是否要买?您在敝店\r\n户头内尚有美金一元二十二分的余额，如您两册都购买，仅\r\n需再付给我们美金二元五十三分。\r\n    我们仍翘首期盼您今夏能来，我家的两个女孩儿都离家住校，所以届时橡原巷3号将有两间卧房可任您挑选。很遗憾地向您报告:博尔顿老太太已被送到老人之家，我们都很难过，但毕竟她在那儿能得到比较好的照料。\r\n弗兰克·德尔谨上\r\n\r\n    纽约市\r\n    东九十五大街14号\r\n    1956年6月1日\r\n亲爱的弗兰克:\r\n    布莱恩介绍我读肯尼思·格雷厄姆的《杨柳风》，因此我迷上了谢泼德的插图，决定自己也要买一本。但是!先别忙着寄来，帮我保留到九月，届时我会迁入新址。\r\n    原本住得舒舒服服的旧公寓老命不保喽。上个月所有住户都收到了搬迁通知。现址要盖新大楼。我想也是该为自己觅一间好公寓、买几件好家具的时候了。于是我打铁趁热地到第二大道的一处工地，订了一间连影儿都还没有的客卧两\r\n用预售屋。我现在忙着四处张罗新家具和书架、地毯，几乎把钱都花光了。可是，我一辈子都跟摇摇晃晃的桌椅、到处爬满蟑螂的厨房为伍，现在我想过点儿像样的日子。至于英国之旅，只好等着有人招待我去了。\r\n    与此同时，我的房东怕大家赖着不走，索性把门房给解雇了，害得垃圾和热水都没人打理;还打算要把信箱间、走道灯和我的厨房和卧室间的隔间墙全拆了(本周即将发生);这些烦心的事加上眼睁睁看着道奇队兵败如山倒，真是心酸谁\r\n人知噢……\r\n对了，新地址如下:\r\n纽约州，纽约市21，东七十二大街305号(九月一日启用)\r\n\r\n    伦敦中西二区，查令十字街84号\r\n    1957年5月3日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    预备好大震一惊:您上回信中提到的三本书，一口气全都找到了!而且已经在上周寄去给您，现在应该正在途中。别惊讶我们究竟是怎么办到的，这理应是本店的服务项目之一。账单附在此信中，扣除账户余额之后的书款为五美元。\r\n    几天前，有两位您的朋友到店里来探望我们——很抱歉我现在不记得他们的大名了，是一对很可爱的新婚夫妇。不过，可惜他们行程匆忙，只能在我们这儿稍坐片刻，抽根烟的光景，就必须告辞了。\r\n    今年的美国游客似乎较以往更多了。我曾见到由上百位律师组成的旅游团，每个人西装上都别着一块大大的名牌—上头写着他们的姓名和居住的城镇名。他们好像都玩得颇开心，所以明年您一定也要来一趟。\r\n致上全体的祝福\r\n弗兰克\r\n\r\n    海莲·汉芙 纽约州，纽约市21。东七十二大街305号\r\n    1958年1月10日\r\n嗨，弗兰基——\r\n    快叫诺拉更新通讯薄里的地址，你们家的圣诞卡寄到旧址去啦!她写成了东九十五大街14号。\r\n    忘了是不是说过了，我好喜欢那本《项狄传》，罗布的插图简直美得令人销魂，托比叔叔。地下有知也可含笑瞑目啦。我在信纸背面列了几本“麦克唐纳绣像经典”的书。其中有一本《伊利亚随笔》，有“麦克唐纳绣像经典”版最好，若是有其他不错的版本也行，但价格得“合理”我才会考虑——现在已经找不到“便宜”的东西了，所有的东西都标榜“价格公道”，要不然就是“收费合理”。对街又在盖大楼了，他们立了一块大招牌，上头写着斗大的字:\r\n    “一房一厅或两房一厅任君选择/租金合理”\r\n    房租什么时候“合理”过?其他东西也没好到哪儿去。尽管招牌上盖得天花乱坠，终究只是个广告文案。\r\n    我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天。就像米尼弗·奇维一样，余生也晚。而我也只能学他“干咳两声，自叹一句:奈何老天作弄。然后继续借酒浇愁。\r\nP.S. 喂，柏拉图的《苏格拉底四论》的下落呢？\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1958年3月11日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    在此向您致歉，无法及早回信。我们前一阵子都累垮了，诺拉生了一场病，在医院里待了几个月，而我只好暂时请假在家代理家务。她现在已经复元得差不多了，再过个把礼拜就能出院回家。这段时间真够折腾我们两人的，不过还真得\r\n感谢国民保健制度，我们几乎没花自己一毛钱。\r\n    关于“麦克唐纳绣像经典”，我们偶尔能收购到一些，不\r\n巧现在手头上都没有。而兰姆的《伊利亚随笔》本来也有好几本，但上回旅游旺季时全被买走了。下周我还会出一趟差，届时再尽力为您搜寻。另，柏拉图的书也会为您留意。\r\n    所有人齐祝您有一个愉快的春假。女孩儿们要我转告:她们对您感到很抱歉，粗心将圣诞卡寄到旧地址去了。\r\n弗兰克敬上\r\n\r\n    伦敦北八区 克罗屈庄 哈索米尔路 橡原巷37号\r\n    1958年5月7日\r\n亲爱的海莲:\r\n    谢谢您接连寄来两封问候信。也很感激您的好意，不过说真的，海莲，我们现在什么都不缺。我真希望我们能自已开一家书店，这样我们就能送几本书给您，多少报答您的一片好心。\r\n    随信附上几张全家福照片。本想寄些拍得更好的，但是比较好一点的都被亲戚们要走了。你一定也会发现了吧，希拉和玛莉竟然长得那么像，这实在是一件很奇妙的事。弗兰克说。玛莉现在长得就像是希拉在她这个年纪时的模样。希拉的母亲是威尔士人，而我的老家则是在爱尔兰，所以她们一定得自弗兰克的遗传多些，不过她们都比弗兰克要漂亮多了，当然弗兰克死都不愿承认的!\r\n    要是您晓得我写这样一封信得绞尽多少脑汁，一定也会可怜我的。弗兰克老爱笑我:成天叽哩呱啦地说个不停，怎么一拿起纸笔就不灵光了呢?\r\n    再次感激您的问候和您的来信。\r\n上帝保佑您!\r\n诺拉\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1959年3月18日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我实在不知如何向您启齿。就在上一封信向您报告，为您的朋友找到了《简明牛津辞典》之后隔两天，我才一转身，这本书就被别人买走了。我之所以这么晚才回信，也是因为希望能尽快再另寻一本，可惜至今仍一无所获。辜负了您的\r\n朋友，我感到万分愧疚，当时我实在应该先将书收起来的。\r\n    我们今天会将约翰逊的《莎士比亚评传》寄出，正好敝店内有这本附沃尔特·雷利导言的牛津出版社版。定价为一元五分，您的账户内尚有余额足够支付此书。\r\n    您参与的电视节目将移师到好莱坞，大家都觉得很可惜;夏天又快到了，预料将会有更多美国游客到英国来，然而我们期盼的“那位美国游客”却仍独独教我们望穿秋水。我很能理解您不愿离开纽约搬到南加州的心情。我们也在此祝您好运，并希望您很快可以再找到类似的工作。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙 纽约州，纽约市21.东七十二大街305号\r\n    1959年8月15日\r\n仁兄：\r\n    告诉你吧，我又有活儿可干啦!\r\n    是我凭实力挣来的。我得到一笔CBS提供的编剧奖助金——为数五千美元，供我未来一年将美国历史编写成电视剧本。我头一个要写的题材是英国占领期间的纽约。我一边写一边踟蹰——贵国同胞于一七七六年到一七八三年在这儿的行径那般龌龊不堪，而我偏挑这一段来写，实在有点儿对不起你们现在待我如此温良友善，体谅慈悲。\r\n    你们可有白话版的《坎特伯雷故事集》?实在罪过，我从没读过乔叟。我想学古盎格鲁——撒克逊或〔和)中世纪英语的念头叫一位要拿它做博士论文的朋友给打消了。教授们对她说:关于古盎格鲁——撤克逊，想写什么题目都行。“乍听之下似乎很棒，”我的朋友哭丧着脸告诉我，“可是惟一能找到够多古盎格鲁——萨克逊语的文章，尽是些‘烧教堂、砍人头’的玩意儿。”\r\n    她还跟我讲了一堆贝奥武甫和他的私生子希卫斯(还是卫希斯?)的故事，她说那些也没啥看头。经她冷水一浇，我的兴致也全没了。所以给我白话版的《坎特伯雷故事集)就行了。\r\n代我问候诺拉。\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    1959年9月2日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州.纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    人家都很高兴听到您得到一笔奖金，而且也有工作了。对于您所挑选的任何题材，我们完全不会介意。但我必须向您报告，店里的年轻同事们坦承:若不是您在信中提及，他们浑然不知英国竟然曾经“占领’过美国。\r\n    关于您的疑问，似乎所有最顶尖的研究学者对于将乔叟的文章译成白话文都敬而远之。但是，在一九三四年由希尔改写、朗文出版社出版的《坎特伯雷故事集》，应该是目前惟一能找到的白话版了。我认为还算是不错的书，此书现已绝\r\n版(这自然不在话下)，我将为您找一本书品较好的。\r\n弗兰克谨上\r\n\r\n        一九六〇年开春头一个糟糕透顶的周日夜\r\n我快没辙啦!弗兰基—\r\n    有人送我这么一本书充圣诞礼物。这是一本“巨匠现代文库”。你可曾见过这种版本?装帧比起纽约州议会公报好不到哪儿去，重量倒是略胜一筹。一个爱附庸风雅的家伙，听说我喜欢约翰·多恩。不知打哪儿找来送我。书名是:\r\n     约翰·多恩\r\n    诗全集、文选\r\n        与\r\n    威廉·布来克\r\n       诗全集?\r\n    问号是我自己加上去的。你学问大，能不能告诉我:这两个老小子浑身上下有哪点相同，得这样子凑合在一块儿?——只因他们俩都是英国佬，也都舞文弄墨?我努力读着导论，想找出些蛛丝马迹来。导论洋洋洒洒共分四大章:头两章巨细靡遗地描述了多恩的学术生涯;第三章开头是这么写的(我真不想引述):\r\n    “话说少年布来克，在茵茵夏日草原，于树下邂逅先知以西结，而遭母亲深责痛打。”我百分之百支持他娘。我的意思是:就算没瞻仰到上帝的后背，好歹领教一下圣母的尊容也不赖呀——谁叫他好死不死见到什么鬼先知以西结?\r\n    反正我不喜欢布来克，他还真是让这本书减色不少。看来我势必又得清掉一些书啦，弗兰基，你得伸出援手。\r\n    我现在蜷瘫在安乐椅里，聆听着收音机传出恬淡闲适的古典音乐——大概是科莱利吧... ...慵懒地享受这天下太平的短暂时刻。而这玩意儿——“巨匠现代文库”就站在桌上直盯着我瞧。我心想:好吧，不妨朗读读第十五篇布道词中的那\r\n三个标准段落好了。”多恩的文章合该大声朗读，它简直就像是巴赫的赋格!\r\n    可是你晓不晓得，这个无端之举给我自己惹了什么天大\r\n的麻烦?\r\n    打开“巨匠现代文库”，翻到“布道文第十五篇”——竟被肢解成三个选段。该到第一段末尾，才一发现“耶洗别篇”被删掉了;我赶紧找来另一本多恩的《布道文选编》(罗根·皮尔史·史密斯编)，花了二十来分钟拼命找“布道文第十五篇”……搞了半天才发现:原来，照史密斯的编法，它不叫“证道文第十五篇”，而是“第126节:人必有终”。好不容易找到了，却——同样没有“耶洗别篇”。再搬出《约翰·多恩诗全集暨文选》（典范版）.依然少了那段“耶洗别篇”;转而向《牛津英语诗选》求助，再度穷花二十分钟，因为在这本书里，既不叫“布道文第十五篇”，也不叫“第126节:人必有终”……好歹“耶洗\r\n别篇”总算是找着了，于是开始正襟危坐大声朗读……读到末尾，啥，又不行了——这本书没有第二、第三段!所以咯，我若要从头到尾朗读一篇完整的布道文，得将三巨册全摊开，翻到正确的页面，然后绕着它们来同奔波!\r\n    请好心地告诉我:一本完整收录约翰·多恩布道文的书有那么难找吗?我得花多少钱才买得到?\r\n    我得去睡了。我会做一个可怕的噩梦——披着道袍的妖魔鬼怪，拎着一把把血淋淋的屠刀—上面分别标示着\r\n“段”、‘’节”、“选”、“删”等字眼、霍霍朝我追来……\r\n        祝好\r\nhh\r\n\r\n    伦敦中西二区 查令十宇街84号\r\n    l960年3月5日\r\n    海莲· 汉芙小姐\r\n    美国\r\n    纽约州，纽约市2l\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    我又迟回您的信了，因为我希望能等到有好消息再向您报告。我找到一本萧伯纳与埃伦·特丽的书信集，虽然装帧不特别吸引人，但书品完好。我想这一次我还是尽快将书寄给您为妙、因为这是一本颇抢手的书，要等到下一本出现，恐\r\n怕得花一段时日。扣掉书价的二元六十五分，您的账户余额尚余五角。\r\n    关于收录完整的多恩布道文，恐怕只能在《约翰·多恩全集》中才能找到，而这套书共有四十余册，若书况稍好些的也\r\n必然所费不赀。\r\n    我们希望您后来没耗费太多心神在那本“巨匠现代文库”上，好好地过了一个愉快的圣诞节和新年。\r\n    诺拉也要祝您一切顾遂。\r\n弗兰克谨上\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1960年5月8日\r\n    德·托克维尔阁下在此致意，宣告他已安然渡抵美利坚。他顾盼满志地坐谈时事，而他的臧否都卓然成理。尤其当他论及“律师治国”时，我只有额手、点头的分儿。我参加了一个民主党的小型聚会，前儿天晚上，出席的十四个人里头有十一个是律师;回到家在报纸上读到一堆名人励志故事——有为青年怀抱鸿鸽之志，力争上游，终于晋身一国之尊——史蒂文森、汉弗莱丹、肯尼迪、史塔生.、尼克松……一整票人除了汉弗莱，全是干律师的。\r\n    我附上三块钱钞票，这是一本漂亮的书，实在不能算是“二手书”，连书页都还未裁开哩。我有没向你说过，我终于找到方便好用的裁页刀了?这是一把珍珠柄的水果刀，我母亲留给我一整打这种刀，我挑了一把搁在案头的笔筒里。大概\r\n是交游不力吧，我好像没有机会请来十二位宾客。让他们围坐在餐桌前集体切水果哩。\r\n    笑口常开\r\n\r\n    海莲·汉芙 纽约州，纽约市21，东七十二大街305号\r\n    1961年2月2日\r\n弗兰克?\r\n你还在吗?\r\n    我答应自己在找到工作前不再写信给你的。\r\n    终于卖了一篇稿子给《哈泼杂志》。被这篇稿子折腾了三个星期，他们付给我两百美元稿费。现在他们再度向我约稿，要我将生平事迹写成一本书，他们将“预付”给我一千五百美元!并预估我不用半年就能写得出来，我是无所谓啦，不过房东可又要头疼了。\r\n    所以这阵子暂且不能买书了。去年十月，有人介绍我读圣西蒙公爵路易的书，不过这是一本乏善可陈的节译本。于是我火速赶往学会图书馆，因为这个图书馆不仅全馆开架，还让我爱借多少就借多少，我在那里找到货真价实的路易。现在我已经对他不可自拔。正在读的这一套是六卷版，昨晚读到第六卷一半时，一想到等我把书归还后，家里就连一本路易也没有了，这实在令我难以接受。\r\n    我现在读的是弗朗西斯·阿克赖特的译本，他的译笔甚雅。不过我放手让你挑你信得过的版本。但先别忙着寄来!找到后暂时搁着，先报价，然后再一本一本卖给我。\r\n    希望诺拉和女孩儿们都一切安好—还有你和其他所有认识我的人。\r\n海莲\r\n\r\n    伦敦中西二区 查令十宇街84号.\r\n    1961年2月15日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    您听到一定会开心的:我们正好有一部《圣西蒙公爵回忆录》在店里，译者正是阿克赖特。六卷全帙，装帧精良且品相完好。我们将于今天寄给您，大约一两个礼拜可望寄达您的手中。这套书的价格约合十八元七十五分，但请勿挂怀书\r\n款，您在敝店的付款记录一向良好。\r\n    同时，很高兴又有了您的消息。我们大家都很好，依然盼望您能到英国一游。\r\n    所有人诚心祝福您。\r\n    弗兰克\r\n\r\n    海莲·汉芙纽约州，纽约市21，东七十二大街305号\r\n    1961年3月10日\r\n亲爱的弗兰基:\r\n    附上“千万不能掉了”的十元钞票，它务必平安到达你们手里。倒不是最近发了什么横财，而是路易要我不能留他一个未赎之身。他在法庭上受够了俗不可耐的赖债痞子，可不希望保持了两百七十年的清誉毁于一旦。\r\n    《哈泼杂志》指派给我的编辑昨晚来家里吃饭，和我讨论“我的生平故事”，于是想起你。我们聊到我曾为电视节目“不朽名人堂”改写兰多的《伊索与萝多彼》(我好像告诉过你了?)——兰多笔下那个天真浪漫的萝多彼由萨拉·丘吉尔\r\n饰演。这一集节目在某个周日下午播出。就在播出前两个钟头，我翻着《纽约时报》周日书评版，在第三版中有一篇针对波莉·阿德勒关于娼妓业的新书《野花莫如家花香》的书评，附了一帧照片—一尊希腊的女人头像，图说这么写着:“萝\r\n多彼——希腊最艳名远播的娼妇。”兰多本人对此倒并未详加着墨，许多学者咸称：兰多笔下的萝多彼其实就是让萨福的兄弟千金散尽的众女子的综合体。我既非学者，而且多年前死记强背下的希腊文语尾变化，我也早忘得一干二净啦。\r\n    我对金(我的编辑)谈起这段轶闻，她问我:“兰多到底是何方神圣啊?”我不厌其详地为她细说从头——正当我苦心孤诣一头热滔滔不绝时，她竟不耐烦地插嘴说:“你还真的中毒不轻唉。”\r\n    唉，这下子你该明白了吧，弗兰基，这个世界上了解我的人只剩你一个了。\r\nP.S.金小姐乃中国人是也。\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年10月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    当您收到弗吉尼亚·吴尔夫的《普通读者》。上、下两册时，一定会十分惊喜——它们已在寄往您的路上。如果您还有其他想要的书。我也会倾全力尽速为您服务。\r\n    我们都很好，仍是活蹦乱跳的。我的大女儿希拉(现在已经二十四岁了)两年前突然决定改行当老师，便辞掉原来的秘书工作。跑去念大学了，她还得待在学校一年。看来，要靠儿女供养我们这些老人家颐养天年的日子还有得等哩。\r\n    所有人诚心祝福您。\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1963年11月9日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    许久以前您曾询问过白话版《坎特伯雷故事集》，前几天我们收购了一部，心想您或许仍想买。虽然这并非是收录所有故事的完整版本，但价格十分便宜而且似乎编写还算严谨。我今天会将它寄给您，书价是一元三十五分。如果您读了\r\n之后，还想要更完整的乔叟作品，我会尽力去找。\r\n    弗兰克谨上\r\n\r\n星欺六\r\n    行了!白话版的乔叟真是够了!简直就像兰姆的《莎士比亚故事集》嘛——适合学龄儿童阅读!\r\n    充其量就是故事嘛，我讨厌虚构故事这事儿你是知道的。倒是里头描述一个吃相优雅、食不沾手的修女的那段还算有趣，换了我就不行，还是得动刀舞叉才成。其余内容全引不起我的兴趣，我就是不喜欢故事。如果乔叟能留下日记，里\r\n头规规矩矩记述他在理查三世的皇宫里当差的经过，那才是我真正该读的东西，否则我辛辛苦苦学文言文所为何来。\r\n    最近才刚扔了一本人家送的书——作者描述奥立弗·克伦威尔时代的社会状况。天晓得这个自作聪明的家伙是不是瞎瓣。他又不是那个时代的人，哪晓得那个时代的社会状况?如果我真想了解那个时代的社会状况，大可左阅弥尔顿，右\r\n读沃尔顿。这些货真价实的作品不仅能清楚明白告诉我那个时代的社会状况，还能引领我身历其境。\r\n    君不见沃尔顿尝曰:“若非身临现场、亲眼目睹，何以让读者尽信余言?”\r\n    这段话铿锵有力，深得吾心!我坚决拥护“亲身经历”的作者、作品。\r\n    附上两元支付这本乔臾，这样我在你们的户头里应该还会有六十五分钱的余额——比起我的其他任何一个户头都多。\r\n\r\n    海莲·汉芙 纽约州 纽约市21 东七十二大街305号\r\n    1964年3月30日\r\n亲爱的弗兰克——\r\n    我手头上正在编写给孩子们读的历史教材(已经写到第四本了，惊讶吧?)，忽然想起要帮一位朋友问你:你们有没有一套萧伯纳全集——他坚称书名前冠着“定本xxx”、暗红褐色的布面装帧——希望你有印象。我附上一张清单，上面列的是他已经有的几本。如果你可以为他补齐其他几本，先别全部寄来!他会分批购买，他和我一样——甲级贫户一个。你们可以直接寄给他，地址就写在清单上头。如果你嫌我的字太潦草看不懂的话，那是“第二十二大道”。\r\n    你可有塞西莉或梅甘的消息?\r\n祝一切顺利\r\n海莲\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1964年4月14日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    关于您的朋友想要的《定本萧伯纳作品全集》，原出版社目前仍有新书发行。红褐色布面精装——正如您所描述的。我想全套共有三十册。旧书反而不常见到，如果您的朋友不介意购买新书，我们可以安排对他方便的方式，每个月寄给\r\n他三或四册。\r\n    我们这几年来都没有塞西莉·法尔的消息，至于梅甘·韦尔斯，她在南非没多久就待不下去了，回国后曾到书店来看大家，给了大伙儿一个发“早跟你说了偏不听”牢骚的机会。不过她后来又搬去澳洲碰运气了。几年前曾收到她寄到书店的圣诞卡，最近则又断了音讯。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1965年10月4日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    很高兴再度收到您的来信。是的，我们都还健在如昔——老态益发龙钟，工作更加忙碌，口袋却没能加倍饱满。\r\n    我们购人了一本E.M.德拉菲尔德的《村姑日记》，一九四二年的麦克米兰版，书品很好，定价两美元。今天我会将书及账单为您寄去。\r\n    我们度过了一个“青春洋溢”的夏天——今年的游客比往年更多，大批年轻人全涌向卡纳比街朝圣。我们只能老远隔着安全距离打量着他们。老实说，我还蛮喜欢披头士的，只希望他们的歌迷们不要放声尖叫。\r\n    诺拉和女孩儿们同我一起寄上祝福——\r\n弗兰克\r\n\r\n    海莲·汉芙  纽约州，纽约市21，东七十二大街305号\r\n    1968年9月30日\r\n我们都仍健在，可不是吗……\r\n    我为儿童编写美国历史读物已经长达四五年，得将这玩意儿告一段落了——为了写这些书，我自己还买了一大堆关于美国历史的书，全都是长相丑、装订差的美国书。我想，大概没有哪个循规蹈矩的英国人会在家里收藏詹姆斯·麦迪\r\n逊的制宪会议记录。或是T.杰弗逊写给J.亚当斯的书信吧。\r\n    你当上祖父没?告诉希拉和玛莉，她们的小孩将可以免费获赠有作者签名的《少年历史读本》，这样子应该能让她们比较愿意安定下来增产报国了吧。\r\n    我挑了一个细雨霏霏的星期天介绍一位年轻朋友读《傲慢与偏见》，她现在果然已经疯狂迷恋简·奥斯汀了。她的生日就在万圣节前后，你能帮我找几本奥斯汀的书当礼物吗?如果是一整套的话，先让我知道价钱，万一太贵，我会叫她的\r\n先生分摊，我和他各送半套。\r\n    祝诺拉和你周围所有人好。\r\n海莲\r\n\r\n    伦敦中西二区 查令十事街84号\r\n    1968年10月16日\r\n    海莲·汉芙小姐\r\n    美国\r\n    纽约州，纽约市X\r\n    东七十二大街305号\r\n亲爱的海莲:\r\n    是的，我们依然健在，手脚也还勉强灵光。这个夏天真是把大家忙坏了。从美国、法国、北欧和其他各国来的大批观光客，几乎把我们比较好的皮面精装书全都搜刮一空。由于书源短缺。加上书价节节攀升，恐怕很难赶在您的朋友生日前找到任何奥斯汀的书，我们会设法在圣诞节之前为您办妥这件事。\r\n    诺拉和女孩儿们都很好。希拉已经开始执起教鞭;玛莉则和一位人品不错的小伙子订了亲—不过一年半载的还结不成婚，因为双方的经济条件都不太宽裕。所以。诺拉一心想当个福福泰泰的祖母，这希望恐怕愈来愈渺茫呢。\r\n    想念您\r\n弗兰克\r\n\r\n    伦敦中西二区 查令十字街84号\r\n    1969年l月8日\r\n    H.汉芙小姐\r\n    美国\r\n    纽约州，纽约市21\r\n    东七十二大街Los号\r\n敬爱的小姐:\r\n    我于近日整理公文档案时，偶然发现一封您于去年九月三十日寄给德尔先生的信。我在此非常遗憾地向您报告:德尔先生甫于上个礼拜天(十二月二十二日)去世了。丧礼则已在上周三(元月一日)举行。\r\n    德尔先生于十二月十五日因罹患急性盲肠炎被紧急送医，虽然立即施行手术，但他仍不幸因病情扩散，导致腹膜炎并发而于七日后不治。\r\n    德尔先生在本书店服务已超过四十年，加上马克斯先生也刚辞世未久，科恩先生对于这个不幸的事件自然万分悲坳。\r\n    您是否仍须本店为您寻找简·奥斯汀的书?\r\n         \r\n马克斯与科恩书店\r\n              秘书 琼·托德敬上\r\n\r\n(未署明日期，邮戳日期为1969年1月9日。无寄信地址〕\r\n\r\n亲爱的海莲:\r\n    感谢您寄来的慰问信，我完全不认为那冒犯了我。我真希望您在弗兰克在世时能够与他见面，并亲自结识他本人。我原先只知道他是一个处事严谨同时也很幽默的人;现在还了解了他在待人处事上更是一位谦冲的君子。我收到许许多多来\r\n自各地的信，都异口同声地赞杨他对古书业的贡献;许多人还说他是如何饱富学识而又不吝予与其他人分享……如果您想要看这些信，我可以将它们寄给您。\r\n    不瞒您说，我过去一直对您心存妒忌，因为弗兰克生前如此爱读您的来信，而你们俩似乎有许多共通点;我也羡慕您能写出那么好的信。弗兰克和我却是两个极端不同的人，他总温和和有耐性;而因为我的爱尔兰出身，我的脾气总是又倔又拗。生命就是选么爱捉弄人，他从前总是试图教导我书中的知识……我现在好想念他。\r\n    孩子们都很懂事、我为此深感欣慰。像我这样要一辈子孤寂以终的人想必大有人在吧。\r\n    希望您能原谅我的字迹潦草。\r\n                 祝福您\r\n                 诺拉\r\n    我盼望有一天您还是能来造访我们，两个孩子都很想见见您。\r\n    1969年4月11日\r\n\r\n亲爱的凯瑟琳——\r\n    我正在整理我的书架，现在抽空蹲在书堆中写信给你，祝你们一路顺风。我希望你和布莱恩在伦敦能玩得尽兴。布莱恩在电话中对我说:“如果你手头宽裕些就好了，这样子你就可以和我们一道去了。”我一听他这么说，眼泪差点儿要夺\r\n眶而出。\r\n    大概因为我长久以来就渴望能踏上那片土地……我曾经只为了瞧伦敦的街景而看了许多英国电影。记得好多年前有个朋友曾经说:人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我:“就在那儿!”\r\n    或许是吧。就算那儿没有，环顾我的四周……我很笃定:它们已在此驻足。\r\n    卖这些好书给我的那个好心人已在数月前去世了，书店老板马克斯先生也已不在人间。但是，书店还在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……\r\n海莲\r\n\r\n尾声1969年10月\r\n\r\n    伦敦北十一区 温顿道\r\n    1969年10月\r\n亲爱的海莲:\r\n    这是德尔家族第二号通讯员首次发言!\r\n    首先，我要对我们长时间的静默向您致歉。请相信我，其实我们心中一直惦记着您。只是不知如何将这样的意念用文辞表达。直到今天我们又收到您的来信，我们都感到万分惭愧，并决定应该立刻动笔回信给您。\r\n    我们很高兴得知您的出版计划，也同意并很愿意提供这些信件供您作为出书之用。\r\n    我们现在搬到可爱的新家，常常会想着:如果父亲依然健在，一定也会喜欢这儿。\r\n    再多的悲坳亦无济于事。虽然父亲生前从未拥有财富、权势，但他始终是一个快乐自得又具有丰富内涵的人，我们应该以拥有一位这样的亲人而深感欣慰。\r\n    也许只是为了冲淡愁思，我们都尽量让自己忙碌着。玛莉白天在大学图书馆辛勤工作，晚上则和朋友开车出游散心，深夜方归;我除了正常的教职外，还兼修一个学位;妈妈则整天忙上忙下，一刻也不教自己闲下来!所以，恐怕大家都无法好好地回信——不过，当然我们会很高兴能继续收到您的来信。无论如何，只要有空，我们还是会努力回信的，并期盼能再有您的消息。\r\n    希拉诚挚敬上\r\n\r\n\r\n**************\r\n*附录\r\n***************\r\n\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。						\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(1)\r\n---------------\r\n\r\n\r\n　　唐诺\r\n　　乍读这本书稿时，我一直努力在回想，查令十字街84号这家小书店究竟是长什么个模样(我坚信写书的海莲·汉芙不是胡诌的，在现实世界中必然有这么一家“坚实”存在的书店)，我一定不止一次从这家书店门口走过，甚至进去过，还取下架上的书翻阅过——《查令十字街84号》书中，通过一封1951年9月10日海莲·汉芙友人玛克辛的书店寻访后的信，我们看到它是“一间活脱从狄更斯书里头蹦出来的可爱铺子”，店门口陈列了几架书(一定是较廉价的)，店内则放眼全是直抵天花板的老橡木书架，扑鼻而来全是古书的气味，那是“混杂着霉味儿、长年积尘的气息，加上墙壁、地板散发的木头香……”，当然，还有一位五十开外年纪、以老英国腔老英国礼仪淡淡招呼你的男士(称店员好像不礼貌也不适切)。\r\n　　但这不也就是半世纪之后今天、查令十字街上一堆老书店的依然长相吗？——如此悬念，让我再次鼓起余勇、生出远志，很想再去查令十字街仔细查看一次，对一个有抽烟习性又加上轻微幽闭恐惧毛病如我者，这长达二十小时的飞行之旅，我自以为是个很大的冲动而且很英勇的企图不是吗？\r\n　　然而，不真的只是84号书店的诱引，我真正想说的是，如果说从事出版工作的人，或仅仅只是喜爱书籍、乐于阅读的人得有一处圣地，正如同麦加城之于穆斯林那样，短短人生说什么也都得想法子至少去它个一次，那我个人以为必定就是查令十字街，英国伦敦这道无与伦比的老书街，全世界书籍暨阅读地图最熠熠发光的一处所在，舍此不应该有第二个答案。\r\n　　至少，本书的译者一定会支持我的武断——陈建铭，就我个人的认识，正是书籍阅读世界的此道中人。一般，社会对他的粗浅身分辨识，是个优美、老英国典雅风味却内向不擅长议价的绝佳书版美术设计者，但这本《查令十字街84号》充分暴露了他的原形，他跳出来翻译了此书，而且还在没跟任何出版社联系且尚未跟国外购买版权的情况下就先译出了全书(因此，陈建铭其实正是本书的选书人)，以他对出版作业程序的理解，不可能不晓得其后只要一个环节没配合上，所有的心血当场成为白工，但安静有条理的陈建铭就可以因为查令十字街忽然疯狂起来。\r\n　　这是我熟悉、喜欢、也经常心生感激的疯子，在书籍和阅读的世界中，他们人数不多但代代有人，是这些人的持续存在，且持续进行他们一己“哈萨克人式的小小游击战”(借用赫尔岑的自况之言)，才让强大到几近无坚不摧的市场法则，始终无法放心地遂行其zhuanzhi统治，从而让书籍和阅读的世界，如汉娜·鄂兰谈本雅明时说的，总是在最边缘最异质的人身上，才得到自身最清晰的印记。\r\n　　在与不在的书街\r\n　　《查令十字街84号》这部美好的书，系以1949年至1969年长达廿年流光，往复于美国纽约和这家小书店的来往信函交织而成——住纽约的女剧作家买书，任职“马克斯与科恩书店”的经理弗兰克·德尔负责寻书寄书，原本是再乏味不过的商业往来，但很快的，书籍击败了商业，如约翰·房龙说“一个马槽击败了一个帝国”(当然，在书籍堆栈的基础之上，一开始是汉芙以她莽撞如火的白羊座人热情凿开缺口，尤其她不断寄送鸡蛋、火腿等食物包裹给彼时因战争物资短缺、仰赖配给和黑市的可怜英国人)，人的情感、心思乃至于咫尺天涯的友谊开始自由流窜漫溢开来。查令十字街那头，他们全体职员陆续加入(共六名)，然后是德尔自己的家人(妻子诺拉和两个女儿)，再来还有邻居的刺绣老太太玛丽·伯尔顿；至于纽约这边，则先后有舞台剧女演员玛克辛、友人金妮和埃德替代汉芙实地造访“她的书店”，惟遗憾且稍稍戏剧性的是，反倒汉芙本人终究没能在一切落幕之前踩上英国，实践她念念不忘的查令十字街之旅。全书结束于1969年10月德尔大女儿替代父亲的一封回信，德尔本人已于1968年底腹膜炎病逝。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(2)\r\n---------------\r\n\r\n\r\n　　一样产自英国的了不起小说家格林，在他的《哈瓦那特派员》中这么说：“人口研究报告可以印出各种统计数值、计算城市人口，借以描绘一个城市，但对城里的每个人而言，一个城市不过是几条巷道、几间房子和几个人的组合。没有了这些，一个城市如同陨落，只剩下悲凉的记忆。”——是的，1969年之后，对海莲·汉芙来说，这家书店、这道书街已不可能再一样了，如同陨落，只因为“卖这些好书给我的好心人已在数月前去世了，书店老板马克斯先生也已不在人间”，这本《查令十字街84号》于是是一本哀悼伤逝的书，纪念人心在二十年书籍时光中的一场奇遇。\r\n　　但海莲·汉芙把这一场写成书，这一切便不容易再失去一次了，甚至自此比她自身的生命有了更坚强抵御时间冲刷的力量——人类发明了文字，懂得写成并印制成书籍，我们便不再徒然无策地只受时间的摆弄宰制，我们甚至可以局部地、甚富意义地击败时间。\r\n　　书籍，确实是人类所成功拥有最好的记忆存留形式，记忆从此可置放于我们的身体之外，不随我们肉身朽坏。\r\n　　也因此，那家书店，当然更重要是用一本一本书铺起来的查令十字街便不会因这场人的奇遇戛然中止而跟着消失，事实上，它还会因多纳入海莲·汉芙的美好记忆而更添一分光晕色泽，就像它从不间断纳入所有思维者、纪念者、张望者、梦想者的书写一般，所以哀伤的汉芙仍能鼓起余勇地说：“但是，书店还是在那儿，你们若恰好路经查令十字街84号，代我献上一吻，我亏欠它良多……”\r\n　　这是不会错的，今天，包括我个人在内，很多人都可以证实，查令十字街的确还在那儿，我是又过了十多年之后的八十年代、九十年代去的，即便84号的“马克斯与科恩书店”很遗憾如书末注释说的，没再撑下去，而成为“柯芬园唱片行”，但查令十字街的确还好好在那里。\r\n　　一道时间大河\r\n　　查令十字街，这个十字不是指十字路口，而是十字架的意思，事实上它是一道长约一公里许的蜿蜒市街，南端直抵泰晤士河，这里有最漂亮的查令十字街车站，如一个美丽的句点，往北路经国家艺廊，穿过苏活区和唐人街，旁及柯芬园，至牛津街为止，再往下走就成了托登罕路，很快就可看到著名的大英博物馆(大英博物馆一带又是另一个书店聚集处，但这里以精印的彩色大版本艺术书为主体)。\r\n　　老英国老伦敦遍地是好东西，这是老帝国长而辉煌的昔日一样样堆栈下来的，如书中汉芙说的(类似的话她说了不止一回)：“记得好多年前有个朋友曾经说：人们到了英国，总能瞧见他们想看的。我说，我要去追寻英国文学，他告诉我：‘就在那儿！’”\r\n　　然而，和老英国其他如夕晖晚照荣光事物大大不同之处在于，查令十字街不是遗迹不是封存保护以待观光客拍照存念的古物，它源远流长，但它却是active，现役的，当下的，就在我们谈话这会儿仍孜孜勤勤劳动之中，我们可同时缅怀它并同时使用它，既是历史从来的又是此时此刻的，这样一种奇特的时间完整感受，仔细想起来，不正正好就是书籍这一人类最了不起发明成就的原来本质吗？我们之所以丧失了如此感受，可能是因为我们持续除魅的现实世界已成功一并驱除了时间，截去了过去未来，成为一种稍纵即逝却又驻留不去的所谓“永恒当下”——有生物学者告诉我们，人类而外的其他动物和时间的关系极可能只有这样，永恒的当下，记忆湮渺只留模糊的鬼影子，从而也就产生不来向前的有意义瞻望，只剩如此窄迫不容发的时间隙缝，于是很难容受得了人独有的持续思维和精致感受，只有不占时间的本能反射还能有效运作，这其实就是返祖。\r\n　　更正确地说，查令十字街的时间景观，指的不单单是它的经历、出身以及悠悠存在的岁月，而是更重要的，就算你不晓得它的历史沿革和昔日荣光，你仍可以在乍乍相见那一刻就清晰捕捉到的实时景观，由它林立的各个书店和店中各自藏书所自然构成——查令十字街的书店几乎每一家一个样，大小、陈列布置、书类书种、价格以及书店整体氛围所透出的难以言喻的鉴赏力、美学和心事。当然，书店又大体参差为一般新书书店和二手古书店的分别，拉开了时间的幅员，但其实就算卖新书的一般书店，彼此差异也是大的，各自收容着出版时日极不一致的各色书籍，呈现出极丰硕极细致的各自时间层次。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(3)\r\n---------------\r\n\r\n\r\n　　不太夸张地说，这于是成了最像时间大河的一条街，更像人类智识思维的完整化石层，你可以而且势必得一家一家地进出，行为上像进陈列室而不是卖场。\r\n　　相对来说，我们在台湾所谓的“逛书店”，便很难不是只让自我感觉良好的溢美之辞。一方面，进单一一家书店比较接近纯商业行为的“购买”，而不是带着本雅明式游手好闲意味的“逛”，一本书你在这家买不到，大概另一家也就休想；另一方面，“逛”，应该是不完全预设标的物的，你期待且预留着惊喜、发现、不期而遇的空间，但台湾既没二手书店，一般书店的书籍进退作业又积极，两三个月前出版的书，很可能和两三千年前的出土文物一样不好找。\r\n　　连书店及其图书景观都是永恒当下的，在我们台湾。\r\n　　永恒当下的灾难\r\n　　海莲·汉芙在书中说到过她看书买书的守则之一，对我们毋宁是极陌生到足以吓人一跳的，她正色告诉德尔，她绝不买一本没读过的书，那不是跟买衣服没试穿过一样冒失吗？当然我们没必要激烈如这位可敬的白羊座女士，但这其实是很有意思的话，说明旧书(广义的，不单指的珍版珍藏之书)的购买、收存和再阅读，不仅仅只是屯积居奇的讨人厌行为或附庸风雅的恶心行为而已。这根源于书籍的不易理解，不易完整掌握的恒定本质，尤其是愈好、内容愈丰硕、创见之路走得愈远的书，往往远远超过我们当下的知识准备、道德准备和情感准备，我们于是需要一段或长或短的回身空间与它相处。好书像真爱，可能一见钟情，但死生契阔与子成说，执子之手与子偕老的杳远理解和同情却总需要悠悠岁月。\r\n　　因此，从阅读的需求面来说，一本书的再阅读不仅仅只是可能，而是必要，你不能希冀自己一眼就洞穿它，而是你十五岁看，二十岁看，四十岁五十岁看，它都会因着你不同的询问、关注和困惑，开放给你不一样的东西，说真的，我努力回想，还想不出哪本我真心喜欢的书没有而且不需要再再重读的(你甚至深深记得其中片段，意思是你在记忆中持续重读)；也因此，从书籍取得的供给面来看，我们就应该聪明点给书籍多一点时间、给我们自己多一点机会，历史经验一再告诉我们，极多开创力十足且意义重大的书，我们当下的社会并没那个能力一眼就认得出来，不信的人可去翻阅大名鼎鼎的纽约时报历来书评(坊间有其结集成书的译本)，百年来，日后证明的经典著作，他们漏失掉的比他们慧眼捕捉到的何止十倍百倍，而少数捕捉到的书中又有诸如塞林格的《麦田里的守望者》或钱德勒的《大眠》被修理得一无是处(理由是脏话太多云云)。一个社会，若意图在两星期到一个月内就决定一本书的好坏去留，要求书籍打它不擅长的单败淘汰赛，这个社会不仅自大愚蠢，而且可悲的一步步向着灾难走去。\r\n　　一种只剩永恒当下的可悲灾难。\r\n　　部分远大于全体\r\n　　便是这个永恒当下的灾难启示，让我们得以在书籍暨阅读的世界中，推翻一项亘古的数学原理——这是柏拉图最爱引用的，全体永远大于部分，但我们晓得事实并不尽然，短短的一道查令十字街，的确只是我们居住世界的一个小小部分，但很多时候，我们却觉得查令十字街远比我们一整个世界还大，大太多了。\r\n　　最是在什么时候，我们会生出如此诡异的感觉呢？特别当我们满心迫切的困惑不能解之时。我们很容易在一本一本书中再再惊异到，原来我们所在的现实世界，相较于既有的书籍世界，懂得的事这么少，瞻望的视野这么窄，思维的续航能力这么差，人心又是这么封闭懒怠，诸多持续折磨我们的难题，包括公领域的和私领域的，不仅有人经历过受苦过认真思索过，甚至还把经验和睿智细腻的解答好好封存在书中。\r\n　　从形态上来看，我们眼前的世界往往只有当下这薄薄的一层，而查令十字街通过书籍所揭示的世界图像，却是无尽的时间层次叠合而成的，包括我们因失忆而遗失乃至于根本不知有过的无尽过去，以及我们无力也无意瞻望的无尽未来。\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(4)\r\n---------------\r\n\r\n\r\n　　看看穆勒的《论自由》和《论代议政治》，这是足足一百五十年前就有的书，今天我们对自由社会和民主政治的建构、挫折、一再摔落的陷阱以及自以为聪明的恶意操弄，不好端端都写在书里头吗？\r\n　　看看李嘉图的《政治经济学原理》，这是两百年前的书，书中再清晰不过所揭示的经济学最基本道理和必要提醒，我们今天，尤其手握财经权力的决策者，不还在日日持续犯错吗？\r\n　　或者看看本雅明的《发达资本主义时代的抒情诗人》，这又是超过半个世纪以前的书，而今天，我们的大台北市才刚刚换好新的人行步道、才刚刚开始学习在城市走路并试图开始理解这个城市不是吗？\r\n　　还是我们要问宪法的问题(内阁制、总统制、双首长制、还有神秘的塞内加尔制)？要问民族主义和民粹主义的问题？问生态环保或仅仅只是整治一条基隆河的问题？问男女平权？问劳工和失业？问选举制度和选区规划？问媒体角色和自律他律？或更大哉问的问整体教育和社会价值暨道德危机等等问题？\r\n　　是的，如海莲·汉芙说的，书店还是在那儿。\r\n　　全世界最便宜的东西\r\n　　而查令十字街不仅比我们眼前的世界大，事实上，它做得更好——查令十字街不仅有着丰硕的时间层次，还呈现具体的空间分割；它是一道川流不息的时间之街，更是一个个书店、隔间、单一书籍所围拥成的自在小世界，让闲步其中的人柳暗花明。\r\n　　我猜，这一部分原因有历史的偶然渗入作用而成，比方说，老式的、动辄百年以上的老伦敦建筑物，厚实坚强的石墙风雨不动的制限了商业流窜的、拆毁一切夷平一切的侵略性格，因此，小书店各自盛开如繁花，即便是大型的综合性书店，内部格局也曲折回旋，每一区块往往是封闭的、隔绝的，自成洞天，毋宁更像书籍层层架起的读书阅览小房间而非卖场；而且，美国的霸权接收，让英文不随老帝国的坠落而衰败，仍是今天的“准世界语”，仍是普世书籍出版活动的总源头和荟萃之地，因此，你一旋身，才两步路便由持续挣扎的东欧世界出来，却马上误入古怪拼字，但极可能正是人类最远古家乡非洲黝暗世界，如同安博托·艾柯在《玫瑰之名》书中最(禁止)的惊心动魄一幕——第七天，威廉修士和见习僧艾森终于进入了大迷宫图书馆中一切秘密埋藏所在的非洲之末。\r\n　　一个无垠无边的智识世界，却是由一个个小洞窟构成的。\r\n　　我尤其喜欢查令十字街的一个个如此洞窟，一方面，这有可能正是人类亘古的记忆存留，是某种乡愁，像每一代小孩都有寻找洞窟打造洞窟置身洞窟的冲动，有某种安适安全之感，而读书，从阅读、思索到着迷，最根柢处，本来就是宛如置身一己洞窟的孤独活动；另一方面，我总时时想到列维—施特劳斯的话，这些自成天地般洞窟的存在，提供我们逃避的机会，逃避什么样的压迫呢？逃避一种列维—施特劳斯指称的大众化现象，意即一种愈发一致的、无趣的、再没性格可言的普世性可怖压逼(正是社会永恒当下的呈现)，而这些动人的洞窟，正像《爱丽丝漫游仙境》的树洞，你穿过它，便掉落到一个完全异质、完全始料未及的世界里去。\r\n　　于是，我遂也时时忧虑我们最终仍会失去属于我们这一代的查令十字街，如同汉芙早已失去她的查令十字街一般，我们的杞忧，一方面是现实中断续传来的不利信息(如商业的腐蚀性只是被减缓，并没真正被阻止)，更是人面对足够美好事物的很自然的神经质反应，你深知万事万物持续流变，珍爱的东西尤其不可能一直存留，如朝霞，如春花，如爱情。\r\n　　但你可以买它——当然不是整条查令十字街，而是它真正赖以存在、赖以得着意义的书籍，市街从不是有效抵御时间风蚀的形式，书籍才是，就像汉芙所说：“或许是吧，就算那儿没有(意指英国和查令十字街)，环顾我的四周(意指她从查令十字街买到的书)……我很笃定，它们已在此驻足。”\r\n\r\n\r\n\r\n---------------\r\n有这一道街，它比整个世界还要大(5)\r\n---------------\r\n\r\n\r\n　　从事出版已超过半辈子之久，我个人仍始终有个问题得不到满意的答案：我始终不真正明白人们为什么不买书？这不是全世界最便宜的一样东西吗？一个人类所曾拥有过最聪明最认真最富想像力最伟大的心灵，你不是极可能只用买一件看不上眼衣服的三千台币就可买下他奇迹一生所有吗(以一名作家，一生十本书，一本书三百元计，更何况这么买通常有折扣)？你不是用吃一顿平价午餐的支付，就可得到一个美好的洞窟、以及一个由此联通的完整世界吗？\r\n　　汉芙显然是同我一国的，她付钱买书，但自掏腰包寄食物还托朋友送丝袜，却仍觉得自己占便宜，在1952年12月12日，她说的是：“我打心里头认为这实在是一桩挺不划算的圣诞礼物交换。我寄给你们的东西，你们顶多一个星期就吃光抹净，根本休想指望还能留着过年；而你们送给我的礼物，却能和我朝夕相处、至死方休；我甚至还能将它遗爱人间而含笑以终。”而在1969年4月11日的最终决算，她仍得到“我亏欠它良多”的结论。\r\n　　美国当前最好的侦探小说家，同样也住纽约的劳伦斯·卜洛克也如此想，他在《麦田贼手》一书，通过一名仗义小偷之口对一名小说家(即塞林格)说：“这个人，写了这么一本书，改变了我们整整一代人，我总觉得我欠他点什么。”所以——买下它，我指的是书，好好读它，在读书时日里若省下花费，存起来找机会去一趟查令十字街，趁它还在，如果你真的成行并顺利到那儿，请代我们献上一吻，我们都亏欠它良多……\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(1)\r\n---------------\r\n\r\n\r\n　　张立宪\r\n　　“你们若恰好路经查令十字街84号，请代我献上一吻，我亏欠她良多……”\r\n　　生活中发生了一桩小小的笑话。一位朋友在英国，按照我的介绍，某一天逛伦敦的书店一条街——查令十字街，并到84号朝圣。奈何那里已经改为一家酒吧，只是在门边挂个铜牌，上书“查令十字街84号，马克斯与科恩书店的旧址，因为海莲·汉芙的书而闻名天下”。收银小姐看着她笑问：“为那本书来的吧？”她只好狠狠地点一瓶红酒，并为我买了期待已久的书《查令十字街84号》，然后寄往北京，还兴冲冲地先用数码相机将书拍了照mail过来，让我预热一下。结果，不幸的很，这本书在大英帝国的邮政系统兜了一个圈子，又回到了朋友的手上——她将收件人与寄件人的位置弄颠倒了。\r\n　　其实以我的英文修养，肯定啃不动原版书，但对于这本书，还是希望能保留一本，因为它被誉为“爱书人的《圣经》”。\r\n　　这本书讲的就是一个纽约爱书人通过书信往来在伦敦一家古旧书店(书名即是这家书店的地址)淘书并建立深厚友谊的故事。来往的书信被她汇集成此书，成为读书人的掌上明珠。\r\n　　得到这本书并非易事。好在，根据原著拍摄的同名电影已经有DVD出售，万幸的是，我买到了，并且看了。该片由美国哥伦比亚公司1986年拍就，片长100分钟，担纲主演的是演技派演员安妮·班克劳夫特和安东尼·霍普金斯，拍得真是无可挑剔。\r\n　　之所以说是“万幸”，是因为影碟被碟商译成了一个耸人听闻的动作片名字——《迷阵血影》。我敢担保，即使你看到这张影碟，最大的可能也是与其失之交臂。这个异想天开的译名惹恼了一个台湾人，他叫陈建铭，是诚品书店古书区的员工，在没与任何出版社联系出版且未跟国外购买版权的情况下，就先译出了全书。按照他的说法，这般牛劲发作，就是因为《迷阵血影》这个名字，而且影片的对白字幕也是惨不忍睹，“我翻译这本书，多少也想为它赎点儿罪罢”。\r\n　　恕我饶舌，复述一遍这个再简单不过的故事。\r\n　　穷困的女作家海莲受不了纽约昂贵庸俗的古旧书店，便按照《星期六文学评论》上的地址，给位于伦敦查令十字街84号的马克斯与科恩书店(后来被海莲派去伦敦侦察的好友形容成一家“活脱从狄更斯书里头蹦出来的可爱铺子”)写了一封信，求购一些绝版图书。这一天是1949年10月5日。\r\n　　很快，回信和她要的书就来了，那些书令海莲的书架相形见绌。双方的信任和欣喜很快达成，除了海莲有一点点麻烦，她是个连付账和找零都搞不清楚的女人，更不用说将英镑换算成美元了。马克斯与科恩书店的经理弗兰克除了满足她购书的要求外，还得给她准备英镑和美元两种发票。\r\n　　温暖的相知借助娓娓道来的书信，很快就俘获了远隔重洋的海莲和弗兰克。\r\n　　五十年代初期的英国百废待兴，物资实行配给制。海莲就从美国给书店的店员们寄来火腿、鸡蛋和香肠，让他们吃到很久没有见过的“完整而大块”的肉。而弗兰克并不是不知感恩的人，他开始在英国各地奔波，出入豪宅，为存货不多的书店添置新品，踏破铁鞋，为她寻觅难得一见的珍本。\r\n　　日子一天天地过去，书信成为他们平静流淌的生活中无时不在的旁白。\r\n　　海莲不是没想过去伦敦看看书店看看弗兰克。她终于有了自己的积蓄，而英女王的登基又使得赴英的费用打了折。眼看可以成行，但她的牙逼着她留在了纽约。她只好给弗兰克写信：“我陪着我的牙，而牙医却在度蜜月，他的结婚费用是我出的……”\r\n　　弗兰克只好为她和刚刚登基的伊丽莎白女王祝福。\r\n　　书照买，信照写。\r\n　　到了这一天，海莲的信三个月后才接到回音，她被告知：弗兰克于1968年12月22日病逝。\r\n　　海莲马上赶到查令十字街84号。走进即将被拆迁的马克斯与科恩书店时，距离她第一次给这里写信，已经过去了二十年。\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(2)\r\n---------------\r\n\r\n\r\n　　她笑着对空荡荡的书店说：“我来了，弗兰克，我终于来了。”\r\n　　通过胶片来诉说图书的故事，总显得不太解气。不过，看平静的生活围绕着他们的讨书买书谈书一幕幕展开，仿佛将惟一彩色的道具放在黑白环境里，使原本素朴的书本也显得绚丽，一如荒漠甘泉。\r\n　　事关读书的故事总是令人解颐。\r\n　　海莲对一本拉丁文版《圣经》极为不满，在给弗兰克的信中说翻译简直是想毁掉这本世界上“最美的散文”，建议拿正宗的拉丁文版对照来读，才不致暴殄天物，并出卖了她七大姑八大姨的说法加以佐证。可爱的女人，总是将自己试图保守的机密在另一种心情下泄露无遗。\r\n　　弗兰克看到纽曼的《大学论》，写信问海莲：“有兴趣买初版的吗？”同时叮嘱店员为她留下来。镜头马上从伦敦切到纽约，海莲对着空气质问：“你有初版的《大学论》，只要六美元，居然还傻傻地问我‘你要吗’？”“亲爱的弗兰克：是的，我要。我本不在乎是不是初版，可这本书的初版！……”\r\n　　等她收到这本有百年历史的初版书后，写信对他说：“我占有它有一种罪恶感，那么漂亮的封面和烫金，它理应属于某幢英国乡间的木造宅邸才对。”\r\n　　这个莽撞如火的白羊座女人颇有豪侠之风，不但体现在她为书店员工邮寄生活用品的慷慨上，甚至她的性格也凸显在信纸上：“我一路活来，眼看着英语一点一滴被摧残蹂躏却又无力可回天，就像米尼弗·奇维一样，余生也晚。而我也只能学他‘干咳两声，自叹一句：奈何老天作弄’，然后继续借酒浇愁。”\r\n　　“这是个堕落颓废的年代，他们居然把漂亮的旧书页撕下来当包装纸。上面描述的是一场战役的中段，但我已经看不出是哪场战役了……”海莲在信中抱怨，又该可怜的弗兰克忙活了。\r\n　　在一家豪宅，弗兰克见到了帮海莲遍寻不着的伊丽莎白一世时期的情诗集，以书店全体员工的名义寄给她。“你们相信它是在我生日那天寄达的吗？这是我拥有的第一本镶金边的书。可惜你们太客气了，将字句写在卡片上，而非扉页上。你们全都是爱书人，惟恐会减损书的价值，其实你们已经为书的主人甚至书未来的主人提升了它的价值。”海莲在回信中兴奋地絮叨。\r\n　　……\r\n　　1969年1月8日，海莲收到马克斯与科恩书店通报弗兰克的死讯，那封信的最后一句是：\r\n　　“你还要我们寻找你所订的书吗？”\r\n　　该说说海莲和弗兰克之间的事儿了。\r\n　　海莲的爱人死于二战，她终身未嫁。弗兰克则有妻子和两个女儿。一大两小三个女人会收到海莲寄自美国的尼龙袜，弗兰克的太太也会和海莲雅谑几句：“弗兰克给你的照片够难看的，但他狡辩说本人比照片帅多了，我们就让他臭美去吧。”\r\n　　一切看来都那么正常，正常到两人相识二十年却缘悭一面，正常到两人通信数百封而未涉一个“爱”字。\r\n　　但是，弗兰克死后，他的太太写信给海莲说：“不怕你见笑，有时候我还会嫉妒你。”\r\n　　马克斯与科恩书店的店员们把海莲想像成一个“年轻，成熟，时髦”的女人，海莲老实告诉他们，自己“和百老汇的乞丐一样时髦”。就是这样一个执拗邋遢的女人，将骄蛮趣致的女性一面全都呈现给弗兰克。她会为一本欺世盗名的书而冲弗兰克发飙，将满腔怨气倾泄到打字机上，然后突然收起霸道，对着空气娇媚地笑了：“弗兰克，你是惟一了解我的人。”\r\n　　独身的海莲是自由的，而弗兰克眼前连这团自由的空气也没有。他只能努力让自己正常地度过二十年的光阴，只是在某一刻，他会注意到书店中驻足的一个女子，大概就是他想像中那个女人的模样？她说她来自美国，他的眼光一下子变得热切，却又不是，他好像习惯了这种失望和等待。电视机里在转播纽约元旦嘉年华的情景，广场上人多如织，他的眼睛在搜寻什么？\r\n\r\n\r\n\r\n---------------\r\n爱情的另外一种译法(3)\r\n---------------\r\n\r\n\r\n　　只是到了打烊的时候，书店里再没有别人，最柔软的情思才在这一刻展开，他会让自己的眼睛盯住某一处，款款道来。此刻，那个女人正躺在自己的床上，身罩破旧的睡衣，翻看着他抚摩过的书，点燃一支烟，不时发出一声声咳嗽。\r\n　　弗兰克死后，海莲来到查令十字街84号，站立的地方，正是他深情凝视的所在。\r\n　　经过了二十年岁月的打磨，他们的眼神都那么一致。\r\n　　海莲所推崇的英国玄学诗人、散文家多恩有一句话：“全体人类就是一本书。当一个人死亡，这并非有一章被从书中撕去，而是被翻译成一种更好的语言。”\r\n　　我想，当爱情以另外一种方式展现铺陈时，也并非被撕去，而是翻译成了一种更好的语言。上帝派来的那几个译者，名叫机缘，名叫责任，名叫蕴藉，名叫沉默。\r\n　　还有一位，名叫怀恋。\r\n----------------------------------------------------------------\r\n更多TXT电子书下载,请访问【雨枫电子书】http://txt.rain8.com  \r\n版权声明：本站所有电子书本着交流学习的目的\r\n均由网友提供并更新，\r\n仅供读者研究学习，不得用作商业用途，\r\n强烈建议广大读者购买正版图书！\r\n电子书版权为原出版社和作者所有，\r\n如侵犯了您的版权,请通过Email或电话告知,我们将立即删除！\r\n-----------------------------------------------------------------\r\n', null);
INSERT INTO `p_task` VALUES ('ffe84513-6fd2-49be-ba58-fae5e8405920', '测试8[卷11]', '历史', 'vip', '2afc37a8-3411-48e4-9f60-6b52259acb17', '2017-09-28 10:27:44', '2017-09-08', '0', '80', '0', '0', '1', '11', '　　马克斯与科恩书店\r\n　　伦敦中西二区查令十字街84号\r\n　　1951年2月2日\r\n　　海莲·汉芙小姐\r\n　　美国\r\n　　纽约州，纽约市28\r\n　　东九十五大街14号\r\n　　亲爱的汉芙小姐：\r\n　　很高兴听到您喜欢那本奎勒—库奇的书。《牛津英语散文选》此刻并无库存，我们将会为您留意。\r\n　　至于《罗杰·德·科弗利爵士正传》，我们手头上恰好有一本十八世纪的文集，除了包含该书中不少篇章之外，亦收入切斯特菲尔德与哥尔德斯密斯的文章。此书由奥斯汀·多布森精心编选，敝店仅标价一元十五分。我们已将书寄去给您。如果您想要更为完整的艾迪生与斯梯尔文集的话，请通知我，我将尽力为您寻找。\r\n　　敝店若不包括马克斯先生与科恩先生，共有职员六人。\r\n　　马克斯与科恩书店\r\n　　弗兰克·德尔敬上\r\n\r\n\r\n\r\n---------------\r\n1951年2月20日\r\n---------------\r\n\r\n\r\n　　米德尔塞克斯品内东厢\r\n　　1951年2月20日\r\n　　亲爱的海莲：\r\n　　做法不只一种，妈妈和我一致认为下面这个方法对你而言应该是最简单的：准备面粉一杯、鸡蛋一只、鲜奶半杯，撒入少许盐，在一个海碗里充分搅拌，直到变成浓稠的奶油状。搁进冰箱里摆几个钟头(所以一大早开始做，时间最刚好不过)。当你要把肉放进炉子时，挪个位子摆一个铁盘让它预热。在肉烤好前一个半钟头，浇一点儿肉汁在铁盘上，不用太多，浅浅的够铺满铁盘即可。记住！铁盘得烘烤得“非常热”才行。接着，把你先前准备好的布丁料全拨到铁盘上头，然后再放着继续烤。这样一来，肉和布丁就可以同时出炉上桌啦！\r\n　　对于从没见过的人，我实在不知道该怎么恰当地形容它。总之，一个大功告成的约克郡布丁应该会蓬松得高高的，表皮焦脆，当你切开它时，会发现里头其实是空心的。\r\n　　道格仍然随着皇家空军驻扎在诺福克。你寄来的圣诞节罐头，我们严格地实施管制囤积，以等他休假回家时再全家一起享用。亲爱的海莲，你一定不难想见，届时我们将会有个多么棒的庆祝餐会啊！不过，你实在不该这样子为我们破费的！\r\n　　该赶快把信寄出去了，这样你才来得及在布莱恩的生日晚宴上推出这道菜，成果如何一定要写信告诉我哟！\r\n　　爱你的\r\n　　塞西莉\r\n\r\n\r\n\r\n---------------\r\n1951年2月25日\r\n---------------\r\n\r\n\r\n　　纽约市\r\n　　东九十五大街14号\r\n　　1951年2月25日\r\n　　亲爱的塞西莉：\r\n　　约克郡布丁简直太棒了！因为我们这儿从没人见识过这玩意儿，我后来只好向别人形容成“一笼高高鼓起、松软细致、入口即化的特大号烤饼”！\r\n　　请别为我寄去的那些食品操心。我自己也觉得不可思议，那家海外邮购公司也不晓得是不是非营利机构，抑或是商品可以免税什么的吧？总之，他们的东西都便宜得很，我自己买的那只火鸡都还比寄给你们的那一大箱圣诞包裹贵哩。他们的确有一些价格比较高的商品，比如大块的烤肋排，或是一整只羊腿之类的。不过，即使是那些东西，也比跟这儿的肉贩买要便宜许多，如果真得那样，把我剁了也没法子寄东西给你们。现在正浏览着目录，我把它摊在地毯上，琢磨着两个旗鼓相当的商品组合：编号105的包裹(内含鸡蛋一打外加甜面饼一箱)和编号217B的包裹(内含鸡蛋两打、没有甜面饼)，我实在不甘心寄一打装的鸡蛋，让你们每人各分得两只能干吗？不过布莱恩跟我说，粉末干燥蛋吃起来味同嚼蜡，还真伤脑筋。\r\n　　有一位制作人刚打电话给我，说他蛮喜欢我写的剧本(还没喜欢到要把它搬上舞台的程度)。他正打算制作一出电视剧集，问我是否有兴趣编电视剧本，他漫不经心地说：“一集给两张！”搞了半天才弄明白他的意思是：每一集的稿费两百元。我原先为剧团修改剧本，一周的酬劳也才不过四十元！明天要去和他详谈，快祝我好运吧！\r\n　　祝福你\r\n　　海莲\r\n\r\n\r\n\r\n', '60547ea2-f301-4976-aa25-cc1750d3dfa8');

-- ----------------------------
-- Table structure for `p_user`
-- ----------------------------
DROP TABLE IF EXISTS `p_user`;
CREATE TABLE `p_user` (
  `userId` varchar(36) NOT NULL COMMENT '用户ID',
  `account` varchar(20) NOT NULL COMMENT '账号',
  `password` varchar(20) NOT NULL COMMENT '密码',
  `username` varchar(20) NOT NULL COMMENT '用户名',
  `email` varchar(20) DEFAULT NULL COMMENT '邮箱',
  `phoneNumber` varchar(20) DEFAULT NULL COMMENT '手机号',
  `role` varchar(20) NOT NULL DEFAULT '普通用户' COMMENT '角色',
  `hobby` varchar(20) DEFAULT NULL COMMENT '兴趣爱好',
  `createTime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `adoptNum` int(11) DEFAULT '0' COMMENT '被采纳的次数',
  `historyTrans` varchar(20) DEFAULT NULL COMMENT '历史翻译',
  `wordNum` int(11) DEFAULT '0' COMMENT '翻译字数',
  `transNum` int(11) DEFAULT '0' COMMENT '翻译次数',
  PRIMARY KEY (`userId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

